[2973] = {"Новый сверкающий плащ",nil, {"Принесите 10 радужных крыльев летучих хамелеонов Кригу Головорубу в Лагерь Мохаче."}},
[2974] = {"Зловещее открытие",nil, {"Принесите 20 рогов тауренов из племени Зловещего Тотема Кригу Головорубу в лагерь Мохаче."}},
[2975] = {"Огры Фераласа",nil, {"Убейте 10 огров из клана Гордунни, 10 огров-магов из клана Гордунни и 5 громил из клана Гордунни по просьбе Роки Орхан из Лагеря Мохаче."}},
