[3161] = {"Газ'ридианские украшения",nil, {"Марвон Клепальщик из Танариса поручил собрать 30 газ'рилловых украшений."}},
[3181] = {"Рог чудовища",nil, {"Отнести гигантский рог Маргола горному пехотинцу Щебеночке."}},
[3182] = {"Доказательство собственности",nil, {"Отнесите гигантский рог Маргола куратору Ториусу в Стальгорн."}},
[3201] = {"Наконец-то!",nil, {"Отнесите доказательство содеянного горному пехотинцу Щебеночке."}},
[3221] = {"Беседа с Ренферрелом",nil, {"Поговорите с аптекарем Ренферрелом в Гробнице."}},
[3261] = {"Джорн Небесный Провидец",nil, {"Поговорите с Джорном Заклинателем Небес в Лагере Таурахо."}},
[3281] = {"Похищенное серебро",nil, {"Принесите украденное серебро Газрогу в Перекресток."}},
[3301] = {"Муура Рунический Тотем",nil, {"Поговорите с Муурой Рунический Тотем в Гробнице."}},
[3321] = {"Не ее ли ищете?",nil, {"Дождитесь, пока Трентон закончит работу."}},
[3341] = {"Да сгинет Хладовей",nil, {"Эндрю Браунелл поручил вам убить Амненнара Хладовея и принести его череп."}},
[3361] = {"Беда беженца",nil, {"Принесите в Старую Наковальню ларец Феликса, сундук Феликса и ведро с винтиками и вручите все это Феликсу."}},
[3362] = {"Долина Кактусов",nil, {"Тран'рек из Прибамбасска просит вас убить 8 бугристых шипошкуров и 8 корнеплетов из долины Кактусов."}},
[3363] = {"Оскверненный песнецвет",nil, {"Вы нашли чахлое, пораженное какой-то порчей растение, похожее на песнецвет. Резкий, нездоровый запах исходит от него. Если вы хотите вернуть его к нормальному состоянию, то срочно необходимо им заняться."}},
[3364] = {"Жгучая бражка",nil, {"Отнесите жгучую бражку в Старую Наковальню и передайте ее Дарнану Мехорезу. Это надо сделать за пять минут, пока бражка не остыла!"}},
[3365] = {"Возвращение кружки",nil, {"Отнесите кружку хозяину – Нори по прозвищу Гордый Штрек."}},
[3366] = {"Светящийся осколок",nil, {"Отправляйтесь в Кабестан и выясните, какие секреты скрывает кристалл кошмаров."}},
[3367] = {"Камни Сунтары",nil, {"Помогите Дориусу вернуться в Стальгорн."}},
[3368] = {"Камни Сунтары",nil, {"Доставьте опаленное письмо Ториусу в Стальгорн. Его можно найти в Зале Исследователей."}},
[3369] = {"Кошмары",nil, {"Принесите осколок кошмара Хамуулу Руническому Тотему на Вершине Старейшин."}},
[3370] = {"Кошмары",nil, {"Принесите осколок кошмара Матренгилу Медвежьей Лапе в Дарнасс."}},
[3371] = {"Правосудие дворфов",nil, {"Вернитесь в Тлеющее ущелье и найдите археологов Дориуса."}},
[3372] = {"Отпусти их",nil, {"Найдите таинственный артефакт, охраняемый идолослужителями Сумеречного Молота в северо-западной области Тлеющего ущелья и возложите его на алтарь Сунтары в Шлаковой Яме."}},
[3373] = {"Сущность Эраникуса",nil, {"Поместите сущность Эраникуса в купель сущности в его логове в затонувшем храме."}},
[3374] = {"Сущность Эраникуса",nil, {"Принесите камень Клятвы драконьего выводка Изеры и скованную сущность Эраникуса на Болото Печали. Там вам придется сделать выбор: помогать Драконам Изеры или нет."}},
[3375] = {"Запасной фиал",nil, {"Принесите зелье маны, уголь и сладкий нектар Талвашу де Кисселю в Стальгорн. Получите запасной фиал виденья Талваша."}},
[3376] = {"Сразить Остроклыка!",nil, {"Принесите голову вождя Остроклыка Терновой Мантии Храбрецу Перу Ветра на плато Красного Облака."}},
[3377] = {"Молитва Элуне",nil, {"Выслушайте Зимаэля."}},
[3378] = {"Молитва Элуне",nil, {"Найдите молитву Элуне и принесите ее Астарии Ловцу Звезд в Дарнасс."}},
[3379] = {"Мастер по тенеткани",nil, {"Убейте банду похитителей тенешелка, хозяйничающую в Тлеющем ущелье. Нилит полагает, что их пятеро. Вернитесь к Нилит по выполнению задания."}},
[3380] = {"Затонувший храм",nil, {"Найдите Марвона Клепальщика в Танарисе."}},
[3381] = {"Встреча с господином",nil, {"Принесите 2 неповрежденных пера гиппогрифа Санату Лим-Йо в в Азшару, если хотите говорить с его господином."}},
[3382] = {"На абордаж!",nil, {"Защитите капитана Ванессу Белтис, ее команду и пассажиров \"Покорителя Горизонтов\" от нападения наг. Капитан Ванесса Белтис должна выжить."}},
[3385] = {"Черный рынок",nil, {"Убейте торговца Ковица и его прихвостня Лязгуна. Снимите с тела сумку и вернитесь к Нилит."}},
[3402] = {"Черный рынок",nil, {"Поговорите с Визгликом в Прибамбасске."}},
[3421] = {"Возвращение",nil, {"Теперь я отправлю тебя назад к лагерю Саната. nnНе обращай внимание на его манеры – он груб со всеми, кроме собственного господина. Возможно, однажды его заносчивость сыграет с ним злую шутку."}},
[3441] = {"Божественное воздаяние",nil, {"Выслушайте Каларана Ветрореза."}},
[3442] = {"Неугасимое пламя",nil, {"Принести 4 сердца пламени и 4 меры масла големов Каларану Ветрорезу."}},
[3443] = {"Древко факела",nil, {"Принесите 8 окованных торием кинжалов Каларану Ветрорезу."}},
[3444] = {"Круглый камень",nil, {"Заберите круглый камень из мастерской Марвона Клепальщика в Кабестане."}},
[3445] = {"Затонувший храм",nil, {"Найдите Марвона Клепальщика в Танарисе."}},
[3446] = {"Во глубине болот",nil, {"Найдите алтарь Хаккара в затонувшем храме на Болоте Печали."}},
[3447] = {"Тайна камня",nil, {"Отправляйтесь в затонувший храм и узнайте, что скрывается в круге статуй."}},
[3448] = {"Передача поручения",nil, {"Поговорите с Тимором в Стальгорне."}},
[3449] = {"Чародейские руны",nil, {"При помощи готовальни сделайте прорисовки руны Бет'Амары, руны Джин'йаэль, руны Маркри и руны Саэл'Хаи. Отправляйтесь на маленький островок близ южного полуострова Азшары и дайте сигнал пилоту Хиггсу Зажигателю забрать их."}},
[3450] = {"Легкая доставка",nil, {"Поговорите с Хиггсом Зажигателем в Стальгорне."}},
[3451] = {"Сигнал \"Взять на борт!\"",nil, {"Поговорите с Хиггсом Зажигателем в Стальгорне и получите от него сигнальное ружье."}},
[3452] = {"Защита пламени",nil, {"Найдите знак Рагнароса и принесите его Каларану Ветрорезу."}},
[3453] = {"Факел воздаяния",nil, {"Подождите, пока Каларан Ветрорез закончит факел воздаяния."}},
[3454] = {"Факел воздаяния",nil, {"Возьмите факел воздаяния."}},
[3461] = {"Возвращение к Тимору",nil, {"Принесите Тимору его готовальню назад в Стальгорн."}},
[3462] = {"Оруженосец Малтрейк",nil, {"Поговорите с оруженосцем Малтрейком."}},
[3463] = {"Задай им жару!",nil, {"Подожгите северную, южную, восточную и западную сторожевые башни с помощью факела воздаяния."}},
[3481] = {"Безделушки...",nil, {"Открыть сундук."}},
[3482] = {"<NYI> <TXT> The Pocked Black Box",nil, {"Отнесите ящик Крейвелу."}},
[3483] = {"Сигнал \"Взять на борт!\"",nil, {"Еще одно сигнальное ружье, да? Это можно.nnИ не забудьте – южная оконечность Азшары. Этот крохотный островок переделан в посадочную площадку. Вы ее не пропустите, если окажетесь в нужном месте."}},
[3501] = {"Счет идет на большие числа",nil, {"Принесите Кум-ише несовершенные фрагменты дренейского аметиста и получите награду за каждый из них."}},
[3502] = {"Дренейский хлам...",nil, {"Принесите Кум-ише несовершенные фрагменты дренейского аметиста. У любого вида монстров в Выжженных землях могут быть эти фрагменты."}},
[3503] = {"Встреча с господином",nil, {"Да, уж не вернуть тех дней, когда я мог требовать с желающих видеть моего господина все, что заблагорассудится. Теперь-то я отправляю к нему любой сброд – только попроси! nnКак меня это бесит! Но приказы не обсуждаются. Я могу потешить себя лишь глумясь над твоей безвкусной одеждой и жалким выражением лица. Впрочем, мне и это уже наскучило! nnИ необходимость трепаться тут с тобой меня бесит! Будешь телепортироваться к господину или нет?!"}},
[3504] = {"Предательница",nil, {"Отнесите запечатанное письмо Аг'тору Кровопалу в Азшару."}},
[3505] = {"Предательница",nil, {"Убейте 10 эльфов крови-землемеров и 10 эльфов крови-геодезистов, потом разыщите лагерь волхва Римтори. В лагере найдите что-нибудь важное для мага, из-за чего она могла бы ринуться в бой."}},
[3506] = {"Предательница",nil, {"Уничтожьте кристалл средоточия чар, связанный с волхвом Римтори, затем убейте ее. Отнесите голову Римтори Аг'тору Кроволапу в Азшаре."}},
[3507] = {"Предательница",nil, {"Поговорите с Белгромом Каменной Буловой в Оргриммаре."}},
[3508] = {"Минуя охрану",nil, {"Подождите, пока Лорамус завершит заклинание просветления."}},
[3509] = {"Имя Зверя",nil, {"Поговорите в Азшаре с лордом Арккоросом. Начните поиски у кромки воды, чтобы найти храм Арккоран."}},
[3510] = {"Имя Зверя",nil, {"Убейте Хетайру и принесите окровавленную голову Хетайры, разбитую голову Хетайры и покрытую синяками голову Хетайры лорду Аккоросу."}},
[3511] = {"Имя Зверя",nil, {"Отнесите состав для закалки Лорамусу Многоногу."}},
[3512] = {"Судя по словам Эраникуса...",nil, {"Отправляйтесь в восточную часть Зимних Ключей и поговорите с Умбрансом Ищущим Духов."}},
[3513] = {"Свиток с рунами",nil, {"Отнесите свиток с рунами в северную сторожевую башню Степей."}},
[3514] = {"Мощь Орды",nil, {"Убейте предводительницу Пещеры Когтей, Ринтариэль Хранительницу Ключей."}},
[3517] = {"Тайненькое знаньице",nil, {"Найдите таблички Бет'Амары, Джин'яэль, Маркри и Саэль'Хай и принесите их Джедиге в Азшару."}},
[3518] = {"Доставка Магате",nil, {"Отнесите табличку Бет'Амары Магате в Громовой Утес."}},
[3519] = {"Друг в беде",nil, {"Поговорите с Диранией Серебряным Сиянием в Тенистой долине."}},
[3520] = {"Духи крикунов",nil, {"Поймайте души трех крикунов из Фераласа и возвращайтесь к Йе'кинье в Порт Картеля."}},
[3521] = {"Противоядие для Иверрона",nil, {"Соберите 7 гиацинтовых грибов, 4 лунные лилии и 1 порцию лимфы чащобного тенетника и принесите Дирании Серебряное Сияние в Тенистую долину."}},
[3522] = {"Противоядие для Иверрона",nil, {"Отнесите противоядие Иверрону, прежде чем оно потеряет свои целебные свойства. Иверрона можно найти около пещеры, что к северу отсюда."}},
[3523] = {"Плеть в холмах",nil, {"Если вы хотите помочь Белнистразу, поговорите с ним еще раз и верните ему Камень клятвы, который он дал вам."}},
[3524] = {"Останки на берегу",nil, {"Соберите кости морской твари, выброшенной на берег к югу от Аубердина, и принесите их Гвеннид Блай'Леггонд в Аубердин."}},
[3525] = {"Уничтожить идола",nil, {"Сопроводите Белнистраза к идолу свинобразов на Курганах Иглошкурых. nnЗащищайте Белнистраза, пока он будет проводить ритуал, чтобы разрушить идола."}},
[3526] = {"Гоблинское инженерное дело",nil, {"Если вы хотите начать обучение гоблинскому инженерному делу, отнесите cправочник по инженерным дисциплинам Никсу Шепружину в Прибамбасск."}},
[3527] = {"Пророчество Мошару",nil, {"Принесите первую и вторую таблички Мошару Йе'кинье в Танарис."}},
[3528] = {"Бог Хаккар",nil, {"Отнесите заполненное яйцо Хаккара Йе'кинье в Танарис."}},
[3541] = {"Доставка Джес'римону",nil, {"Отнесите табличку Джин'яэль Джес'римону в Оргриммар."}},
[3542] = {"Доставка Андрону Ганту",nil, {"Отнесите табличку Маркри Андрону Ганту в Подгород."}},
[3561] = {"Доставка верховному магу Ксилему",nil, {"Отнесите табличку Саэль'Хай верховному магу Ксилему в Азшару."}},
[3562] = {"Плата Магаты Джедиге",nil, {"Отнесите записку Магаты Джедиге в Азшару."}},
[3563] = {"Плата Джес'римона Джедиге",nil, {"Доставьте записку Джес'римона Джедиге в Азшару."}},
[3564] = {"Плата Андрона Джедиге",nil, {"Доставьте записку Андрона Джедиге в Азшару."}},
[3565] = {"Плата Ксилема Джедиге",nil, {"Доставь записку Ксилема Джедиге в Азшару."}},
[3566] = {"Восстань, Обсидион!",nil, {"Убейте Латорика Черного и Обсидиона и принесите голову Латорика и сердце Обсидиона Ториусу в Стальгорн."}},
[3567] = {"Умный в гору не пойдет...",nil, {"Хочешь посмотреть, какой вид открывается во-он оттуда, с тех Двух Колоссов? Могу тебя туда отправить, |3-6(нежить).nnТолько подумай заранее, как потом будешь спускаться. Это путешествие в один конец!"}},
[3568] = {"Разносчик порчи",nil, {"Наполните все 4 пустых фиала из приливных заводей в руинах Эльдарата и возвращайтесь к химику Карабосу."}},
[3569] = {"Разносчик порчи",nil, {"Дайте эликсир Карабоса Терсе Песне Ветра в Подгороде."}},
[3570] = {"Разносчик порчи",nil, {"Я и не думал, что эликсир сработает так быстро. Спасибо, Томпсон. Займусь-ка работой над остальными образцами. Уверен, вскоре леди Сильвана и Отрекшиеся будут их активно использовать."}},
[3601] = {"Оскорбленный Ким'джаель",nil, {"Обыщите Талассийское поселение и найдите компас, прицел, плюшевого цыпленка и центробежный растягиватель Ким'джаеля. Верните все это Ким'джаелю в Азшару."}},
[3602] = {"Азшарит",nil, {"Найдите в южной Азшаре двадцать кристаллов азшарита. Вы можете использовать Гончих Скверны, которые помогут вам \"вынюхивать\" местонахождение хорошо сокрытых залежей. nnКогда задача будет завершена, возвращайтесь к Лорамусу Многоногу."}},
[3621] = {"Создание отравы Скверны",nil, {"Отнести груз Гальвану Древнему в Тернистую долину."}},
[3625] = {"Зачарованное азшаритовое оружие, отравленное Скверной",nil, {"Подождите, пока Гальван закончит отковывать оружие."}},
[3626] = {"Возвращение в Выжженные земли",nil, {"Вернитесь к павшему герою Орды."}},
[3627] = {"Воссоединить расколотый амулет",nil, {"Убейте Грола Разрушителя, леди Севину и верховного мага Аллистария. С их трупов заберите Амулет Грола, Амулет Севины и Амулет Аллистария.nnПомните о том, что нужно использовать зачарованное азшаритовое оружие, отравленное Скверной, чтобы ослабить троицу! Их нельзя убить иначе – но используйте оружие разумно..."}},
[3628] = {"Ты – Рах'лих, демон!",nil, {"Уничтожьте Разелиха Осквернителя и вернитесь к павшему герою Орды с отрубленным рогом Осквернителя. Вам также понадобится вернуть оберег Осквернителя, чтобы павший герой мог уничтожить его, дабы оберег не попал к тому, кто использует его во зло."}},
[3629] = {"Гоблинское инженерное дело",nil, {"Если вы хотите начать обучение гоблинскому инженерному делу, отнесите cправочник по инженерным дисциплинам Никсу Шепружину в Прибамбасск."}},
[3630] = {"Гномское инженерное дело",nil, {"Если вы хотите узнать больше о гномском инженерном деле, отнесите справочник по инженерным дисциплинам мехмастеру Замыкальцу в Стальгорн."}},
[3631] = {"Призывание коня Скверны",nil, {"Поговорите в Кабестане со Страхадом Фарсаном."}},
[3632] = {"Гномское инженерное дело",nil, {"Если вы хотите узнать больше о гномском инженерном деле, отнесите справочник по инженерным дисциплинам мехмастеру Замыкальцу в Стальгорн."}},
[3633] = {"Гоблинское инженерное дело",nil, {"Если вы хотите начать обучение гоблинскому инженерному делу, отнесите cправочник по инженерным дисциплинам Никсу Шепружину в Прибамбасск."}},
[3634] = {"Гномское инженерное дело",nil, {"Если вы хотите узнать больше о гномском инженерном деле, отнесите справочник по инженерным дисциплинам мехмастеру Замыкальцу в Стальгорн."}},
[3635] = {"Гномское инженерное дело",nil, {"Если вы хотите узнать побольше о гномском инженерном деле, возьмите справочник по инженерным дисциплинам и отнесите его Карлсу Пропеллерзу в Пиратскую Бухту."}},
[3636] = {"Нести свет",nil, {"Убейте Амненнара Хладовея на Курганах Иглошкурых."}},
[3637] = {"Гномское инженерное дело",nil, {"Если вы хотите узнать побольше о гномском инженерном деле, возьмите справочник по инженерным дисциплинам и отнесите его Карлсу Пропеллерзу в Пиратскую Бухту."}},
[3638] = {"Договор о неразглашении",nil, {"Если вы согласны заниматься гоблинским инженерным делом, щелкните правой кнопкой мыши по Договору о неразглашении и еще раз обратитесь к Никсу Шепружину в Прибамбасске."}},
[3639] = {"Покажи свою работу",nil, {"Принесите 20 больших железных бомб, 20 шашек плотного динамита и 5 взрывоопасных овец Никсу Шепружину в Прибамбасск."}},
[3640] = {"Договор о неразглашении",nil, {"Щелкните ПКМ по Договору о неразглашении и еще раз обратитесь к мехмастеру Замыкальцу в Стальгорне."}},
[3641] = {"Покажи свою работу",nil, {"Принесите 6 мифриловых трубок, точный прицел и 2 усовершенствованных манекена-мишени мехмастеру Замыкальцу в Стальгорн."}},
[3642] = {"Договор о неразглашении",nil, {"Если вы согласны заниматься гномским инженерным делом, щелкните ПКМ по Договору о неразглашении и еще раз обратитесь к Карлсу Пропеллерзу в Пиратской Бухте."}},
[3643] = {"Покажи свою работу",nil, {"Принесите 6 мифриловых трубок, точный прицел и 2 усовершенствованных манекена-мишени Карлсу Пропеллерзу в Пиратскую Бухту."}},
[3644] = {"Продление членского билета",nil, {"Чтобы продлить членский билет, тебе придется заплатить пошлину в размере двух золотых."}},
[3645] = {"Продление членского билета",nil, {"Чтобы продлить членский билет, тебе придется заплатить пошлину в размере двух золотых."}},
[3646] = {"Продление членского билета",nil, {"Чтобы продлить членский билет, тебе придется заплатить пошлину в размере двух золотых."}},
[3647] = {"Продление членского билета",nil, {"Чтобы продлить членский билет, вам придется заплатить два золотых в качестве пошлины за продление."}},
[3661] = {"Любимцы Элуны?",nil, {"Собрерите во Внутренних землях 15 перьев диких совухов и отнесите их Эреласу Янтарному Небу в деревню Рут'теран."}},
[3681] = {"Фолиант Божественности",nil, {"Разыщите в Штормграде Даториана Ралла и поговорите с ним."}},
[3701] = {"Дымящиеся руины Тауриссана",nil, {"Пойдите в руины Тауриссана в Пылающих степях и добудьте информацию из таурисанских реликвий. Вернитесь к королевскому историку Аркессону."}},
[3702] = {"Дымящиеся руины Тауриссана",nil, {"Узнайте от королевского историка Аркессона историю Тауриссана."}},
[3721] = {"Собственный ККХ",nil, {"Тебе удалось выручить всех трех моих роботов серии ООХ! Ты сделал все, что требовалось и даже больше, поэтому я хочу особым образом отблагодарить тебя за помощь. Это миниатюрная версия того же робота, с которым ты провел так много времени и полюбил, ООХ!nnОбычно я никому не дарю такие опасные устройства, но ты оказал мне неоценимую помощь. Кроме того у этой тварюшки отключены боевые механизмы и устройство самонаведения.nnА в остальном это полноценный ООХ!"}},
[3741] = {"Ожерелье Хилари",nil, {"Найдите ожерелье Хилари и верните его Хилари в Приозерье."}},
[3761] = {"Почва Ун'Горо",nil, {"Принесите 20 образцов земли из Кратера Ун'Горо Геду на Вершину Старейшин Громового Утеса."}},
[3762] = {"Помощь верховному друиду Руническому Тотему",nil, {"Поговорите с верховным друидом Хамуулом Руническим Тотемом на Уступе Древних Громового Утеса."}},
[3763] = {"Помощь верховному друиду Оленьему Шлему",nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнассе."}},
[3764] = {"Почва Ун'Горо",nil, {"Принесите 20 образцов земли из кратера Ун'Горо Дженалю в дарнасский анклав Кенария."}},
[3765] = {"Проблема за морем",nil, {"Отправляйтесь в Аубердин к Гершалу Шепоту Ночи."}},
[3781] = {"Изучение рассветницы",nil, {"Отнесите расписку верховного друида в получении семян Матренгилу Медвежьей Лапе в дарнасский анклав Кенария."}},
[3782] = {"Изучение рассветницы",nil, {"Отнесите расписку в получении семян Башане Рунический Тотем в Громовой Утес."}},
[3783] = {"Йети где-то рядом…",nil, {"Соберите 10 толстых кусков меха йети для Уми Смехотреп из Круговзора."}},
[3784] = {"Помощь верховному друиду Руническому Тотему",nil, {"Поговорите с верховным друидом Хамуулом Руническим Тотемом на Уступе Древних Громового Утеса."}},
[3785] = {"Изучение рассветницы",nil, {"При помощи сумы вечного цветения, пакетика семян Тарлендриса и двух порций земли Ун'Горо попытайтесь прорастить рассветницу.nnПринесите 10 рассветниц Матренгилу Медвежьей Лапе в Дарнасс."}},
[3786] = {"Изучение рассветницы",nil, {"При помощи сумы вечного цветения, пакетика семян Тарлендриса и двух порций земли Ун'Горо попытайтесь прорастить рассветницу.nnПринесите 10 рассветниц Башане Рунический Тотем в Громовой Утес."}},
[3787] = {"Кинтис Пламень",nil, {"Поговорите с друидом по имени Квинтис Ночной Костер в Крепости Оперенной Луны."}},
[3788] = {"Кинтис Пламень",nil, {"Поговорите с друидом по имени Квинтис Ночной Костер в Крепости Оперенной Луны."}},
[3789] = {"Помощь верховному друиду Оленьему Шлему",nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнассе."}},
[3790] = {"Помощь верховному друиду Оленьему Шлему",nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнассе."}},
[3791] = {"Тайна рассветницы",nil, {"Принесите 10 рассветниц друиду по имени Квинтис Ночной Костер в Крепости Оперенной Луны."}},
[3792] = {"Рассветница для Крепости Оперенной Луны",nil, {"А, здравствуйте, Серыйаскет! Мои исследования продвигаются неплохо, но, если у вас найдется еще пять рассветниц, я бы с удовольствием их у вас приобрел. Хотя, конечно, с удовольствием – это только фигура речи: чем больше я имею с нею дело, тем сильнее мне не по себе. И тем не менее я не намерен отказываться от дальнейших экспериментов!"}},
[3801] = {"Наследие Черного Железа",nil, {"Чтобы получить ключ от города, поговорите с Франклорном Искусником."}},
[3802] = {"Наследие Черного Железа",nil, {"Убейте Финия Темностроя и добудьте великий молот, Железный Друг. Отнесите Железного Друга в святилище Тауриссана и вложите его в руки статуи Франклорна Искусника."}},
[3803] = {"Рассветница для Дарнасса",nil, {"Верховному друиду постоянно не хватает рассветницы для проведения исследований Круга Кенария. Мы принимаем ее партиями по десять пучков и в награду даем несколько полезных вещей. Когда соберешь десять пучков, сообщи мне, и я достойно награжу тебя."}},
[3804] = {"Рассветница для Громового Утеса",nil, {"Верховный друид постоянно ищет дополнительные источники рассветницы для исследовательского проекта Круга Кенария в Кратере Ун'Горо. Мы берем их партиями по десять штук и в награду даем несколько полезных вещей. Когда наберешь десять штук, сообщи мне, и я достойно награжу тебя."}},
[3821] = {"Скала Молота Ужаса",nil, {"Отправляйтесь в Пылающие степи и выясните, что сталось с Ша'ни Гордым Клыком."}},
[3822] = {"Кром'Грул",nil, {"Заберите ноздревое кольцо Ша'ни у Кром'Грула и верните его Тал'траку из Каргата. Обыщите различные пещеры и рудники скалы Молота Ужаса."}},
[3823] = {"Угасание Огненного Чрева",nil, {"Убейте 15 огров-магов, 7 огров и 7 громил из клана Огненного Чрева. По выполнении задания вернитесь к Орелиусу."}},
[3824] = {"Гор'теш Жестокий",nil, {"Найдите Гор'теша на западе от скалы Молота Ужаса. Убейте Гор'теша и отнесите его голову Орелиусу в Пылающие степи."}},
[3825] = {"Голова огра на палочке = вечеринка",nil, {"Отнесите голову Гор'теша на вершину скалы Молота Ужаса. Поищите кучу рыхлой земли и воткните в нее пику."}},
[3841] = {"Бездомный сиротка",nil, {"Поговорите с Квентином в Тысяче Игл."}},
[3842] = {"Инкубатор",nil, {"Принесите 2 эликсира стойкости Квентину в Тысячу Игл."}},
[3843] = {"Новый член семьи",nil, {"Отнесите хрупкое яйцо летучего хамелеона во Внутренние земли и поговорите с Агнаром Зверопасом. У вас есть на это всего 1 час. В противном случае детеныш в яйце умрет."}},
[3844] = {"Тайна, покрытая мраком",nil, {"Осмотритесь и попытайтесь выяснить, кому мог бы принадлежать разбитый плот."}},
[3845] = {"Тайна, покрытая мраком",nil, {"Изучите найденное в сумке и верните владельцу."}},
[3861] = {"Куд-кудаааа!",nil, {"Найдите немного цыплячьего корма и вернитесь к вашей курочке-подружке. Когда найдете ее, /поклонитесь ей прежде, чем накормить ее специальным цыплячьим кормом."}},
[3881] = {"Спасение экспедиции",nil, {"Принесите ящик с провизией и исследовательское оборудование Виллидену Маршалу в Кратер Ун'Горо."}},
[3882] = {"Игра в кости",nil, {"Принесите 8 костей динозавра Искру Пустокопу в Кратер Ун'Горо."}},
[3883] = {"Экология чужих",nil, {"Воспользуйтесь фиалом для образцов, чтобы взять образец материала стены улья из одного из инкубаторов Гориши в Кратере Ун'Горо. Ищите в ульях комнаты со свисающими гроздьями личинок. nnПринесите образец материала стены улья Холании Маршал в Кратер Ун'Горо."}},
[3884] = {"Дневник Вилидена",nil, {"Верните дневник Виллидену Маршалу, который находится где-то в Кратере Ун'Горо."}},
[3901] = {"Игрушки-погремушки",nil, {"Убейте 12 скелетов-костетрясов, а затем вернитесь к жрецу Тьмы Сарвису в Могильник."}},
[3902] = {"Поиски старой одежды",nil, {"Обыщите Могильник и его окрестности и найдите 6 ящиков с добычей и передайте их стражу смерти Салтену."}},
[3903] = {"Милли Осворт",nil, {"Поговорите с Милли Осворт."}},
[3904] = {"Урожай Милли",nil, {"Принесите 8 корзин с виноградом Милли Осворт в Аббатство Североземья."}},
[3905] = {"Уведомление о поставке винограда",nil, {"Отнесите уведомление о поставке винограда брату Нилзу в Аббатство Североземья."}},
[3906] = {"Дисгармония пламени",nil, {"Отправьтесь в карьер у Черной горы и уничтожьте Подчинителя Пирона. По выполнению вернитесь к Громосерду."}},
[3907] = {"Дисгармония пламени",nil, {"Ступайте в глубины Черной горы и выследите лорда Опалителя. Убейте его и принесите Громосерду все, что может дать какую-то информацию."}},
[3908] = {"Тайна, покрытая мраком",nil, {"Отнесите меч Линкена Донове Снегоноре."}},
[3909] = {"Эликсир Видере",nil, {"Найдите Грегана Пивоплюя на севере Фераласа. Выясните у него, как получить эликсир Видере, и вернитесь к Донове Снегоноре."}},
[3911] = {"Последняя Стихия",nil, {"Отправляйтесь в Глубины Черной горы и добудьте 10 мер сущности Стихий. Стоит начать поиски с големов и их создателей. Вивиан Лаграв также бормотала что-то про элементалей."}},
[3912] = {"Встречаемся на кладбище",nil, {"Отправляйтесь на кладбище на окраине Прибамбасска и выпейте там эликсир Видере. Далее, следуя инструкциям Доновы, найдите Гаэриана."}},
[3913] = {"Замогильная история",nil, {"Воскресните, найдите могильный камень, о котором говорил Гаэриан."}},
[3914] = {"Меч Линкена",nil, {"Доставьте превосходный меч Линкена Линкену в Кратер Ун'Горо."}},
[3921] = {"Венаки Бенике",nil, {"Отнесите сломанный самофланж Венаки Бенике."}},
[3922] = {"Металлические заготовки",nil, {"Принесите 15 металлических заготовок для деталей Венаки Бенике в Степи."}},
[3923] = {"Рилли Грязегоб",nil, {"Отнесите разбитый и сломанный самофланж Рилли Грязегобу в Оргриммар."}},
[3924] = {"Руководство по самофланжу",nil, {"Отнесите руководство по самофланжу Рилли Грязегобу в Оргриммар."}},
[3941] = {"Помощь гномов",nil, {"Найдите Джей Ди Колли в Укрытии Маршалла."}},
[3942] = {"Память Линкена",nil, {"Найдите Эридану Грусть Ветра на юге Оскверненного леса."}},
[3961] = {"Приключения Линкена",nil, {"Отнесите серебряный тотем Аквамонтоса Линкену в Укрытие Маршалла."}},
[3962] = {"Один в поле не воин",nil, {"Отправляйтесь к Вулкану Огненного Венца, к югу от Укрытия Маршалла. nnНайдите Яропламеня и разрушьте его защитную ауру с помощью тотема. Когда дух будет сражен, заберите из пещеры Золотое пламя."}},
[3981] = {"Командир Гор'шак",nil, {"Найдите командира Гор'шака в Глубинах Черной горы. nnСудя по рисунку в записке, искать следует в темнице где-то вроде того."}},
[3982] = {"Что происходит?",nil, {"Защитите Горшака."}},
[4001] = {"Что происходит?",nil, {"Поговорите с дворфом по имени Каран Могучий Молот и соберите информацию о похищении принцессы Мойры Бронзобород. Передайте информацию Траллу в Оргриммар.nnКарана держат в темнице напротив."}},
[4002] = {"Восточные королевства",nil, {"Поговорите с Траллом."}},
[4003] = {"Спасение принцессы",nil, {"Убейте императора Даграна Тауриссана и освободите принцессу Мойру Бронзобород от его черного заклятия."}},
[4004] = {"Спасенная принцесса",nil, {"Вернитесь к Траллу."}},
[4005] = {"Аквамонтос",nil, {"Отнесите книгу Аквора, серебристые когти и сердце Железнолесья в Танарис. nnВоспользуйтесь книгой для призыва Аквамонтоса. nnПо получении тотема, отнести его Джей Ди Колли в Укрытие Маршалла."}},
[4021] = {"Контратака!",nil, {"Принесите кусок знамени Кром'зара Регтару Вратам Смерти. Его можно найти на западе от Перекрестка."}},
[4022] = {"Вкус пламени",nil, {"Покажите Цирусу Раскаивателю сброшенную кожу черного дракона, полученную от Каларана Ветрореза."}},
[4023] = {"Вкус пламени",nil, {"Покажите Цирусу Раскаивателю доказательство своих деяний.nnВозникает чувство, что Цирус заранее сомневается в ваших качествах."}},
[4024] = {"Вкус пламени",nil, {"Отправьтесь в глубины Черной горы и убейте Бейл'Гара.nnИзвестно лишь то, что великан живет в глубинах. Воспользуйтесь измененной сброшенной шкурой черного дракона и возьмите из останков Бейл'Гара его огненную сущность.nnОтнесите сущность Цирусу Раскаивателю."}},
[4041] = {"Эликсир Видере",nil, {"Эликсир Видере? Ну да, смогу, конечно, только принеси сначала мне эвокорень.nnВ травках и алхимии я разбираюсь неплохо... Вот послушай, как я однажды трансмутировал в золото сыр, которым собирался пообедать... Эй, ты куда?"}},
[4061] = {"Восстание машин",nil, {"Отправляйтесь в Пылающие степи и соберите 10 осколков элементалей для верховной жрицы Теодоры Мальвадании. nnТеодора упоминала, что осколки можно найти у элементалей и големов."}},
[4062] = {"Восстание машин",nil, {"Отнесите образец осколка элементаля Лотвилю Вериатусу. nnТеодора упоминала, что его лагерь находится на востоке."}},
[4063] = {"Восстание машин",nil, {"Найдите и убейте повелителя големов Аргелмаха. Принесите его голову Лотвилю. Также соберите 10 невредимых ядер стихий с беспощадных големов и созданий-завоевателей, охраняющих Аргелмаха. Информация передана посредством телепатии."}},
[4081] = {"УНИЧТОЖИТЬ НА МЕСТЕ: Дворфы Черного Железа",nil, {"Отправляйтесь в Глубины Черной горы и уничтожьте подлых агрессоров! nnПо приказу полководца Клинозуба уничтожьте 15 охранников, 10 надсмотрщиков и 5 пехотинцев из клана Ярости Горна. Вернитесь сразу по выполнении задания."}},
[4082] = {"УНИЧТОЖИТЬ НА МЕСТЕ: Высокопоставленные чины Черного Железа",nil, {"Отправляйтесь в Глубины Черной горы и уничтожьте подлых агрессоров! nnПо приказу полководца Клинозуба уничтожьте 10 медиков, 10 солдат и 10 офицеров из клана Ярости Горна. Вернитесь сразу по выполнении задания."}},
[4083] = {"Призрачный кубок",nil, {"Призрачный кубок парит в воздухе, медленно поднимаясь и опускаясь... как будто следуя ритмам биения умирающего сердца."}},
[4084] = {"Серебряное сердце",nil, {"Добудьте 11 серебристых когтей и 1 сердце Железнолесья для Эриданы Грусти Ветра."}},
[4101] = {"Очищение Оскверненного леса",nil, {"Принесите 15 кровавых янтарей Аратандис Серебряному Небу в Оскверненный лес."}},
[4102] = {"Очищение Оскверненного леса",nil, {"Принесите 15 кровавых янтарей Майбессе Речному Ветру в Оскверненный лес."}},
[4103] = {"Лекарство из охотничьих трофеев",nil, {"Тот, кто имеет при себе Маяк Кенария, может оскверненные осколки души затронутых порчей животных, убитых ради вящего блага. Я измельчаю эти осколки и делаю из них реактив, который идет на изготовление кенарийского бальзама исцеления растений. Мы используем это средство для исцеления пораженных порчей растений.nnЗа эти осколки я дам тебе немного уже готового кенарийского бальзама исцеления растений."}},
[4104] = {"Лекарство из минералов",nil, {"Если во время поиска ископаемых в Оскверненном лесу у тебя будет с собой Маяк Кенария, ты сможешь найти порченый купорос, проникающий в жилы металлов. Я знаю, как очищать этот купорос и превращать его в реактив, который идет на изготовление кенарийского бальзама исцеления растений. Затем этим бальзамом можно исцелять пораженные растения.nnЕсли ты принесешь мне порченого купороса, я дам тебе уже готового кенарийского бальзама исцеления растений."}},
[4105] = {"Лекарство из оскверненных трав",nil, {"Маяк Кенария позволяет опытному травнику замечать коварный зародыш Скверны – мерзостный результат поразившей Оскверненный лес порчи. В результате долгих моих трудов здесь мне удалось научиться превращать зародыш Скверны в очищающее вещество... которое мы используем для приготовления так называемого кенарийского бальзама исцеления растений.nnВо время сбора растений в Оскверненном лесу ты сможешь замечать зародыш Скверны, пока на тебе лежит заклятие \"Маяк Кенария\". За то, что ты мне принесешь, я дам тебе уже готового бальзама Кенария."}},
[4106] = {"Лекарство из оскверненных шкур",nil, {"Маяк Кенария позволяет опытному специалисту по снятию шкур находить куски пораженных порчей шкур среди добытого в Оскверненном лесу добра. Я перерабатываю эти вроде бы бесполезные обрывки в густую пасту, которая ускоряет действие кенарийского бальзама исцеления растений. Когда ты будешь свежевать туши убитых в Оскверненном лесу животных, позаботься, чтобы на тебе лежало заклятие Маяка Кенария!nnКогда соберешь порченые клочки, принеси их мне, и я дам тебе немного готового кенарийского бальзама исцеления растений."}},
[4107] = {"Лекарство из распыленных предметов",nil, {"Сила магического предмета соответствует силе эссенций, получающихся в результате его распыления. Мной изобретен способ переработки сырой субстанции, используемой зачаровывателями, в реактив, пригодный для создания кенарийского бальзама исцеления растений. Для этого тебе не нужен Маяк Кенария. Подойдет любой предмет, с которого сняты чары, и который выделяет простую субстанцию Пустоты.nnМне нужна всего одна порция простой субстанции Пустоты, а за это я тебе дам бальзама исцеления Кенария. Эта субстанция чрезвычайно сильна – даже малого количества хватит надолго!"}},
[4108] = {"Лекарство из охотничьих трофеев",nil, {"Обладание Маяком Кенария позволяет увидеть оскверненные осколки души затронутых порчей животных, убитых ради вящего блага. Я измельчаю эти осколки и делаю из них реактив, который идет на изготовление кенарийского бальзама исцеления растений. Мы используем это средство для исцеления пораженных порчей растений.nnЗа эти осколки я дам тебе немного уже готового кенарийского бальзама исцеления растений."}},
[4109] = {"Лекарство из минералов",nil, {"Если во время поиска ископаемых в Оскверненном лесу у тебя будет с собой Маяк Кенария, ты сможешь найти порченый купорос, проникающий в жилы металлов. Я знаю, как очищать этот купорос и превращать его в реактив, который идет на изготовление кенарийского бальзама исцеления растений. Затем этим бальзамом можно исцелять пораженные растения.nnЕсли ты принесешь мне порченого купороса, я дам тебе уже готового кенарийского бальзама исцеления растений."}},
