[4488] = {"Призывание коня Скверны",nil, {"Поговорите в Кабестане со Страхадом Фарсаном."}},
[4489] = {"Призывание коня Скверны",nil, {"Поговорите в Кабестане со Страхадом Фарсаном."}},
[4490] = {"Призывание коня Скверны",nil, {"Поговорите в Кабестане со Страхадом Фарсаном, чтобы получить способность призывать коня Скверны."}},
[4491] = {"Дружеская помощь",nil, {"Отведите Ринго к Муче Кусора в Укрытие Маршалла. nnЕсли Ринго потеряет сознание, плесните на него воды из фляги Спрэггл, чтобы привести его в чувство."}},
[4492] = {"Потерялся!",nil, {"Найдите Ринго на Вулкане Огненного Венца."}},
[4493] = {"Нашествие силитидов",nil, {"Поговорите с алхимиком Пестычинсом в Прибамбасске."}},
[4494] = {"Нашествие силитидов",nil, {"Поговорите с алхимиком Пестычинсом в Прибамбасске."}},
[4495] = {"Добрый друг",nil, {"Найдите Иверрона возле пещеры на севере."}},
[4496] = {"Путаница в джунглях",nil, {"Принесите ароматическую железу Гориши и 5 образцов почвы из Кратера Ун'Горо алхимику Пестычинсу в Прибамбасск."}},
[4501] = {"Осторожно, злой жутедактиль",nil, {"Убейте 10 жутедактилей и 15 бесноватых жутедактилей, затем поговорите со Мучей Кусора в Укрытии Маршалла."}},
[4502] = {"Вулканическая активность",nil, {"Добудьте 9 образцов пепла Ун'Горо из элементалей огня, что водятся вокруг вулкана в Кратере Ун'Горо, и доставьте их Лив Быстрочин в Кабестан."}},
[4503] = {"Крылолет Шиззла",nil, {"Принесите 8 чешуй перепончатого деметродона 8 чешуй перепончатого жутедактиля Шиззлу в Укрытие Маршалла."}},
[4504] = {"Суперлипучка",nil, {"Отнесите 12 образцов сверхлипкой смолы Тран'реку в Прибамбасск."}},
[4505] = {"Источник порчи",nil, {"Принесите образец оскверненной воды из лунного колодца сатиров из племени Нефритового Пламени Винне Хеззард на заставу Отравленной Крови."}},
[4506] = {"Зараженные саблезубы",nil, {"Отнесите котенка Винны к оскверненному лунному колодцу, выпустите его, затем снова принесите Винне.nnКак только вернетесь к Винне, щелкните мышкой по котенку, чтобы выпустить его."}},
[4507] = {"Пешка берет королеву",nil, {"Призовите и сразите матку улья Гориши.nnПринесите мозг матки Гориши алхимику Пестычинсу в Прибамбасск."}},
[4508] = {"Затишье перед бурей",nil, {"Отнесите отчет Пестычинса о ситуации в Ун'Горо Грацине Силе Духа в Дарнасс."}},
[4509] = {"Затишье перед бурей",nil, {"Отнесите отчет Пестычинса о ситуации в Ун'Горо Зилзибину Барабанному Бою в Оргриммар."}},
[4510] = {"Затишье перед бурей",nil, {"Отнесите банковскую расписку Идриане в банк Дарнасса."}},
[4511] = {"Затишье перед бурей",nil, {"Отнесите банковскую расписку Карусу в банк Оргриммара."}},
[4512] = {"Долгий путь слизнюка",nil, {"Принесите 6 банок с проклятыми слизнюками и 6 банок с нечистыми слизнюками Ларису Обалдую в Стальгорн."}},
[4513] = {"Долгий путь слизнюка",nil, {"Принесите 10 банок с чистыми образцами Ларису Обалдую в Стальгорн.nnЕсли будет трудно их найти, возможно, стоит попытаться добиться от Лариса подсказки."}},
[4521] = {"Одичавшие стражи",nil, {"Убейте для Трулла Баловня Судьбы 15 бешеных совухов и 15 лохматых совухов."}},
[4542] = {"Письмо на Заставу Вольного Ветра",nil, {"Отнесите срочное послание дозорному утесов Долгорогу на Заставу Вольного Ветра."}},
[4561] = {"Образцы на пробу из кратера Ан-Горо",nil, {"Вы наполняете слизью, добытой в Кратере Ун'Горо, различные колбы и мензурки, стоящие на столе. Все начинает бурлить и брызгаться, а слизь меняет цвет. Потом процесс замедляется, жидкость перестает кипеть, и вы видете маленькую чашечку Петри с пробой измененной слизи и какие-то ошметки."}},
[4581] = {"Кайнет Штиль",nil, {"Отнесите записку Шиндрелл Кайнету Штилю в Лесную Песнь."}},
[4601] = {"Чистер 5200!",nil, {"Опустите покрытый грязью предмет и три серебряные монеты в Чистер 5200."}},
[4602] = {"Чистер 5200!",nil, {"Опустите покрытый грязью предмет и три серебряные монеты в Чистер 5200."}},
[4603] = {"Работа для Чистера",nil, {"Приемник Чистера 5200 пуст. Чтобы машина заработала, требуется загрязненный предмет и три серебряные монетки."}},
[4604] = {"Работа для Чистера",nil, {"Приемник Чистера 5200 пуст. Чтобы машина заработала, требуется загрязненный предмет и три серебряные монетки."}},
[4605] = {"Чистер 5200!",nil, {"С проглотив кольцо, машина несколько минут с гудением вертит его в своих недрах и наконец останавливается, звенящим звуком оповестив вас о том, что работа закончена. Окошко приемника открывается, и вы видите в нем аккуратно завернутую коробочку. Видимо, машина выдает очищенные предметы в подарочной упаковке. Ну, даже если кольцо не удалось очистить, вы получили довольно миленькую коробочку за свои деньги... nnЧто ж, посмотрим..."}},
[4606] = {"Чистер 5200!",nil, {"С проглотив кольцо, машина несколько минут с гудением вертит его в своих недрах и наконец останавливается, звенящим звуком оповестив вас о том, что работа закончена. Окошко приемника открывается, и вы видите в нем аккуратно завернутую коробочку. Видимо, машина выдает очищенные предметы в подарочной упаковке. Ну, даже если кольцо не удалось очистить, вы получили довольно миленькую коробочку за свои деньги... nnЧто ж, посмотрим..."}},
[4621] = {"Стой, Адмирал!",nil, {"Убейте командира флота Морского Рога и барона Ревилгаза из Пиратской Бухты, а потом возвращайтесь к командиру флота Фираллону на борту \"Кровавой завесы\" неподалеку от побережья Тернистой долины."}},
[4641] = {"Твое место в мире",nil, {"Поговорите с Горнеком. Вы помните, как Калтунк отметил на карте его местонахождение и упомянул, что Горнек живет в Логове, здании, расположенном на западе."}},
[4642] = {"Слияние",nil, {"Принесите образец перемешанной слизи химику Барабосу в Подгород."}},
[4661] = {"Проба – образцы из Оскверненного леса",nil, {"Вы наполняете слизью, добытой в Оскверненном лесу, различные колбы и мензурки, стоящие на столе. Все начинает бурлить и брызгаться, а слизь меняет цвет. Потом процесс замедляется, жидкость перестает кипеть, и вы видете маленькую чашечку Петри с пробой измененной слизи и какие-то ошметки."}},
[4681] = {"Останки на берегу",nil, {"Извлеките останки морской черепахи из скелета морской черепахи, лежащего в воде к западу от Аубердина, а затем поговорите с Гвеннид Блай'Леггонд в Аубердине."}},
[4701] = {"Устранение опасности",nil, {"Отправьтесь на пик Черной горы и уничтожьте источник опасности. Хелендис кричит вам вслед одно имя: Халикон. Именно его упоминали орки в связи с воргами."}},
[4721] = {"Одичавшие стражи",nil, {"Убейте для Трулла Баловня Судьбы из Оскверненного леса 10 яростных совухов в Зимних Ключах."}},
[4722] = {"Останки морской черепахи",nil, {"Отнесите останки морской черепахи Гвеннид Блай'Леггонд в Аубердин."}},
[4723] = {"Останки морской твари",nil, {"Отнесите кости морской твари Гвеннид Блай'Леггонд в Аубердин."}},
[4724] = {"Праматерь стаи",nil, {"Убейте Халикон, праматерь стаи воргов Кровавого Топора."}},
[4725] = {"Останки морской черепахи",nil, {"Отнесите ящик со странными пометками Гвеннид Блай'Леггонд в Аубердин."}},
[4726] = {"Сущность детеныша дракона",nil, {"Принесите 8 сущностей детенышей драконов и Драко-инкарцинатор 900 Тинки Кипеллер в Пламенеющий Стяг в Пылающих степях."}},
[4727] = {"Останки морской черепахи",nil, {"Отнесите останки морской черепахи Гвеннид Блай'Леггонд в Аубердин."}},
[4728] = {"Останки морской твари",nil, {"Отнесите кости морской твари Гвеннид Блай'Леггонд в Аубердин."}},
[4729] = {"Редкие звери Киблера",nil, {"Отправьтесь на пик Черной горы и отыщите там щенков воргов легиона Кровавого Топора. Посадите маленьких тварей в клетку и отнесите Киблеру."}},
[4730] = {"Останки морской твари",nil, {"Отнесите кости морской твари Гвеннид Блай'Леггонд в Аубердин."}},
[4731] = {"Останки морской черепахи",nil, {"Отнесите ящик со странными пометками Гвеннид Блай'Леггонд в Аубердин."}},
[4732] = {"Останки морской черепахи",nil, {"Отнесите останки морской черепахи Гвеннид Блай'Леггонд в Аубердин."}},
[4733] = {"Останки морской твари",nil, {"Отнесите кости морской твари Гвеннид Блай'Леггонд в Аубердин."}},
[4734] = {"Заморозка яйца",nil, {"Испытайте прототип яйцехладоскопа на одном из яиц в Гнездовье."}},
[4735] = {"Сбор яиц",nil, {"Принесите 8 драконьих яиц и устройство-коллекционер Тинки Кипеллер в Пламенеющий Стяг в Пылающих степях."}},
[4736] = {"Поиски Менары Расщепительницы Бездны",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4737] = {"Поиски Менары Расщепительницы Бездны",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4738] = {"Поиски Менары Расщепительницы Бездны",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4739] = {"Поиски Менары Расщепительницы Бездны",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4740] = {"Разыскивается: Глубомрак!",nil, {"Найдите и убейте мурлока, известного как Глубомрак. Предполагается, что это существо охраняет и защищает хижины мурлоков в поселении к югу от Аубердина, у воды.nnО смерти Глубомрака доложите часовому Глинде Нал'Шии в Аубердине."}},
[4741] = {"Одичавшие стражи",nil, {"Убейте для Трулла Баловня Судьбы из Оскверненного леса 13 лунных совухов."}},
[4742] = {"Печать Вознесения",nil, {"Найдите самоцвет Тлеющего Терновника, самоцвет Черной Вершины и самоцвет Кровавого Топора. Верните их Ваэлану вместе с простой печатью Вознесения.nnКамни могут быть у трех генералов: у воеводы Вуна из клана Тлеющего Терновника, у вождя Омокка из клана Черной Вершины и у повелителя Змейталака из клана Кровавого Топора."}},
[4743] = {"Печать Вознесения",nil, {"Идите в Драконьи топи в Пылевых топях. Найдите Огнебора и бейте его, не жалея, чтобы сломить его волю.nnКак только воля великого зверя ослабнет, надо немедленно положить перед ним заготовку печати Вознесения и воспользоваться сферой Энергии дракона, чтобы подчинить себе его разум и направить его пламя на заготовку."}},
[4761] = {"Тандрис Ветропряд",nil, {"Поговорите с Тандрисом Ветропрядом в Аубердине."}},
[4762] = {"Река Скалистая",nil, {"Отправляйтесь на север от Аубердина, к первому водопаду вверх по реке Скалистой, и там возьмите пробу.nnС пробой воды Скалистой реки возвращайтесь в Аубердин к Тандрису Ветропряду."}},
[4763] = {"Порабощенные фурболги Чернолесья",nil, {"Наполните пустую чашу очищения из лунного колодца Аубердина.nnСоберите образцы фруктов, орехов и зерна в расположенных на севере лагерях фурболгов.nnСмешайте все в чаше и поместите ее возле ближайшего к реке Скалистой большого костра в северном лагере, вызвав таким образом сатира.nnСнимите с него талисман порчи и принесите его Тандрису Ветропряду в Аубердин."}},
[4764] = {"Пряжка Роковой оснастки",nil, {"Принесите пряжку роковой оснастки Майре Светлое Крыло в Пылающие степи."}},
[4765] = {"Доставить Риджвеллу",nil, {"Отнесите ящик Риджвелла Ремингтону Риджвеллу в Штормград."}},
[4766] = {"Майра Светлое Крыло",nil, {"Поговорите с Майрой Светлое Крыло в Пылающих степях."}},
[4767] = {"Ветрокрыл",nil, {"Соберите в Скальном гнездовье 10 яиц виверн и отнесите их Элу на Заставу Вольного Ветра."}},
[4768] = {"Табличка Темнокамня",nil, {"Принесите табличку Темного Камня тенемагу Вивиан Лаграв в Каргат."}},
[4769] = {"Вивиан Лягроб и табличка Темнокамня",nil, {"Переговорите с темным магом Вивианой Лягроб."}},
[4770] = {"Путь домой",nil, {"Выведите Паоку Ходячую Гору из Скального гнездовья, а потом поговорите с Мотегой Огненной Гривой на заставе Белого Плеса."}},
[4771] = {"Рассветный гамбит",nil, {"Отнесите \"Рассветный гамбит\" в демонстрационную комнату в Некроситете. Уничтожьте Вектуса и возвращайтесь к Бетине Биггльцинк."}},
[4781] = {"Материалы для зачарованной золотой кроводежды",nil, {"Отнесите слиток золота Зиску Ладноскрою в Тернистую долину."}},
[4782] = {"Материалы для зачарованной золотой кроводежды",nil, {"Возвращайтесь к Менаре Расщепительнице Бездны в Степи."}},
[4783] = {"Материалы для зачарованной золотой кроводежды",nil, {"Принесите 10 фиалов крови сатира из племени Ярой Ненависти и 1 простой камень Преисподней в Степи, Менаре Менара Расщепительнице Бездны."}},
[4784] = {"Материалы для зачарованной золотой кроводежды",nil, {"Принесите Менаре Расщепительнице Бездны в Степи нить из чистого золота, 2 тлеющих угля и осколок души."}},
[4785] = {"Нить из чистого золота",nil, {"Вот, бери. И пожалуйста, передай от нас привет Менаре.nnМы ждем от тебя великих свершений. Быть может, когда Легион падет на колени, прозвучит твое имя и хвала твоей силе. Жду не дождусь!"}},
[4786] = {"Завершенное одеяние",nil, {"Дождитесь, пока Менара Расщепительница Бездны закончит работу над вашим одеянием, и еще раз поговорите с ней."}},
[4787] = {"Древнее яйцо",nil, {"Принесите древнее яйцо Йе'кинье в Танарис."}},
[4788] = {"Последние таблички",nil, {"Принесите пятую и шестую таблички Мошару геологу Железному Башмаку в Танарисе."}},
[4801] = {"Э'ко ледопардов",nil, {"В Зимних Ключах тебе биться предстоит с огромными кошками, ледопардами. Эти звери жутко быстрые и ловкие. Если хочешь обрести скорость ледопарда принеси э‘ко, что найдешь у них.nnИ не забудь, что сокровище Мау'ари с собой нужно носить, если хочешь э‘ко добыть."}},
[4802] = {"Э'ко фурболгов племени Зимней Спячки",nil, {"В Зимних Ключах тебе придется сражаться со свихнувшимися существами – фурболгами племени Зимней Спячки. По воле своего вождя они обрели невероятную силу. Если и ты хочешь получить силу этого племени, принеси мне э‘ко, что найдешь у них.nnИ не забудь, что сокровище Мау'ари с собой нужно носить, если хочешь камень Э‘ко добыть."}},
[4803] = {"Э'ко щербозубов",nil, {"В Зимних Ключах тебе встретятся большие медведи с очень толстой шкурой. Это медведи-щербозубы. Судя по тому, что они способны выдержать – отлично защищены. Если хочешь получить сопротивляемость огню, принеси мне э‘ко, что найдешь у них.nnИ не забудь, что сокровище Мау'ари с собой нужно носить, если хочешь э‘ко добыть."}},
[4804] = {"Э'ко стылодыхов",nil, {"В Зимних Ключах тебе предстоит биться с тварями, которые зовутся химеры-стылодыхами. Эти существа владеют мощной магией холода. Если хочешь обрести способность противостоять этой магии, принеси мне э'ко, что найдешь у них.nnИ не забудь, что сокровище Мау'ари с собой нужно носить, если хочешь э‘ко добыть."}},
[4805] = {"Э'ко йети",nil, {"В Зимних Ключах тебе предстоит биться с тварями, которые зовутся йети с холмов Ледополоха. Они неповоротливы, но их толстая шкура выдерживает любые удары. Если хочешь обрести эту способность, принеси мне э'ко, что найдешь у них.nnИ не забудь, что сокровище Мау'ари с собой нужно носить, если хочешь э'ко добыть."}},
[4806] = {"Э'ко великанов",nil, {"В Зимних Ключах тебе предстоит биться с великанами ледяного молота. Они сильны и яростны. Если хочешь обрести их силу, принеси мне э‘ко, что найдешь у них.nnИ не забудь, что сокровище Мау'ари с собой нужно носить, если хочешь э‘ко добыть."}},
[4807] = {"Э'ко диких совухов",nil, {"В Зимних Ключах тебе предстоит сражаться с существами, обладающими врожденной способностью к магии. Сильная магия у этих диких совухов, да, сильная... Если хочешь обрести их магию и использовать в своих целях, принеси мне э'ко, что найдешь у них.nnИ не забудь, что с собой нужно носить сокровище Мау'ари, если хочешь э‘ко добыть."}},
[4808] = {"Фелнок Сталлист",nil, {"Отнесите письмо Тинки Фелноку Сталлисту."}},
[4809] = {"Рога стылодыхов",nil, {"Принесите 8 безупречных рогов стылодыха Фелноку Сталлисту."}},
[4810] = {"Возвращение к Тинки",nil, {"Отнесите ящик Фелнока Тинки Кипеллер в Пылающие степи."}},
[4811] = {"Красный кристалл",nil, {"Направляйтесь на восток от Аубердина и поищите большой красный кристалл в горной гряде на востоке Темного Берега. Вернитесь с докладом к часовому Глинде Нал'Шие в Аубердин."}},
[4812] = {"Пока струится вода...",nil, {"Наполните пустую колбу для воды в лунном колодце Аубердина, а затем исследуйте красный кристалл, находящийся в горной гряде на востоке Темного Берега."}},
[4813] = {"Скрытые фрагменты",nil, {"Доложите о результатах ваших изысканий часовому Глинде Нал'Шие в Аубердине."}},
[4821] = {"Странное яйцо",nil, {"Принесите неизвестное яйцо Хагару Грозовому Копыту на Заставу Вольного Ветра."}},
[4822] = {"Женщинам – цветы, детям -...",nil, {"Купите своему подопечному клубничное мороженое. Кажется, мальчик предпочитает мороженое Тигуле и Форора."}},
[4841] = {"Упокоение кентавров",nil, {"Убейте 12 разведчиков, 10 пастухов и 6 ветроловок из племени Галак, после чего вернитесь к дозорному утесов Долгорогу на Заставу Вольного Ветра."}},
[4842] = {"Выяснение причин",nil, {"Последуйте указаниям Доновы Снегоноры, а затем вернитесь к ней."}},
[4861] = {"Взбесившиеся дикие совухи",nil, {"Найдите разоренный лагерь Джерона Гранильщика и его припасы на юге Зимних Ключей."}},
[4862] = {"Товар на любителя",nil, {"Отправьтесь на пик Черной горы и принесите Киблеру 15 яиц скального паука.nnСудя по всему, яйца надо искать недалеко от пауков."}},
[4863] = {"Взбесившиеся дикие совухи",nil, {"Найдите опрокинутый фургон Джерона Гранильщика."}},
[4864] = {"Взбесившиеся дикие совухи",nil, {"Подберите припасы Джерона из снега, и найдите дикого совуха, похитившего амулет, о котором говорил Джерон.nnПринесите припасы и ожерелье миних перьев Джерону Гранильщику в его дом в Зимних Ключах."}},
[4865] = {"Змеиная дикость",nil, {"Расскажите о найденном Мотеге Огненной Гриве."}},
[4866] = {"Материнское молоко",nil, {"Найдите в самом сердце пика Черной горы мать Дымную Паутину. Сражайтесь с ней, пока она не введет вам свой яд. Скорее всего ее также придется убить. Когда яд будет в вас, вернитесь к Джону-Оборванцу, чтобы он смог добыть яд."}},
[4867] = {"Аррок Смертный Вопль",nil, {"Прочитайте записку Вароша. Принесите Варошу его амулет."}},
[4881] = {"Убийственный заговор",nil, {"Предупредите Канати Серое Облако на заставе Белого Плеса, что на него готовится покушение."}},
[4882] = {"Умение хранить секреты",nil, {"Отнесите ожерелье синих перьев Труллу Баловню Судьбы на заставу Отравленной Крови."}},
[4883] = {"Умение хранить секреты",nil, {"Отнесите ожерелье синих перьев Наре Буйногривой."}},
[4901] = {"Стражи алтаря",nil, {"Защитите Раншаллу, пока она пытается активировать алтарь Элуны. Сообщите о ваших находках Эреласу Янтарному Небу в деревне Рут'теран."}},
[4902] = {"Питомцы Элуны",nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнассе."}},
[4903] = {"Приказ полководца",nil, {"Убейте вождя Омокка, воеводу Вуна и повелителя Змейталака. Найденные при них важные бумаги Черной горы доставьте полководцу Клинозубу в Каргат."}},
[4904] = {"Долгожданная свобода",nil, {"Выведите Лакоту Песню Ветра с Пика Темного Облака, а потом поговорите с Талией Янтарной Шкурой на Заставе Вольного Ветра."}},
[4906] = {"Снова порча!",nil, {"Эридана Грусть Ветра из Оскверненного леса просит вас убить 8 призывателей огня, 8 предателей и 8 плутов из племени Нефритового огня, а также Ксаварика. По выполнении задания возвращайтесь к Эридане."}},
[4907] = {"Тинки Кипеллер",nil, {"Поговорите с Тинки Кипеллер."}},
[4921] = {"Пропавшая без вести",nil, {"Отыщите жену Манкрика и возвращайтесь к нему в Перекресток."}},
[4941] = {"Мудрость Эйтригга",nil, {"Поговорите с Эйтриггом в Оргриммаре. После этого обратитесь к вождю Траллу. nnВ последний раз Эйтригг был в в зале приемов Тралла."}},
[4961] = {"Очищение шара Орахила",nil, {"Убейте демона сферы, а потом поговорите с Табетой."}},
[4962] = {"Осколок гончей Скверны",nil, {"Возьмите рубин Сквернолова и поймайте в него одну из гончих скверны в Пустошах. Добившись успеха, отнесите плененный дух Гончей Скверны и рубин Сквернолова Менаре Расщепительнице Бездны из Степей."}},
[4963] = {"Осколок инфернала",nil, {"Возьмите Пылающий изумруд и поймайте в него одного из инферналов в Пустошах. Добившись успеха, отнесите плененный дух инфернала и пылающий изумруд Менаре Расщепительнице Бездны из Степей."}},
[4964] = {"Завершение шара Дар'орахила",nil, {"Дождитесь, пока Менара Расщепительница Бездны закончит работу над шаром Дар'орахила, и еще раз поговорите с ней."}},
[4965] = {"Знание шара Орахила",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4966] = {"Защитить Канати Серое Облако",nil, {"Защитите Канати Серое Облако от нападения кентавров."}},
[4967] = {"Знание шара Орахила",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4968] = {"Знание шара Орахила",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4969] = {"Знание шара Орахила",nil, {"Поговорите с Менарой Расщепительницей Бездны в Степях."}},
[4970] = {"Накормить ледопардов",nil, {"Соберите 5 кусков мяса медведей-щербозубов и 5 кусков мяса стылодыхов для Риверна Холодного Ветра в Зимних Ключах."}},
[4971] = {"Вопрос времени",nil, {"Воспользуйтесь временным переместителем рядом с одним из амбаров Андорала и найдите временных паразитов.nnУбейте 10 временных паразитов и верните временной переместитель Хроми в Андоральскую таверну в Западных Чумных землях."}},
[4972] = {"Отсчет времени",nil, {"Найдите 5 часов Андорала, спрятанных в сундуки среди руин. Принесите их Хроми в таверну Андорала в Западных Чумных землях."}},
[4973] = {"Отсчет времени",nil, {"Найдите 5 часов Андорала, спрятанных в сундуки среди руин. Принесите их Хроми в таверну Андорала в Западных Чумных землях."}},
[4974] = {"За Орду!",nil, {"Пойдите в Пик Черной горы и убейте вождя Ренда Чернорука. Принесите его голову в Оргриммар."}},
[4975] = {"Завершение шара Дар'орахила",nil, {"Дождитесь, пока Менара Расщепительница Бездны закончит работу над шаром Дар'орахила, и еще раз поговорите с ней."}},
[4976] = {"Возвращение к Менаре",nil, {"Отнесите очищенную сферу инфернала Менаре Расщепительнице Бездны в Степи."}},
[4981] = {"Агент Блестяшка",nil, {"Отправьтесь к пику Черной горы и выясните, что сталось с Блестяшкой."}},
[4982] = {"Вещи Блестяшки",nil, {"Найдите вещи Блестяшки и верните их владелице. Блестяшка сказала, что она спрятала свое оборудование где-то на нижнем ярусе подземелья."}},
[4983] = {"По данным разведки",nil, {"Передайте донесение Блестяшки гроссмейстеру Лекслорту в Каргат."}},
[4984] = {"Страдания природы",nil, {"Убейте 8 больных волков и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях."}},
[4985] = {"Страдания природы",nil, {"Убейте 8 болезненных гризли и вернитесь к Мулгрису Глубокой Реке в Удел Страданий в Западных Чумных землях."}},
[4986] = {"Покрытая письменами дубовая ветвь",nil, {"Судя по магии, вложенной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Дарнассе. Попросите о помощи одного из тамошних друидов."}},
[4987] = {"Покрытая письменами дубовая ветвь",nil, {"Судя по магии, записанной в покрытую письменами дубовую ветвь, друид-таурен желал отнести ее в Круг Кенария в Громовом Утесе. Попросите о помощи одного из тамошних друидов."}},
[5001] = {"Вещи Блестяшки",nil, {"Найдите вещи Блестяшки и верните их владелице. Удачи!"}},
[5002] = {"Донесение Максвелла",nil, {"Передайте маршалу Максвеллу информацию Блестяшки."}},
[5021] = {"Лучше поздно, чем никогда",nil, {"Судя по сбивчивым объяснениям призрачной женщины, она хочет доставить посылку. Бедняга уверяет, что посылка лежала там, где стоял ее конь. Где именно это было, женщина не сказала."}},
[5022] = {"Лучше поздно, чем никогда",nil, {"Выясните у королевских представителей Переписи Штормграда местонахождение Эммы Фелстоун. Офицера-переписчика можно найти в Ратуше."}},
[5023] = {"Лучше поздно, чем никогда",nil, {"Поговорите с королевскими инспекторами Подгорода и наведите справки о Иеремии Фелстоуне. Офицера-переписчика можно найти рядом с пунктом регистрации гильдий и гербовых накидок."}},
[5041] = {"Снабжение Перекрестка",nil, {"Найдите и верните Тхорку из Степей ящики с припасами из Перекрестка."}},
[5042] = {"Сила Агамаггана",nil, {"Если ты желаешь обрести силу Агамаггана, |3-6(орк), принеси мне 4 кровавых осколка.nnВраги будут трепетать перед тобой, когда я благословлю тебя. *фыркает*nnЛишь величайшим воинам племени Иглогривых дарована такая сила!"}},
[5043] = {"Ловкость Агамаггана",nil, {"Величайшие разведчики моего племени издавна отличались несравненной меткостью, и ни одному разбойнику *фыркает* даже и не снилась подобная стремительность в битве.nnПринеси мне 4 кровавых осколка, и превзойдешь ловкостью любого иглогрива."}},
[5044] = {"Мудрость Агамаггана",nil, {"Сила Агамаггана помогает шаманам и рудознатцам иглогривов *фыркает* в бою и во время проведения ритуалов.nnЕсли ты тоже желаешь обрести благословение Агамаггана, принеси мне 4 кровавых осколка, и Кривозуб тебе поможет. *фыркает*"}},
[5045] = {"Высокий дух",nil, {"Высокий дух – благо для всех воинов. *фыркает* Он ведет к победе, даже когда надежды уже нет. Племя Иглогривых знает об этом. *фыркает* Мы ценим свою связь с Агамагганом больше доблести *фыркает* в бою.nnПринеси мне 4 кровавых осколка, и ты получишь благословение духа перед тем, как идти в бой. *фыркает*"}},
[5046] = {"Иглошкура",nil, {"Идущему в бой *фыркает* защита не помешает. Кости должны быть крепкими, а шкура толстой. *фыркает* Мое племя отлично знает об этом, и, помимо дарованных нам от природы игл и толстой шкуры, *фыркает* мы защищаем себя и другими способами. Перед тем как идти на войну, мы просим Агамаггана защитить нас от урона.nnПринеси мне 4 кровавых осколка, *фыркает* и я дарую тебе ту же защиту."}},
[5047] = {"Айс Вентурон, к вашим услугам!",nil, {"Переговорите с Малифоем Черномолотом в Круговзоре."}},
[5048] = {"Добросердечная Эмма",nil, {"Найдите в Штормграде старушку Эмму и узнайте, действительно ли она – Эмма Фелстоун. Если да, то, может, ей понравится та посылочка, что собрала для нее Джанис Фелстоун."}},
[5049] = {"Грусть Иеремии",nil, {"Найдите в Подгороде Иеремию Пэйсона и выясните, в самом ли деле он Иеремия Фелстоун. Если да, передайте ему посылку от Джанис Фелстоун."}},
[5050] = {"Оберег удачи",nil, {"Отнесите половинку талисмана Джанис Фелстоун в Западные Чумные земли."}},
[5051] = {"Вторая половина",nil, {"Отыщите вторую половину оберега Удачи на ферме Фелстоун в Западных Чумных землях и соберите его.nnОтдайте оберег Удачи Джанис Фелстоун."}},
[5052] = {"Кровавые осколки Агамаггана",nil, {"Принесите кровавый осколок Кривозубу в Лагерь Таурахо в Степи.nnСоберите побольше кровавых осколков, и он одарит вас силой, дарованной ему Агамагганом."}},
[5054] = {"Урсиус из щербозубов",nil, {"По просьбе Шторма Призрачное Копыто в Круговзоре в Зимних Ключах убейте Урсиуса."}},
[5055] = {"Убить Брумеран",nil, {"Убейте Брумеран по просьбе Шторма Призрачное Копыто в Круговзоре в Зимних Ключах."}},
[5056] = {"Ши-Ротам",nil, {"Убейте Ши-Ротам для Шторма Призрачное Копыто."}},
[5057] = {"Прежние свершения",nil, {"Отнесите Знак Шторма Призрачное Копыто Мелору Каменное Копыто в Громовой Утес."}},
[5058] = {"Дневник миссис Далсон",nil, {"В дневнике с пожелтевшими от времени страницами открыта последняя запись:nn\"Гарольд превратился в нежить, и я не знаю, что теперь будет. Я из последних сил утащила его в единственное место, которое можно надежно запереть. Я держу ключ при себе, будем надеяться, что моя уловка сработает...nnНадеюсь, я смогу выбраться отсюда, но для этого нужны деньги. Все наши вещи в шкафу... и, кажется, Гарольд держал ключ от него при себе.nnПора мне отдохнуть, я смертельно устала.\""}},
[5059] = {"Под замком",nil, {"Дверь в служебное помещение потрескалась, будто кто-то долго и упорно пытался ее выломать. В настоящий момент она заперта снаружи.nnПодергав за ручку, мы слышите изнутри утробные стоны! Что бы ни скрывалось внутри, оно кажется совсем недружелюбным."}},
[5060] = {"Под замком",nil, {"Перед вами массивный шкаф, надежно запертый на врезной замок. Дверцы шкафа сильно исцарапаны. Судя по всему, его не раз пытались взломать, чтобы посмотреть, что внутри.nnПохоже, без ключа этот шкаф не открыть."}},
[5061] = {"Водный облик",nil, {"Вернитесь в Дарнасс и покажите Матренгилу Медвежьей Лапе свою подвеску Морского Льва."}},
[5062] = {"Священное пламя",nil, {"Соберите 10 листов огненной агавы и поговорите с Магатой Зловещий Тотем на Вершине Старейшин в Громовом Утесе."}},
[5063] = {"Шапка ученого из Алого ордена",nil, {"Для изготовления шапки ученого из Алого ордена вам нужно принести Малифою Черномолоту 1 безупречную звериную шкуру, 5 кусков потертого шовного материала поганища, 8 чародейских кристаллов, 5 зачарованных алых нитей. nnВы также должны будете уплатить 50 золотых монет за изготовление головного убора."}},
[5064] = {"Наблюдение за Зловещим Тотемом",nil, {"Найдите и заберите три тайные записки на Пике Темного Облака."}},
[5065] = {"Утраченные таблички Мошару",nil, {"Принесите третью и четвертую таблички Мошару геологу Железному Башмаку в Танарис."}},
[5066] = {"Призыв к оружию: Чумные земли!",nil, {"Найдите командира Ашлама Отважного Кулака в лагере Промозглого Ветра на севере Альтеракских гор."}},
[5067] = {"Поножи тайны",nil, {"Для изготовления поножей тайны вам нужно принести Малифою Черномолоту 1 безупречную звериную шкуру, 5 кусков потертого шовного материала поганища, 5 арканитовых слитков и 5 порций бальзамировочного состава Снегошепота. nnВам придется также заплатить 50 золотых за изготовление этого предмета."}},
[5068] = {"Кираса кровавой жажды",nil, {"Для изготовления кирасы кровавой жажды вам нужно принести Малифою Черномолоту 1 безупречную звериную шкуру, 10 кусков потертого шовного материала поганища, 5 арканитовых слитков и 5 шкур тени.nnВы также должны будете уплатить 50 золотых за изготовление доспеха."}},
[5081] = {"Миссия Максвелла",nil, {"Отправляйтесь на пик Черной горы и устраните воеводу Вуна, вождя Омокка и повелителя Змейталака. По выполнении задания вернитесь к маршалу Максвеллу."}},
[5082] = {"Угроза со стороны Зимней Спячки",nil, {"По просьбе Доновы Снегоноры из Зимних Ключей убейте 8 землепроходцев, 8 дозорных и 8 тотемистов из племени Зимней Спячки."}},
[5083] = {"Огненный эликсир Зимнего Сна",nil, {"Принесите Донове Снегоноре пустую флягу из-под огненной воды."}},
[5084] = {"Поддавшиеся порче",nil, {"Исследуйте поселение фурболгов из племени Мертвого Леса в северной части Оскверненного леса в поисках ключа к разгадке."}},
[5085] = {"Таинственная слизь",nil, {"Вернитесь к Донове и сообщите ей о своих находках."}},
[5086] = {"Ядовитый Ужас",nil, {"Соберите 3 капли ядовитого ужаса и принесите их Донове Снегоноре в Зимние Ключи."}},
[5087] = {"Гонцы Зимней Спячки",nil, {"Найдите гонцов из племени Зимней Спячки и не позвольте им донести их груз до их деревни."}},
[5088] = {"Арикара",nil, {"Убейте Арикару и в качестве доказательства принесите ее останки вместе с воспламеняющим порошком Мотеге Огненной Гриве на заставу Белого Плеса."}},
[5089] = {"Приказ генерала Драккисата",nil, {"Отнесите приказ генерала Драккисата маршалу Максвеллу в Пылающие степи."}},
[5090] = {"Призыв к оружию: Чумные земли!",nil, {"Найдите командира Ашлама Отважного Кулака в лагере Промозглого Ветра на севере Альтеракских гор."}},
[5091] = {"Призыв к оружию: Чумные земли!",nil, {"Найдите командира Ашлама Отважного Кулака в лагере Промозглого Ветра на севере Альтеракских гор."}},
[5092] = {"Зачистка территории",nil, {"Убейте 10 скелетов-свежевателей и 10 слюнявых вурдалаков на холме Печали.nnВернитесь к командиру Ашламу Отважному Кулаку в лагерь Промозглого Ветра в Западных Чумных землях."}},
[5093] = {"Призыв к оружию: Чумные земли!",nil, {"Отыщите верховного палача Деррингтона. Его базовый лагерь находится у Бастиона, к востоку от Тирисфальских лугов и Подгорода."}},
[5094] = {"Призыв к оружию: Чумные земли!",nil, {"Отыщите верховного палача Деррингтона. Его базовый лагерь находится у Бастиона, к востоку от Тирисфальских лугов и Подгорода."}},
[5095] = {"Призыв к оружию: Чумные земли!",nil, {"Найдите верховного палача Деррингтона. Его базовый лагерь расположен в Бастионе к востоку от Тирисфальских лесов и Подгорода."}},
[5096] = {"Вылазка в стан Алого ордена",nil, {"Отправляйтесь в лагерь Алого ордена между полем Джанис и Слезами Далсона и уничтожьте командный шатер.nnРазместите в лагере знамя Плети и возвращайтесь к верховному палачу Деррингтону в Бастион, в Западные Чумные земли."}},
[5097] = {"Сторожевые башни",nil, {"Отметьте все башни Андорала при помощи сигнального факела; задействовать факел следует, находясь в дверном проеме каждой из башен.nnОтметив все четыре башни, следует вернуть сигнальный факел командиру Ашламу Отважному Кулаку в лагерь Промозглого Ветра в Западных Чумных землях."}},
[5098] = {"Сторожевые башни",nil, {"Отметьте все башни Андорала при помощи сигнального факела; задействовать факел следует, находясь в дверном проеме каждой из башен.nnОтметив все четыре башни, следует вернуть сигнальный факел верховному палачу Деррингтону в Бастион в Западных Чумных землях."}},
[5101] = {"Великое тестовое задание... Судьбы!",nil, {"Убейте 6 овец для Бенни и бегите со всех ног!nnnnnНасчет ног я пошутил."}},
[5102] = {"Кончина генерала Драккисата",nil, {"Отправьтесь на пик Черной горы и устраните генерала Драккисата. По выполнении задания вернитесь к маршалу Максвеллу."}},
[5103] = {"Смерть в огне",nil, {"Наверняка в этом мире есть кто-то, кто знает, что делать с этими рукавицами."}},
[5121] = {"Верховный вождь племени Зимней Спячки",nil, {"По просьбе Доновы Снегоноры из Зимних Ключей убейте верховного вождя племени Зимней Спячки."}},
[5122] = {"Медальон Веры",nil, {"Укрывшись в этой часовне, я могу противостоять порче, заполняющей мое существо. Но если я покину эти стены, я немедленно превращусь в нежить. Я знаю, что есть на свете медальон, именуемый медальоном веры, который может дать мне силу противостоять порче. nnПрошу тебя, Димиллион, принеси мне медальон. Его охраняет безумный фанатик Малор Ревностный, один из Алых. Ты найдешь его в недрах бастиона Алого ордена в западной части Стратхольма."}},
[5123] = {"Последний фрагмент мозаики",nil, {"Отнесите неразборчиво исписанный журнал Донове Снегоноре в Зимние Ключи."}},
[5124] = {"Огненные латные рукавицы",nil, {"Принесите Малифою Черномолоту 6 зачарованных ториевых слитков, 2 сущности огня и 4 звездных рубина. Вам также нужно будет расстаться с неопалимыми латными рукавицами."}},
[5125] = {"Слова Аурия",nil, {"Я умираю, Арраон… но душа моя обрела спасение. Я так многим обязан тебе... Прошу, прими это... И продолжай биться за правое дело, храбрый |3-6(Друид)!"}},
[5126] = {"История Лоракса",nil, {"Поговорите с Лораксом. Выслушайте то, что он вам расскажет."}},
[5127] = {"Демонова кузня",nil, {"Отправляйтесь на пик Черной горы и найдите Горалука Разбитую Наковальню. Убейте его, а потом воткните в труп окровавленную пику. Таким образом пика вытянет его душу и окрасится ей.nnКроме того, вам нужно найти заготовку рунической кирасы.nnОтнесите пику и кирасу Лораксу в Зимних Ключах."}},
[5128] = {"Слова верховного вождя",nil, {"Отнесите неразборчиво исписанный журнал Келеку Стражу Неба в южную часть Оскверненного леса."}},
[5141] = {"Кожевничество: чешуя драконов",nil, {"Принесите 2 прочные кирасы из панциря скорпида, 2 пары прочных перчаток из панциря скорпида и 10 потертых чешуек дракона Питеру Галену в Азшару. nnПо выполнении этого задания вы получите возможность обучения кожевничеству школы чешуи драконов. nnЕсли вы выполните это задание, вы не сможете изучать кожевничество школы традиций предков и школы стихий."}},
[5142] = {"Малышка Памела",nil, {"Найдите Памелу Редпат."}},
[5143] = {"Кожевничество: традиции предков",nil, {"Принесите жилет и шлем из дикой кожи Кариссии Лунной Охотнице из Фераласа.nnВыполнив это задание, вы станете кожевником школы традиций предков.nnТакже вы лишитесь возможности изучать кожевничество школы чешуи драконов и школы стихий; убедитесь, что избрали именно этот путь, прежде чем выполнять задание."}},
[5144] = {"Кожевничество: сила стихий",nil, {"Принесите Саре Дубильщице 2 сердца огня, 2 сферы воды, 2 ядра земли и 2 дыхания ветра. nnВыполнение этого задания позволит вам изучать кожевничество школы силы стихий. nnКроме того, вы откажетесь от изучения кожевничества школ чешуи драконов и традиций предков. Нужно быть уверенным, что выбрана верная школа, прежде чем выполнять задание."}},
[5145] = {"Кожевничество: чешуя драконов",nil, {"Принесите 2 прочные кирасы из панциря скорпида, 2 пары прочных перчаток из панциря скорпида и 10 потертых чешуек дракона Торкафу Оку Дракона в Бесплодные земли. nnПо выполнении этого задания вы получите возможность обучения кожевничеству школы чешуи драконов. nnЕсли вы выполните это задание, вы не сможете изучать кожевничество школы традиций предков и школы стихий."}},
[5146] = {"Кожевничество: сила стихий",nil, {"Принесите Брумну Заиндевевшему Копыту на нагорье Арати 2 сердца огня, 2 сферы воды, 2 ядра земли и 2 дыхания ветра. nnВыполнение этого задания позволит вам изучать кожевничество школы силы стихий. nnКроме того, вы откажетесь от изучения кожевничества школ чешуи драконов и традиций предков. Нужно быть уверенным, что выбрана верная школа, прежде чем выполнять задание."}},
[5147] = {"Разыскивается: Арнак Зловещий Тотем",nil, {"Убейте Арнака Зловещего Тотема и принесите доказательство дозорному утесов Долгорогу на заставу Вольного Ветра."}},
[5148] = {"Кожевничество: традиции предков",nil, {"Принесите жилет и шлем из дикой кожи Кариссии Лунной Охотнице из Фераласа.nnВыполнив это задание, вы станете кожевником школы традиций предков.nnТакже вы лишитесь возможности изучать кожевничество школы чешуи драконов и школы стихий; убедитесь, что избрали именно этот путь, прежде чем выполнять задание."}},
[5149] = {"Кукла Памелы",nil, {"Найдите куклу Памелы."}},
[5150] = {"Даданга проголодалась!",nil, {"Даданга внимательно смотрит на вас, она явно чего-то ждет... nnВидимо, она голодна! nnВ кратере должно расти что-нибудь подходящее для крупного травоядного... Даданга – крупная девочка, придется потрудиться, чтобы принести ей достаточно еды!"}},
[5151] = {"Механизм гипервместимости",nil, {"Убейте зверя и принестите механизм гипервместимости и ключ от клетки пантеры Визло Фингалету."}},
[5152] = {"Тетушка Марлен",nil, {"Поговорите с Марлен в южной части руин Андорала."}},
[5153] = {"Странный историк",nil, {"Принесите Хроми обручальное кольцо Джозефа."}},
[5154] = {"Анналы Дарроушира",nil, {"Принесите Анналы Дарроушира Хроми в Андорал."}},
[5155] = {"Силы Джеденара",nil, {"Отправляйтесь в Джеденар и убейте 4 джеденарских гончих, 4 джеденарских стражей, 6 джеденарских адептов и 6 джеденарских сектантов, затем возвращайтесь к Грете Замшелое Копыто в Оскверненный лес."}},
[5156] = {"Проверка на порчу",nil, {"Найдите и уничтожьте 2 энтропических зверей и 2 энтропических ужасов при обследовании долины Рваных Ран. После проверьте, по-прежнему ли заполнены кратеры оскверненной водой. Затем возвращайтесь к Таруну Красному Перу в Оскверненный лес."}},
[5157] = {"Собрать оскверненную воду",nil, {"Возьмите пустую флягу и наполните ее водой из лунного колодца, что находится в центре Джеденара. Возвращайтесь к Грете Замшелое Копыто в Оскверненный лес."}},
[5158] = {"В поисках духовной помощи",nil, {"Отнесите порченую воду из Лунного колодца Ислен Заклинателю Воды в Степи."}},
[5159] = {"Чистая вода возвращается в Оскверненный лес",nil, {"Отнесите очищенную воду из лунного колодца Грете Замшелое Копыто в Оскверненный лес."}},
[5160] = {"Матрона-защитница",nil, {"Дойдите до Зимних Ключей и найдите Хале. Отдайте ей пластину чешуи Ауби."}},
[5161] = {"Гнев синих драконов",nil, {"Если у вас есть желание продолжить, поговорите снова с Хале."}},
[5162] = {"Гнев синих драконов",nil, {"Поговорите с Джезибой в Чумных землях. Он обитает в Андорале."}},
[5163] = {"Йети где-то рядом…",nil, {"Возьмите механического йети Уми и напугайте им ее друзей:nnЛегакки в Круговзоре (Зимние Ключи) nполивалку в Прибамбасске (Танарис)nКвикксиля в Укрытии Маршалла (Кратер Ун'Горо)nnКогда проделаете все это, принесите механического йети обратно Уми."}},
[5164] = {"Перечень Заблудших",nil, {"Прочитайте перечень Заблудших."}},
[5165] = {"Погасить Пламя Защиты",nil, {"При помощи очищенной воды из лунного колодца загасите пламя в четырех жаровнях защиты внутри Оплота Теней в Оскверненном лесу. Затем возвращайтесь к Грете Замшелое Копыто в Изумрудное святилище."}},
[5166] = {"Кираса Всецветных драконов",nil, {"Джезиба \"Скульптор\" может сковать кирасу Всецветных драконов, если вы принесете ему следующее:nn1 всецветный панцирь.nn10 сверкающих всецветных чешуек.nn10 порций крови героев.nn10 мотков потертого шовного материала."}},
[5167] = {"Ножные латы Всецветного воина",nil, {"Джезиба 'Скульптор' может сковать ножные латы Всецветного воина, если вы принесете ему следующее:nn1 всецветный панцирь.nn10 сверкающих всецветных чешуек.nn10 порций крови героев.nn5 шкур тени."}},
[5168] = {"Герои Дарроушира",nil, {"Принесите книгу Дейвила и щит Редпата Карлину Редпату."}},
[5181] = {"Негодяи Дарроушира",nil, {"Принесите череп Хоргуса и сломанный меч Мардука Карлину Редпату в часовню Последней Надежды."}},
[5201] = {"Вторжение племени Зимней Спячки",nil, {"По заданию Риверна Холодного Ветра убейте 5 шаманов и 5 ведмедей из клана Зимней Спячки."}},
[5202] = {"Странный красный ключ",nil, {"Обыщите Джеденар и найдите замок, который открывается странным красным ключом."}},
[5203] = {"Спасение из Джеденара",nil, {"Сопроводите Арко'нарин туда, где лежат меч Светлая Сталь и снаряжение Арконарин, и защитите ее при побеге из Оплота Теней. Потом разыщите Джессиру Лунную Радугу в Оскверненном лесу и расскажите ей, что случилось с ее друзьями."}},
[5204] = {"Воздаяние Света",nil, {"Вернитесь в Джеденар и убейте суккуба Ракайю, затем отыщите останки Трея Светогорна."}},
[5206] = {"Мародеры Дарроушира",nil, {"Принесите 5 резонирующих черепов и загадочный кристалл Карлину Редпату в Восточные Чумные земли."}},
[5210] = {"Брат Карлин",nil, {"Отнесите дополненные анналы Дарроушира Карлину Редпату в Восточные Чумные земли."}},
[5211] = {"Защитники Дарроушира",nil, {"Освободите 15 дарроуширских духов и возвращайтесь к Карлину Редпату."}},
[5212] = {"Плоть не лжет",nil, {"Принесите 10 препаратов чумной плоти из Стратхольма Бетине Биггльцинк. Предположительно, любая тварь из Стратхольма сойдет за препарат."}},
[5213] = {"Вирус чумы",nil, {"Отправляйтесь в Стратхольм и исследуйте зиккураты. Доставьте сведения о Плети Бетине Биггльцинк."}},
[5214] = {"Великий Фрас Сиаби",nil, {"Найдите табачную лавку Фраса Сиаби в Стратхольме, отыщите в ней пачку лучшего табака Сиаби и принесите ее Смоки Ля Рю."}},
[5215] = {"Котлы Плети",nil, {"Поговорите с верховной жрицей Макдонелл на Берегу Промозглого Ветра в Западных Чумных землях."}},
[5216] = {"Цель: поле Джанис",nil, {"Отправляйтесь на поле Джанис в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Джанис."}},
[5217] = {"Возвращение в Лагерь Промозглого Ветра",nil, {"Принесите полную бутылку с поля Джанис верховной жрице Макдонелл в Лагерь Промозглого Ветра, что в Западных Чумных землях."}},
[5218] = {"Котел поля Джанис",nil, {"Перед вами – котел Плети. Он бурлит, испуская в воздух огромное количество ядовитых испарений. У массивного основания котла находится небольшая панель доступа.nnОдолев Хранителя котла и забрав у него ключ, мы можете добавить в котел любые составляющие, чтобы превратить его в средство борьбы с чумой нежити."}},
[5219] = {"Цель: Слезы Далсона",nil, {"Отправляйтесь в Слезы Далсона в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Слез Далсона."}},
[5220] = {"Возвращение в Лагерь Промозглого Ветра",nil, {"Принесите полную бутылку с поля Слез Далсона верховной жрице Макдонелл в Лагерь Промозглого Ветра, что в Западных Чумных землях."}},
[5221] = {"Котел Слез Далсона",nil, {"Перед вами – котел Плети. Он бурлит, испуская в воздух огромное количество ядовитых испарений. У массивного основания котла находится небольшая панель доступа.nnОдолев Хранителя котла и забрав у него ключ, мы можете добавить в котел любые составляющие, чтобы превратить его в средство борьбы с чумой нежити."}},
[5222] = {"Цель: Удел Страданий",nil, {"Отправляйтесь в Удел Страданий в Западных Чумных землях. Найдите и одолейте Хранителя котла. Взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка с Удела Страданий."}},
[5223] = {"Возвращение в Лагерь Промозглого Ветра",nil, {"Принесите полную бутылку с Призрачной фермы верховной жрице Макдонелл в Лагерь Промозглого Ветра, в Западные Чумные земли."}},
[5224] = {"Котел Призрачной фермы",nil, {"Перед вами – котел Плети. Он бурлит, испуская в воздух огромное количество ядовитых испарений. У массивного основания котла находится небольшая панель доступа.nnОдолев Хранителя котла и забрав у него ключ, мы можете добавить в котел любые составляющие, чтобы превратить его в средство борьбы с чумой нежити."}},
[5225] = {"Цель: пустошь Гаррона",nil, {"Отправляйтесь на пустошь Гаррона в Западных Чумных землях, найдите и одолейте Хранителя котла. Взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка с путоши Гаррона."}},
[5226] = {"Возвращение в Лагерь Промозглого Ветра",nil, {"Отнесите полную бутылку с пустоши Гаррона высшей жрице Макдонелл в Лагерь Промозглого Ветра, что в Западных Чумных землях."}},
[5227] = {"Котел пустоши Гаррона",nil, {"Перед вами – котел Плети. Он бурлит, испуская в воздух огромное количество ядовитых испарений. У массивного основания котла находится небольшая панель доступа.nnОдолев Хранителя котла и забрав у него ключ, мы можете добавить в котел любые составляющие, чтобы превратить его в средство борьбы с чумой нежити."}},
[5228] = {"Котлы Плети",nil, {"Поговорите со жрицей Тьмы Вандис в Бастионе, что в Западных Чумных землях."}},
[5229] = {"Цель: поле Джанис",nil, {"Отправляйтесь на поле Джанис в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Джанис."}},
[5230] = {"Возвращение в Бастион",nil, {"Принесите полную бутылку с поля Джанис жрице Тьмы Вандис в Бастион, что в Западных Чумных землях."}},
[5231] = {"Цель: Слезы Далсона",nil, {"Отправляйтесь в Слезы Далсона в Западных Чумных землях, найдите и одолейте Хранителя котла, взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка c поля Слез Далсона."}},
[5232] = {"Возвращение в Бастион",nil, {"Отнесите полную бутылку с поля Слез Далсона жрице Тьмы Вандис в Бастион в Западных Чумных землях."}},
[5233] = {"Цель: Удел Страданий",nil, {"Отправляйтесь в Удел Страданий в Западных Чумных землях. Найдите и одолейте Хранителя котла. Взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка с Удела Страданий."}},
[5234] = {"Возвращение в Бастион",nil, {"Принесите полную бутылку с Призрачной фермы жрице Тьмы Вандис в Бастион, что в Западных Чумных землях."}},
[5235] = {"Цель: пустошь Гаррона",nil, {"Отправляйтесь на пустошь Гаррона в Западных Чумных землях, найдите и одолейте Хранителя котла. Взяв у него ключ, доберитесь до самого котла. Чтобы взять образец яда из котла, вам понадобится пустая бутылка с путоши Гаррона."}},
[5236] = {"Возвращение в Бастион",nil, {"Отнесите полную бутылку с пустоши Гаррона жрице Тьмы Вандис в Бастион в Западных Чумных землях."}},
[5237] = {"Задание выполнено!",nil, {"Успешная диверсия против всех котлов – огромная победа!nn*Командир откашливается и оглушительно орет.*nnСлушайте, слушайте все! За верную и преданную службу королевству Штормграда и Альянсу, за выполнение долга и даже более того – перед лицом невероятной опасности, отметим – я вручаю Лорегон награду и передаю ему нашу вечную благодарность. Он – истинный герой Альянса. Ура!"}},
[5238] = {"Задание выполнено!",nil, {"Успешная диверсия против всех котлов – огромная победа!nn*Верховный палач откашливается и оглушительно орет.*nnСлушайте, слушайте все! За верную и преданную службу Отрекшимся и Орде, за выполнение долга и даже более того – перед лицом невероятной опасности, отметим – я вручаю Томпсон награду и передаю ему нашу вечную благодарность. Он – истинный герой Орды. Ура!"}},
[5241] = {"Дядюшка Карлин",nil, {"Найдите Карлина Редпата."}},
[5242] = {"Окончательный удар",nil, {"Убейте Моору и Салию и принесите голову владыки теней Скверн'дана Грете Замшелое Копыто в Оскверненный лес."}},
[5243] = {"Святая вода",nil, {"Отправляйтесь на север, в Стратхольм. Обыщите брошенные ящики с припасами и соберите 5 мер святой воды Стратхольма. Возвращайтесь к Леониду Бартоломею Чтимому, как только воды будет достаточно."}},
[5244] = {"Руины Кел'Терила",nil, {"Поговорите с Джероном Гранильщиком в деревне Звездопада."}},
[5245] = {"Беспокойные духи Кел'Терила",nil, {"Используйте кирку Джерона, чтобы добыть фрагменты реликвии высокорожденных, Принесите их Авроре Зов Небес в Восточные Чумные земли."}},
[5246] = {"Фрагменты прошлого",nil, {"Найдите Священные письмена высокорожденных в Храме Зин-Малор, в Азшаре и принесите их Авроре Зов Небес к Башне Северного перевалa, в Восточных Чумных землях."}},
[5247] = {"Фрагменты прошлого",nil, {"Принесите зачарованный слиток тория, 5 кристаллов восстановления и фиал пугающей воды Авроре Зов Небес к башне Северного Перевала, в Восточных Чумных землях."}},
[5248] = {"Измученный воспоминаниями о прошлом",nil, {"Отнесите кристалл Зин-Малора к озеру Кел'Терил в Зимних Ключах и найдите духа высокорожденного, который согласится с вами поговорить."}},
[5249] = {"В Зимние Ключи!",nil, {"Найдите в Зимних Ключах деревню Звездопада и поговорите там с Вундом Ночным Охотником."}},
[5250] = {"Звездопад",nil, {"Найдите в Зимних Ключах деревню Звездопада и поговорите там с Вундом Ночным Охотником."}},
[5251] = {"Архивариус",nil, {"Отправляйтесь в Стратхольм и отыщите архивариуса Галфорда из Алого ордена. Убейте его и сожгите архив Алого ордена."}},
[5252] = {"Раскаявшийся высокорожденный",nil, {"Поговорите с Вундом Ночным Охотником в деревне Звездопада и узнайте у него, куда нужно поместить кристалл Зин-Малора."}},
[5253] = {"Кристалл Зин-Малора",nil, {"Доставьте Кристалл Зин-Малора верховному друиду Фэндралу Оленьему Шлему в Дарнасс."}},
[5261] = {"Иган Меховщик",nil, {"Поговорите с Иганом Меховщиком."}},
[5262] = {"Ошеломляющая истина",nil, {"Отнесите голову Балназзара герцогу Николасу Зверенхоффу в Восточные Чумные земли."}},
[5263] = {"Быстрее, выше, сильнее",nil, {"Отправляйтесь в Стратхольм и убейте барона Ривендера. Принесите его голову герцогу Николасу Зверенхоффу."}},
[5264] = {"Лорд Максвелл Тиросс",nil, {"Поговорите с лордом Максвеллом Тироссом. Вы найдете его внутри храма."}},
[5265] = {"Серебряный Оплот",nil, {"Откройте Серебряный Оплот и получите награду."}},
[5281] = {"Мятущиеся души",nil, {"Найдите Эгана. В последний раз его видели недалеко от Стратхольма."}},
[5282] = {"Мятущиеся души",nil, {"Стреляйте в фантомных и призрачных горожан на улицах Стратхольма из излучателя Эгана. Когда душа вырвется из призрачной оболочки, выстрелите в нее еще раз, и она обретет свободу.nnОсвободите 15 неупокоенных душ и возвращайтесь к Эгану."}},
[5283] = {"Искусство школы Брони",nil, {"Изготовьте и отдайте Грумнусу 4 изысканных мифриловых шлема, 2 пары изысканных мифриловых сапог и 1 изысканную мифриловую кирасу."}},
[5284] = {"Путь оружейника",nil, {"Сделайте и отдайте Железнусу 4 палаша из лунной стали, 4 массивных железных топора, 2 тяжелых мифриловых топора и 2 большие черные палицы."}},
[5301] = {"Искусство школы Брони",nil, {"Чтобы присоединиться к школе брони, изготовьте и отдайте Окотосу 4 изысканных мифриловых шлема, 2 пары изысканных мифриловых сапог и 1 изысканную мифриловую кирасу."}},
[5302] = {"Путь оружейника",nil, {"Изготовьте и отдайте Боргошу 4 палаша из лунной стали, 4 массивных железных топора, 2 тяжелых мифриловых топора и 2 больших черных палицы."}},
[5305] = {"Секрет безмятежности",nil, {"Отправляйтесь в Стратхольм и убейте молотобойца из Багрового легиона. Возьмите его фартук и возвращайтесь к Лилит."}},
[5306] = {"Змеекамень Пленяющей Духов",nil, {"Отправляйтесь на Пик Черной горы, убейте Темную охотницу Вос'гаджин. Заберите у нее змеекамень и принесите Килраму."}},
[5307] = {"Порча",nil, {"Найдите в Стартхольме оружейника Черной Стражи и уничтожьте его. Возьмите его Знак Черной Стражи и принесите Сирилу Плетебою."}},
[5321] = {"Спящий пробудился",nil, {"Сопроводите Керлониана Вечный Сумрак к Лиладрис Лунной Реке на Заставу Мейстры в Ясеневом лесу."}},
[5341] = {"Сокровище Баровых",nil, {"Отправляйтесь в Некроситет и добудьте сокровище семьи Баровых. Оно состоит из четырех документов: на Каэр Дарроу, на Брилл, на Мельницу Таррен и на Южнобережье. После выполнения задания вернитесь к Алексию Барову."}},
[5342] = {"Последний из Баровых",nil, {"Отправляйтесь в лагерь Промозглого Ветра – на земли Альянса – и убейте Велдона Барова. Отнесите его голову Алексию Барову.nnСоблюдайте осторожность – скорее всего, Велдона охраняет мразь из Альянса."}},
[5343] = {"Сокровище Баровых",nil, {"Отправляйтесь в Некроситет и добудьте сокровище семьи Баровых. Оно состоит из четырех документов: на Каэр Дарроу, на Брилл, на Мельницу Таррен и на Южнобережье. После выполнения задания вернитесь к Велдону Барову."}},
[5344] = {"Последний из Баровых",nil, {"Отправляйтесь в Бастион – на земли Орды – и убейте Алексия Барова. Отнесите его голову Велдону Барову.nnСоблюдайте осторожность – скорее всего, Алексия охраняет ордынская мразь."}},
[5361] = {"Семейное древо",nil, {"Доставьте отчет дозорного утесов Долгорога Натаке Длинному Рогу на Заставу Скитающихся Духов в Пустоши."}},
[5381] = {"Рука Ируксоса",nil, {"Добудьте в крепости Громового Топора демоническую шкатулку, а затем принесите ее и кирку демонов Тайге Мудрой Гриве в деревню Ночных Охотников."}},
[5382] = {"Доктор Теолен Крастинов – Мясник",nil, {"Найдите в Некроситете доктора Теолена Крастинова. Убейте его, затем сожгите останки Евы и Люсьена Саркофф. Выполнив задание, возвращайтесь к Еве Саркофф."}},
[5383] = {"Мешок ужасов Крастинова",nil, {"Отнесите мешок ужасов Еве Саркофф в Каэр Дарроу."}},
[5384] = {"Киртонос Глашатай",nil, {"Вернитесь в Некроситет с кровью невинных. Найдите балкон и вылейте кровь в жаровню. На зов явится Киртонос.nnСражайтесь как герой, не сдавайтесь! Уничтожьте Киртоноса и возвращайтесь к Еве Саркофф."}},
[5385] = {"Останки Трея Светогорна",nil, {"Принесите останки Трея Светогорна Джессире Лунной Радуге в Оскверненный лес."}},
[5386] = {"Дорога рыбка к обеду",nil, {"Отправляйтесь в деревню Ночных Охотников и поговорите с Джинар'Зилленом, а затем принесите Натаке Длинному Рогу 2 рыбы-кровобрюха."}},
[5401] = {"Жетон Серебряного Рассвета",nil, {"Все, кто сражаются со злом во имя Серебряного Рассвета, должны получить наш амулет. Он даст тебе право получать награды у наших интендантов.nnПока ты носишь его на шее, ты сможешь собирать камни Плети с убитой нежити; да-да, иногда они носят такие знаки различия. Собирай их и приноси мне. За каждый набор ты получишь по знаку доблести Серебряного Рассвета – так мы благодарим наших героев.nnСтупай навстречу судьбе – вступай в битву со злом!"}},
[5402] = {"Камни приспешников плети",nil, {"Камни Плети приспешников – знаки различия Плети. Иногда их можно найти у нежити, у более слабых бойцов, хотя слово \"слабый\" – относительное понятие. Не стоит недооценивать Плеть. nnОт имени Серебряного Рассвета я готов вручить тебе по знаку доблести за каждые двадцать камней, причем с радостью – чем больше ты убьешь приспешников Плети, тем лучше."}},
[5403] = {"Камни захватчиков Плети",nil, {"Камни Плети приспешников – знаки различия элитных войск Плети. Порой их можно найти у сильных бойцов в Чумных землях. Убить таких бойцов – настоящий подвиг, который должно совершать во имя борьбы со злом.nnВ настоящий момент Серебряный Рассвет предлагает по знаку доблести в обмен на десять таких камней. Принеси их мне, и непременно получишь достойную награду."}},
[5404] = {"Камни осквернителей Плети",nil, {"Камень Плети Осквернителя – знак различия предводителей Плети. Он есть у каждого обладающего властью в Плети. Убийство этих проклятых тварей служит благу Серебряного Рассвета... да и вообще всеобщему благу!nnОт имени Серебряного Рассвета я вручу тебе по знаку доблести за каждый из этих знаков. Только соблюдай осторожность, Вилдан – с этими тварями шутки плохи."}},
[5405] = {"Жетон Серебряного Рассвета",nil, {"Все, кто сражаются со злом во имя Серебряного Рассвета, должны получить наш амулет. Он даст тебе право получать награды у наших интендантов.nnПока ты носишь его на шее, ты сможешь собирать камни Плети с убитой нежити; да-да, иногда они носят такие знаки различия. Собирай их и приноси мне. За каждый набор ты получишь по знаку доблести Серебряного Рассвета – так мы благодарим наших героев.nnСтупай навстречу судьбе – вступай в битву со злом!"}},
[5406] = {"Камни осквернителей Плети",nil, {"Камень Плети Осквернителя – знак различия предводителей Плети. Он есть у каждого обладающего властью в Плети. Убийство этих проклятых тварей служит благу Серебряного Рассвета... да и вообще всеобщему благу!nnОт имени Серебряного Рассвета я вручу тебе по знаку доблести за каждый из этих знаков. Только соблюдай осторожность, Херин – с этими тварями шутки плохи."}},
[5407] = {"Камни захватчиков Плети",nil, {"Камни Плети приспешников – знаки различия элитных войск Плети. Порой их можно найти у сильных бойцов в Чумных землях. Убить таких бойцов – настоящий подвиг, который должно совершать во имя борьбы со злом.nnВ настоящий момент Серебряный Рассвет предлагает по знаку доблести в обмен на десять таких камней. Принеси их мне, и непременно получишь достойную награду."}},
[5408] = {"Камни приспешников плети",nil, {"Камни Плети приспешников – знаки различия Плети. Иногда их можно найти у нежити, у более слабых бойцов, хотя слово \"слабый\" – относительное понятие. Не стоит недооценивать Плеть. nnОт имени Серебряного Рассвета я готов вручить тебе по знаку доблести за каждые двадцать камней, причем с радостью – чем больше ты убьешь приспешников Плети, тем лучше."}},
[5421] = {"Рыбка в ведерке",nil, {"Я награжу любого, кто хоть немного облегчит мою жизнь... Чем меньше я делаю, тем мне лучше, дружище!nnЗдесь неподалеку, в Деревне Ночных Охотников, прямо за пристанью, я расставил ловушки на моллюсков. Много, много ловушек! Будь так любезен, сделай за меня мою работу: собери улов и принеси мне, а я уж тебя награжу кое-чем нужным... понимаешь мою мысль, да? За каждые пять моллюсков я дам тебе по отличной рыбе-кровобрюху!"}},
[5441] = {"Ленивые батраки",nil, {"Проучите ленивых батраков дубинкой старшины. Разбудите 5 батраков, потом верните дубинку штейгеру Тазз'рилу в долине Испытаний."}},
[5461] = {"Рас Ледяной Шепот – человек",nil, {"Отправляйтесь в высокогорья Арати, в руины Стромгарда. Поищите в Стромгарде книгу Воспоминаний. Если удастся ее отыскать, отнесите ее мировому судье Мардуку."}},
[5462] = {"Рас Ледяной Шепот – гибель",nil, {"Отправляйтесь в часовню Светлых надежд в Восточных Чумных землях и найдите Леонида Бартоломея Чтимого. Покажите ему книгу Воспоминаний и расскажите обо всем, о чем удалось узнать."}},
[5463] = {"Дар Менетила",nil, {"Отправляйтесь в Стратхольм и отыщите Дар Менетила. Положите книгу Воспоминаний на оскверненную землю."}},
[5464] = {"Дар Менетила",nil, {"Отнесите Книгу Души Леониду Бартоломею в Восточные Чумные земли."}},
[5465] = {"Книга Души",nil, {"Возвращайтесь к мировому судье Мардуку в Каэр Дарроу. Покажите ему книгу души."}},
[5466] = {"Рас Ледяной Шепот – лич",nil, {"Отыщите в Некроситете Раса Ледяного Шепота. Найдя его, воспользуйтесь книгой души против его посмертного облика. Если удастся превратить Раса в смертного, убейте его и заберите человеческую голову Раса Ледяного Шепота. Отнесите голову мировому судье Мардуку."}},
[5481] = {"Задание Толстяка",nil, {"Соберите 3 ветки мракоцвета для младшего аптекаря Холланда на кладбище Брилла."}},
[5482] = {"Погибельник",nil, {"Соберите 10 веток погибельника для младшего аптекаря Холланда."}},
[5501] = {"Собиратель костей",nil, {"Принесите 10 костей с Кладбища кодо в хижину Кормека и отдайте Биббли Скобкофузу."}},
[5502] = {"Опекун Орды",nil, {"Возвратите своего подопечного в приют Оргриммара, вернув орочий сиротский свисток попечительнице сирот Батлвейл в Аллею Чести."}},
[5503] = {"Жетон Серебряного Рассвета",nil, {"Все, кто сражаются со злом во имя Серебряного Рассвета, должны получить наш амулет. Он даст тебе право получать награды у наших интендантов.nnПока при тебе амулет, ты можешь собирать камни Плети с убитой нежити – иногда они носят такие знаки различия. Собирай их и приноси мне. За каждый из них ты получишь по знаку доблести Серебряного Рассвета – так мы благодарим наших героев.nnСтупай на встречу судьбе – вступай в битву со злом!"}},
[5504] = {"Оплечья Рассвета",nil, {"Принесите 10 знаков доблести Серебряного Рассвета интенданту Хасане в Бастион."}},
[5505] = {"Ключ от Некроситета",nil, {"Ну, вот, готово, Август – это ключ от Некроситета. Я не сомеваюсь, что он позволит тебе проникнуть в пределы школы. Впрочем, есть лишь один способ узнать наверняка. И не теряй времени даром.nnУдачи тебе, герой. Да рассеют твои победы мрак!"}},
[5507] = {"Оплечья Рассвета",nil, {"Принесите 10 знаков доблести Серебряного Рассвета интенданту Искре Света в Лагерь Промозглого Ветра."}},
[5508] = {"Камни осквернителей Плети",nil, {"Камень Плети Осквернителей – знак различия предводителей Плети. Их всегда можно найти у наиболее могучих тварей. Конечно, для этого придется их убить, тем самым способствуя делу Серебряного Рассвета и добра вообще!nnОт имени Серебряного Рассвета я вручу тебе по знаку доблести за каждый из этих знаков. Только соблюдай осторожность, Мард – с этими тварями шутки плохи."}},
[5509] = {"Камни захватчиков Плети",nil, {"Камни Плети Захватчиков – знаки различия элитных войск Плети. Порой их можно найти у сильных бойцов в Чумных землях. Убить таких бойцов – настоящий подвиг, который должно совершать во имя борьбы со злом.nnСеребряный Рассвет вручает знак доблести в обмен на десять таких камней. Принеси их мне, и непременно получишь достойную награду."}},
[5510] = {"Камни приспешников плети",nil, {"Камень Плети Осквернителей – знак различия войск Плети. Такие знаки обычно можно найти у более слабых тварей низшего ранга. Однако характеристика \"более слабый\" едва ли уместна, когда речь идет о Плети. Этих тварей никогда нельзя недооценивать!nnОт имени Серебряного Рассвета я вручу тебе по знаку доблести за каждый из этих знаков – с огромным удовольствием."}},
[5511] = {"Ключ от Некроситета",nil, {"Ну, вот, готово, Дисскомаг – это ключ от Некроситета. Я не сомеваюсь, что он позволит тебе проникнуть в пределы школы. Впрочем, есть лишь один способ узнать наверняка. И не теряй времени даром.nnУдачи тебе, герой. Разгроми Некроситет и добейся заслуженной славы!"}},
[5513] = {"Оплечья Рассвета",nil, {"Принесите 10 знаков доблести Серебряного Рассвета интенданту Затвор в Часовню Последней Надежды в Восточные Чумные земли."}},
[5514] = {"Форма денег стоит",nil, {"Отнесите Кринкелю Сталлинну из Прибамбасска прочные фрагменты скелета и 15 золотых монет."}},
[5515] = {"Мешок ужасов Крастинова",nil, {"Найдите в Некроситете Джандис Барову и уничтожьте ее. Заберите мешок ужасов Крастинова. Отнесите мешок Еве Саркофф."}},
[5517] = {"Многоцветная драгоценность Рассвета",nil, {"Принесите 25 знаков доблести Серебряного Рассвета интенданту Затвор в Часовню Последней Надежды в Восточные Чумные земли."}},
[5518] = {"Броня огров Гордока",nil, {"Принесите 4 рулона рунической ткани, 8 кусков грубой кожи, 2 мотка рунической нити и огрскую дубильную кислоту Уззлу Наперстяку. Он прикован в секторе Гордока в Забытом Городе."}},
[5519] = {"Броня огров Гордока",nil, {"Вам снова нужна личина, да? Ну, мне сейчас не к спеху – принесите мне то же, что и в прошлый раз, и я изготовлю вам еще одну огрскую личину."}},
[5521] = {"Многоцветная драгоценность Рассвета",nil, {"Принесите 25 знаков доблести Серебряного Рассвета интенданту Искре Света в Лагерь Промозглого Ветра."}},
[5522] = {"Леонид Барталомей",nil, {"Принесите охлажденные яйца Леониду Бартоломею в Восточные Чумные земли."}},
[5524] = {"Многоцветная драгоценность Рассвета",nil, {"Принесите 25 знаков доблести Серебряного Рассвета интенданту Хасане в Бастион, что в Западных Чумных землях."}},
[5525] = {"Освободите Нотта!",nil, {"Эта вещь называется \"ключ от оков Гордока\" – по крайней мере, так называют его эти \"остроумцы\". Думаю, такой ключ может быть у любого из огров, но у кого именно – сказать не возьмусь. Есть вероятность, что такие ключи имеются у стражников…nnНо о чем я говорю? Он у вас есть, да? Он при вас?!"}},
[5526] = {"Осколки сквернита",nil, {"Отыщите сквернит в Забытом Городе и подберите его осколок. Есть шанс, что вам удастся его добыть, только убив Алззина Вертоградаря. Крепко заприте осколок в реликварии чистоты, затем верните его Рабину Сатурне в Ночную Гавань на Лунной поляне."}},
[5527] = {"Реликварий Чистоты",nil, {"Отправляйтесь в Силитус и найдите реликварий чистоты в руинах деревни Южного Ветра. Если вам удастся его найти, возвращайтесь к Рабину Сатурне в Ночную Гавань на Лунной поляне."}},
[5528] = {"Лучшее пойло Гордока",nil, {"Ну, ты теперь у нас большая шишка, так попробуй задаром моего лучшего пойла! Оно и сил добавляет, и голову кружит! Пей, пей, начальник – попробуй, сам увидишь!"}},
[5529] = {"Зачумленные детеныши дракона",nil, {"Убейте 20 зачумленных детенышей дракона, затем возвращайтесь к Бетине Биггльцинк в Часовню Последней Надежды."}},
[5531] = {"Бетина Биггльцинк",nil, {"Принесите охлажденные яйца Бетине Биггльцинк."}},
[5533] = {"Некроситет",nil, {"Поговорите с алхимиком Арбингтоном с вершины Холодной Скалы, что в Западных Чумных землях."}},
[5534] = {"\"Пропавшее\"оборудование Ким'джаеля",nil, {"Найдите некую руну, которую хранят наги в руинах Эльдарата и принесите ее Ким'джаелю в Азшару."}},
[5535] = {"Духовное беспокойство",nil, {"Уничтожьте по 6 теней высокорожденных обоих полов и возвращайтесь к Ло'хату в Азшару."}},
[5536] = {"Земли, полные ненависти",nil, {"Уничтожьте 6 сатиров, 2 плутов Халдарра и 2 скверноподданных из племени Халдарр и возвращайтесь к Ло'хату к границе Ясеневого леса."}},
[5537] = {"Фрагменты скелетов",nil, {"Принесите 15 фрагментов скелетов алхимику Арбингтону в Берег Промозглого Ветра в Западных Чумных землях."}},
[5538] = {"Форма денег стоит",nil, {"Отнесите Кринкелю Сталлинну из Прибамбасска прочные фрагменты скелета и 15 золотых монет."}},
[5541] = {"Боеприпасы для Громострела",nil, {"Доставьте боеприпасы Хегнару Громострелу в Дун Мороге."}},
[5542] = {"Псы-демоны",nil, {"Убейте 20 карликовых чумных псов, 5 чумных псов и 5 бесноватых чумных псов. По выполнении задания вернитесь к Тириону Фордрингу."}},
[5543] = {"Окровавленные небеса",nil, {"Убейте 30 чумных нетопырей и вернитесь к Тириону Фордрингу."}},
[5544] = {"Мясо личинок-трупоедов",nil, {"Найдите личинок-падальщиков и падальщиков-пожирателей. Убейте побольше и наберите из мяса. Возвращайтесь к Тириону Фордрингу, когда накопите 15 кусков мяса червей-трупоедов."}},
[5545] = {"Тридцать три несчастья",nil, {"Принесите 8 вязанок дров Раэлен на Лесопилку Восточной долины."}},
[5561] = {"Отлов кодо",nil, {"Приведите пять прирученных кодо Смиду Заржавеню в Лагерь Заржавня. Не забудьте вернуть ему энергетический инкапсулятор."}},
[5581] = {"Порталы Легиона",nil, {"Уничтожьте 6 порталов на поле Маннорок, а затем вернитесь к Тайге Мудрой Гриве в деревню Ночных Охотников."}},
[5582] = {"Здоровая чешуя дракона",nil, {"Отнесите здоровую чешую дракона Бетине Биггльцинк в Часовню Последней Надежды в Восточные Чумные земли."}},
[5601] = {"Сестренка Памела",nil, {"Найдите Памелу Редпат в Дарроушире."}},
[5621] = {"Облачение Луны",nil, {"Найдите часовую Шайю и залечите ее раны \"Слабым исцелением\" 2-го уровня. Затем примените к ней \"Слово силы: Стойкость\" и вернитесь к Лорне Утренний Свет в Доланаар."}},
[5622] = {"Во имя Элуны",nil, {"Поговорите в Тельдрассиле с Лорной Утренний Свет."}},
[5623] = {"Во имя Света!",nil, {"Поговорите с жрицей Жозеттой в Элвиннском лесу."}},
[5624] = {"Облачение Света",nil, {"Найдите стражника Робертса и залечите его раны \"Слабым исцелением\" 2-го уровня. Затем примените к нему \"Слово силы: Стойкость\" и вернитесь к жрице Жозетте в Златоземье."}},
[5625] = {"Облачение Света",nil, {"Надите горного пехотинца Дольфа и залечите его раны \"Слабым исцелением\" 2-го уровня. Затем примените к нему \"Слово силы: Стойкость\" и вернитесь к Максану Ковальне в Каранос."}},
[5626] = {"Во имя Света!",nil, {"Поговорите с Максаном Ковальней в Дун Мороге."}},
