[5663] = {"Прикосновение слабости",nil, {"Поговорите с Эльталисте в Подгороде."}},
[5672] = {"Благодать Элуны",nil, {"Посвятив себя служению Элуне и нашему народу, ты достигла такого положения, которое не всякому из нас доступно. По традиции за великую твою преданность тебя полагается обучить более могучим способностям. Согласна ли ты?"}},
[5673] = {"Благодать Элуны",nil, {"Поговорите в Дарнассе со жрицей Алатеей."}},
[5674] = {"Благодать Элуны",nil, {"Поговорите в Дарнассе со жрицей Алатеей."}},
[5675] = {"Благодать Элуны",nil, {"Поговорите в Дарнассе со жрицей Алатеей."}},
[5676] = {"Волшебное искажение",nil, {"Чтобы защититься от тайной магии, которая является нашим основным врагом, мы должны знать некоторые заклинания.nnЯ научу тебя заклинаниям, которые помогут защитить себя и спутников от вредоносного воздействия тайной магии."}},
[5677] = {"Волшебное искажение",nil, {"Поговорите с Верховной жрицей Лореной в Штормграде."}},
[5679] = {"Всепожирающая чума",nil, nil},
[5680] = {"Страж Тьмы",nil, {"Время обряда уже близко. Теперь я научу тебя заклинанию, которое очень пригодится тебе в битвах с врагами."}},
[5713] = {"Каждый выстрел – в цель",nil, {"Защитите жизнь часового Айнаши, а потом поговорите с Ойнеей на Заставе Мейстры в Ясеневом лесу."}},
[5721] = {"Битва при Дарроушире",nil, {"Положите сверток с реликвиями на городскую площадь Дарроушира.nnПредотвратите смерть Дейвила Яркосвета от рук Хоргуса.nnНе допустите смерти капитана Редпата до появления Редпата Падшего. nnУбейте Редпата Падшего. nnПоговорите с духом Джозефа Редпата, когда он вновь появится на городской площади, затем отправляйтесь к Памеле и поговорите с ней."}},
[5722] = {"В поисках потерянного ранца",nil, {"Обыщите Огненную пропасть в поисках тела Маура Зловещего Тотема и найдите все необычные предметы."}},
[5723] = {"Испытание силы врага",nil, {"Найдите в Оргриммаре Огненную пропасть, убейте 8 троггов и 8 шаманов Огненной пропасти и возвращайтесь к Рахауро в Громовой Утес."}},
[5724] = {"Возвращение потерянной сумки",nil, {"Отнесите сумку Маура Зловещего Тотема Рахауро в Громовой Утес."}},
[5725] = {"Силы разрушения...",nil, {"Принесите книги \"Заклинания Тьмы\" и \"Заклятия Пустоты\" Вариматрасу в Подгород."}},
[5726] = {"Тайные враги",nil, {"Принесите лейтенантский знак отличия Траллу в Оргриммар."}},
[5727] = {"Тайные враги",nil, {"Отнесите лейтенантский знак отличия Ниру Огненному Клинку. Убедите его в своей преданности клану Пылающего Клинка. Вернитесь к Траллу в Оргриммар."}},
[5728] = {"Тайные враги",nil, {"Убейте Баззалана и Жергоша Призывателя Духов и вернитесь в Оргриммар к Траллу."}},
[5729] = {"Тайные враги",nil, {"Поговорите с Нииру Огненным Клинком в Оргриммаре."}},
[5730] = {"Тайные враги",nil, {"Поговорите с Траллом в Оргриммаре."}},
[5741] = {"Скипетр Света",nil, {"Отыщите Скипетр Света и принесите его Азору Альдаморту в башню Этель-Ретор."}},
[5742] = {"Искупление",nil, {"Выслушайте Тириона Фордринга."}},
[5761] = {"Убить тварь",nil, {"Спуститесь в Огненную пропасть, убейте Тарагамана Ненасытного и принесите его сердце Нииру Огненному Клинку в Оргриммаре."}},
[5762] = {"Хеминг Эрнестуэй-младший",nil, {"Отнесите ящик Крейвела Хемингу Эрнестуэю младшему в Тернистую долину."}},
[5763] = {"Охота в Тернистой долине",nil, {"Отнесите рог кодо, принадлежащий Руну, Хемингу Эрнестуэю младшему в Тернистую долину."}},
[5781] = {"Символ ушедших дней",nil, {"Отправляйтесь в Крипту – в самой южной части Восточных Чумных земель – и добудьте молот Телана. Отнесите его Тириону Фордрингу."}},
[5801] = {"Кузня Пламенного хребта",nil, {"Отнесите форму для отливки отмычек и 2 ториевых слитка на вершину вулкана Огненного Венца в кратере Ун'Горо. Окуните форму для отливки отмычки в озеро лавы. Получится недоделанная отмычка.nnОтнесите недоделанную отмычку алхимику Арбингтону на берег Промозглого Ветра в Западные Чумные земли."}},
[5802] = {"Кузня Пламенного хребта",nil, {"Отнесите форму для отливки отмычек и 2 ториевых слитка на вершину вулкана Огненного Венца в кратере Ун'Горо. Окуните форму для отливки отмычки в озеро лавы. Получится недоделанная отмычка.nnОтнесите недоделанную отмычку аптекарю Дитерсу в Бастион, что в Западных Чумных землях."}},
[5803] = {"Скарабей Араджи",nil, {"Убейте Аража Призывателя отнесите его скарабея алхимику Арбингтону на Берег Промозглого Ветра, что в Западных Чумных землях."}},
[5804] = {"Скарабей Араджи",nil, {"Убейте Аража Призывателя и отнесите его скарабея аптекарю Дитерсу в Бастион, что в Западных Чумных землях."}},
[5805] = {"Привет!",nil, {"Передайте подарочный сертификат Златоземья Мериссе Стилвелл."}},
[5821] = {"Наемный телохранитель",nil, {"Охраняйте караван Гизлтона на пути мимо деревни кентавров из племени Колкар. О награде поговорите со Смидом в Лагере Заржавня."}},
[5841] = {"Привет!",nil, {"Принести Йори Шлемолому подарочный сертификат Караноса."}},
[5842] = {"Привет!",nil, {"Отнесите подарочный серитификат Доланаара Орентилу Шепоту Ветра."}},
[5843] = {"Привет!",nil, {"Передайте подарочный сертификат Колючего Холма Магге в Колючем Холме."}},
[5844] = {"Привет!",nil, {"Отнесите подарочный сертификат деревни Кровавого Копыта Ворну Небесному Провидцу."}},
[5845] = {"Символ утраченной чести",nil, {"Отправляйтесь в Северный дол, что в северо-восточной части Восточных Чумных земель и найдите там символ утраченной чести. После этого вернитесь к Тириону Фордрингу."}},
[5846] = {"Символ семейной любви",nil, {"Отправляйтесь на остров Каэр Дарроу в центральной части Чумных земель и поищите там картину."}},
[5847] = {"Привет!",nil, {"Отнесите подарочный сертификат Брилла Клэр Вилловер."}},
[5848] = {"Символ семейной любви",nil, {"Отправляйтесь в Статхольм в северную часть Чумных земель. Найдите в бастионе Алого ордена картину \"Символ семейной любви\", спрятанную за другой, изображающей две луны нашего мира.nnОтнесите картину Тириону Фордрингу."}},
[5861] = {"Найти Миранду",nil, {"Отправляйтесь к гробнице Утера, которая находится в южных Чумных землях. Покажите Миранде Колдунье подарок Тириона."}},
[5862] = {"Уловка против Алого ордена",nil, {"Поговорите с Мирандой, пусть она наложит на вас алую иллюзию. Отправляйтесь в Дольный Очаг, покуда она действует, и отнесите подарок Тириона верховному лорду Телану Фордрингу.nnДольный Очаг на севере, а Телан должен быть внутри крепости Марденхольд."}},
[5863] = {"Поселение Песчаного Молота",nil, {"Анди Линн из Прибамбасска просит уничтожить поселение Песчаного Молота, убив 10 громил и 10 мародеров из клана Песчаного Молота и Гор'марока Опустошителя."}},
[5881] = {"Смена караула",nil, {"Маггран просит вас передать письмо Гришу Выносливому Гонцу у Великого подъемника в Степи."}},
[5882] = {"Лекарство из охотничьих трофеев",nil, {"Тот, кто имеет при себе Маяк Кенария, может оскверненные осколки души затронутых порчей животных, убитых ради вящего блага. Я измельчаю эти осколки и делаю из них реактив, который идет на изготовление кенарийского бальзама исцеления растений. Мы используем это средство для исцеления пораженных порчей растений.nnЗа эти осколки я дам тебе немного уже готового бальзама."}},
[5883] = {"Лекарство из минералов",nil, {"Если во время поиска ископаемых в Оскверненном лесу у тебя будет с собой Маяк Кенария, ты сможешь найти порченый купорос, проникающий в жилы металлов. Я знаю, как очищать этот купорос и превращать его в реактив, который идет на изготовление кенарийского бальзама исцеления растений. Затем этим бальзамом можно исцелять пораженные растения.nnЕсли ты принесешь мне порченого купороса, я дам тебе уже готового кенарийского бальзама исцеления растений."}},
[5884] = {"Лекарство из оскверненных трав",nil, {"Маяк Кенария позволяет опрытному травнику замечать коварный зародыш Скверны – мерзостный результат поразившей Оскверненный лес порчи. В результате долгих моих трудов здесь мне удалось научиться преврашать зародыш Скверны в очищающее вещество... которое мы используем для приготовления так называемого кенарийского бальзама исцеления растений.nnВо время сбора растений в Оскверненном лесу ты сможешь замечать зародыш Скверны, пока на тебе лежит заклятие Маяк Кенария. За то, что ты мне принесешь, я дам тебе уже готового бальзама Кенария."}},
[5885] = {"Лекарство из оскверненных шкур",nil, {"Маяк Кенария позволяет опытному специалисту по снятию шкур находить куски пораженных порчей шкур среди добытого в Оскверненном лесу добра. Я перерабатываю эти вроде бы бесполезные обрывки в густую пасту, которая ускоряет действие кенарийского бальзама исцеления растений. Когда ты будешь свежевать туши убитых в Оскверненном лесу животных, позаботься, чтобы на тебе лежало заклятие Маяка Кенария!nnКогда соберешь порченые клочки, принеси их мне, и я дам тебе немного готового кенарийского бальзама исцеления растений."}},
[5886] = {"Лекарство из распыленных предметов",nil, {"Сила магического предмета соответствует силе эссенций, получающихся в результате его распыления. Мной изобретен способ переработки сырой субстанции, используемой зачаровывателями, в реактив, пригодный для создания кенарийского бальзама исцеления растений. Для этого тебе не нужен Маяк Кенария. Подойдет любой предмет, с которого сняты чары, и который выделяет простую субстанцию Пустоты.nnМне нужна всего одна порция простой субстанции Пустоты, а за это я тебе дам бальзама исцеления Кенария. Эта субстанция чрезвычайно сильна – даже малого количества хватит надолго!"}},
[5887] = {"Лекарство из охотничьих трофеев",nil, {"Обладание Маяком Кенария позволяет увидеть оскверненные осколки души затронутых порчей животных, убитых ради вящего блага. Я измельчаю эти осколки и делаю из них реактив, который идет на изготовление кенарийского бальзама исцеления растений. Мы используем это средство для исцеления пораженных порчей растений.nnЗа эти осколки я дам тебе немного уже готового кенарийского бальзама исцеления растений."}},
[5888] = {"Лекарство из минералов",nil, {"Если во время поиска ископаемых в Оскверненном лесу у тебя будет с собой Маяк Кенария, ты сможешь найти порченый купорос, проникающий в жилы металлов. Я знаю, как очищать этот купорос и превращать его в реактив, который идет на изготовление кенарийского бальзама исцеления растений. Затем этим бальзамом можно исцелять пораженные растения.nnЕсли ты принесешь мне порченого купороса, я дам тебе уже готового кенарийского бальзама исцеления растений."}},
[5889] = {"Лекарство из оскверненных трав",nil, {"Маяк Кенария позволяет опытному травнику замечать коварный зародыш Скверны – мерзостный результат поразившей Оскверненный лес порчи. В результате долгих моих трудов здесь мне удалось научиться превращать зародыш Скверны в очищающее вещество... которое мы используем для приготовления так называемого кенарийского бальзама исцеления растений.nnВо время сбора растений в Оскверненном лесу ты сможешь замечать зародыш Скверны, пока на тебе лежит заклятие \"Маяк Кенария\". За то, что ты мне принесешь, я дам тебе уже готового бальзама Кенария."}},
[5890] = {"Лекарство из оскверненных шкур",nil, {"Маяк Кенария позволяет опытному специалисту по снятию шкур находить куски пораженных порчей шкур среди добытого в Оскверненном лесу добра. Я перерабатываю эти вроде бы бесполезные обрывки в густую пасту, которая ускоряет действие кенарийского бальзама исцеления растений. Когда ты будешь свежевать туши убитых в Оскверненном лесу животных, позаботься, чтобы на тебе лежало заклятие Маяк Кенария!nnКогда соберешь испорченные куски, принеси их мне, и я дам тебе немного готового кенарийского бальзама исцеления растений."}},
[5891] = {"Лекарство из распыленных предметов",nil, {"Сила магического предмета соответствует силе эссенций, получающихся в результате его распыления. Мной изобретен способ переработки сырой субстанции, используемой зачаровывателями, в реактив, пригодный для создания кенарийского бальзама исцеления растений. Для этого тебе не нужен Маяк Кенария. Подойдет любой предмет, с которого сняты чары, и который выделяет простую субстанцию Пустоты.nnМне нужна всего одна порция простой субстанции Пустоты, а за это я тебе дам бальзама исцеления Кенария. Эта субстанция чрезвычайно сильна – даже малого количества хватит надолго!"}},
[5892] = {"Припасы Железного рудника",nil, {"Принесите 10 ящиков припасов Железного рудника интенданту Альянса в Дун Болдар."}},
[5893] = {"Припасы из рудника Ледяного Зуба",nil, {"Доставьте 10 припасов Ледяного Зуба интенданту Орды в крепость Северного Волка."}},
[5901] = {"Чума на вашу лесопилку!",nil, {"Возьмите пустую банку для термитов и отправляйтесь к большим термитникам в Восточных Чумных землях. Наберите 100 термитов Восточных Чумных земель и возвращайтесь к Микки Левину в Бастион в Тирисфальских лесах."}},
[5902] = {"Чума на вашу лесопилку!",nil, {"Найдите подходящее место в центре лесопилки Северного Кряжа в Западных Чумных землях и оставьте там бочонок термитов из Чумных земель."}},
[5903] = {"Чума на вашу лесопилку!",nil, {"Возьмите пустую банку для термитов и отправляйтесь к большим термитникам в Восточных Чумных землях. Наберите 100 термитов Восточных Чумных земель и возвращайтесь к Натаниэлю Дамаху в Лагерь Промозглого Ветра в Восточные Чумные земли."}},
[5904] = {"Чума на вашу лесопилку!",nil, {"Найдите подходящее место в центре лесопилки Северного Кряжа в Западных Чумных землях и оставьте там бочонок термитов из Чумных земель."}},
[5921] = {"Лунная поляна",nil, {"При помощи заклинания \"Телепортация: Лунная поляна\" перенеситесь на Лунную поляну. По прибытии поговорите с Дендритом Блеском Звезд в селении Ночная Гавань."}},
[5922] = {"Лунная поляна",nil, {"При помощи заклинания \"Телепортация: Лунная поляна\" перенеситесь на Лунную поляну. По прибытии поговорите с Дендритом Блеском Звезд в селении Ночная Гавань."}},
[5923] = {"Зов Природы",nil, {"Поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария в Дарнассе."}},
[5924] = {"Зов Природы",nil, {"Поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария в Дарнассе."}},
[5925] = {"Зов Природы",nil, {"Поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария в Дарнассе."}},
[5926] = {"Зов Природы",nil, {"Поговорите с Тюраком Руническим Тотемом на Вершине Старейшин в Громовом Утесе."}},
[5927] = {"Зов Природы",nil, {"Поговорите с Тюраком Руническим Тотемом на Вершине Старейшин в Громовом Утесе."}},
[5928] = {"Зов Природы",nil, {"Поговорите с Тюраком Руническим Тотемом на Вершине Старейшин в Громовом Утесе."}},
[5929] = {"Дух Великого Медведя",nil, {"Найдите духа Великого Медведя на северо-западе Лунной поляны и усвойте все, что он расскажет вам о природе медведя. Когда уроки будут закончены, вернитесь к Дендриту Блеску Звезд на Ночную Гавань в Лунной поляне."}},
[5930] = {"Дух Великого Медведя",nil, {"Найдите духа Великого Медведя на северо-западе Лунной поляны и усвойте все, что он расскажет вам о природе медведя. Когда уроки будут закончены, вернитесь к Дендриту Блеску Звезд в Ночную Гавань на Лунной поляне."}},
[5931] = {"Возвращение в Дарнасс",nil, {"Вернитесь в Дарнасс и поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария."}},
[5932] = {"Возвращение в Громовой Утес",nil, {"Вернитесь в Громовой Утес и поговорите с Тюраком Руническим Тотемом на Вершине Старейшин."}},
[5941] = {"Возвращение к Хроми",nil, {"Отнесите сумку с реликвиями Хроми в Андорал."}},
[5942] = {"Спрятанные сокровища",nil, {"Откройте сундук Джозефа ключом Джозефа."}},
[5943] = {"Караван Гизлтона",nil, {"Проводите караван Гизлтона через Поле Маннорок. О награде поговорите со Смидом: его можно найти в Лагере Заржавня."}},
[5944] = {"Во сне",nil, {"Вывести Телана Фордринга из Дольного Очага."}},
[5961] = {"Защитник королевы банши",nil, {"Поговорите с Натаносом Гнилостенем в Восточных Чумных землях. Он живет в Поместье Маррисов."}},
[5981] = {"Истребление великанов",nil, {"По просьбе Риверна Холодного Ветра убейте 4 великанов и 4 оберегателей ледяного молота."}},
[6001] = {"Тело и дух",nil, {"Посыпьте лунной пылью Кенария камень лунного совуха в Аубердине, чтобы вызвать Лунокогтя. Победите Лунокогтя, чтобы получить силу его тела и духа.nnЗатем поговорите с Матренгилом Медвежьей Лапой в Дарнассе."}},
[6002] = {"Тело и дух",nil, {"Посыпьте лунной пылью Кенария Камень Лунного совуха, что между Мулгором и Степями, чтобы вызвать Лунокогтя. Теперь вам надо побороть Лунокогтя, чтобы заслужить его силу духа и тела.nnКогда выполните это, поговорите с Тюраком Руническим Тотемом в Громовом Утесе."}},
[6004] = {"Неоконченное дело",nil, {"Убейте 2 медиков, 2 охотников, 2 волхвов и 2 рыцарей из Алого ордена и вернитесь к Кристе Чернотени в Западных Чумных землях."}},
[6021] = {"Зельдарр Изгой",nil, {"Принесите голову Зельдарра Управляющему Алену в Часовню Последней Надежды."}},
[6022] = {"Преднамеренное убийство",nil, {"Отправляйтесь на восток в Перекресток Корина и убейте несколько населяющих его созданий Плети. Соберите 7 кусков живой гнили менее, чем за 10 минут. С помощью ступки и пестика превратите живую гниль в сгущенную гниль."}},
[6023] = {"Неоконченное дело",nil, {"Убейте охотника Радли и кавалериста Даргена и вернитесь к Кристе Чернотени в Западные Чумные земли."}},
[6024] = {"Мольба Хамейи",nil, {"Убейте секретного агента Хамейю. С помощью ключа извлеките сокровище из кучи грязи за Криптой."}},
[6025] = {"Неоконченное дело",nil, {"Проберитесь в Дольный Очаг и найдите возвышенность, с которой открывается вид на весь город. Оцените силы Алого ордена и вернитесь к Кристе Чернотени в Западных Чумных землях."}},
[6026] = {"Работу пополам",nil, {"Принесите 2 ториевых слитка, 1 золотой жезл, 8 высоковзрывчатых бомб и 8 нестабильных детонаторов Смоки Ля Рю.nnДля этого вам придется найти кузнеца и инженера."}},
[6027] = {"Книга Древних",nil, {"Отыщите Книгу Древних и принесите ее Азору Альдаморту в башню Этель-Ретор."}},
[6028] = {"Донесение из Круговзора",nil, {"Доставьте донесение из Круговзора офицеру Чистосердой из ордена Серебряного Рассвета в Лагерь Промозглого Ветра в Западных Чумных землях."}},
[6029] = {"Донесение из Круговзора",nil, {"Доставьте донесение из Круговзора офицеру Гарушу из ордена Серебряного Рассвета в Бастион в Западных Чумных землях."}},
[6030] = {"Герцог Николас Зверенхофф.",nil, {"Доставьте книгу \"Исследования речи духов\" Герцогу Николасу Зверенхоффу в Часовню Последней Надежды в Восточных Чумных землях."}},
[6031] = {"Руническая ткань",nil, {"Принесите 30 отрезов рунической ткани Мелиошу в крепость Древобрюхов."}},
[6032] = {"Священная ткань",nil, {"Принесите 2 отреза луноткани Мелиошу в крепость Древобрюхов."}},
[6041] = {"Дымок над водою",nil, {"Отправляйтесь в Проклятый лес, к северо-западу от часовни Светлых надежд. Уничтожьте 8 строений Плети, отмеченных знаком взрыва, поместив особый состав Дымка внутрь строения. Дымок отметил зиккураты и бойни.nnРазместив бомбу внутри строения, немедленно выбирайтесь наружу и любуйтесь делом рук своих с безопасного расстояния. nnАх да, вот еще что, особый состав Дымка – вещество весьма нестабильное."}},
[6042] = {"Мелочи нежизни",nil, {"Истребите 20 пагубных нетопырей и 10 чудовищных чумных нетопырей. После возвращайтесь к Натаносу Гнилостеню."}},
[6061] = {"Приручение зверя",nil, {"Приручите взрослого равнинного долгонога при помощи хлыста приручения. Потренируйтесь и верните хлыст Йо Колкой Гриве в Деревне Кровавого Копыта."}},
[6062] = {"Приручение зверя",nil, {"Приручите лютого пятнистого вепря с помощью хлыста приручения. Потренируйтесь, потом верните хлыст приручения Тотару в Колючем Холме."}},
[6063] = {"Приручение зверя",nil, {"С помощью хлыста приручения приручите чащобного тенетника. Поупражняйтесь, затем верните хлыст приручения Дазалару в Доланаар."}},
[6064] = {"Приручение зверя",nil, {"Приручите большого скального вепря с помощью хлыста приручения. Потренируйтесь со своим новым питомцем, а затем верните хлыст приручения Грифу Дикому Сердцу в Каранос."}},
[6065] = {"Путь охотника",nil, {"Поговорите с Йо Колкой Гривой в Деревне Кровавого Копыта."}},
[6066] = {"Путь охотника",nil, {"Поговорите с Йо Колкой Гривой в Деревне Кровавого Копыта."}},
[6067] = {"Путь охотника",nil, {"Поговорите с Йо Колкой Гривой в Деревне Кровавого Копыта."}},
[6068] = {"Путь охотника",nil, {"Поговорите с Тотаром в Колючем Холме."}},
[6069] = {"Путь охотника",nil, {"Поговорите с Тотаром в Колючем Холме."}},
[6070] = {"Путь охотника",nil, {"Поговорите с Тотаром в Колючем Холме."}},
[6071] = {"Путь охотника",nil, {"Поговорите в Доланааре с Дазаларом."}},
[6072] = {"Путь охотника",nil, {"Поговорите в Доланааре с Дазаларом."}},
[6073] = {"Путь охотника",nil, {"Поговорите в Доланааре с Дазаларом."}},
[6074] = {"Путь охотника",nil, {"Поговорите с Грифом Диким Сердцем в Караносе."}},
[6075] = {"Путь охотника",nil, {"Поговорите с Грифом Диким Сердцем в Караносе."}},
[6076] = {"Путь охотника",nil, {"Поговорите с Грифом Диким Сердцем в Караносе."}},
[6081] = {"Обучение зверя",nil, {"Поговорите с Ормаком Лютострелом в Оргриммаре."}},
[6082] = {"Приручение зверя",nil, {"Приручите бронескорпа с помощью хлыста приручения. Потренируйтесь, потом вернитесь к Тотару в Колючем Холме."}},
[6083] = {"Приручение зверя",nil, {"Приручите приливного краба с помощью хлыста приручения. Потренируйтесь, потом верните хлыст приручения Тотару в Колючем Холме."}},
[6084] = {"Приручение зверя",nil, {"Приручите снежного леопарда с помощью хлыста приручения. Потренируйтесь со своим новым питомцем, а затем верните хлыст приручения Грифу Дикому Сердцу в Каранос."}},
[6085] = {"Приручение зверя",nil, {"Приручите медведя-ледолапа с помощью хлыста приручения. Потренируйтесь со своим новым питомцем, а затем верните хлыст приручения Грифу Дикому Сердцу в Каранос."}},
[6086] = {"Обучение зверя",nil, {"Поговорите в с Белией Громовой Гранит в Стальгорне."}},
[6087] = {"Приручение зверя",nil, {"Приручите охотника прерий при помощи хлыста приручения. Потренируйтесь и верните хлыст Йо Колкой Гриве в Деревне Кровавого Копыта."}},
[6088] = {"Приручение зверя",nil, {"Приручите перепелятника при помощи хлыста приручения. Потренируйтесь и верните хлыст Йо Колкой Гриве в Деревне Кровавого Копыта."}},
[6089] = {"Обучение зверя",nil, {"Поговорите с Хольтом Громовым Рогом в Громовом Утесе."}},
[6101] = {"Приручение зверя",nil, {"С помощью хлыста приручения приручите ночного саблезуба-охотника. Поупражняйтесь, затем верните хлыст приручения Дазалару в Доланаар."}},
[6102] = {"Приручение зверя",nil, {"С помощью хлыста приручите совиного крикуна. Поупражняйтесь, затем верните хлыст приручения Дазалару в Доланаар."}},
[6103] = {"Обучение зверя",nil, {"Поговорите с Иокастой в анклаве Кенария в Дарнассе."}},
[6121] = {"Новый урок",nil, {"Отправляйтесь в Лунную поляну и поговорите с Дендритом Блеском Звезд в селении Ночная Гавань."}},
[6122] = {"Основной источник",nil, {"Используйте пустую колбу для взятия пробы воды из устья пещеры у водопада Скалистый. nnДоставьте наполненную колбу Аландариан Песне Ночи в Аубердин, на Темный Берег."}},
[6123] = {"Создание лекарства",nil, {"Принесите 5 корешков землекорня и 12 лунных грибов Аландариан Песне Ночи в Аубердине, чтобы она могла сделать целительную мазь."}},
[6124] = {"Исцеление больных",nil, {"Нанесите мазь исцеления животных на 10 больных оленей, обитающих на Темном Берегу, – это должно их исцелить. По слухам, ареал обитания больных оленей – южнее реки Скалистой и севернее Аубердина, а далее – по всей области, вплоть до южных границ Темного Берега, где начинается Ясеневый лес."}},
[6125] = {"Власть над ядом",nil, {"Вернитесь к Матренгилу Медвежьей Лапе в анклав Кенария в Дарнассе."}},
[6126] = {"Новый урок",nil, {"Отправляйтесь в Лунную поляну и поговорите с Дендритом Блеском Звезд в селении Ночная Гавань."}},
[6127] = {"Основной источник",nil, {"Используйте пустой пробоотборник Вершины Багрового Тумана для отбора воды из озера на вершине.nnДоставьте наполненный пробоотборник Вершины Багрового Тумана Тонге Руническому Тотему в Перекресток, что в Степях."}},
[6128] = {"Создание лекарства",nil, {"Принесите 5 корешков землекорня и 5 рогов кодо Тонге Руническому Тотему в Перекресток, и он сделает целебную мазь."}},
[6129] = {"Исцеление больных",nil, {"Нанесите мазь исцеления животных на 10 больных газелей, обитающих в северной части Степей, – это должно их вылечить. По слухам, больные газели живут севернее дороги, проходящей через Перекресток с востока на запад."}},
[6130] = {"Власть над ядом",nil, {"Вернитесь к Тюраку Руническому Тотему на Вершину Старейшин в Громовой Утес."}},
[6131] = {"Союзник Древобрюхов",nil, {"Гразл желает, чтобы вы заслужили доверие. Вам нужно убить 5 воинов 5 землепроходцев и 5 садовников из племени Мертвого Леса."}},
[6132] = {"Вытащи меня отсюда!",nil, {"Проводите Мелиццу Жужжалит в лагерь у Кладбища кодо в Пустошах. Поговорите с Хормицем Жужжалитом."}},
[6133] = {"Охота на cледопытов",nil, {"Отправляйтесь в северные пределы Восточных Чумных земель и заполучите реестр Кель'Таласа. Документ находится где-то в Сторожке Кель'Литиен.nnТакже убейте 8 следопытов, 8 разведчиц дорог и 8 лесников."}},
[6134] = {"Сбор эктоплазмы",nil, {"Принесите 8 единиц эктоплазмы и ящик призрачных магнитов Хормицу Жужжалиту в Пустоши."}},
[6135] = {"Проклятый Тенекрыл",nil, {"Выследите Тенекрыла и убейте его. Принесите полоску меха Тенекрыла Натаносу Гнилостеню.nnГоворят, Тенекрыл рыщет в землях к северу от имения Марриса."}},
[6136] = {"Огромный червь",nil, {"Найдите и уничтожьте Мерзкослиза в северных пустошах Восточных Чумных земель. Возвращайтесь к Натаносу Гнилостеню по выполнению задания."}},
[6141] = {"Брат Антон",nil, {"Найдите в Пустошах брата Антона и поговорите с ним."}},
[6142] = {"Наживка из моллюска",nil, {"Найдите 10 кусочков мяса мягкокожего моллюска и принесите Май'Лахии в деревню Ночных Охотников."}},
[6143] = {"Смерть земноводным!",nil, {"Убейте 7 мирмидонов, 7 наг и 5 колдуний из клана Скользящего Плавника, а затем вернитесь в деревню Ночных Охотников и поговорите с Друлзегаром."}},
[6144] = {"Приказ по армии",nil, {"Отправляйтесь в Подгород и предстаньте перед Вериматасом."}},
[6145] = {"Курьер из Багрового Легиона",nil, {"Возвращайтесь в Восточные Чумные земли и выследите курьера из Багрового легиона. Убейте его и заберите приказ верховного рыцаря.nnЗа дальнейшими указаниями обращайтесь к Натаносу Гнилостню."}},
[6146] = {"Уловка Натаноса",nil, {"Отправляйтесь в Длань Тира к юго-востоку от поместья Маррисов. Прибыв в город, откусите от гнилого яблока. Под личиной рыцаря Алого ордена доставьте приказ верховного рыцаря лорду-рыцарю Вальдермару."}},
[6147] = {"Назад к Натаносу",nil, {"Возвращайтесь к Натаносу и отчитайтесь о выполнении задания."}},
[6148] = {"Алый Оракул Деметрия",nil, {"Алый орден сейчас в пути. Найдите оракула Деметрию и ее свиту на дороге от Длани Тира в Стратхольм. Убейте оракула и возвращайтесь к Натаносу Гнилостню."}},
[6161] = {"Сокровища Ракмора",nil, {"Найдите серебряный и золотой ключи Ракмора, а затем разыщите и отоприте сундук Ракмора."}},
[6162] = {"Последняя битва супруга",nil, {"Убейте властителя Рора и принесите его коготь Дрека'Сур на заставу Отравленной Крови в Оскверненном лесу."}},
[6163] = {"Рамштейн",nil, {"Отправляйтесь в Стратхольм и убейте Рамштайна Ненасытного. Принесите его голову Натаносу в качестве сувенира."}},
[6164] = {"Книга рецептов Августа",nil, {"Принесите Августу Помешанному в Долину Ужаса его книгу рецептов."}},
[6181] = {"Срочное сообщение",nil, {"Отнесите записку Льюиса укротителю грифонов Тору."}},
[6182] = {"Первый и последний",nil, {"Поговорите с Матиасом Шоу в Старом Городе в Штормграде. Матиас находится в казармах."}},
[6183] = {"Почитание мертвых",nil, {"Еще раз поговорите с Матиасом Шоу, если хотите принять его задание."}},
[6184] = {"Флинт Тенемор",nil, {"Отправляйтесь в Лагерь Промозглого Ветра в Западных Чумных землях и найдите Флинта Тенемора."}},
[6185] = {"Чума на востоке",nil, {"Поищите в Восточных Чумных землях сведения про некоего Гнилостеня и пропавших агентов ШРУ. Если удастся найти знаки отличия ШРУ, отнесите их Флинту Тенемору в Лагерь Промозглого Ветра."}},
[6186] = {"Пришествие Гнилостеня",nil, {"Возвращайтесь в Штормград и сообщите верховному лорду Болвару Фордрагону о судьбе Натаноса Марриса."}},
[6187] = {"Порядок должен быть восстановлен",nil, {"Соберите войско и отправляйтесь в Восточные Чумные земли. Проведите полномасштабную атаку на Натаноса Гнилостеня и любых воинов Орды, которые могут попытаться его защитить.nnБудьте осторожны, потому что Орда всеми силами будет защищать бывшего предводителя следопытов."}},
[6221] = {"Северные фурболги Мертвого Леса",nil, {"Нафиэн просит вас убить 5 дозорного из племени Мертвого Леса, 5 мстителей и 5 шаманов из племени Мертвого Леса."}},
[6241] = {"Боевые действия в деревне Зимней Спячки",nil, {"Убейте 6 тотемистов, 6 дозорных и 6 землепроходцев из племени Зимней Спячки по просьбе Сальфы."}},
[6261] = {"Дунгар Долгопив",nil, {"Отнесите ящик Озрика к укротителю грифонов Дунгару Долгопиву."}},
[6281] = {"Продолжение пути в Штормград",nil, {"Оплатите укротителю грифонов Тору полет до Штормграда и принесите записку Льюиса Озрику Штрангу в магазинчик \"Ограниченная неуязвимость\", расположенный в районе Старого города."}},
[6282] = {"Угроза со стороны гарпий",nil, {"Землепряд Маггран из Приюта у Солнечного Камня просит вас убить 7 гарпий, 7 душительниц, 7 душегубок и 7 бродяжниц из стаи Кровавой Ярости."}},
[6283] = {"Предводительница клана Кровавой Ярости",nil, {"Маггран из Приюта у Солнечного Камня просит убить Потрошительницу из стаи Кровавой Ярости и принести ему ее останки в доказательство того, что она мертва."}},
[6284] = {"Арахнофобия",nil, {"Убейте Бесселет и принесите клык Бесселет Магграну в Приют у Солнечного Камня."}},
[6285] = {"Возвращение к Льюису",nil, {"Оплатите укротителю грифонов Дунгару Долгопиву полет до Сторожевого холма, а затем отнесите ящик Озрика Льюису."}},
[6301] = {"Цикл возрождения",nil, {"Таммра Перекати-поле из Приюта у Солнечного Камня просит вас собрать 10 семян Геи."}},
[6321] = {"Поставка оружия",nil, {"Отнесите заказ Подрига Каросу Раззоку."}},
[6322] = {"Майкл Гарретт",nil, {"Отправляйтесь к Майклу Гарретту с ящиком Гордона."}},
[6323] = {"Полет в Подгород",nil, {"Оплатите у дрессировщика летучих мышей Кароса Раззока полет до Подгорода, а затем отнесите заказ Подрига Гордону Уиндему в Подгороде."}},
[6324] = {"Обратно к Подригу",nil, {"Оплатите у дрессировщика летучих мышей Майкла Гарретта полет до Гробницы, а затем отдайте ящик Гордона стражу смерти Подригу в Гробнице."}},
[6341] = {"Изобилие Тельдрассила",nil, {"Отнесите коллекцию Нессы к Веспристу."}},
[6342] = {"Полет в Аубердин",nil, {"Совершите полет в Аубердин на гиппогрифе укротителя Весприста и отнесите коллекцию Нессы Лайрду."}},
[6343] = {"Возвращение к Нессе",nil, {"Принесите ответ Лайрда Нессе Песне Теней."}},
[6344] = {"Несса Песнь Теней",nil, {"Поговорите с Нессой Песнь Теней."}},
[6361] = {"Связка шкур",nil, {"Отнесите связку шкур Зарга укротителю ветрокрылов Девраку из Перекрестка."}},
[6362] = {"Дорога до Громового Утеса",nil, {"Оплатите перелет в Громовой Утес укротителю ветрокрылов Девраку и, оказавшись на месте, отнесите связку шкур Ахану."}},
[6363] = {"Укротитель ветрокрылов Тал",nil, {"Отнесите кожаные вещи Ахану Талу из Громового Утеса."}},
[6364] = {"Возвращение к Джахану",nil, {"Оплатите перелет до Перекрестка у укротителя ветрокрылов Тала, а затем отнесите кожаные вещи Ахану Джахану Ястребиному Крылу."}},
[6365] = {"Мясные продукты в Оргриммар",nil, {"Отнесите мясные продукты Зарга Девраку из Перекрестка."}},
[6381] = {"Новая жизнь",nil, {"Посадите 10 семян Геи в земляные курганы Геи, а затем верните оставшиеся зачарованные семена Геи Таммре в Приют у Солнечного Камня."}},
[6382] = {"Большая охота",nil, {"Поговорите с Сенани Громовое Сердце на заставе Расщепленного Дерева в Ясеневом лесу."}},
[6383] = {"Большая охота",nil, {"Великая охота в Ясеневом лесу заключается в убийстве трех легендарных тварей. Ты можешь выследить их и испытать свои умения против их хитрости и силы. И в этом испытании ты, надеюсь, поймешь что-то и о себе. Вот эти чудища – медведь Топтыжень, ночной саблезуб Тенумбра и гиппогриф Острокоготь.nnЭти твари могучи, и, возможно, тебе понадобится помощь. Ели тебе удастся их одолеть, принеси мне доказательства своих подвигов."}},
[6384] = {"Дорога до Оргриммара",nil, {"Оплатите полет в Оргриммар укротителю ветрокрылов Девраку и, оказавшись в Оргриммаре, отнесите мясные продукты Зарга Гришке."}},
[6385] = {"Укротитель ветрокрылов Дорас",nil, {"Отнесите письмо Гришки Дорасу из Оргриммара."}},
[6386] = {"Возвращение на Перекресток",nil, {"Оплатите перелет в Перекресток укротителю ветрокрылов Доресу и, оказавшись на месте, передайте письмо Гришки Заргху из Перекрестка."}},
[6387] = {"Награды ученикам",nil, {"Отнесите список Брока Торгруму Боррелсону в Телcамаре."}},
[6388] = {"Грит Турден",nil, {"Отнесите почетные кирки Гриту Турдену в Стальгорне."}},
[6389] = {"Чума на вашу лесопилку!",nil, {"Выпустите термитов с Чумных земель на лесопилке Северного Кряжа и возвращайтесь к Натаниэлю Дамаху в Лагерь Промозглого Ветра в Западные Чумные земли."}},
[6390] = {"Чума на вашу лесопилку!",nil, {"Выпустите термитов с Проклятых земель на лесопилке Северного Кряжа и возвращайтесь к Микки Левину в Бастион в Тирисфальских лесах."}},
[6391] = {"Полет в Стальгорн",nil, {"Заплатите укротителю грифонов Торгруму Боррелсону, долетите на грифоне до Стальгорна и отдайте список Брока Голниру Твердопалу."}},
[6392] = {"Возвращение к Броку",nil, {"Заплатите укротителю грифонов Гриту Турдену, долетите на грифоне до Телcамара и отдайте почетные кирки Броку Камнекопу."}},
[6393] = {"Война элементалей",nil, {"Принесите 10 Огненных кристаллов Цунаману в приют Солнечного Камня."}},
[6394] = {"Кирка Тазз'рила",nil, {"Принесите штейгеру Тазз'рилу его кирку."}},
[6395] = {"Последнее желание Марлы",nil, {"Отнесите останки Самуэля Фиппса на могилу Марлы, а затем вернитесь к ученице Элрет."}},
[6401] = {"Кайя жива!",nil, {"Передайте хорошие новости Таммре Перекати-поле, живущей в Приюте у Солнечного Камня."}},
[6402] = {"Встреча в Штормграде",nil, {"Отправьтесь в Штормград и подойдите к городским воротам. Поговорите с оруженосцем Роу, чтобы он сообщил маршалу Виндзору о вашем прибытии."}},
[6403] = {"Великий Маскарад",nil, {"Сопроводите Реджинальда Виндзора через весь Штормград. Защитите его!"}},
[6421] = {"Ущелье Камнепадов",nil, {"Обследуйте глубины пещеры в ущелье Камнепадов и принесите 10 кристаллов резонита для исследования Мор'рогалу в Приют у Солнечного Камня."}},
[6441] = {"Рога сатира",nil, {"Добудьте 16 рогов сатира для Пиксель на заставе Расщепленного Дерева."}},
[6442] = {"Наги на Зорамском взморье",nil, {"Принесите 20 голов наг из клана Зловещего Хвоста Марукайе на Зорамское взморье."}},
[6461] = {"Кровопийцы",nil, {"Ксен'Зилла из Малака'джина просит вас убить 10 ползун из густого мха и 7 моховых ядоплюев."}},
[6462] = {"Оберег троллей",nil, {"Принесите 8 оберегов троллей Митсуве в форт Зорам'гар."}},
[6481] = {"Пробуждение земельника",nil, {"Откройте резонитовую емкость при помощи зачарованного кристалла резонита и затем убейте Гоггерока. Вернитесь к Мор'рогалу с новостями и зачарованным кристаллом резонита."}},
[6482] = {"Свободу Руулу!",nil, {"Сопроводите Руула, затем поговорите с Ями Снежное Копыто на заставе Расщепленного Дерева."}},
[6501] = {"Око дракона",nil, {"Найдите существа, способные вернуть силу фрагменту амулета Ока дракона. Все, что об этих существах известно – это то, что они есть."}},
[6502] = {"Амулет Пламени дракона",nil, {"Добудьте кровь могучего черного дракона генерала Драккисата, которого можно найти сидящим на троне в залах Вознесения в Пике Черной горы."}},
[6503] = {"Вестницы Ясеневого леса",nil, {"Убейте 9 вестниц из Ясеневого леса, а затем вернитесь к Курай'бину на заставу Расщепленного Дерева."}},
[6504] = {"Утраченные страницы",nil, {"Найдите 12 пропавших страниц руководства по использованию резака и соберите их по порядку глав 1, 2 и 3. Верните страницы Гурде Печати Гнева близ заставы Расщепленного Дерева."}},
[6521] = {"Нечестивый союз",nil, {"Принести голову посла Малкина Вариматрасу в Подгород."}},
[6522] = {"Нечестивый союз",nil, {"Отнесите маленький свиток Вариматрасу в Подгород."}},
[6523] = {"Защита Кайи",nil, {"Проводите Кайю Плоское Копыто до Лагеря Апарахе, затем вернитесь к Макабе Плоскому Копыту, находящемуся поблизости от юго-восточного края Каменного Когтя."}},
[6541] = {"Донесение Кадраку",nil, {"Доложитесь Кадраку в сторожевой башне в северных Степях."}},
[6542] = {"Донесение Кадраку",nil, {"Доложитесь Кадраку в сторожевой башне в северных Степях."}},
[6543] = {"Отчет клана Песни Войны",nil, {"Распакуйте связку донесений. Доставьте отчеты клана Песни Войны разведчику, гонцу и всаднику из клана Песни Войны. Возьмите у них новые донесения для Кадрака и доставьте ему в северную сторожевую башню в Степях."}},
[6544] = {"Налет Торека",nil, {"Сопровождайте Торека при нападении на аванпост Среброкрылых и в сражении с Дариэлем Лунным Пламенем, затем поговорите с Эртогом Лютоклыком на заставе Расщепленного Дерева."}},
[6545] = {"Сведения гонца из клана Песни Войны",nil, {"Так у тебя есть для меня приказ? Всегда приятно увидеть новобранца с горячей душой и твердой волей.nnТебе нужна хорошая соображалка, если хочешь задержаться здесь. Наги все сильнее угрожают нам, Ахахаха. Я сам видел. А с тех пор как я сюда прибыл, мне несколько раз приходилось отбивать их нападения на этой самой заставе.nnНо если тебе охота поучаствовать в этом, поговори с кем-нибудь еще на заставе."}},
[6546] = {"Сведения всадника из клана Песни Войны",nil, {"На этой дороге путника подстерегает много опасностей, Ахахаха. Помни это, путешествуя по этим землям. Альянс не дремлет!nnКругом враги. На востоке Ясеневого леса самой жуткой и могучей силой являются сатиры. Еще я узнал, что наги захватили большой кусок земли в Азшаре.nnПриятно, что мы получаем подкрепление. Будь всегда наготове, Ахахаха."}},
[6547] = {"Сведения разведчика из клана Песни Войны",nil, {"Держи ухо востро, |3-6(орк). В тенях часто скрываются эльфы...nnМы должны быть начеку постоянно. Нам слишком тяжело достались эти земли, и эльфы всегда ищут случая отнять их у нас.nnКак я понимаю, у тебя есть очередное поручение для меня от Кадрака?"}},
[6548] = {"Отомсти за мою деревню",nil, {"Убейте 8 бузотеров и 6 наемников из племени Зловещего Тотема, затем вернитесь к Макабе Плоскому Копыту, ожидающему вас на юго-восточной границе Когтистых гор."}},
[6561] = {"Жестокость Черных Глубин",nil, {"Принесите голову Повелителя сумрака Келриса Башане Рунический Тотем в Громовой Утес."}},
[6562] = {"Угроза из Глубин",nil, {"Поговорите с Дже'неу Санкри в Ясеневом лесу."}},
[6563] = {"Сущность Аку'май",nil, {"Принесите 20 сапфиров Аку'май Дже'неу Санкри в Ясеневый лес."}},
[6564] = {"Верность Древним богам",nil, {"Принесите отсыревшую записку Дже'неу Санкри в Ясеневый лес."}},
[6565] = {"Верность Древним богам",nil, {"Убейте Лоргуса Джетта в Непроглядной Пучине и вернитесь к Дже'неу Санкри в Ясеневый лес."}},
[6566] = {"Что принес ветер",nil, {"Выслушайте Тралла."}},
[6567] = {"Герой Орды",nil, {"Найдите Рокаро в деревне Ночных Охотников в Пустошах."}},
[6568] = {"Госпожа обмана",nil, {"Доставьте письмо Рокаро Миранде Колдунье в Западные Чумные земли."}},
[6569] = {"Иллюзии Ока",nil, {"Отправляйтесь на пик Черной горы и добудьте 20 глаз черных драконоидов. По выполнении задания вернитесь к Миранде Колдунье."}},
[6570] = {"Огнебор",nil, {"Отправляйтесь в Драконьи топи в Пылевых топях и найдите Логово Огнебора. Оказавшись внутри, наденьте амулет Обмана драконов и поговорите с Огнебором."}},
[6571] = {"Поставка для клана Песни Войны",nil, {"Соберите полностью поставку Песни Войны: лезвия для пил, веревку для бревен, масло и партию топоров Песни Войны для Локе Окарра на заставе Расщепленного Дерева."}},
[6581] = {"Пилы клана Песни Войны",nil, {"Прямо из Кабестана, свеженькие лезвия для пил Песни Войны! Да, они у меня с собой.nn..Но чем вы мне заплатите? Вы же не можете забрать их просто так! Мое время дорого стоит!nnЧестно говоря, мне нужно доброе ружье для моих поездок. Девушке ведь надо уметь защищать себя, вы ведь понимаете? Есть одно ружье, которое мне по нраву, и я уверена, что инженер без проблем такое сделает.nnИ если вы принесете мне хороший мушкетон, я отдам вам партию лезвий. Идет?"}},
[6582] = {"Черепа: Провидец",nil, {"Найдите в Зимних Ключах избранного бойца синих дракона по имени Провидец и убейте его. С его черепом вернитесь к Огнебору."}},
[6583] = {"Черепа: Сомнус",nil, {"Убейте вожака зеленых драконов Сомнуса и вернитесь с его черепом к Огнебору."}},
[6584] = {"Черепа: Хроналис",nil, {"Убейте Хроналиса, охраняющего Пещеры Времени в Пустыне Танарис и вернитесь с его черепом к Огнебору."}},
[6585] = {"Черепа: Акстроз",nil, {"Отправьтесь в Грим Батол и убейте Акстроза, избранного бойца красных драконов. Принесите его череп Огнебору."}},
[6601] = {"Вознесение",nil, {"Казалось бы, загадка разгадана. Вы знаете, что амулет Обмана Драконов, который сделала для вас Миранда Колдунья, не будет работать на пике Черной горы. Найдите Рокаро и расскажите о ваших затруднениях. Покажите ему амулет Угасающего Пламени Дракона. Будем надеяться, он знает что делать."}},
[6602] = {"Кровь могучего черного дракона",nil, {"Отправляйтесь к пику Черной горы и убейте генерала Драккисата. Соберите его кровь и вернитесь к Рокаро."}},
[6603] = {"Сложности в Зимних ключах!",nil, {"Найдите Донову Снегонору у горячего источника в Зимних ключах."}},
[6604] = {"Взбесившиеся дикие совухи",nil, {"Поговорите с Джероном Гранильщиком в деревне Звездопада."}},
[6605] = {"Странная дамочка",nil, {"Поговорите с Винной Хезард в Оскверненном лесу."}},
[6606] = {"Немного удачи",nil, {"Поговорите со знахаркой Мау'ари в Кругозоре."}},
[6607] = {"Экстремальная рыбалка Ната Пэгла",nil, {"Поймайте для Ната Пэгла следующих рыб:nМахи-махи с берега Туманных Тростников на Болоте Печали. nУдарник Сартериса с побережья Сар'Терис из Пустошей.nФералас-ахи из реки Вердантис из Фераласа.nСиний плавник Гибельного берега из Тернистой долины.nПо выполнении вернитесь к Нату Пэглу."}},
[6608] = {"Больше учить нечему",nil, {"Похоже, Лумак хочет сказать, что ему больше нечему вас учить. Возможно, стоит найти его друга Ната Пэгла – человека, который удит рыбу на островах к югу от Терамора. Кажется, Лумак имеет в виду именно это."}},
[6609] = {"С меня больше нечего взять!",nil, {"Отправляйтесь на Калимдор, в приморский город Терамор. Там к югу от города найдите остров. Нат Пэгл уже ждет."}},
[6610] = {"Мидии с сюрпризом",nil, {"Могиль Быстроруб поручил принести ему следующее:nn12 гигантских яиц. Их можно отыскать у рухов Танариса или других крупных птиц.nn10 кусков острого мяса моллюсков. Разумеется, его добывают у моллюсков!nn20 кусков альтеракского сыра. Из сыроварни!nnСобрав все составляющие, возвращайтесь к Диргу."}},
[6611] = {"Ступай в Прибамбасск!",nil, {"Отправляйтесь в Прибамбасск в пустыне Танарис и поговорите с Могилем Быстрорубом."}},
[6612] = {"Я знаю одного парня...",nil, {"Пойдите в Прибамбасск в пустыне Танарис и поговорите с Могилем Быстрорубом насчет продвижения в поварской карьере."}},
[6621] = {"Король Гиблой Чащи",nil, {"Водрузите знамя Каранга на тотемном кургане Гиблой Чащи. Не дайте фурболгам унитожить знамя. Одолейте вождя Мургута и принесите тотем Мургута Карангу Амаккару в Зорам'гар."}},
[6622] = {"Очередь на медобслуживание",nil, {"Окажите помощь пациентам, используя для перевязки их ран очередные перевязочные бинты. Вы должны оказать помощь 15 пациентам прежде, чем 6 из них успеют умереть от ран.nnПомните о порядке оказания помощи! Находящиеся при смерти солдаты умрут быстрее, чем тяжелораненые. Легкораненые могут прожить дольше всех, потому им надо оказывать помощь ПОСЛЕ того, как перевяжете тех, кто при смерти, и тяжелораненых."}},
[6623] = {"Травматология",nil, {"Отправляйтесь в Павший Молот на нагорье Арати и поговорите с доктором Грегори Виктором о том, чтобы стать травматологом."}},
[6624] = {"Очередь на медобслуживание",nil, {"Окажите помощь пациентам, используя для перевязки их ран очередные перевязочные бинты. Вы должны оказать помощь 15 пациентам прежде, чем 6 из них успеют умереть от ран.nnПомните о порядке оказания помощи! Находящиеся при смерти солдаты умрут быстрее, чем тяжелораненые. Легкораненые могут прожить дольше всех, потому им надо оказывать помощь ПОСЛЕ того, как перевяжете тех, кто при смерти, и тяжелораненых."}},
[6625] = {"Травматологи Альянса",nil, {"Отправляйтесь в Терамор в Пылевых топях и поговорите с доктором Густавом ван Схлиффом насчет зачисления в травматологи Альянса."}},
[6626] = {"Воинство зла",nil, {"Убейте 8 боевых стражей и 8 терноплетов из племени Иглошкурых, а также 8 сектанток из племени Мертвой Головы и возвращайтесь к Мириам Лунной Певице на Курганы Иглошкурых."}},
[6627] = {"Испытание знаний",nil, {"Дайте верный ответ на вопрос Брауга Тусклого Духа и поговорите с ним еще раз. Брауга можно в любое время найти в Когтистых горах."}},
[6628] = {"Испытание знаний",nil, {"Дайте верный ответ на вопрос Парквела Финталласа и поговорите с ним еще раз. Парквелла можно в любое время найти в Подгороде."}},
[6629] = {"Убить Грундига Темное Облако",nil, {"Убейте Грундига Темное Облако и 6 громил из племени Зловещего Тотема и вернитесь к Макабе Плоскому Копыту, который ждет на юго-восточной границе Когтистых гор."}},
[6641] = {"Ворша Хлестунья",nil, {"Охраняйте Муглаша на его пути к жаровне. Помогите ему погасить жаровню, а затем защищайте его, ведя бой с нагами.nnЕсли вам все удастся, возвращайтесь в форт Зорам'гар и сообщите гонцу клана Песни Войны о смерти Ворши Хлестуньи."}},
[6642] = {"Покровительство братства, черное железо",nil, {"Хотя черное железо в изобилии водится в Глубинах, оно высоко ценится в братстве. Священное оружие Рагнароса куется именно из него.nnЗа каждые 10 кусков руды черного железа, которые ты пожертвуешь братству, наградой тебе будет наше доброе отношение."}},
[6643] = {"Покровительство братства, огненное ядро",nil, {"Горе тем, кто искушает судьбу и безоглядно бросается в Огненные Недра. Кара за дерзость – мгновенная смерть, каковая не ведает жалости.nnМногие братья расстались с жизнью, отправившись на поиски огненных ядер.nnПожертвуй братству огненное ядро, и можешь рассчитывать на наше доброе отношение."}},
[6644] = {"Покровительство братства, ядро лавы",nil, {"Братство Тория помогает только тем, кто заслужил нашу дружбу и уважение, |3-6(таурен). Собственно поэтому Локтоса и уполномочили заключить с тобой договор.nnПожертвуй братству ядро лавы, и можешь рассчитывать на нашу помощь."}},
[6645] = {"Покровительство братства, сердцевинная кожа",nil, {"Во многих священных рецептурах упоминаются шкуры питомцев Рагнароса.nnПожертвуй братству два куска сердцевинной кожи, снятой с псов Недр из Огненных Недр, – и заслужишь добрую славу."}},
[6646] = {"Покровительство братства, кровь горы",nil, {"Слезы Повелителя огня – поистине великая редкость.nnПожертвуй братству кровь горы, и можешь рассчитывать на наше доброе отношение."}},
[6661] = {"Охота на крыс",nil, {"Поймайте 5 подземных крыс, используя Флейту крысолова, и принесите их Монти. Не забудьте вернуть флейту владельцу."}},
[6662] = {"Братишка Нипси",nil, {"Отвезите Нипси в подземное депо Штормграда коробку таинственного мяса."}},
[6681] = {"Поместье, Черный Ворон",nil, {"Отнесите печать Черного Ворона Фахраду в поместье Черный Ворон, сокрытое среди холмов Хилсбрада.nnНе теряйте бдительности. Поиски поместья – проверка на ловкость."}},
[6701] = {"Эмблемы Синдиката",nil, {"Мы уже давно воюем с Синдикатом. Недостаток умения они с лихвой восполняют численностью. К сожалению, их число растет быстрее, чем мы их убиваем!nnОказывается, лучший способ справиться с ними – это похитить их эмблемы. Лишившись эмблемы, опозоренный сторонник Синдиката лишается и жизни.nnОбчисти их карманы и принеси эмблемы мне или другим стражам.nnМожешь начать с Дарнхольда, что находится к югу отсюда."}},
[6721] = {"Путь охотника",nil, {"Поговорите в Доланааре с Дазаларом."}},
[6722] = {"Путь охотника",nil, {"Поговорите в Доланааре с Дазаларом."}},
[6741] = {"Больше добычи!",nil, {"Мне нужно больше обломков брони, Профессия! У тебя есть еще?"}},
[6761] = {"Новое пограничье",nil, {"Поговорите с Матренгилом Медвежьей Лапой в анклаве Кенария в Дарнассе."}},
[6762] = {"Рабин Сатурна",nil, {"Поговорите с Рабином Сатурной в селении Ночная Гавань на Лунной поляне. Лунная поляна находится между Оскверненным лесом и Зимними Ключами, туда можно добраться по дороге, идущей от крепости Древобрюхов."}},
[6781] = {"Больше обломков брони",nil, {"Хлуси, нам нужно больше обломков брони для поставки в наши войска!"}},
[6801] = {"Локолар Владыка Льда",nil, {"Я из Громового Утеса, |3-6(орк). Кэрн лично велел мне помочь клану Северных Волков в эти суровые времена.nnНо не будем тратить время! Убивайте наших врагов и несите мне их кровь. Как только наберется достаточно крови, можно будет начать ритуал призыва.nnКогда могучий элементаль обрушится на армию Грозовой Вершины, победа будет наша!"}},
[6804] = {"Отравленная вода",nil, {"Исцелите отравленных элементалей в Восточных Чумных землях с помощью аспекта Нептулона. Принесите 12 браслетов колыхающихся волноплесков и аспект Нептулона герцогу Гидраксису в Азшару."}},
[6805] = {"Буря в пустыне",nil, {"Убейте 15 пылевихрей и 15 пустынных грохотунов, и возвращайтесь к герцогу Гидраксису в Азшару."}},
[6821] = {"Око Углеглаза",nil, {"Принесите глаз Созерцателя Углей герцогу Гидраксису в Азшару."}},
[6822] = {"Огненные Недра",nil, {"Убейте 1 Повелителя огня, 1 лавового гиганта, 1 древнюю гончую бездны и 1 лавового волноплеска и возвращайтесь к герцогу Гидраксису в Азшару."}},
[6823] = {"Агент Гидраксиса",nil, {"Добейтесь уважительного отношеия к себе со стороны Гидраксианских Повелителей Вод, затем отправляйтесь к герцогу Гидраксису в Азшару."}},
[6824] = {"Руки врага",nil, {"Принесите руки Люцифрона, Сульфурона, Гееннаса и Шаззраха герцогу Гидраксису в Азшару."}},
[6825] = {"Воздух зовет – флот Гуса",nil, {"Нужно подготовить новый флот боевых виверн, |3-6(нежить)! Чтобы мои виверны могли нанести в битве решающий удар, они должны быть сытыми.nnИм нужна плоть солдат Грозовой Вершины! Сотни фунтов мяса! Уверен, ты справишься с этим заданием. Понял? Ступай!"}},
[6826] = {"Воздух зовет – флот Джезтора",nil, {"Ты отлично поработал, Бхт, но это только начало.nnМои боевые виверны должны наесться вдоволь. Тогда мы сможет сокрушить врага с неумолимой точностью!nnМой флот – второй по силе в воздушных войсках. А значит, они должны сразаться с самыми грозными противниками! Моим боевым вивернам нужно мясо лейтенантов Грозовой Вершины.nnИ поторопись, солдат!"}},
[6827] = {"Воздух зовет – флот Маэстра",nil, {"Я провел в этой проклятой дворфийской дыре долгие дни. Можешь поверить, я жажду мести!nnМы должны все четко спланировать.nnМоим боевым вивернам нужна особая цель! Я собираюсь накормить их мясом командиров Грозовой Вершины. К сожалению, эти трусы глубоко окопались в своих укреплениях. Ничего – я уверен, ты справишься с этой задачей!"}},
[6843] = {"Штуковина",nil, nil},
[6844] = {"Умбер, архивариус",nil, {"Отнесите покрытый коркой предмет Умберу – архивариусу Круга Кенария – в деревню Ночная Гавань, что на Лунной поляне."}},
[6845] = {"Раскрытие древних секретов",nil, {"Поговорите с Рабином Сатурной в деревне Ночная Гавань на Лунной поляне."}},
[6846] = {"В атаку!",nil, nil},
[6847] = {"Всевидящее око мастера Рисона",nil, {"Заберите всевидящее око мастера Рисона из пещер Ледяной Секиры и возвращайтесь к мастеру Рисону.nnБудьте осторожны: говорят, что на всевидящее око мастера Рисона наложено ужасное проклятие!"}},
[6848] = {"Всевидящее око мастера Рисона",nil, {"Заберите всевидящее око мастера Рисона из пещер Ледяной Секиры и возвращайтесь к мастеру Рисону.nnБудьте осторожны: говорят, что на всевидящее око мастера Рисона наложено ужасное проклятие!"}},
[6861] = {"Портативный резак Зинфиззлекса",nil, {"Принесите мастеру-инженеру Спустипаруnn30 ториевых слитковnn50 мифриловых слитковnn75 железных слитковnn1 паровую пилу."}},
[6862] = {"Портативный резак Зинфиззлекса",nil, {"Принесите мастеру-инженеру Спустипаруnn30 ториевых слитковnn50 мифриловых слитковnn75 железных слитковnn1 паровую пилу."}},
[6881] = {"Ивус Лесной Властелин",nil, {"Клан Северного Волка защищает энергия стихий. Если мы ослабим бдительность, их шаманы могут нас уничтожить.nnУщерб становится все больше и больше, Круг скоро не сможет его контролировать! Нам нужно звать на помощь.nnСолдаты Северного Волка носят талисманы стихий, которые называются кристаллами бури. Мы можем использовать их, чтобы связаться с Айвусом. Ступай и добудь эти кристаллы, Докторус!"}},
[6901] = {"В атаку!",nil, nil},
[6921] = {"Среди руин",nil, {"Принесите глубинный сердечник Дже'неу Санкри в форт Зорам'гар в Ясеневый лес."}},
[6922] = {"Барон Акванис",nil, {"Принесите странную водяную сферу Дже'неу Санкри в форт Зорам'гар в Ясеневом лесу."}},
[6941] = {"Воздух зовет – флот Змейера",nil, {"Мы должны что-то сделать с элитными войсками Северного Волка, которые охраняют границы, солдат! Возвращайся ко мне с медалями их лейтенантов и легионеров. Когда я пойму, что этому сброду хорошо досталось, то добью их с воздуха."}},
[6942] = {"Зов воздуха – флот Слидора",nil, {"Мои грифоны должны отправиться вглубь территории противника, чтобы нанести удар, но линия фронта слишком хорошо охраняется.nnВоины клана Северного Волка, которые охраняют линию фронта, носят на груди медали за верную службу. Сорви эти медали с их трупов и принеси мне.nnКак только линия фронта будет ослаблена, мы нанесем удар с воздуха!"}},
[6943] = {"Зов воздуха – флот Ичмена",nil, {"Их дух сломлен, солдат. После нашего неудачного налета на Орду они отказываются подниматься в воздух! Но ты можешь поднять их боевой дух.nnВозвращайся на поле битвы и лиши клан Северного Волка командования. Сокруши всех стражей и командиров! Все они носят медали. Собери с трупов столько их медалей, сколько сможешь унести, и возвращайся ко мне. Как только мои грифоны увидят награду и почуют запах поверженного врага, они снова сумеют подняться в воздух. А теперь ступай!"}},
[6961] = {"Здравствуй, Дедушка Зима!",nil, {"Поговорите с Дедушкой Зимой. Он находится неподалеку от торговца из Пастбищ Дымного Леса в Оргриммаре."}},
[6962] = {"Угощение для Дедушки Зимы",nil, {"Принесите 5 кусочков имбирного печенья и ледяное молоко Дедушке Зиме в Оргриммар."}},
[6963] = {"Кража угощения Зимнего Покрова",nil, {"Найдите агента Пастбищ Дымного Леса, пропавшего в снежных районах Альтеракских гор."}},
[6964] = {"Повод для праздника",nil, {"Поговорите с Сагорном Гривастым Странником в Аллее Мудрости Оргриммара о празднике Зимнего Покрова."}},
[6981] = {"Светящийся осколок",nil, {"Отправляйтесь в Кабестан и найдите там кого-нибудь, кто сможет вам рассказать об этом светящемся осколке.nnЗатем отнесите осколок туда, куда вас направят."}},
[6982] = {"Припасы из рудника Ледяного Зуба",nil, {"Принесите 10 ящиков с припасами Ледяного Зуба интенданту Альянса в Дун Болдар."}},
[6983] = {"Прохладное чувство юмора",nil, {"Отыщите украденные лакомства и верните их Каймарду Меднокнопу в Оргриммар. Лакомства должны быть у Омерзительного Гринча, который бродит где-то среди заснеженных вершин Альтеракских гор."}},
[6984] = {"\"Пастбища Дымного Леса\" благодарны тебе!",nil, {"Поговорите с Дедушкой Зимой в Оргриммаре."}},
[6985] = {"Припасы Железного рудника",nil, {"Доставьте 10 ящиков припасов Железного рудника интенданту Орды в крепость Северного Волка."}},
[7001] = {"Пустые стойла",nil, {"Найдите северного волка в Альтеракской долине. Подойдите к нему на достаточное расстояние, чтобы его \"приручить\", и используйте намордник клана Северного Волка. После приручения северный волк проследует за вами к смотрителю стойл из клана Северного Волка. Поговорите со смотрителем, чтобы получить награду за пойманных волков."}},
[7002] = {"Упряжь из бараньей кожи",nil, {"Одни наши солдаты занимаются поимкой волков для стойл, другие должны раздобыть необходимую упряжь.nnТебе придется поохотиться на местных баранов. Тех самых, на которых ездит кавалерия Грозовой Вершины!nnУбей этих баранов и принеси мне их шкуры. Из них мы соорудим упряжь для волков. Наездники снова сядут в седла!"}},
[7003] = {"Ликвидация великанов",nil, {"Используйте ультрасжиматель Зорбина, чтобы уменьшить великанов любого вида, каких вы найдете в Фераласе. Принесите 15 остатков миниатюризации этих великанов Зорбину Дребезгуну в порт Забытого берега в Фераласе."}},
[7021] = {"Здравствуй, Дедушка Зима!",nil, {"Поговорите с Дедушкой Зимой. Он находится неподалеку от торговца из Пастбищ Дымного Леса в Оргриммаре."}},
[7022] = {"Здравствуй, Дедушка Зима!",nil, {"Поговорите с Дедушкой Зимой – он находится неподалеку от торговца из Пастбищ Дымного Леса в Стальгорне."}},
[7023] = {"Здравствуй, Дедушка Зима!",nil, {"Поговорите с Дедушкой Зимой – он находится неподалеку от торговца из Пастбищ Дымного Леса в Стальгорне."}},
[7024] = {"Здравствуй, Дедушка Зима!",nil, {"Поговорите с Дедушкой Зимой. Он находится неподалеку от торговца из Пастбищ Дымного Леса в Оргриммаре."}},
[7025] = {"Угощение для Дедушки Зимы",nil, {"Принесите 5 кусочков имбирного печенья и ледяное молоко Дедушке Зиме в Стальгорн."}},
[7026] = {"Упряжь ездовых баранов",nil, {"Бригада Грозовой Вершины не может отправиться в бой, не имея седел! Мы же не дикари, нам нужна упряжь для наших баранов.nnМы бы, конечно, могли снять шкуры с баранов, которые пасутся рядом с базой, но это было бы исключительной глупостью. А ездить мы на чем будем?nnТы должен напасть на базу наших врагов, клана Северного Волка и снять шкуры с волков, которых они используют как верховых животных. Когда добудешь шкуры, возвращайся, из них получится отличная упряжь. Ступай!"}},
[7027] = {"Пустые стойла",nil, {"Найдите в Альтеракской долине альтеракского барана. Чтобы приручить барана, используйте учебный ошейник клана Грозовой Вершины. Для этого надо подойти в барану поближе. Пойманный баран проследует за вами к смотрителю стойл. Поговорите со смотрителем стойл, чтобы получить вознаграждение за пойманных баранов."}},
[7028] = {"Хрустальные орнаменты",nil, {"Соберите 15 терадрических хрустальных орнаментов для Ивы из Пустошей."}},
[7029] = {"Порча Злоязыкого",nil, {"Наполните церуленовый фиал с покрытием в оранжевом пруду Мародона.nnПолейте гнусь-лозу из полного церуленового фиала, выгнав ядовитую лозу.nnИсцелите 8 растений, убивая ядовитые лозы, и вернитесь к Варку Боевому Шраму в деревню Ночных Охотников."}},
[7041] = {"Порча Злоязыкого",nil, {"Наполните церуленовый фиал с покрытием в оранжевом пруду Мародона.nnПолейте гнусь-лозу из полного церуленового фиала, выгнав ядовитую лозу.nnИсцелите 8 растений, убивая ядовитые лозы, и вернитесь к Талендрии из Высоты Найджела."}},
[7042] = {"Кража угощения Зимнего Покрова",nil, {"Найдите агента Пастбищ Дымного Леса, пропавшего в снежных районах Альтеракских гор."}},
[7043] = {"Прохладное чувство юмора",nil, {"Отыщите украденные лакомства и верните их Кардену Деньгозвону в Стальгорн. Лакомства должны быть у Омерзительного Гринча, который бродит где-то среди заснеженных вершин Альтеракских гор."}},
[7044] = {"Легенды Мародона",nil, {"Добудьте две части скипетра Келебраса – жезл Келебраса и бриллиант Келебраса.nnНайдите способ поговорить с Келебрасом."}},
[7045] = {"Пастбища Дымного Леса благодарят тебя!",nil, {"Поговорите с Дедушкой Зимой в Стальгорне."}},
[7046] = {"Скипетр Келебраса",nil, {"Помогите Селебрасу Искупленному воссоздать скипетр Селебраса.nnПо завершении ритуала снова обратитесь к нему."}},
[7061] = {"Пир Зимнего покрова",nil, {"Прочитайте книгу \"Праздник Зимнего Покрова\". Отнесите книгу Кэрну Кровавому Копыту в Громовой Утес."}},
[7062] = {"Повод для праздника",nil, {"Поговорите с историком Карником в лиге Исследователей в Стальгорне о празднике Зимнего Покрова."}},
[7063] = {"Пир Зимнего покрова",nil, {"Прочтите книгу \"Праздник Зимнего Покрова\", узнайте побольше о происхождении праздника. Закончив читать книгу, отнесите ее королю Магни Бронзобороду в Стальгорн."}},
[7064] = {"Яблочко от яблоньки...",nil, {"Убейте принцессу Терадрас и вернитесь к Селендре неподалеку от деревни Ночных Охотников в Пустошах."}},
[7065] = {"Яблочко от яблоньки...",nil, {"Убейте принцессу Терадрас и вернитесь к хранителю Марандису на Высоту Найджела в Пустоши."}},
[7066] = {"Семя Жизни",nil, {"Найдите на Лунной поляне Ремула и отдайте ему семя жизни."}},
[7067] = {"Инструкции кентавра-парии",nil, {"Прочтите инструкции кентавра-парии, добудьте из Мародона амулет Соединения и верните его кентавру-парии из южной части Пустошей."}},
[7068] = {"Фрагменты осколка сумрака",nil, {"Соберите в Мародоне 10 фрагментов осколков сумрака и отнесите их Утель'наю в Оргриммар."}},
[7070] = {"Фрагменты осколка сумрака",nil, {"Соберите в Мародоне 10 фрагментов осколков сумрака и отнесите их верховному магу Тервошу в Терамор на побережье Пылевых топей."}},
[7081] = {"Кладбища Альтеракской долины",nil, {"Захватите кладбище, затем возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы."}},
[7082] = {"Кладбища долины Альтерака",nil, {"Захватите кладбище, затем возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы."}},
[7101] = {"Башни и бункеры",nil, {"Захватите башню врага и возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы."}},
[7102] = {"Башни и бункеры",nil, {"Уничтожьте вымпел на вражеской башне или бункере и возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы."}},
[7121] = {"Интендант",nil, {"Поговорите с интендантом клана Грозовой Вершины."}},
[7122] = {"Захват рудника",nil, {"Захватите рудник, который не принадлежит клану Грозовой Вершины, и возвращайтесь к сержанту Даргену Грозовой Вершине в Альтеракские горы."}},
[7123] = {"Разговор с интендантом",nil, {"Поговорите с интендантом клана Северного Волка."}},
[7124] = {"Захват рудника",nil, {"Захватите рудник и возвращайтесь к капралу Тике Кровавому Рыку в Альтеракские горы."}},
[7141] = {"Битва за Альтерак",nil, {"Отправляйтесь в Альтеракскую долину, сразитесь с генералом Орды Дрек'Таром и возвращайтесь к проспектору Камнетерке в Альтеракские горы."}},
[7142] = {"Битва за Альтерак",nil, {"Отправляйтесь в Альтеракскую долину и убейте генерала дворфов, Ванндара Грозовую Вершину. После этого возвращайтесь к Вогге Смертной Хватке в Альтеракские горы."}},
[7161] = {"Испытательные земли",nil, {"Отправляйтесь в пещеры Дикой Лапы, расположенные на юго-востоке от основной базы в Альтеракской долине, и добудьте знамя клана Северного Волка. Отнесите знамя воеводе Лаггронду."}},
[7162] = {"Испытательные земли",nil, {"Отправляйтесь в пещеры Ледяного Крыла в Альтеракской долине, на юго-западе от Дун Болдара, и добудьте знамя клана Грозовой Вершины. Отдайте знамя лейтенанту Хаггердину в Альтеракских горах."}},
[7163] = {"Награда найдет героя",nil, {"*Воевода Лаггронд отдает вам салют.*nnПришло твое время, Снатяжкой. Ты столько сделал для клана Северного Волка, что можешь считаться не просто солдатом, а настоящим другом.nnОтлично! Давай свой знак новобранца."}},
[7164] = {"Уважение клана",nil, {"Рад снова тебя видеть, |3-6(Жрец). Не ожидал, что ты вернешься. Но вот, ты зарекомендовал себя как отважный и доблестный воитель клана Северного Волка.nnПришло время получить новое звание, Шэдоустайл. А с ним и новый знак отличия.nnДай мне свой знак."}},
[7165] = {"Заслуженное почтение",nil, {"Ты убил так много врагов, что реки покраснели от крови. Твоя стойкость в бою достойна всяческих похвал. Я верю, ты приведешь нашу армию к великим победам. Солдат, ты заслужил новое звание.nnПредъяви свой знак отличия."}},
[7166] = {"Легендарные герои",nil, {"Я чувствую исходящие от тебя мощь и силу, |3-6(Чернокнижник). Клан Северного Волка восхищен твоим героизмом. Мы знаем, что враги трепещут при одном упоминании твоего имени.nnВстань, великий воитель Северного Волка. Встань и прими хвалу!nnДай мне свой знак отличия."}},
[7167] = {"Око Командования",nil, {"Дрек'Тар наслышан о твоих подвигах. В Калимдоре только и говорят, что о твоем героизме. Сам Вождь гордится тем, что ты сделал для нашего клана.nnТы заслужил Око Командования.nnПредъяви свой знак отличия, командир Дулозалепи.nn*Воевода Лаггронд отдает салют.*"}},
[7168] = {"Награда найдет героя",nil, {"Жизнь многих заканчивается раньше, чем они получают то, чего заслуживают. Ты показал себя образцовым солдатом, |3-6(Охотник). Время пришло.nnДай мне свой отличительный знак."}},
[7169] = {"Уважение Стражи",nil, {"База полна слухами о твоих подвигах на Поле брани. Ты нанес нашим врагам могучий удар, от которого они не скоро оправятся. Ты заслужил уважение Грозовой Вершины.nnПредъяви свой знак отличия!"}},
[7170] = {"Заслуженное почтение",nil, {"Ответь мне, Рен. Видишь ли ты страх в глазах своих врагов? Они разбегаются в панике при виде тебя? Должно быть, понимают, что у них нет ни шанса.nnТы заслужил почтительное отношение Стражи. Дай мне свой знак отличия."}},
[7171] = {"Легендарные герои",nil, {"Предо мной стоит великий воитель Альянса!nn*Лейтенант Мурп отдает салют.*nnНемногие в Грозовой Вершине удостоены такого ранга. Я видел, как ты сокрушаешь врагов. Один твой взгляд повергает их в трепет. Когда ты вступаешь в бой, в сердцах наших солдат загорается надежда.nnДай мне свой знак отличия."}},
[7172] = {"Око Командования",nil, {"Пусть они услышат тебя, командир Дайчи! Пусть познают, что такое страх. Покажи им силу Грозовой Вершины!nnПредъяви свой знак отличия."}},
[7181] = {"Легенда о Корраке",nil, {"Согласно легенде, вождь могущественного племени Ледяной Секиры периодически появляется на поле боя, внося хаос и опустошение в жизнь обитателей Альтеракской долины.nnЕсли Коррак появится, убейте его и возвращайтесь к воеводе Лаггронду."}},
[7201] = {"Последняя Стихия",nil, {"Отправляйтесь в Глубины Черной горы и добудьте 10 мер сущности Стихий. Стоит начать поиски с големов и их создателей. Вивиан Лаграв также бормотала что-то про элементалей."}},
[7202] = {"Коррак Кровопуск",nil, {"Согласно легенде, вождь могущественного племени Ледяной Секиры периодически появляется на поле боя, внося хаос и опустошение в жизнь обитателей Альтеракской долины.nnЕсли вам удастся найти Коррака, убейте его и возвращайтесь к лейтенанту Хаггердину в Альтеракские горы."}},
[7221] = {"Разговор с геологом Камнетеркой",nil, {"Поговорите с геологом Камнетеркой в гарнизоне Дун Болдара."}},
[7222] = {"Разговор с Воггой Смертобоем",nil, {"Поговорите с Воггой в крепости Северного Волка."}},
[7223] = {"Обломки брони",nil, {"Принесите 20 обломков брони Марготу Подземной Кузне в Дун Болдар."}},
[7224] = {"Вражеский трофей",nil, {"Принесите 20 обломков доспехов кузнецу Регзару в деревню Северного Волка."}},
[7241] = {"Защита клана Северного Волка",nil, {"Отправляйтесь в Альтеракскую долину, расположенную в Альтеракских горах. Найдите воеводу Лаггронда у входа в тоннель и поговорите с ним, чтобы начать свою военную карьеру в клане Северного Волка. Альтеракская долина находится к северу от Мельницы Таррен у подножия Альтеракских гор."}},
[7261] = {"Королевское право",nil, {"Отправляйтесь в Альтеракскую долину, к предгорьям Хилсбрада. Найдите лейтенанта Хаггердина рядом со входом в туннель и поговорите с ним.nnВо славу клана Бронзобородов!"}},
[7281] = {"Братская любовь",nil, {"Отправляйтесь на территорию клана Грозовой Вершины и убейте командора Карла Филипса. После выполнения задания возвращайтесь к командору Луи Филипсу."}},
[7282] = {"Братская любовь",nil, {"Отправляйтесь на территорию клана Северного Волка и убейте командира Луи Филипса. После выполнения задания возвращайтесь к командиру Карлу Филипсу."}},
[7301] = {"Свергнутые властители небес",nil, {"Отправляйтесь в крепость Северного Волка и поищите командиров звена Слидора, Змейера и Ичмана. Вернитесь с отчетом к командиру Даффи."}},
[7302] = {"Свергнутые властители небес",nil, {"Выясните, что стало с командирами звена Гусом, Джезтором и Маэстром. Вернитесь с отчетом к командиру Мулфорту."}},
[7321] = {"Нежный черепаховый суп",nil, {"Принесите 10 кусков черепашьего мяса и немного смягчающих пряностей Кристофу Френчу в Мельницу Таррен."}},
[7341] = {"Честная сделка",nil, {"Привет, |3-6(орк)! Я – Коларк, охотник за головами из Громового Утеса. Я обошел чуть ли не весь мир и повидал немало.nnПутешествуя в Южных морях, я раздобыл эти замечательные стрелы с ториевыми наконечниками. Этих стрел у меня сколько угодно, но, увы, у меня кончились ториевые патроны! А какой уважающий себя таурен будет пользоваться хлипким луком? Стрелы мне ни к чему!nnДавай поменяемся: я тебе – стрелы, а ты мне – такое же количество ториевых патронов! Идет?"}},
[7342] = {"Стрелы – для неженок!",nil, {"Нет, ты представляешь?! Последнюю партию снарядов из Караноса запороли! И остается стрелять этими хлипкими, ни на что не годными стрелами! Что мне делать с 500 000 стрел с ториевыми наконечниками?nnВВот что, парень. Если принесешь мне ториевых патронов, я отдам за них эти стрелы с ториевыми наконечниками, прямо сразу и отдам! Идет?"}},
[7361] = {"Благосклонность племени Черного Копья",nil, {"Вам поручили убивать в Альтеракской долине ночных эльфов.nnУбив ночного эльфа, вернитесь с его отрубленной головой к Нажьяку Проклятуну в крепость Северного Волка."}},
[7362] = {"Союзник тауренов",nil, {"Убивайте гномов в Альтеракской долине.nnУбив гнома, принесите Раваку Зловещему Тотему в крепость Северного Волка пучок волос гнома."}},
[7363] = {"Человечья жизнь",nil, {"Вам поручили убивать людей в Альтеракской долине.nnУбив человека, принесите командору Луи Филипсу (который бродит между линией фронта и крепостью Северного Волка) осколок человеческой кости.nnОкончить человеческие страдания совсем несложно!"}},
[7364] = {"Награда Гномрегана",nil, {"Убивайте тауренов в Альтеракской долине.nnУбив таурена, вернитесь к Дирку Надувалло в Дун Болдар с копытом таурена."}},
[7365] = {"Реквием Оленьего Шлема",nil, {"Ваше задание – убивать в Альтеракской долине троллей.nnУбейте тролля и возвращайтесь к Атраманису в Дун Болдар с амулетом тролля из племени Черного Копья."}},
[7366] = {"Милосердие архиепископа",nil, {"Ваше задание – убивать в Альтеракской долине Отрекшихся.nnУбейте Отрекшегося и возвращайтесь к Карлу Филипсу (он ходит между лесопилкой и рудником) с сердцем Отрекшегося."}},
[7367] = {"Обезвредить угрозу",nil, {"Найдите эксперта по взрывчатым веществам из клана Северного Волка и убейте его. После завершения задания возвращайтесь к эксперту по взрывчатым веществам из клана Грозовой Вершины."}},
[7368] = {"Обезвредить угрозу",nil, {"Найдите эксперта по взрывчатым веществам из клана Грозовой Вершины и убейте его. После завершения задания возвращайтесь к эксперту по взрывчатым веществам из клана Северного Волка."}},
[7381] = {"Возвращение Коррака",nil, {"В случае, если Коррак Кровопуск возвратится на Поле брани, найдите его и уничтожьте.nnПринесите череп Коррака воеводе Лаггронду в предгорья Хилсбрада."}},
[7382] = {"Коррак Бессмертный",nil, {"В случае, если Коррак Кровопуск возвратится на Поле брани, найдите его и уничтожьте.nnПринесите череп Коррака лейтенанту Хаггердину в предгорья Хилсбрада."}},
[7383] = {"Венец Земли",nil, {"Наполните аметистовый фиал и принесите его Коритрасу Лунному Гневу в Доланаар."}},
[7385] = {"Галлон крови",nil, {"Знаешь, Профессия – давай-ка ты мне будешь носить кровь галлонами. Зачем мелочиться?"}},
[7386] = {"Гроздь кристаллов",nil, {"Наверняка ты попадешь в битвы, которые будут длиться долгие дни. В такие времена не забывай собирать кристаллы Бури клана Северного Волка.nnКруг Кенариуса будет рад подобным дарам, Хлуси."}},
[7401] = {"РАЗЫСКИВАЮТСЯ: дворфы!",nil, {"Ваша задача – убивать дворфов в Альтеракской долине.nnУбейте дворфа и вырвите ему хребет. Отнесите хребет дворфа сержанту Язре Кровавый Рык в крепость Северного Волка."}},
[7402] = {"Разыскиваются: ОРКИ!",nil, {"Ваше задание – убивать орков в Альтеракской долине.nnУбейте орка и доставьте зуб орка капралу Норгу Грозовой Вершине в Дун Болдар."}},
[7421] = {"Защита Черного Копья",nil, nil},
[7422] = {"Хоть шерсти клок...",nil, nil},
[7423] = {"Костяная лихорадка",nil, nil},
[7424] = {"Какое такое копыто?",nil, nil},
[7425] = {"Амулеты для Оленьего Шлема",nil, nil},
[7426] = {"Любовь к ближнему",nil, nil},
[7427] = {"Разыскивается: БОЛЬШЕ ДВОРФОВ!",nil, nil},
[7428] = {"Разыскивается: БОЛЬШЕ ОРКОВ!",nil, nil},
[7429] = {"Освободите Нотта!",nil, {"Эта вещь называется \"ключ от оков Гордока\" – по крайней мере, так называют его эти \"остроумцы\". Думаю, такой ключ может быть у любого из огров, но у кого именно – сказать не возьмусь. Есть вероятность, что такие ключи имеются у стражников…nnНо о чем я говорю? Он у вас есть, да? Он при вас?!"}},
[7441] = {"Пузиллин и старейший Аж'Тордин",nil, {"Отправляйтесь в Забытый Город и отыщите беса Пузиллина. Любыми доступными средствами убедите Пузиллина отдать вам книгу заклинаний Аж'Тордина.nnЕсли добудете книгу, вернитесь к Аж'Тордину в павильон Лорисс в Фераласе."}},
[7461] = {"Древнее безумие",nil, {"Перебейте стражей, которые охраняют 5 столпов, снабжающих энергией тюрьму Пламе'тара. После того как столпы угаснут, силовое поле, удерживающее Пламе'тара, рассеется.nnВойдите в тюрьму Пламе'тара и уничтожьте злобного демона, который находится внутри. И, наконец, сразитесь с принцем Тортелдрином в Читальне.nnЕсли задание будет выполнено успешно, вернитесь во двор, к древней Шен'дралар."}},
[7462] = {"Сокровище Шен'драларов",nil, {"Вернитесь в Читальню и найдите сокровище Шен'драларов. Получите свою награду!"}},
[7463] = {"Магический напиток",nil, {"Отправляйтесь в квартал Криводревов Забытого Города и убейте элементаля воды Гидротварь. Вернитесь в Читальню к сказителю Лидросу и принесите ему сущность Гидротвари."}},
[7481] = {"Эльфийские легенды",nil, {"Попытайтесь найти в Забытом Городе Кариэля Винтхалуса. Вернитесь в лагерь Мохаче к ведуну Королуску и сообщите ему все, что вам удалось узнать."}},
[7482] = {"Эльфийские легенды",nil, {"Попытайтесь найти в Забытом Городе Кариэля Винтхалуса. Вернитесь в крепость Оперенной Луны к исследовательнице Рунический Шип и сообщите ей все, что вам удалось узнать."}},
[7483] = {"\"Манускрипт Скорости\"",nil, {"Принесите сказителю Лидросу в Забытом Городе \"Манускрипт Скорости\", 1 безупречный черный алмаз, 2 больших сияющих осколка и 2 образца крови героев, чтобы получить магический камень стремительности."}},
[7484] = {"\"Манускрипт Средоточия\"",nil, {"Принесите сказителю Лидросу в Забытом Городе \"Манускрипт Средоточия\", 1 безупречный черный алмаз, 4 больших сияющих осколка и 2 образца шкуры тени, чтобы получить магический камень сосредоточения."}},
[7485] = {"\"Манускрипт Защиты",nil, {"Принесите сказителю Лидросу в Забытом Городе \"Манускрипт Защиты\", 1 безупречный черный алмаз, 2 больших сияющих осколка и 1 потертый шовный материал поганища, чтобы получить магический камень защиты."}},
[7486] = {"Награда для героя",nil, {"Заберите свою награду из сундука Гидраксиса."}},
[7487] = {"Сродство с недрами",nil, {"Отправляйтесь через портал, ведущий к Огненным Недрам в Глубинах Черной горы и добудьте фрагмент ядра. Принесите его Лотосу Хранителю Портала к Черной горе."}},
[7488] = {"Сеть Лефтендрис",nil, {"Принесите сеть Лефтендрис Латроникусу Лунному Копью в Крепость Оперенной Луны в Фераласе."}},
[7489] = {"Сеть Лефтендрис",nil, {"Принесите сеть Лефтендрис Тало Терновому Копыту в Лагере Мохаче в Фераласе."}},
[7490] = {"Победа Орды",nil, {"Отнесите голову Ониксии Траллу в Оргриммар."}},
[7491] = {"На виду у всех",nil, {"Найдите властителя Рунтака в Аллее Силы."}},
[7492] = {"Лагерь Мохаче",nil, {"Поговорите с Тало Терновым Копытом в Лагере Мохаче в Фераласе."}},
[7493] = {"Все еще только начинается!",nil, {"Драконоубийца и опытный кожевник:опытная кожевница;? Ты не перестаешь изумлять меня, герой. Я сражен.nnПожалуйста, прими как дар то, чему я собираюсь тебя научить. Это знание окажется бесценным для тебя, если ты намерен уничтожить последних черных драконов.nnИзготовь плащ из чешуи драконьей царицы. Он будет защищать тебя и твоих союзников от опаляющего дыхания владыки Черной горы... Нефариана."}},
[7494] = {"Крепость Оперенной Луны",nil, {"Поговорите с Латроникусом Лунным Копьем в Крепости Оперенной Луны в Фераласе."}},
[7495] = {"Славная победа Альянса",nil, {"Отнесите голову Ониксии Верховному лорду Болвару Фордрагону в Штормград."}},
[7496] = {"Праздник добрых времен",nil, {"Найдите майора Маттингли в Аллее Героев."}},
[7497] = {"Все еще только начинается!",nil, {"Драконоубийца и опытный кожевник:опытная кожевница;? Ты не перестаешь изумлять меня, герой. Я сражен.nnПожалуйста, прими как дар то, чему я собираюсь тебя научить. Это знание окажется бесценным для тебя, если ты намерен уничтожить последних черных драконов.nnИзготовь плащ из чешуи драконьей царицы. Он будет защищать тебя и твоих союзников от опаляющего дыхания владыки Черной горы... Нефариана."}},
[7498] = {"Гарона: Исследование уловок и предательства",nil, {"Верните книгу законным владельцам."}},
[7499] = {"Кодекс Обороны",nil, {"Верните книгу законным владельцам."}},
[7500] = {"Поваренная книга чародея",nil, {"Верните книгу законным владельцам."}},
[7501] = {"Свет и как его раскачать",nil, {"Верните книгу законным владельцам."}},
[7502] = {"Укрощая тени",nil, {"Верните книгу законным владельцам."}},
[7503] = {"Величайшая гонка охотников",nil, {"Верните книгу законным владельцам."}},
[7504] = {"Святая Болонья: О чем не говорит Свет",nil, {"Верните книгу законным владельцам."}},
[7505] = {"Ледяной шок и вы",nil, {"Верните книгу законным владельцам."}},
[7506] = {"Изумрудный сон",nil, {"Верните книгу законным владельцам."}},
[7507] = {"Справочник Форора",nil, {"Верните \"Справочник Форора по убийству драконов\" в Читальню."}},
[7508] = {"Ковка Кель-Серрара",nil, {"Отдайте тусклый и плоский эльфийский клинок сказителю Лидросу."}},
[7509] = {"Ковка Кель-Серрара",nil, {"Заставьте Ониксию дохнуть своим огненным дыханием на потускневший древний клинок. После этого схватите раскаленный древний клинок. Имейте в виду, что раскаленным он останется ненадолго, так что медлить не следует!nnПоследнее, что нужно сделать – это убить драконицу и вонзить раскаленный древний клинок в ее труп.nnСделайте это – и Кель-Серрар навеки станет вашим!"}},
[7521] = {"Громораан Искатель Ветра",nil, {"Чтобы освободить Громораана Искателя Ветра из тюрьмы, отнесите правый и левый наручник Искателя Ветра, 10 слитков элементия и сущность Повелителя Огня верховному лорду Демитриану."}},
[7522] = {"Сосуд Возрождения",nil, {"Изучите Сосуд Возрождения и вернитесь к верховному лорду Демитриану."}},
[7541] = {"Служба Орде",nil, {"Нашим ученым нужно время, чтобы изучить принесенную тобой подвеску. Но за то, что ты сделал для Орды, я хочу наградить тебя этим знаком.nnСпасибо, Томпсон, твой поступок делает честь тебе и полезен нам."}},
[7562] = {"Мор'зул Вестник Крови",nil, {"Поговорите с Мор'зулом Кровопуском в Пылающих степях."}},
[7563] = {"Ярость крови",nil, {"Принесите 30 бутылок крови яростных зверей Мор'зулу Кровопуску в Пылающие степи."}},
[7564] = {"Дикоглаз",nil, {"Принесите ларец Крови Горзиеки Дикоглазу в Пылающие степи."}},
[7581] = {"Тюремные кандалы",nil, {"Отправляйтесь в Забытый Город, что в Фераласе и добудьте 15 порций крови сатиров из племени Исчадий Природы, что живут в Квартале Криводревов. Вернитесь к Дайо из Гниющего шрама по выполнении поручения."}},
[7582] = {"Тюремная камера",nil, {"Отправляйтесь к Теснине Зловещего Шепота в Зимних ключах и добудьте 5 слез Хедерины у демонов Хедерины, что населяют эту теснину. Вернитесь к Дайо из Гниющего шрама по выполнении поручения."}},
[7583] = {"Подавление",nil, {"Отправляйтесь в Гниющий шрам и и найдите командира стражников ужаса.nnПри помощи светящегося кристалла заточения поймайте командира стражников ужаса. Будьте готовы к отчаянным атакам и сопротивлению, поскольку демон будет пытаться избежать заточения.nnЕсли вы добьетесь успеха, отнесите пленного стражника ужаса Дайо в Гниющий шрам."}},
[7601] = {"Что хочет Ниби",nil, {"Поговорите с Бесенком в Оскверненном лесу."}},
[7602] = {"Безупречная субстанция Скверны",nil, {"Бесенок из Оскверненного леса просит вас принести ему три порции безупречной субстанции скверны из трех различных мест.nnБезупречной субстанцией в Азшаре владеют сатиры из племени Легаши. В Джеденаре она есть у джеженарских легионеров. Стражи Скверны в Выжженных землях владеют этой субстанцией в своих краях.nnДобудьте три типа безупречной субстанции Скверны и возвращайтесь к Бесенку в Оскверненный лес."}},
[7603] = {"Огненное ядро Крошиуса",nil, {"Обыщите долину Рваных Ран в Оскверненном лесу и найдите останки Крошиуса. Когда вы их найдете, поднесите к ним пламень Скверны и подождать, пока Крошиус не восстанет из праха. Когда инфернал очнется, убейте его и возьмите из его тела огненное ядро Крошиуса.nnПринесите огненное ядро Крошиуса Ниби Всемогущему в Оскверненный лес."}},
[7604] = {"Заключение договора",nil, {"Подпишите договор с представителем Братства Тория Локтосом Зловещим Торговцем, если вам нужен чертеж Сульфурона."}},
[7621] = {"Предупреждение",nil, {"Жрец, ты стоишь на пороге серьезных испытаний, которым подвергнешься для проверки твоих способностей.nnПослушай, что я скажу тебе.nnЗапомни, ты должен проходить испытания самостоятельно. Пусть твои союзники отойдут на безопасное расстояние и ждут, не вмешиваясь. Любое вмешательство может повлечь за собой самую строгую кару.nnСудьба привела сюда тебя и только тебя. Вступи в битву с честью. Да пребудет с тобой Свет."}},
[7622] = {"Баланс Света и Тьмы",nil, {"Спасите жизни 50 крестьян прежде, чем 15 из них будут убиты. Поговорите с Эрис Тайнопламень по выполнению задания.nnЧисло убитых крестьян можно узнать из почты Смерти."}},
[7623] = {"Лорд Гиблопуст",nil, {"Купите зелья Теней у Горзиеки в Пылающих степях.nnПриняв зелья, пройдите в Джеденар и поговорите с лордом Гиблопустом."}},
[7624] = {"Улатек Предатель",nil, {"Сразитесь с Улатеком и принесите сердце предателя лорду Гиблопусту в Джеденар."}},
[7625] = {"Зоротианская звездная пыль",nil, {"Купите зоротианскую звездную пыль у Ур'Дана. Отнесите ее Горзиеки Дикоглазу в Пылающие степи."}},
[7626] = {"Колокол Детмуры",nil, {"Отнесите 2 эликсира Силы Теней Горзиеки Дикоглазу в Пылающие степи."}},
[7627] = {"Колесо Черного Похода",nil, {"Отнесите 6 больших блестящих осколка и 25 кусков руды черного железа Дикоглазу в Пылающие степи."}},
[7628] = {"Свеча Судного Дня",nil, {"Принесите 3 куска чешуи черного дракона Горзики Дикоглазу в Пылающие степи."}},
[7629] = {"Доставка беса",nil, {"Отнесите беса в бутылке в алхимическую лабораторию Некроситета. После создания пергамента верните бутылку Горзики Дикоглазу."}},
[7630] = {"Арканит",nil, {"Принесите 1 арканитовый слиток Горзики в Пылающие степи."}},
[7631] = {"Ксоротианский конь погибели",nil, {"Прочтите инструкции Мор'зула. Призовите ксоротианского коня погибели, одолейте его и подчините его дух."}},
[7632] = {"Древний лист",nil, {"Найдите хозяина древнего окаменелого древесного листа. Удачи, <имя>, мир велик."}},
[7633] = {"Посвящение",nil, {"Твоя преданность и искренность не остались незамечены, |3-6(Охотник).nnМы – Дерева, защитники этого некогда великого леса. Наши тела погибли 10 000 лет назад, когда Архимонд и Пылающий Легион прошли по горе Хиджал. Однако, души наши не погибли.nnЕсть у нас несколько заданий для тебя. Если ты их выполнишь, тебе будут открыты тайны Дерев.nnЗнай – если выполнишь их, просто возвращайся к этой скале, и мы ответим на твой зов."}},
[7634] = {"Перетянутый жилами лист древня",nil, {"Хастат Древний просит вас принести ему жилу взрослого синего дракона. По выполнении задания возвращайтесь к Хастату в Оскверненный лес."}},
[7635] = {"Подходящая тетива",nil, {"Стома Древний просит вас принести ему жилу взрослого черного дракона. Добыв жилу, возвращайтесь к Стоме в Оскверненный лес."}},
[7636] = {"Ветвь ходячего Древа",nil, {"Вы должны найти и уничтожить следующих четверых демонических разносчиков порчи :nnСимон Совратительницу.nnКлинфрана Безумного.nnСоленора Убийцу.nnАрториуса Судьбоносного.nnУничтожьте этих тварей и возвращайтесь к Вартусу Древнему в Оскверненный лес с их головами.nnВ поисках местонахождения этих демонов сверяйтесь с окаменевшей корой.nnВы ОБЯЗАНЫ выполнить это задание в одиночку."}},
[7637] = {"Жертва необходима",nil, {"Доберитесь до Стальгорна и получите курильницу экзорцизма у верховного жреца Рохана. За курильницу нужно будет внести плату в размере 150 золотых."}},
[7638] = {"Лорд Грейсон Тенелом",nil, {"Поговорите с лордом Грейсоном Тенеломом в Храмовом квартале Штормграда."}},
[7639] = {"Демонстрация истинного правосудия",nil, {"Принесите курильницу экзорцизма лорду Грейсону Тенелому в храмовый квартал Штормграда."}},
[7640] = {"Изгнание призраков из Долины Ужаса",nil, {"Используйте курильницу экзорцизма для вызова из-под земли призраков, мучающих долину Ужаса. Когда убьете 25 духов долины Ужаса, вернитесь к лорду Грейсону Тенелому в храмовый квартал Штормграда."}},
[7641] = {"Работа Гриманда Элмора",nil, {"Поговорите с Гримандом Элмором в Квартале Дворфов Штормграда."}},
[7642] = {"Сбор необходимого",nil, {"Принесите Гриманду Элмору в Квартал дворфов Штормграда 40 отрезов рунической ткани, 6 арканитовых слитков, 10 цветков слезы Артаса, 5 сосудов со святой водой из Стратхольма и 150 золотых монет."}},
[7643] = {"Дух древнего коня",nil, {"Купите лошадиный корм для духа древнего коня у Меридет Карлсон в Южнобережье.nnУбейте Тендриса Криводрева в башне Забытого Города в Фераласе и освободите духа. Накормите духа кормом и получите благословление для арканитового чепрака."}},
[7644] = {"Благословенный арканитовый чепрак",nil, {"Вернитесь с благословенным арканитовым чепраком к лорду Грейсону Тенелому в храмовый квартал Штормграда."}},
[7645] = {"Обогащенный манной лошадиный корм",nil, {"Принесите 20 витаминизированных печений из манны – ключевых составляющих обогащенного манной лошадиного корма – Меридет Карлсон в Южнобережье в предгорьях Хилсбрада. Продаются они только в Серебряном Рассвете.nnКроме того, понадобится дать ей 50 золотых, чтобы успокоить ее ущемленное самолюбие."}},
[7646] = {"Магический кристалл",nil, {"Принесите азеротский алмаз и безупречный черный алмаз лорду Грейсону Тенелому в Храмовый квартал Штормграда."}},
[7647] = {"Суд и расплата",nil, {"Используйте магический кристалл в центре подвала Великого оссуария в Некроситете. Победите вызванных духов, а затем – рыцаря смерти Темного Терзателя, который появится после поражения духов.nnВерните падшему скакуну Темного Терзателя его спасенную душу и покройте его благословенным зачарованным чепраком."}},
[7648] = {"Шедевр Гриманда",nil, {"Покажите арканитовый чепрак лорду Грейсону Тенелому в храмовом квартале Штормграда."}},
[7649] = {"\"Зачарованная ториевая броня\", том I",nil, {"Верните книгу законным владельцам."}},
[7650] = {"\"Зачарованная ториевая броня\", том II",nil, {"Верните книгу законным владельцам."}},
[7651] = {"\"Зачарованная ториевая броня\", том III",nil, {"Верните книгу законным владельцам."}},
[7652] = {"Выгодная сделка",nil, {"Отлично, сударь у меня для вас деловое предложение. Я тут хочу продать несколько чертежей имперских латных доспехов, и мне необходим покупатель. В обмен я хочу получить слитки тория. Как вам мое предложение?"}},
[7653] = {"Имперский латный пояс",nil, {"За чертежи пояса я прошу 20 слитков тория."}},
[7654] = {"Имперские латные сапоги",nil, {"За чертежи этих сапог я хочу как минимум 40 слитков тория. Точно, 40. Ну не плачь, сынок, хочешь носовой платок?nn*Деротайн смеется.*"}},
[7655] = {"Имперские латные наручи",nil, {"Для изготовления наручей мне понадобится 20 слитков тория…nnС тобой все в порядке, сынок? Что-то ты покраснел..."}},
[7656] = {"Имперский латный нагрудник",nil, {"Для чертежей нагрудного доспеха мне нужно будет 60 слитков тория. nnИ что ты опять ноешь? Опять будешь жаловаться своим божкам, спрашивая, почему они отреклись от тебя?! Держись, приятель! Никто и никогда не говорил, что жизнь справедлива."}},
[7657] = {"Имперский латный шлем",nil, {"С тебя 50 слитков тория, и чертежи шлема – твои."}},
[7658] = {"Имперские латные поножи",nil, nil},
[7659] = {"Имперские латные наплечники",nil, {"За чертежи наплечников я прошу 20 слитков тория."}},
[7660] = {"Обмен волками – полярный волк",nil, nil},
[7661] = {"Обмен волками – красный волк",nil, nil},
[7662] = {"Новый кодо – бирюзовый",nil, nil},
[7663] = {"Новый кодо – зеленый",nil, nil},
[7664] = {"Новый бежевый ящер",nil, nil},
[7665] = {"Новый красный ящер",nil, nil},
[7666] = {"Возвращение в Великий оссуарий",nil, nil},
[7667] = {"Существенная помощь",nil, {"Принесите Сагорну Гребнестраннику в Аллею Мудрости в Оргриммаре азеротский алмаз и безупречный черный алмаз."}},
[7668] = {"Угроза Темного Губителя",nil, {"Используйте кристальный предсказатель в Великом оссуарии в Некроситете. Сразитесь с духами. Когда появится Темный Терзатель, убейте его.nnПринесите голову Темного Терзателя Сагорну Гривастому Страннику на аллею Мудрости Оргриммара."}},
[7669] = {"Возвращение в Великий оссуарий",nil, {"Ты снова отправляешься в Некроситет, Бхт? Раз уж ты так дорого заплатил за использование кристального предсказателя, может быть, возьмешь с собой еще один? Он поможет тебе преодолеть опасности Главного склепа.nnБери, бери. Заслужил!"}},
[7670] = {"Лорд Грейсон Тенелом",nil, {"Поговорите с лордом Грейсоном Тенеломом в Храмовом квартале Штормграда."}},
[7671] = {"Новый ледопард",nil, nil},
[7672] = {"Новый ночной саблезуб",nil, nil},
[7673] = {"Замена снежного барана",nil, nil},
[7674] = {"Замена черного барана",nil, nil},
[7675] = {"Замена синего механодолгонога",nil, nil},
[7676] = {"Замена белого механодолгонога",nil, nil},
[7677] = {"Белый жеребец",nil, nil},
[7678] = {"Игреневый конь",nil, nil},
[7681] = {"Hunter test quest",nil, {"Покормите своего питомца и поговорите с персонажем, который выдал это задание."}},
[7682] = {"Hunter test quest2",nil, {"Покормите своего питомца и поговорите с персонажем, который выдал это задание."}},
[7701] = {"Разыскивается: надзиратель Мальториус",nil, {"Дворф из клана Черного Железа, вероломный надзиратель Мальториус, скрывается в глубине Шлаковой Ямы в Котле, что в Тлеющем ущелье. nnУбейте надзирателя Мальториуса и принесите его голову дозорному капитану Лоло Долгобою."}},
[7703] = {"Неоконченное дело Гордоков",nil, {"Добудьте латную рукавицу Мощи Гордока и вернитесь с ней к капитану Давигрому в Забытом Городе.nnПо словам Давигрома, в \"старых байках\" говорится, что Тортелдрин – \"страшный эльф\", называющий себя принцем, – похитил эту рукавицу у одного из королей Гордоков."}},
[7721] = {"Энергия для уменьшения",nil, {"Соберите 10 ядер элементалей воды, убивая морских элементалей и морских валов на Забытом берегу в Фераласе, и принесите их Зорбину Дребезгуну, который находится неподалеку от порта."}},
[7722] = {"Какой еще плавень?",nil, {"Верните секретные документы по изготовлению огненного плавня, украденные надзирателем Мальториусом. Инспектор скорее всего находится в Шлаковой Яме в Котле. Документы верните мастеру-кузнецу Гориврату в Тлеющем ущелье."}},
[7723] = {"Отекшие пальцы",nil, {"По поручению Хансела Большерука убейте 20 тяжелых боевых големов, которых можно найти в землях, окружающих Котел в Тлеющем ущелье. По выполнении задания вернитесь к Ханселу."}},
[7724] = {"Огненная угроза",nil, {"По указанию Хансела Большерука убейте 20 больших лавовых пауков в Тлеющем ущелье. Пауки водятся на вулканических плато и в озерах лавы в Тлеющем ущелье.nnкогда убьете достаточно пауков, вернитесь к Ханселу."}},
[7725] = {"Новая ликвидация великанов",nil, {"Используйте ультрасжиматель Зорбина, чтобы уменьшить великанов любого вида, каких вы найдете в Фераласе. Принесите 10 остатков миниатюризации этих великанов Зорбину Дребезгуну в порт Забытого берега в Фераласе."}},
[7726] = {"Новая энергия для уменьшения",nil, {"Соберите 6 ядер элементалей воды, убивая морских элементалей и морских валов на Забытом берегу в Фераласе, и принесите их Зорбину Дребезгуну, который находится неподалеку от порта."}},
[7727] = {"Пламезавры? Ну и название...",nil, {"По поручению Хансела Большерука убейте 20 пламезавров, обитающих в озерах лавы в Шлаковой Яме - это часть Котла в Тлеющем ущелье. nnПо выполнении задания вернитесь к Ханселу в Тлеющее ущелье."}},
[7728] = {"УКРАДЕНО: фурма и подзорная труба наблюдателя",nil, {"Найдите и верните фурму и подзорную трубу наблюдателя надсмотрщику Скренджу в Тлеющем ущелье. Единственная известная информация: вещи были украдены дворфами Черного Железа. Подзорная труба является бесценным прибором для наблюдения, а фурма – незаменимым кузнечным инструментом."}},
[7729] = {"ВАКАНСИЯ: устранитель конкурентов",nil, {"Убейте 15 надсмотрщиков и 15 поработителей из клана Черного Железа. По выполнении задания вернитесь к десятнику Скренджу в Тлеющее ущелье."}},
[7730] = {"Вторжение улья Цуккаш",nil, {"Принесите 20 панцирей Цуккаш Хадокену Быстрому Страннику в Лагерь Мохаче в Фераласе."}},
[7731] = {"Жалохвост",nil, {"Убейте жалохвоста в Гудящей Бездне и принести железы ядохлеста Хадокену Быстрому Страннику в Лагерь Мохаче в Фераласе."}},
[7732] = {"Донесение об улье Цуккаш",nil, {"Доставьте отчет о нашествии насекомых-Цуккаш на Лагерь Мохаче Зилзибину Барабанному Бою, который проживает на Волоке в Оргриммаре."}},
[7733] = {"Борьба за качество",nil, {"Принесите 10 шкур йети из пещеры Бешеного оврага Пратту Макгруббену в Крепость Оперенной Луны в Фераласе."}},
[7734] = {"Борьба за качество",nil, {"Принесите 10 шкур йети из пещеры Бешеного оврага Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе."}},
[7735] = {"Безупречная шкура йети",nil, {"Отнесите безупречную шкуру йети Пратту Макгруббену в Крепость Оперенной Луны в Фераласе."}},
[7736] = {"Пополнить запасы огненного плавня с помощью королевской крови",nil, {"Не будем тратить драгоценное время на ерунду, |3-6(нежить). Пора пополнить запасы огненного плавня – они уже совсем на исходе. nnВот что мне от тебя нужно: nn*Чешуя пламезавра nn*Королевская кровь nn*Уголь nnМне нужно все, что ты сможешь предложить! И поторопись, если хочешь угодить Братству."}},
[7737] = {"Завоевать благосклонность",nil, {"Ты быстро продвигаешься по служебной лестнице в Братстве, |3-6(нежить). Ты начинаешь в самом деле завоевывать нашу благосклонность. nnЕсли хочешь заслужить еще большее уважение, слушай внимательно. nnМы хотим повысить качество нашего огненного плавня путем добавления в него окалины черного железа. Окалину можно добыть в Глубинах Черной горы. nnПринеси мне как можно больше!"}},
[7738] = {"Безупречная шкура йети",nil, {"Отнесите безупречную шкуру йети Джангору Быстрому Страннику в Лагерь Мохаче в Фераласе."}},
[7741] = {"Похвала Изумрудного Круга <NYI> <TXT>",nil, nil},
[7761] = {"Приказ Чернорука",nil, {"Вот уж действительно идиот... Похоже, вам нужно найти это клеймо и выжечь на руке метку Драккисата, чтобы получить доступ к сфере Приказа.nnВ письме ясно сказано, что генерал Драккисат охраняет клеймо. Наверное, стоит проверить."}},
[7781] = {"Владыка Черной горы",nil, {"Отнесите голову Нефариана Верховному лорду Болвару Фордрагону в Штормград."}},
[7782] = {"Владыка Черной горы",nil, {"Поговорите с фельдмаршалом Афрасиаби в Аллее Героев рядом со Штормградом."}},
[7783] = {"Владыка Черной горы",nil, {"Отнесите голову Нефариана Траллу в Оргриммар."}},
[7784] = {"Владыка Черной горы",nil, {"Поговори с верховным воеводой Саурфангом. Он ждет тебя на аллее Силы в Оргриммаре."}},
[7785] = {"Сосуд Возрождения",nil, {"Изучите Сосуд Возрождения и вернитесь к верховному лорду Демитриану."}},
[7786] = {"Громораан Ветроносец",nil, {"Чтобы освободить Громораана Ветроносца из тюрьмы, отнесите правый и левый наручник Ветроносца, 10 слитков элементия и сущность Повелителя огня верховному лорду Демитриану."}},
[7787] = {"Громовая ярость",nil, {"Отдайте Дремлющий клинок Игривого Ветра верховному лорду Демитриану."}},
[7788] = {"Долой захватчиков!",nil, {"Войдите в ущелье Песни Войны, одержите победу над Ордой, получите почетный знак ущелья Песни Войны и вернитесь с ним к часовой Дальней Песне в рощу Среброкрылых."}},
[7789] = {"Громи захватчиков-Среброкрылых!",nil, {"Войдите в ущелье Песни Войны, одержите победу над Альянсом, получите почетный знак ущелья Песни Войны и вернитесь с ним к капитану Лободробу в лагерь Мор'шан."}},
[7791] = {"Пожертвование: шерсть",nil, {"Мы всегда готовы принять шерсть в качестве пожертвования. Шестьдесят отрезов – дар, который жители Штормграда запомнят надолго. Ты ведь знаешь пословицу: \"С миру по нитке – голому рубашка\". А нам очень нужна шерсть!nnЕсли у тебя есть 60 отрезов шерстяной ткани и ты хочешь сделать пожертвование, то я могу принять дар прямо сейчас."}},
[7792] = {"Пожертвование: шерсть",nil, {"Мы сейчас собираем пожертвования в виде шерстяной ткани. За щедрый дар вы получите признательность Дарнасса. Наши запасы таковы, что нам нужно от вас всего лишь шестьдесят отрезов. Мы могли бы купить их и на стороне, чтобы продолжить работу.nnНо если вы имеете шестьдесят отрезов шерстяной ткани и готовы пожертвовать их в дар, я с готовностью приму их у вас прямо сейчас."}},
[7793] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7794] = {"Пожертвование: магическая ткань",nil, {"Магическая ткань подходит к концу, и нам нужна твоя помощь для пополнения наших запасов. По подсчетам, нам нужно собрать не меньше 60 отрезов магической ткани. Подобную щедрость Штормград не забудет!nnЕсли у тебя есть 60 отрезов магической ткани, я могу принять их у тебя в качестве пожертвования."}},
[7795] = {"Пожертвование: руническая ткань",nil, {"Огневар, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунической ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты не против, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7796] = {"Больше рунической ткани",nil, {"Снова приветствую тебя, Варрах! Мы постоянно нуждаемся в рунической ткани, и дополнительный запас нам не помешает. Если ты не против выделить нам еще двадцать отрезов рунической ткани, я постараюсь, чтобы твои труды на благо Штормграда были оценены по достоинству.nnКак только ты соберешь ткань, я охотно ее приму."}},
[7797] = {"Пространственный проходчик - Круговзор",nil, nil},
[7798] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7799] = {"Пожертвование: магическая ткань",nil, {"Магическая ткань кончается, и чтобы пополнить запасы, мы хотели бы воспользоваться вашей помощью! Нам понадобится всего лишь 60 отрезов на нужды сообщества. Твоя щедрость не останется незамеченной в Дарнассе, уверяю тебя!nnЕсли у тебя есть шестьдесят отрезов магической ткани и ты готовы их пожертвовать, я могу принять их у тебя сейчас же."}},
[7800] = {"Пожертвование: руническая ткань",nil, {"Вилдан, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7801] = {"Больше рунической ткани",nil, {"Снова приветствую тебя, Ренгольд! Мы постоянно нуждаемся в рунной ткани, и дополнительный запас нам не помешает. Если ты готов выделить нам еще двадцать отрезов рунной ткани, я постараюсь, чтобы твои труды на благо Дарнасса были оценены по достоинству.nnКак только ты будешь готов передать рунную ткань, я охотно ее приму."}},
[7802] = {"Пожертвование: шерсть",nil, {"Мы сейчас собираем пожертвования в виде шерстяной ткани. За щедрый дар вы получите признательность Стальгорна. Наши запасы таковы, что нам нужно от вас всего лишь шестьдесят отрезов. Мы могли бы купить их и на стороне, чтобы продолжить работу.nnНо если вы имеете шестьдесят отрезов шерстяной ткани и готовы пожертвовать их в дар, я с готовностью приму их у вас прямо сейчас."}},
[7803] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7804] = {"Пожертвование: магическая ткань",nil, {"Магическая ткань кончается, и чтобы пополнить запасы, мы хотели бы воспользоваться вашей помощью! Нам понадобится всего лишь 60 отрезов на нужды сообщества. Твоя щедрость не останется незамеченной в Стальгорне, уверяю тебя!nnЕсли у тебя есть шестьдесят отрезов магической ткани и ты готов их пожертвовать, я могу принять их у тебя сейчас же."}},
[7805] = {"Пожертвование: руническая ткань",nil, {"Хэпин, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7806] = {"Больше рунической ткани",nil, {"Снова привествую вас, Тарлемар! Мы постоянно нуждаемся в рунной ткани, и дополнительный запас нам не помешает. Если вы готовы выделить нам еще двадцать отрезов рунной ткани, то, смею вас заверить, изгнанники Гномригана не забудут ваших трудов.nnКак только вы будете готовы передать рунную ткань, я охотно ее приму."}},
[7807] = {"Пожертвование: шерсть",nil, {"Мы сейчас собираем пожертвования в виде шерстяной ткани. За щедрый дар вы получите признательность Стальгорна. Наши запасы таковы, что нам нужно от вас всего лишь шестьдесят отрезов. Мы могли бы купить их и на стороне, чтобы продолжить работу.nnНо если вы имеете шестьдесят отрезов шерстяной ткани и готовы пожертвовать их в дар, я с готовностью приму их у вас прямо сейчас."}},
[7808] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7809] = {"Пожертвование: магическая ткань",nil, {"Магическая ткань на исходе, и мы просим вашей помощи в пополнении наших запасов! Нам нужно всего лишь 60 отрезов магической ткани на нужды сообщества. Твоя щедрость не останется незамеченной гномреганскими изгнанниками, уверяю тебя!nnЕсли у тебя найдется шестьдесят отрезов магической ткани и ты готов их пожертвовать, я охотно приму их от тебя прямо сейчас."}},
[7810] = {"Мастер Арены",nil, {"Поговорите с Коротышкой Джоном Мифрилом на арене Тернистой долины."}},
[7811] = {"Пожертвование: руническая ткань",nil, {"Пластырь, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7812] = {"Больше рунической ткани",nil, {"Снова привествую вас, Ишт! Мы постоянно нуждаемся в рунной ткани, и дополнительный запас нам не помешает. Если вы готовы выделить нам еще двадцать отрезов рунной ткани, то, смею вас заверить, изгнанники Гномрегана не забудут ваших трудов.nnКак только вы будете готовы передать рунную ткань, я охотно ее приму."}},
[7813] = {"Пожертвование: шерсть",nil, {"Мы охотно примем в дар шерстяную ткань. Если ты принесешь ее нам, жители Подгорода будут чрезвычайно признательны тебе за щедрость. 60 отрезов нам вполне хватит, чтобы продолжать работу.nnЕсли у тебя при себе имеется 60 отрезов шерсти и ты готов ее пожертвовать, я могу принять ткань прямо сейчас."}},
[7814] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7815] = {"Хрустогрызы, чувак!",nil, {"Убейте 15 морских хрустогрызов по поручению Катума Удильщика из деревни Сломанного Клыка во Внутренних землях."}},
[7816] = {"Гаммерита!",nil, {"Убейте Гаммериту и верните Катуму Удильщику из деревни Сломанного Клыка его лучшую блесну.nnКатум сказал, что Гаммерита ползает по берегу среди остальных черепах."}},
[7817] = {"Пожертвование: магическая ткань",nil, {"У нас заканчивается магическая ткань, и твоя щедрость была бы очень кстати! Нам всего-то 60 отрезов на все про все и нужно. Уверяю тебя, жители Подгорода не забудут твоей доброты!nnЕсли у тебя при себе имеется 60 отрезов магической ткани и ты готов ее пожертвовать, я могу принять ее прямо сейчас."}},
[7818] = {"Пожертвование: руническая ткань",nil, {"Кастрол, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7819] = {"Больше рунической ткани",nil, {"Приветствую еще раз, Колг! Нам постоянно нужна рунная ткань, и если у тебя найдутся излишки, с которыми не жалко расстаться, мы бы с удовольствием их взяли. Двадцать отрезов рунной ткани помогут тебе добиться большего расположения Подгорода!nnЯ приму у тебя ткань в любое время!"}},
[7820] = {"Пожертвование: шерсть",nil, {"Мы сейчас собираем пожертвования в виде шерстяной ткани. За щедрый дар в шестьдесят отрезов ты получишь признательность Громового Утеса. Наши запасы таковы, что нам нужно от вас всего лишь шестьдесят отрезов. Мы могли бы купить их и на стороне, чтобы продолжить работу.nnНо если ты имеешь при себе шестьдесят отрезов шерстяной ткани и готов пожертвовать их в дар, я с радостью приму их у тебя прямо сейчас."}},
[7821] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7822] = {"Пожертвование: магическая ткань",nil, {"Магическая ткань кончается, и чтобы пополнить запасы, мы хотели бы воспользоваться твоей помощью! Нам понадобится всего лишь 60 отрезов магической ткани на нужды сообщества. Твоя щедрость не останется незамеченной в Громовом Утесе, будь уверен!nnЕсли у тебя есть шестьдесят отрезов магической ткани и ты готов их пожертвовать, я могу принять их у тебя сейчас же."}},
[7823] = {"Пожертвование: руническая ткань",nil, {"Рокхан, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7824] = {"Пожертвование: руническая ткань",nil, {"Магелио, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7825] = {"Больше рунической ткани",nil, {"Снова привествую тебя, Нердзул! Мы постоянно нуждаемся в рунной ткани, и дополнительный запас нам не помешает. Если ты готов уделить нам еще двадцать отрезов рунной ткани, то, будь уверен я постараюсь, чтобы щедрость твоя стала известна всему Громовому Утесу!nnКак только ты будешь готов передать рунную ткань, я охотно ее приму."}},
[7826] = {"Пожертвование: шерсть",nil, {"Мы охотно приняли бы в дар шерстяную ткань. Если ты дашь нам 60 отрезов, Оргриммар будет тебе чрезвычайно признателен за щедрость. 60 отрезов нам вполне бы хватило – вместе с дарами от прочих пожертвователей этого было бы достаточно, чтобы продолжать работу.nnЕсли у тебя при себе имеется 60 отрезов шерсти и ты готов ее пожертвовать, я могу ее принять прямо сейчас."}},
[7827] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7828] = {"Охота на бродяг",nil, {"Убейте 10 среброгривых бродяг и 10 среброгривых ревунов по поручению охотника Мархора из деревни Сломанного Клыка во Внутренних землях.nnМархор сказал, что волки обитают в лесах Внутренних земель."}},
[7829] = {"Охота на дикарей",nil, {"Убейте 20 диких совухов по поручению охотника Мархора из деревни Сломанного Клыка во Внутренних землях.nnМархор сказал, что совухи обитают в лесах Внутренних земель."}},
[7830] = {"Отмщение за погибших",nil, {"Убейте остроклюва-повелителя небес и принесите его перо охотнику Мархору в деревню Сломанного Клыка.nnМархор говорил, что остроклювы водятся во Внутренних землях."}},
[7831] = {"Пожертвование: магическая ткань",nil, {"У нас заканчивается магическая ткань и твоя щедрость была бы очень кстати! Нам всего-то 60 отрезов на все сообщество и нужно. Уверяю тебя, Оргриммар не забудет твоей доброты!nnЕсли у тебя при себе имеется 60 отрезов магической ткани и ты готов ее пожертвовать, я могу ее принять прямо сейчас."}},
[7832] = {"Больше рунической ткани",nil, {"Еще раз приветствую, Сатан! Нам постоянно нужна рунная ткань, и если у тебя найдется лишняя, с которой не жалко расстаться, мы бы с удовольствием ее взяли. Двадцать отрезов рунной ткани помогут тебе добиться большего расположения Оргриммара!nnЯ приму у тебя эту ткань в любое время!"}},
[7833] = {"Пожертвование: шерсть",nil, {"Мы охотно приняли бы в дар шерстяную ткань. Если ты дашь нам 60 отрезов, тролли Черного Копья будут тебе чрезвычайно признательны за щедрость. 60 отрезов нам бы вполне хватило – вместе с дарами от прочих пожертвователей этого было бы достаточно, чтобы продолжать работу.nnЕсли у тебя при себе имеется 60 отрезов шерсти и ты готов ее пожертвовать, я могу ее принять прямо сейчас."}},
[7834] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[7835] = {"Пожертвование: магическая ткань",nil, {"У нас заканчивается магическая ткань, и твоя щедрость была бы очень кстати! Нам всего-то 60 отрезов на все сообщество и нужно. Уверяю тебя, тролли Черного Копья не забудут твоей доброты!nnЕсли у тебя при себе имеется 60 отрезов магической ткани и ты готов ее пожертвовать, я могу ее принять прямо сейчас."}},
[7836] = {"Пожертвование: руническая ткань",nil, {"Шкек, ты очень помог нам в кампании по сбору ткани. Мы стараемся пополнить наши запасы, но одного вида ткани нам все равно катастрофически не хватает, а именно рунной ткани. Мы чрезвычайно в ней нуждаемся и надеемся, что ты поможешь нам, как и в прошлый раз!nnЕсли ты согласен, пожалуйста, пожертвуй нам ткани – сколько сможешь. Обычно мы принимаем начальное пожертвование от одного лица в размере шестидесяти отрезов, с этого и начнем."}},
[7837] = {"Больше рунической ткани",nil, {"Еще раз приветствую, Дэдоутсайд! Нам постоянно нужна рунная ткань, и если у тебя найдется лишняя, с которой не жалко расстаться, мы бы с удовольствием ее взяли. Двадцать отрезов рунной ткани помогут тебе добиться большего расположения племени Черного Копья!nnЯ приму у тебя эту ткань в любое время!"}},
[7838] = {"Гроссмейстер Арены",nil, {"Эй ты, увалень! Принеси мне 12 безделушек Мастера арены, и станешь гроссмейстером Арены Гурубаши! Гррр!"}},
[7839] = {"Хулиганы из племени Порочной Ветви",nil, {"Найдите потерянные инструменты кузнеца Углежога из деревни Сломанного Клыка во Внутренних землях.nnУглежог сказал, что инструменты могут быть в одном из храмов троллей из племени Порочной Ветви в северо-восточной части Внутренних земель. Также следует проверить на Осклизлой скале."}},
[7840] = {"Лярд потерял свой обед",nil, {"Найдите обед Лярда из деревни Сломанного Клыка во Внутренних землях.nnЛярд сказал, что потерял свой обед на острове к северу, где могут встретиться тролли из племени Порочной Ветви."}},
[7841] = {"Послание для Громового Молота",nil, {"Убейте 15 гонцов, 15 разведчиков, 15 стрелков и 15 разведчиков Высокодолья по поручению Отхо Можи'ко из деревни Сломанного Клыка.nnНочных эльфов из Высокодолья можно найти в сторожке Кель'Данил в северо-западной части Внутренних земель."}},
[7842] = {"Еще одно послание для Громового Молота",nil, {"Принесите 10 длинных элегантных перьев Отхо Можи'ко из деревни Сломанного Клыка во Внутренних землях. Перья нужно собрать с местных грифонов, которых можно найти практически в любом месте Внутренних земель."}},
[7843] = {"Последнее послание для Громового Молота",nil, {"Доберитесь до Заоблачного Пика в западной части Внутренних земель и оставьте возле колодца в центре города последнее предупреждение для клана Громового Молота. После выполнения задания возвращайтесь к Отхо Можи'ко в деревню Сломанного Клыка.nnБерегитесь дворфов из клана Громового Молота и других сторонников Альянса."}},
[7844] = {"Собратья-каннибалы",nil, {"Убейте 15 охотников за скальпами и 10 ворожеев из племени Порочной Ветви по поручению Яо'джин из деревни Сломанного Клыка.nnЯо'джин сказала, что эти тролли живут близ храмов Шаол'вата и Агол'вата в северо-восточной части Внутренних земель."}},
[7845] = {"Похищен старейшина Зазубренный Клык!",nil, {"Спасите старейшину Зазубренного Клыка из Джинта'Алора по поручению его жены из деревни Сломанного Клыка.nnСтарейшину держат в плену на самом верху Джинта'Алора – главного поселения троллей из племени Порочной Ветви, расположенного на юге Внутренних земель."}},
[7846] = {"Отыщите ключ!",nil, {"Убейте хранительницу Хита'йю и заберите у нее ключ от оков, после чего возвращайтесь к старейшине Зазубренному Клыку и освободите его."}},
[7847] = {"Возвращение к старейшине Зазубренный Клык",nil, {"Возвращайтесь к старейшине Зазубренный Клык и сообщите ей, что ее муж спасен."}},
[7848] = {"Сродство с недрами",nil, {"Отправляйтесь через портал, ведущий к Огненным Недрам в Глубинах Черной горы и добудьте фрагмент ядра. Принесите его Лотосу Хранителю Портала к Черной горе."}},
[7849] = {"Воссоединение останков",nil, {"Принесите охотнику Мархору из деревни Сломанного Клыка во Внутренних землях кости и череп его брата-близнеца.nnМархор сказал, что останки его брата бросили на съедение слизнюкам и волкам племени Порочной Ветви – все эти твари водятся неподалеку от вершины Джинта'Алора: слизнюки в компостной яме, а волки – в логове."}},
[7850] = {"Темные сосуды",nil, {"Принесите 10 сосудов с порченой кровью старейшине Зазубренный Клык во Внутренние земли. Сосуды с кровью находятся в поселении Джинта'Алор."}},
[7861] = {"Разыскивается: коварная жрица Ведьмиса и ее прислужники",nil, {"Убейте коварную жрицу Ведьмису и 10 стражей Аман'заси из племени Порочной Ветви, после чего вернитесь к старейшине Зазубренный Клык во Внутренних землях.nnКоварную жрицу Ведьмису и стражей Аман'заси можно найти в верхней части Джинта'Алора во Внутренних землях."}},
[7862] = {"Требуется: капитан стражи в деревню Сломанного Клыка",nil, {"Убейте 10 берсерков, 5 темных охотников, 5 кровопийц и 5 пожирателей душ из племени Порочной Ветви.nnПосле выполнения задания вернитесь к старейшине Зазубренный Клык в деревню Сломанного Клыка."}},
