[8989] = {"Правая часть амулета Лорда Вальтхалака",nil, {"Вызвать дух Мора Серого Копыта с помощью жаровни Призыва, прикончить его и забрать недостающую часть амулета Лорда Вальтхалака. Вернуться к Бодли в Черную гору, отдать ему восстановленный амулет и жаровню Призыва."}},
[8990] = {"Правая часть амулета Лорда Вальтхалака",nil, {"С помощью жаровни Призыва вызвать дух Изалиен и убить ее. Вернуться к Бодли в Черную гору, отдать ему восстановленный амулет и жаровню Призыва."}},
[8991] = {"Правая часть амулета Лорда Вальтхалака",nil, {"Вызвать духов Джариен и Сотоса с помощью жаровни Призыва и убить их обоих. Вернуться к Бодли в Черную гору, отдать ему восстановленный амулет и жаровню Призыва."}},
[8992] = {"Правая часть амулета Лорда Вальтхалака",nil, {"С помощью жаровни Призыва вызвать дух Кормока и убить его. Вернуться к Бодли в Черную гору, отдать ему восстановленный амулет и жаровню Призыва."}},
[8993] = {"Подношение даров",nil, {"Ты здесь для того, чтобы поднести дар любви и уважения своему великому вождю?"}},
[8994] = {"Последние приготовления",nil, {"Соберите 40 наручей Черной горы и добудьте настой великой силы. Отнесите все это Бодли в Черную гору."}},
[8995] = {"Mea Culpa, Лорд Вальтхалак",nil, {"Вызвать Лорда Вальтхалака с помощью жаровни Призыва. Убить его и использовать амулет. Потом вернуть амулет духу Вальтхалака."}},
