[9128] = {"Уравнение стихий",nil, {"Принесите 30 средоточий стихий верховному магу Анджеле Досантос в Часовню Последней Надежды в Восточные Чумные земли."}},
[9129] = {"Средоточие стихий",nil, {"Я использую средоточия стихий для изготовления доспехов Ледяного пламени – эти доспехи используют маги, отправляясь в Наксрамас. Без таких как ты, Мард, битва наша проиграна.nnПринеси мне побольше средоточий стихий, и я вручу тебе знаки отличия."}},
[9130] = {"Товары из Луносвета",nil, {"Доставьте накладную интенданта Лимель повелителю небес Солнечному Крылу в Транквиллион."}},
[9131] = {"Броня дредноута",nil, {"Принесите 30 пластин черного железа Корфаксу в Часовню Последней Надежды в Восточные Чумные земли."}},
[9132] = {"Лом черного железа",nil, {"За каждую горсть пластин я дам тебе знак. Сам понимаешь, чем больше, тем лучше. Бегом выполнять!"}},
[9133] = {"Полет в Луносвет",nil, {"Оплатите полет в Луносвет и доставьте накладную от интенданта Лимель Сатрену Лазурному Рассвету в Луносвет."}},
[9134] = {"Повелительница небес Полудымка",nil, {"Доставьте товар интенданта Лимель повелительнице небес Предзакатке к вратам Пастыря города Луносвет."}},
[9135] = {"Возвращение к интенданту Лимель",nil, {"Оплатите обратный полет в Транквиллион в Призрачных землях и доставьте товары интенданта Лимель интенданту Лимель в Транквиллион в Призрачных землях."}},
[9136] = {"Дикая флора",nil, {"Принесите 30 диких ростков Райн в Часовню Последней Надежды в Восточные Чумные земли."}},
[9137] = {"Дикие ростки",nil, {"За каждый пучок ростков получишь знак Рассвета или Марша на твой выбор.nnЗнаки можно обменять на различные вещи у интендантов."}},
[9138] = {"Деревня Солнечной Короны",nil, {"Убейте 10 нерубов-стражей и возвращайтесь к чародею Вандрилу в Транкивиллион, что в Призрачных землях."}},
[9139] = {"Деревня Золотистой Дымки",nil, {"Уничтожьте 6 кель'дорайских привидений и 4 кель'дорайских призрака в деревне Золотистой Дымки, затем возвращайтесь к чародею Вандрилу в Транквиллион, что в Призрачных землях."}},
[9140] = {"Деревня Ветрокрылых",nil, {"Соберите 6 порций иллюзорного вещества и 4 обломка горгульи. Возвращайтесь к чародею Вандрилу в Транквиллион, что в Призрачных землях."}},
[9141] = {"Не все так просто",nil, {"Принесите знак доблести Серебряного Рассвета командиру Метцу в Часовню Последней Надежды в Восточные Чумные земли."}},
[9142] = {"Задание мастера",nil, {"Ты знаешь правила парень. Получишь задание, только когда предъявишь знак доблести."}},
[9143] = {"Неприятности в Зеб'Соре",nil, {"Добудьте 6 ушей троллей из Зеб'Соры и принесите их следопыту Валанне в Призрачные земли."}},
[9144] = {"Пропавший в Призрачных землях",nil, {"Идите по дороге, ведущей из деревни Легкий Ветерок на юг, и найдите курьера Рассветного Странника."}},
[9145] = {"Спасти следопыта Валанну",nil, {"Найдите следопыта Валанну и лейтенанта Томатрена на берегу озера Эрлендар в Призрачных землях близ деревни Солнечной Короны."}},
[9146] = {"Донесение капитану Гелиосу",nil, {"Поговорите с капитаном Гелиосом в анклаве Странников на южном берегу озера Элрендар в Призрачных землях."}},
[9147] = {"Умирающий курьер",nil, {"Аптекарь Тедра из лесов Вечной Песни просит вас принести ей 4 образца зараженной крови животных из Призрачных земель."}},
[9148] = {"Письмо в Транкивиллион",nil, {"Отнесите письмо из Луносвета чародею Вандрилу в Транквиллион, что в Призрачных землях."}},
[9149] = {"Чумной берег",nil, {"Принесите 6 хребтов чумных мурлоков аптекарю Рензитену в Транквиллион, что в Призрачных землях."}},
[9150] = {"Спасение прошлого",nil, {"Магистр Даренис хочет, чтобы вы отправились в святилище Луны к западу от Транквиллиона и добыли 8 кристализованных субстанций маны с волшебных пожирателей и маначервей, что населяют это место."}},
[9151] = {"Святилище Солнца",nil, {"Поговорите с магистром Идонисом в святилище Солнца в Призрачных землях."}},
[9152] = {"Припасы могильщика",nil, {"Найдите припасы Ратиса Могильщика и верните их хозяину в Транквиллион, что в Призрачных землях."}},
[9153] = {"Под тенью",nil, {"Найдите по карте регион, атакуемый Плетью. Отправляйтесь туда и излейте свою ярость на некрополь, разгромив толпы Плети вокруг него. Доставьте 10 некротических рун Плети командиру Томасу Элерану в Часовню Последней Надежды в Восточные Чумные земли."}},
[9154] = {"Часовня Последней Надежды",nil, {"Отнесите объявление о мобилизации Хранителю свитков в Часовню Последней Надежды в Восточные Чумные земли."}},
[9155] = {"По Тропе Мертвых",nil, {"Убейте 10 восставших ненасытников и 10 уродливых каннибалов на Тропе Мертвых. Возвращайтесь к ловчему смерти Ратиэлю в Транквиллион за наградой."}},
[9156] = {"Разыскиваются: Гнилоступ и Лузран",nil, {"Найдите в Призрачных землях Гнилоступа и Лузрана. Принесите их головы ловчему смерти Ратиэлю в Транквиллион и получите награду."}},
[9157] = {"Забытые обряды",nil, {"Принесите 8 медальонов Волны Геранису Белому Рассвету в Призрачные земли."}},
[9158] = {"Разносчики заразы",nil, {"Странница Седайна из анклава Странников в Призрачных землях хочет, чтобы вы убили 10 светлопалых рысей."}},
[9159] = {"Обуздание заразы",nil, {"Странница Седайна из анклава Странников, что в Призрачных землях, хочет, чтобы вы убили 8 воронковых пауков-прядильщиков и 10 мрачниц-вампиров."}},
[9160] = {"Обследование Ан'дарота",nil, {"Обследуйте район Ан'дарота, убейте 12 часовых-шпионов и возвращайтесь к госпоже Златилен в Транквиллион, что в Призрачных землях."}},
[9161] = {"Тень предателя",nil, {"Отправляйтесь к башне Утренней Звезды в Призрачных землях и найдите любую информацию о прошлом Дар'Хана."}},
[9162] = {"Отблески былого",nil, {"Вернитесь к следопыту Винне в анклав Странников, что в Призрачных землях, и отдайте ей дневник Дар'Хана."}},
[9163] = {"Вылазка на оккупированную территорию",nil, {"Выкрадите планы ночных эльфов относительно Ан'дарота, Ан'овина и магического наблюдения за син'дорай, затем доставьте их госпоже Златилен в Транквиллион, что в Призрачных землях."}},
[9164] = {"Пленники Смертхольма",nil, {"Возьмите приводящее в сознание зелье Рензитена с собой в Смертхольм и спасите ученика Варниса, аптекаря Инит и следопыта Ведорана. Возвращайтесь к чародейке Джанеде в святилище Солнца за наградой."}},
[9165] = {"Подорожная",nil, {"Доставьте подписанную подорожную командиру Метцу в Часовню Последней Надежды в Восточные Чумные земли."}},
[9166] = {"Донесение в Ан'телас",nil, {"Передайте планы ночных эльфов магистру Силастору из Ан'теласа в Призрачных землях."}},
[9167] = {"Гибель предателя",nil, {"Убейте Дар'Хана Дратира в Смертхольме и принесите его голову магистру Кендрису в святилище Солнца в Призрачных землях."}},
[9168] = {"Сердце Смертхольма",nil, {"Уничтожьте 8 очей Дар'Хана и доложите об этом магистру Идонису в святилище Солнца в Призрачных землях."}},
[9169] = {"Ан'овин: отключение",nil, {"Добудьте сферу управления кристаллом и с ее помощью отключите лунный кристалл ночных эльфов в Ан'овине. Затем возвращайтесь к магистру Силастору в Ан'телас в Призрачных землях."}},
[9170] = {"Приспешники Дар'Хана",nil, {"Магистр Идонис хочет, чтобы вы отправились в Смертхольм и сразили Масофета Черного, Джуриона Обманщика, Боргота Кровопускателя и Мирдорана Мерзостного. Выполнив поручение, отчитайтесь перед магистром Идонисом в святилище Солнца Призрачных земель."}},
[9171] = {"Хрустящий деликатес",nil, {"Добудьте 5 хрустящих паучьих лапок и принесите их главному шеф-повару Плеснеру в Транквиллион, что в Призрачных землях."}},
[9172] = {"Доклад магистру Кендрису",nil, {"Принесите дневник Дар'Хана магистру Кендрису в святилище Солнца, что в Призрачных землях."}},
[9173] = {"Захват шпилей Ветрокрылых",nil, {"Убейте 8 послушников из Смертхольма и 10 павших следопытов, затем возвращайтесь к верховному вершителю Маврену в Транквиллион, что в Призрачных землях."}},
[9174] = {"Победа над Аквантионом",nil, {"Геранис Белый Рассвет хочет, чтобы вы нырнули на дно озера к востоку от деревни Солнечной Короны и возложили связку медальонов на алтарь укрощения волн. Призовите и сразите элементаля по имени Аквантион, затем возвращайтесь к Геранису."}},
[9175] = {"Ожерелье Госпожи",nil, {"Отнесите ожерелье госпожи верховному вершителю Маврену в Транквиллион, что в Призрачных землях."}},
[9176] = {"Зиккураты-близнецы",nil, {"Отправьтесь к Кровоточащему зиккурату и Воющему зиккурату и добудьте оттуда камень света и камень пламени. После этого возвращайтесь к магистру Кендрису в святилище Солнца."}},
[9177] = {"Путь в Подгород",nil, {"Отнесите Леди Сильване Ветрокрылой ожерелье Госпожи в Королевский Квартал Подгорода."}},
