[9310] = {"Тусклый некротический кристалл",nil, {"Отнесите тусклый некротический кристалл лейтенанту Рукагу во внутренний двор Подгорода."}},
[9311] = {"Шпион эльфов крови",nil, {"Убейте геодезиста Кандресс и расскажите о случившемся воздаятелю Алдару на месте крушения в долине Аммен."}},
[9312] = {"Излучатель",nil, {"Поговорите с техником Жанаей на месте крушения в долине Аммен."}},
[9313] = {"На Лазурную заставу",nil, {"Поговорите с техником Дивууном на Лазурной заставе на острове Лазурной Дымки."}},
[9314] = {"Вести с Лазурной заставы",nil, {"Поговорите с корчмарем Челланой на Лазурной заставе на острове Лазурной Дымки."}},
[9315] = {"Анок'сутен",nil, {"Убейте Анок'сутен и возвращайтесь к чародею Вандрилу в Транквиллион, что в Призрачных землях."}},
[9317] = {"Освященное точило",nil, {"Если ты принесешь мне восемь некротических рун, отобрав их у захватчиков из Плети, я дам тебе освященное точило. Оно способно помочь тебе в борьбе против приспешников Короля-лича."}},
[9318] = {"Благословенное волшебное масло",nil, {"В обмен на некротические руны Плети Серебряный Рассвет дарует тебе один фиал благословенного волшебного масла."}},
[9319] = {"Свет в темных местах",nil, {"Найдите костры, размещенные в пике Черной горы, Забытом Городе, Некроситете и Стратхольме и коснитесь их; затем поговорите с хранителем праздничного пламени."}},
[9320] = {"Сильнейшее зелье маны",nil, {"Помимо других поставок, у нас есть немного сильнейших зелий маны, которые могут тебе пригодиться. Я дам тебе одну порцию зелья в обмен на пятнадцать некротических рун."}},
[9321] = {"Хорошее лечебное зелье",nil, nil},
[9322] = {"Костры Калимдора",nil, {"Посетите лагеря праздника Огненного солнцеворота, размещенные в Азшаре, Силитусе, Кратере Ун'Горо и Зимних Ключах, затем вернитесь к хранителю праздничного пламени."}},
[9323] = {"Костры Восточных королевств",nil, {"Посетите лагеря Праздника Огня, размещенные в Выжженных землях, Восточных Чумных землях, во Внутренних землях и Тлеющем ущелье, затем поговорите с хранителем праздничного пламени."}},
[9324] = {"Похищение пламени Оргриммара",nil, {"Верните Пламя Оргриммара хранителю мудрости Праздника."}},
[9325] = {"Похищение пламени Громового Утеса",nil, {"Верните Пламя Громового Утеса хранителю мудрости Праздника."}},
[9326] = {"Похищение пламени Подгорода",nil, {"Верните Пламя Подгорода хранителю мудрости Праздника."}},
[9327] = {"Отрекшиеся",nil, {"Поговорите с верховным вершителем Мавреном в Транквиллионе, что в Призрачных землях."}},
[9328] = {"Герой син'дорай",nil, {"Принесите голову Дар'Хана Лор'темару Терону в Луносвет."}},
[9329] = {"Отрекшиеся",nil, {"Поговорите с верховным вершителем Мавреном в Транквиллионе, что в Призрачных землях."}},
[9330] = {"Похищение пламени Штормграда",nil, {"Верните Пламя Штормграда Праздничному Сказителю."}},
[9331] = {"Похищение пламени Стальгорна",nil, {"Верните Пламя Стальгорна Праздничному Сказителю."}},
[9332] = {"Похищение пламени Дарнасса",nil, {"Верните пламя Дарнасса праздничному сказителю."}},
[9333] = {"Перчатки Серебряного Рассвета",nil, {"Если принесешь мне тридцать некротических рун, я подарю тебе защитные перчатки для рук, по твоему выбору. Они оказались очень ценными в борьбе против нежити Плети"}},
[9334] = {"Благословенное волшебное масло",nil, {"В обмен на некротические руны Плети Серебряный Рассвет дарует тебе один фиал благословенного волшебного масла."}},
[9335] = {"Освященное точило",nil, {"Если ты принесешь мне восемь некротических рун, отобрав их у захватчиков из Плети, я дам тебе освященное точило. Оно способно помочь тебе в борьбе против приспешников Короля-лича."}},
[9336] = {"Хорошее лечебное зелье",nil, nil},
[9337] = {"Сильнейшее зелье маны",nil, {"Помимо других поставок, у нас есть немного сильнейших зелий маны, которые могут тебе пригодиться. Я дам тебе одну порцию зелья в обмен на пятнадцать некротических рун."}},
[9338] = {"Верность Кругу Кенария",nil, nil},
[9339] = {"Награда вора",nil, {"Верните Пламя Штормграда (NAME)"}},
[9340] = {"Глубокий Разлом",nil, {"Ступайте на восток от Соколиного дозора, к Глубокому разлому, и убейте 8 детенышей камнеруба и 3 камнерубов-вожаков по приказу капитана следопытов Венн'рена."}},
[9341] = {"Гербовая накидка Серебряного Рассвета",nil, {"Твоя помощь в нашем деле была очень ценной, Трансмастер. Если пожелаешь, я могу сделать так, что ты получишь гербовую накидку Серебряного Рассвета. Мы гордимся тем, что ты в числе наших союзников."}},
[9342] = {"Хищный землерой",nil, {"Убейте 15 хищных землероев по просьбе штейгера Разлкраза. Эти черви предпочитают нападать неожиданно, из-под земли."}},
[9343] = {"Гербовая накидка Серебряного Рассвета",nil, nil},
[9344] = {"Поспешный отъезд",nil, {"Разыщите Грелага, ученика провозвестницы Регулкут."}},
[9345] = {"Изготовление мази",nil, {"Соберите 12 листьев пламенного шиполиста и возвращайтесь к Грелагу на Заставу Хребтолома."}},
[9346] = {"Зловепрь – птица гордая",nil, {"Принесите командиру стражи Кранку из Траллмара 10 раздутых трупов голодных зловепрей."}},
[9349] = {"Сбор яиц опустошителей",nil, {"Принесите 12 яиц опустошителей Легасси на место крушения дирижабля на полуострове Адского Пламени."}},
[9351] = {"Дикие демоны Бездны",nil, {"Принесите 10 конденсированных сущностей демона Бездны \"Крикуну\" Скриду Многослову на место крушения дирижабля."}},
[9352] = {"Вторжение из Дарнасса",nil, {"Отправляйтесь в Западное святилище, к юго-западу от площади Соколиных Крыльев, и уничтожьте всех чужаков, которых вы там встретите. Затем сообщите о результатах хранительнице энергии Велании."}},
[9355] = {"Работа для сообразительных",nil, {"Убейте 15 хищных землероев и возвращайтесь к штейгеру Большие Десны в Оплот Чести."}},
[9356] = {"Летать как птица",nil, {"Принесите 12 мясистых крыльев канюка Легасси на место крушения дирижабля."}},
[9357] = {"Задание Аэльдона Озаренного Солнцем",nil, {"Поговорите с Аэльдоном Озаренным Солнцем у Внутренних эльфийских ворот в Лесах Вечной Песни."}},
[9358] = {"Следопыт Сарейн",nil, {"Поговорите со следопытом Сарейн у деревни Легкий Ветерок, что в лесах Вечной Песни."}},
[9359] = {"Обитель Странников",nil, {"Поговорите с лейтенантом Вестницей Зари в обители Странников, что в лесах Вечной Песни."}},
[9360] = {"Вторжение племени Амани",nil, {"Доставьте планы вторжения племени Амани лейтенанту Вестнице Зари в обитель Странников."}},
[9361] = {"Зловепрь – тоже мясо",nil, {"Легасси требует принести ему 8 кусков мяса зловепря на место крушения дирижабля. Убейте зловепря, добудьте порченое мясо зловепря, а затем очистите его с помощью микстуры очищения."}},
[9362] = {"Полководец Креллиан",nil, {"Принесите радужную раковину верховному магу Ксилему в башню в утесах Азшары."}},
[9363] = {"Предупреждение деревне Легкий Ветерок",nil, {"Отправляйтесь в деревню Легкий Ветерок в лесах Вечной Песни и поговорите со следопытом Деголиеном."}},
[9364] = {"Частицы магии",nil, {"Наложите заклинание превращения на тварей Бича Злобы в Азшаре и убейте клонов, которые появятся несколько мгновений спустя. Убейте 50 клонов и возвращайтесь к верховному магу Ксилему в Азшару."}},
[9365] = {"Награда вора",nil, {"Верните Пламя Штормграда (NAME)"}},
[9366] = {"Оскверненная кровь",nil, {"Принесите 6 образцов оскверненной крови исчадий ужаса, обитающих у прудов Аггонара, Риатену Мрачному из Соколиного дозора на полуострове Адского Пламени."}},
[9367] = {"Праздник Огня",nil, {"Прикоснитесь к пламени праздничных костров в Штормграде, Стальгорне и Дарнассе, затем поговорите с хранителем мудрости праздника в столичных городах."}},
[9368] = {"Праздник Огня",nil, {"Прикоснитесь к пламени праздничных костров в Оргриммаре, Громовом Утесе и Подгороде, затем поговорите с Праздничным сказителем в столичных городах."}},
[9369] = {"Исцеляющие кристаллы",nil, {"Добудьте 8 фиалов с кровью мотылька из долинных мотыльков и принесите их Проэнитусу на место крушения в долине Аммен."}},
[9370] = {"Остановить очищение",nil, {"Поставьте сигнальный самоцвет на алтарь Аггонара, рядом с прудами Аггонара, и уничтожьте дренейского анахорета и всех, кто явится на сигнал. После выполнения задания заберите самоцвет и возвращайтесь к Риатену Мрачному в Соколиный дозор."}},
[9371] = {"Ботаник Таэрикс",nil, {"Поговорите с ботаником Таэрикс на месте крушения в долине Аммен."}},
[9372] = {"Распространение демонической скверны",nil, {"Соберите 6 образцов крови зловепря у крупных зловепрей по просьбе Тии Рыжей Гривы с Кенарийской заставы на полуострове Адского Пламени."}},
[9373] = {"Потерянное послание",nil, {"Доставьте подпорченный кожаный чехол Тие Рыжей Гриве, которая стоит лагерем рядом с руинами Ша'наара на западе полуострова Адского Пламени."}},
[9374] = {"Записи Арелиона",nil, {"Отправляйтесь к прудам Аггонара и найдите записи Арелиона по просьбе магистра Каринды из Соколиного дозора на полуострове Адского Пламени."}},
[9375] = {"Дорога к Соколиному дозору",nil, {"Помогите израненной эльфийке крови – пилигриму добраться до Соколиного дозора, затем поговорите с Талерисом Созерцателем Рассвета."}},
[9376] = {"Скарб пилигрима",nil, {"Перозубые опустошители напали на израненную эльфийку крови – пилигрима, когда она остановилась в Лощине Пыльного Пера. Разыщите ее стоянку и потерянные вещи."}},
[9378] = {"DND FLAG Цитадель ужаса – Наксрамас",nil, {"Верховный маг Анжела Досантос из Часовни Последней Надежды, что в Восточных Чумных землях, готова бесплатно наложить на вас заклинание чародейского покрова. Вы должны заслужить полное доверие Серебряного Рассвета."}},
[9380] = {"В погоне за большим",nil, {"Соберите 6 неповрежденных трупов астральных гончих и доставьте в Оплот Чести геологу Мурантусу."}},
[9381] = {"Стрелы, бьющие без промаха",nil, {"Принесите 8 хвостовых перьев стервятника-костеклюя сокольничему Дренне Речной Ветер в Соколиный дозор."}},
[9382] = {"Жестокий рок копытня",nil, {"Убейте 15 щетинистых копытней и возвращайтесь к Амаану Мудрейшему в Храм Телхамата."}},
[9383] = {"Честолюбивый план",nil, {"Используйте освященный кристалл против раненого неконтролируемого демона Бездны, затем возвращайтесь к Элсаане в храм Телхамат."}},
[9385] = {"Разъяренные Опустошители",nil, {"Убейте 10 перозубых опустошителей и возвращайтесь к Гремни Длиннобороду на полуострове Адского Пламени."}},
[9386] = {"Свет в темных местах",nil, {"Найдите костры, размещенные в пике Черной горы, Забытом Городе, Некроситете и Стратхольме и коснитесь их; затем поговорите с хранителем праздничного пламени."}},
[9387] = {"Источник порчи",nil, {"Отправляйтесь в руины Ша'наара на полуострове Адского Пламени и добудьте из иллидари-надсмотрщиков 5 демонических сущностей. После завершения задания возвращайтесь к аптекарю Азетену в Соколиный дозор."}},
[9388] = {"Мерцающие огни Калимдора",nil, {"Посетите праздничные костры в Ясеневом лесу, Степях, на Темном Берегу и в Когтистых горах, затем поговорите с хранителем праздничного пламени."}},
[9389] = {"Мерцающие огни Восточных королевств",nil, {"Посетите праздничные костры в предгорьях Хиллсбрада, в Серебряном бору, Западном Крае и Болотине, затем поговорите с хранителем праздничного пламени."}},
[9390] = {"Найти Седаи",nil, {"Разыщите Седаи, брата анахорета Обадия. Седаи может находиться где-то поблизости, на востоке, на полуострове Адского Пламени."}},
[9391] = {"Осветить путь",nil, {"Отправляйтесь к Глубокому разлому на полуострове Адского Пламени и зажгите южный маяк, западный маяк и центральный маяк. После выполнения задания возвращайтесь к капитану следопытов Венн'рену из Соколиного дозора вместе с зажженным факелом."}},
[9392] = {"Обучение разбойника",nil, {"Поговорите с наставником разбойников следопытом Кариэлем в Солнечном Шпиле на острове Солнечного Скитальца."}},
[9393] = {"Обучение охотника",nil, {"Поговорите с следопытом Саллиной в Солнечном Шпиле на острове Солнечного Скитальца."}},
[9394] = {"Куда подевался Виллитен?",nil, {"Найдите смотрителя Виллитена в землях Блеклых Сумерек, что в лесах Вечной Песни."}},
[9395] = {"Вилла Салтерила",nil, {"Поговорите с лордом Салтерилом на вилле Салтерила в лесах Вечной Песни."}},
[9396] = {"Магия араккоа",nil, {"Принесите 4 свитка племени Хаал'эш чародею Калестрису Утренней Звезде в Соколиный дозор на полуострове Адского Пламени."}},
[9397] = {"Пух и перья",nil, {"Отправляйтесь в логово Хаал'эш на полуострове Адского Пламени и найдите молодую самку калири в гнезде калири. Посадите птенца в пустую клетку и принесите сокольничему Дренне Речной Ветер в Соколиный дозор."}},
[9398] = {"Смертельно опасные хищники",nil, {"Убейте 8 детенышей камнеруба и 4 камнерубов-вожаков по просьбе разведчицы Вануры из храма Телхамат на полуострове Адского Пламени."}},
[9399] = {"Безжалостные десятники",nil, {"Убейте 4 иллидари-надсмотрщиков на руинах Ша'наара и возвращайтесь к Икану в храм Телхамат на полуострове Адского Пламени."}},
[9400] = {"Убийца",nil, {"Ступайте по дороге к северу от цитадели Адского Пламени и разыщите убийцу по имени Крун Хребтолом по просьбе Назгрела."}},
[9401] = {"Странное оружие",nil, {"Принесите тяжелый каменный топор Назгрелу в Траллмар на полуострове Адского Пламени."}},
[9402] = {"Апорт!",nil, {"Нырните на дно пруда Тихого Шелеста и добудьте лазурный фиал для инструктора Антеола."}},
[9403] = {"Чистейшая вода",nil, {"Наполните лазурный флакон водой из водопада Элрендар для Антеола."}},
[9404] = {"Живая ветка",nil, {"Убивайте хранителей лесов Вечной Песни, пока не найдете животворную ветвь для инструктора Антеола."}},
[9405] = {"Поручение Вождя",nil, {"Принесите тяжелый каменный топор провозвестнице Регулкут в Траллмар на полуострове Адского Пламени."}},
[9406] = {"Маг'хары",nil, {"Доставьте письмо маг'харов Назгрелу в Траллмар на полуострове Адского Пламени."}},
[9407] = {"Через Темный портал",nil, {"Пройдите через Темный портал и найдите генерал-лейтенанта Ориона. Он ждет вашего прибытия на другой стороне, в Запределье."}},
[9408] = {"Забытые герои",nil, {"Поговорите с командиром армии Данатом Троллебоем в Оплоте Чести."}},
[9409] = {"Срочная доставка",nil, {"Отнесите сверток с фиалами Залдууну на место крушения в долине Аммен."}},
[9410] = {"Хранитель душ",nil, {"Установите тотем духа волка на том месте, где вы нашли тело Круна Хребтолома. Когда появится дух древнего волка, следуйте за ним."}},
[9415] = {"Маршал Синяя Синестен",nil, {"Поговорите с маршалом Синестеном в лагере Альянса возле Улья Зора."}},
[9416] = {"Генерал Кирика",nil, {"Поговорите с генералом Кирикой в лагере Орды рядом с Ульем Регал."}},
[9417] = {"Угроза Араккоа",nil, {"Убейте 4 ветроступов и 6 коготарей из племени Хаал'эш по просьбе Гремни Длинноборода с полустрова Адского Пламени."}},
[9418] = {"Сфера Авруу",nil, {"Отнесите сферу Авруу на алтарь племени Хаал'эш, который находится на юго-востоке от логова Хаал'эш на полуострове Адского Пламени. После того как Аэранас обретет свободу, отдайте ему сферу."}},
[9419] = {"Истощение пустыни",nil, {"Найдите и принесите силитист к экстрактору силитистов и поговорите с маршалом Синестеном в лагере Альянса возле Улья Зора."}},
[9420] = {"Прекрасное оперение",nil, {"Принесите Миррену Длиннобороду на полуострове Адского Пламени 8 перьев калири от различных калири из логова Хаал'эш."}},
[9421] = {"Обучение шамана",nil, {"Я Фирманваар. Ты не зря пришел ко мне. Мое призвание - учить молодых шаманов, особенно тех, что лишь теперь ступают на этот путь.nnДренеи заново открывают для себя шаманизм, Шамакс. Избрание не самого накатанного пути требует особого мужества.nnЯ с радостью помогу тебе, как только ты сочтешь, что пришло время снова поучиться."}},
[9422] = {"Истощение пустыни",nil, {"Найдите и принесите силитист к экстрактору силитистов и поговорите с генералом Кирикой в лагере Орды возле Улья Регал."}},
[9423] = {"Возвращение к Обадию",nil, {"Возвращайтесь к анахорету Обадию в храм Телхамат на полуострове Адского Пламени и расскажите о смерти Седаи."}},
[9424] = {"Месть Макуру",nil, {"Принесите 10 ожерелий предков маг'харов Макуру в храм Телхамат на полуострове Адского Пламени."}},
[9425] = {"Возвращение в Мельницу Таррен",nil, {"Отправляйтесь в Мельницу Таррен в предгорьях Хилсбрада и поговорите с советником Вечерней Зарей."}},
[9426] = {"Пруды Аггонара",nil, {"Отправляйтесь к прудам Аггонара и убейте 6 исчадий ужаса и 6 волдырных гнилостней по просьбе Амаана Мудрого."}},
[9427] = {"Очищение вод",nil, {"Отправляйтесь к прудам Аггонара и используйте фиал очищения над телом Аггонара по просьбе Амаана Мудрого. После того как вода будет очищена от сущности Аггонара, возвращайтесь обратно к Амаану."}},
[9428] = {"На заставу Расщепленного Дерева",nil, {"Поговорите с советником Верным Солнцу на заставе Расщепленного Дерева в Ясеневом лесу."}},
[9429] = {"Путешествие в Темнолесье",nil, {"Поговорите с анахоретом Деланом в городе Темнолесье в Сумеречном лесу."}},
[9430] = {"Реликвии Ша'наара",nil, {"Отправляйтесь в руины Ша'наара и соберите 10 реликвий Ша'наара по просьбе Амаана Мудрого из храма Телхамат на полуострове Адского Пламени."}},
[9431] = {"Разный подход",nil, {"Соберите 2 горсти очищающей земли с грохочущих камнедоров и отнесите Визло Фингалету на заставу Белого Плеса."}},
[9432] = {"Дорога в Астранаар",nil, {"Отправляйтесь в Астранаар в Ясеневом лесу и поговорите с воздаятельницей Паланаарой."}},
[9433] = {"Проба из лунного колодца",nil, {"На каменной игле, ближайшей к границе с Таланааром, в кустах спрятано устройство управления Роботроном 3000. Найдите ее и включите робота при помощи устройства, которое вам дал Визло. Подведите робота к западному краю Тысячи Игл, где находится застава Альянса под названием Таланаар, и прикажите ему набрать немного воды из лунного колодца, после чего вернитесь к Визло Фингалету на заставу Белого Плеса."}},
[9434] = {"Испытание тоника",nil, {"Доставьте ящик с реагентами магистра Элозай на Заставу Вольного Ветра."}},
[9435] = {"Пропавшие кристаллы",nil, {"Отберите у нежити посылку с редкими кристаллами и верните ее Ураану из Южнобережья."}},
[9436] = {"Изучение Кровавого Скальпа",nil, {"Немет Остроглаз из лагеря Гром'гол попросил вас принести ему тотем Кровавого Скальпа, добыв его у шамана из племени Кровавого Скальпа."}},
[9437] = {"Сумерки \"Гонца Рассвета\"",nil, {"Верните груз с \"Гонца Рассвета\" Балиндару Ясной Звезде в деревню Гиблотопь и спасите его первого помощника Итанию из рук тераморцев, из Северной башни."}},
[9438] = {"Донесение Траллу",nil, {"Отправляйтесь в Оргриммар и доставьте Траллу письмо Назгрела."}},
[9439] = {"Потерянный багаж",nil, {"Верните советнику Сарофасу в Каргат вещи советника и рапиру советника из Крепости Ангор."}},
[9440] = {"Маленькие кусочки",nil, {"Накормите испорченными кусочками мяса домашний скот Заблудших. Потом верните отстатки Серсее Певице Заката в Каменор на Болото Печали."}},
[9441] = {"Посол к маг'харам",nil, {"Вернитесь к Горкану Кровопалу на заставу маг'харов на полуострове Адского Пламени и предложите ему свои услуги."}},
[9442] = {"Изнуряющая слабость",nil, {"Принесите гриб-сухошляпку заклинательнице земли Риге с заставы маг'харов на полуострове Адского Пламени. Сухошляпки растут только в пещере внутри Глубокого разлома."}},
[9443] = {"Так называемый знак Светоносного",nil, {"Добудьте знак Светоносного и принесите его Мелару Клинку Рассвета в Бастион, что в Западных Чумных землях."}},
[9444] = {"Осквернение гробницы Утера",nil, {"Оскверните гробницу Утера и возвращайтесь к Мелару Клинку Рассвета в Бастион, что в Западных Чумных землях."}},
[9446] = {"Гробница Светоносного",nil, {"Проводите анахорета Трууна к гробнице Утера в Западные Чумные земли. После поговорите с верховной жрицей Макдонелл из Лагеря Промозглого Ветра."}},
[9447] = {"Применение целебной мази",nil, {"Нанесите целебную мазь на 10 изнуренных маг'харских рубак рядом с заставой маг'харов на полуострове Адского Пламени по просьбе заклинательницы земли Риги."}},
[9448] = {"Милость к проклятым",nil, {"Убейте 3 проклятых из племени Заблудших, потом вернитесь к анахорету Авууну в Убежище на Болото Печали."}},
[9449] = {"Зов земли",nil, {"Поговорите с духом долины в Священной роще в долине Аммен."}},
[9450] = {"Зов земли",nil, {"Убейте 4 неупокоенных духов земли и возвращайтесь к духу долины в Священную рощу в долине Аммен."}},
[9451] = {"Зов земли",nil, {"Отнесите кристалл Земли Фирманваару на место крушения в долине Аммен."}},
[9452] = {"Деликатесный красный луциан",nil, {"Поймайте 10 красных луцианов рыболовной сетью дренеев. Отдайте рыбу и сеть Диктинне на острове Лазурной Дымки."}},
[9453] = {"Поиски Актеона",nil, {"Отнесите ящик красного луциана Актеону на Лазурную заставу."}},
[9454] = {"Великая охота на лунных оленей",nil, {"Принесите 6 кусков вырезки лунного оленя Актеону на Лазурную заставу."}},
[9455] = {"Странные находки",nil, {"Отнесите слабо мерцающий кристалл экзарху Менелаусу на Лазурную заставу."}},
[9456] = {"Истребление ночных ловцов, остров 2...",nil, {"Уничтожьте 8 зараженных ночных ловцов – недоростков и вернитесь на Лазурную заставу."}},
[9457] = {"Необычный посетитель",nil, {"Воспользуйтесь даром Найаса неподалеку от алтаря Найаса и призовите этого элементаля воды. Одолейте его и принесите сердце Найаса Немету Остроглазу в лагерь Гром'гол."}},
[9460] = {"Объединенными усилиями",nil, {"Украдите у предводительницы часовых кружевной носовой платок и принесите его Келтусу Темному Листу."}},
[9461] = {"Зов огня",nil, {"Найдите пророка Велена в Чертоге Света Экзодара."}},
[9462] = {"Зов огня",nil, {"Поговорите с Тулууном на Лазурной заставе на острове Лазурной Дымки."}},
[9463] = {"Ради науки",nil, {"Принесите 8 побегов корнехватов анахоретке Фатиме на Лазурную заставу."}},
[9464] = {"Зов огня",nil, {"Поговорите с Пламенравом на Тлеющей поляне на острове Лазурной Дымки."}},
[9465] = {"Зов огня",nil, {"Верните ритуальный факел Пламенраву на Тлеющую поляну на острове Лазурной Дымки."}},
[9466] = {"Разыскивается: Свирепая Черноскребка",nil, {"Принесите когти Черноскребки капитану следопытов Венн'рену в Соколиный дозор на полуострове Адского Пламени."}},
[9467] = {"Зов огня",nil, {"Добудьте пепел Спесивца и принесите его и ритуальный факел Пламенраву на Тлеющую поляну на острове Лазурной Дымки. Вы можете воспользоваться сферой возвращения, чтобы телепортироваться к Пламенраву."}},
[9468] = {"Зов огня",nil, {"Отнесите пепел Спесивца Тулууну на Лазурную заставу на острове Лазурной Дымки."}},
[9469] = {"Встреча с Перобородом",nil, {"Навестите дворфа Пероборода в его лачуге. Если его нет дома, то поищите, где он может быть."}},
[9470] = {"Жест доброй воли",nil, {"Убейте знахаря Май'джина и его питомца-паука Чаказа по поручению посла Руалета с Заоблачного пика."}},
[9471] = {"Охота на хищников",nil, {"Убейте 10 облезлых среброгривов и 5 среброгривых волков по поручению посла Руалета с Заоблачного пика."}},
[9472] = {"Любовница Арелиона",nil, {"Выманите Виеру Шепот Солнца из поселения и примените к ней свиток воздаяния Каринды по приказу магистра Каринды из Соколиного дозора."}},
[9473] = {"Другое решение",nil, {"Принесите 5 луковиц лазурного львиного зева Дедалу на Лазурную заставу."}},
[9474] = {"Знак Светоносного",nil, {"Добудьте знак Светоносного и принесите его анахорету Трууну в Лагерь Промозглого Ветра, что Западных Чумных землях."}},
[9475] = {"Похищенные яйца",nil, {"Соберите 5 яиц грифона и принесите их послу Руалету на Заоблачный пик."}},
[9476] = {"В поисках Пероборода",nil, {"Отправляйтесь в Шадра'Алор и поищите следы Пероборода."}},
[9483] = {"Изысканные удовольствия жизни",nil, {"Мы, конечно, отправились в паломничество, но это вовсе не значит, надо отказываться от всех радостей жизни. Кажется, прошло столетие с той поры, как я пробовала хорошее вино.nnДа за бутылку кенарийского настойки я и убить могу!nnЗнаю, знаю, я очень опустилась, если готова пить вино ночных эльфов."}},
[9484] = {"Приручение зверя",nil, {"Приручите безумного дракондора с помощью хлыста приручения, потом вернитесь к лейтенанту Вестнице Рассвета."}},
[9485] = {"Приручение зверя",nil, {"Приручите мрачницу с помощью хлыста приручения, потом вернитесь к лейтенанту Вестнице Рассвета."}},
[9486] = {"Приручение зверя",nil, {"Приручите взрослого прыголапа с помощью хлыста приручения, потом вернитесь к лейтенанту Вестнице Рассвета."}},
[9487] = {"Волшебные разорители",nil, {"Чародей Вандрил хочет, чтобы вы принесли ему 10 осколков тайной магии."}},
[9488] = {"Простое одеяние",nil, {"Чародей Вандрил хочет, чтобы вы принесли ему 15 кусков льняной ткани и паутинную железу паука-прядильщика."}},
[9489] = {"Поднять боевой дух",nil, {"Примените \"Слово Силы: Стойкость\" к 6 следопытам из лесов Вечной Песни на Тропе Мертвых."}},
[9490] = {"Матриарх камнедеров",nil, {"Добудьте когти Свирепой Черноскребки, которая обитает в Глубоком Разломе и принесите их Икану в храм Телхамат на полуострове Адского Пламени."}},
[9491] = {"Не откажусь",nil, {"Эраланна из Транквиллиона хочет, чтобы вы добыли исцарапанное золотое кольцо из катакомб Амани."}},
[9492] = {"Новый поворот событий",nil, {"Принесите кулак вождя Каргата боевому командиру Данату Троллебою в Оплот Чести."}},
[9493] = {"Элита Орды Скверны",nil, {"Убейте 8 легионеров клана Изувеченной Длани, 4 центуриона клана Изувеченной Длани и 4 воителей клана Изувеченной Длани по приказу боевого командира Ромуса из Оплота Чести."}},
[9494] = {"Угли Скверны",nil, {"Принесите уголь Скверны волхву Забраксис из Оплота Чести."}},
[9495] = {"Воля Вождя",nil, {"Принесите Кулак Вождя Каргата Назгрелу в Траллмар."}},
[9496] = {"Элита Орды Скверны",nil, {"Убейте 8 легионеров клана Изувеченной Длани, 4 центуриона клана Изувеченной Длани и 4 воителей из клана Изувеченной Длани по приказу темного охотника Туджина из Траллмара."}},
[9498] = {"Соколиный дозор",nil, {"Доложитесь капитану следопытов Венн'рену в Соколином дозоре на полуострове Адского Пламени."}},
[9499] = {"Соколиный дозор",nil, {"Отправляйтесь в Соколиный дозор, на юго-запад полуострова Адского Пламени, и поговорите с капитаном следопытов Венн'реном."}},
[9500] = {"Зов воды",nil, {"Найдите на острове Лазурной Дымки в Кристальном зале Экзодара предсказателя Нобундо и поговорите с ним."}},
[9501] = {"Зов воды",nil, {"Найдите Водяницу у Подводного Рифа возле острова Кровавой Дымки и поговорите с ней."}},
[9502] = {"Зов воды",nil, {"Найдите на острове Лазурной Дымки в Кристальном зале Экзодара предсказателя Нобундо и поговорите с ним."}},
[9503] = {"Зов воды",nil, {"Принесите 6 бутылочек смрадной эссенции на остров Кровавой Дымки в храм Сокрытого Рифа и отдайте Водянице."}},
[9504] = {"Зов воды",nil, {"Наполните пустой бурдюк, а затем вернитесь на остров Кровавой Дымки в храм Сокрытого Рифа и отдайте бурдюк Водянице."}},
[9505] = {"Пророчество Велена",nil, {"Отправляйтесь на юг и отыщите народ, о котором говорило раненое существо."}},
[9506] = {"Начало положено",nil, {"Верните адмиралу Одиссию морской компас и морскую карту, украденные Торговой компанией."}},
[9508] = {"Зов воды",nil, {"Принесите голову Телатиона на остров Кровавой Дымки в храм Сокрытого Рифа и отдайте Водянице."}},
[9509] = {"Зов воды",nil, {"Отнесите флакон чистейшей воды предсказателю Нобундо. Его можно найти на острове Лазурной Дымки в Экзодаре."}},
[9510] = {"Шкура щетинистого копытня",nil, {"Добудьте 5 шкур щетинистого копытня по просьбе поставщика Бракнара с Заставы маг'харов на Полуострове Адского Пламени."}},
[9511] = {"Боевые планы Каргата",nil, nil},
[9512] = {"Крабовый супчик поваренка",nil, {"Принесите 6 кусков мяса шустрых крабов \"Поваренку\" Максоусу в лагерь Одиссия."}},
[9513] = {"Возвращение руин",nil, {"Убейте 5 мирмидонов, 5 наг и 5 сирен из клана Зловещей Чешуи для жрицы Кайлин Ил'динар."}},
[9514] = {"Покрытая рунами табличка",nil, {"Отнеcите покрытую рунами табличку жрице Кайлин Ил'динар в лагерь Одиссия на острове Лазурной Дымки."}},
[9515] = {"Полководец Шрисс'тиз",nil, {"Жрица Кайлин Ил'динар из лагеря Одиссия попросила вас убить полководца Шрисс'тиза."}},
[9516] = {"Уничтожить Легион!",nil, {"Уничтожьте 6 бичевательниц Маннорока, 6 стражей Скверны и 6 опаляющих инферналов, а затем вернитесь к воздаятелю Ведаару в Лесную Песнь в Ясеневом лесу."}},
[9517] = {"Напрасные усилия",nil, {"Доставьте 15 связок древесины Песни Войны и 5 связок оскверненной древесины Сатирнаара Кривокрону в Лесную Песнь."}},
[9518] = {"Пособники разрушения",nil, {"Часовая Люсьель Шепот Звезд из Лесной Песни хочет, чтобы вы убили надзирателя Гортака, 2 пилотов крошшеров клана Песни Войны, 5 ордынских лесозаготовителей и 10 ордынских разведчиков."}},
[9519] = {"Потерянная чаша",nil, {"Принесите чашу Элуны Кайнету Штилю в Лесную Песнь."}},
[9520] = {"Демонические планы",nil, {"Доставьте демонические планы воздаятелю Ведаару в Лесную Песнь в Ясеневом лесу."}},
[9521] = {"Донесение с северного фронта",nil, {"Доставьте донесение часовой Люсьель часовой Дальней Песне в рощу Среброкрылых."}},
[9522] = {"Больше никогда!",nil, {"Убейте Горганнона и Диатора Ищейку и вернитесь к воздаятелю Ведаару в Лесную Песнь в Ясеневом лесу."}},
[9523] = {"Хрупкие предметы. Не кантовать!",nil, {"Принесите 8 древних реликвий археологу Адаманту Железному Сердцу в лагерь Одиссия на острове Лазурной Дымки."}},
[9524] = {"Узник Цитадели",nil, {"Спасите капитана Алину в Цитадели Адского Пламени до того, как ее казнят.nnЭто задание выполняется в режиме героической сложности."}},
[9525] = {"Imprisoned in the Citadel",nil, {"Rescue Captain Boneshatter inside Hellfire Citadel before he is executed.nnThis quest must be completed in Heroic dungeon difficulty."}},
[9526] = {"Возрождение Холма Демонического Огня",nil, {"Посадите 8 древесных саженцев в кучи плодородной земли и вернитесь к Кривокрону в Лесную Песнь в Ясеневом лесу."}},
[9527] = {"Все, что осталось",nil, {"Верните Коулену останки его семьи."}},
[9528] = {"Крик о помощи",nil, {"Проводите Магвин через остров до дома."}},
[9529] = {"Камень",nil, {"Добудьте камень Бездны с Тропы Мертвых."}},
[9530] = {"Созревший план",nil, {"Принесите выдолбленный ствол и 5 охапок листьев адмиралу Одиссию в лагерь Одиссия на острове Лазурной Дымки."}},
[9531] = {"У деревьев есть уши",nil, {"Проследите за встречей Гизла с предателем.nnРасскажите обо всем увиденном адмиралу Одиссию."}},
[9532] = {"Найти Келтуса Темного Листа",nil, {"Найдите Келтуса Темного Листа."}},
[9533] = {"Рука помощи",nil, {"Отправляйтесь в Лесную Песнь и поговорите с архитектором Немосом."}},
[9534] = {"Уничтожить Легион!",nil, {"Уничтожьте 6 бичевательниц Маннорока, 6 стражей Скверны и 6 опаляющих инферналов, а затем вернитесь к Валуше на заставу Расщепленного Дерева в Ясеневом лесу."}},
[9535] = {"Демонические планы",nil, {"Доставьте демонические планы Валуше на заставу Расщепленного Дерева, что в Ясеневом лесу."}},
[9536] = {"Больше никогда!",nil, {"Убейте Горганнона и Диатора Ищейку, затем вернитесь к Валуше на заставу Расщепленного Дерева в Ясеневом лесу."}},
[9537] = {"Не ведая пощады",nil, {"Добудьте для адмирала Одиссия переписку предателя у инженера \"Искра\" Перемолоттера"}},
[9538] = {"Изучение языка",nil, {"Изучите пергамент \"Язык фурболгов из племени Тихвой для начинающих\", полученный от Ауррена, а затем изучите тотемом Акиды на Лазурной заставе."}},
[9539] = {"Тотем Ку",nil, {"Найдите тотем Ку с помощью проводника."}},
[9540] = {"Тотем Тикти",nil, {"Найдите тотем Тикти с помощью проводника."}},
[9541] = {"Тотем Йора",nil, {"Найдите тотем Йора с помощью проводника."}},
[9542] = {"Тотем Варк",nil, {"Найдите тотем Варк с помощью проводника."}},
[9543] = {"Искупление",nil, {"Поговорите с Амааном Мудрым в храме Телхамат на полуострове Адского Пламени."}},
[9544] = {"Пророчество Акиды",nil, {"Освободите 8 пленников из племени Тихвой из клеток в деревне Косолапов. Вернитесь к Аругу из племени Тихвой на Лазурную заставу."}},
[9545] = {"Реликвия ясновидца",nil, {"Вернитесь к телу Седаи, которое находится к северо-востоку от храма Телхамат, и используйте там реликвию ясновидца по просьбе Амаана Мудрого из храма Телхамат на полуострове Адского Пламени. После выполнения задания возвращайтесь к Амаану."}},
[9547] = {"Зов воздуха",nil, {"Поговорите с предсказателем Нобундо в Кристальном зале Экзодара на острове Лазурной Дымки."}},
[9548] = {"Украденное снаряжение",nil, {"Верните Клопперу Бацбаху его оборудование. Клоппера можно найти в старом черепашьем панцире на берегу."}},
[9549] = {"Артефакты племени Черного Ила",nil, {"Принесите 3 варварских идола и 6 варварских ножа мурлоков Клопперу Бацбаху, который прячется в старом черепашьем панцире на берегу."}},
[9550] = {"Загадочная карта",nil, {"Найдите павильон, изображенный на карте, и выясните, что в нем необычного."}},
[9551] = {"Зов воздуха",nil, {"Поговорите с предсказателем Нобундо в Кристальном зале Экзодара на острове Лазурной Дымки."}},
[9552] = {"Зов воздуха",nil, {"Поговорите с Велаадой на Штормовом Пике острова Лазурной Дымки."}},
[9553] = {"Зов воздуха",nil, {"Поговорите с Шептателем на Штормовом Пике острова Лазурной Дымки."}},
[9554] = {"Зов воздуха",nil, {"Отдайте Завихрение воздуха предсказателю Нобундо в Экзодаре."}},
[9555] = {"Зов огня",nil, {"Поговорите с предсказателем Нобундо в Кристальном зале Экзодара."}},
[9556] = {"Вода для мага",nil, {"Найдите бутылку святой воды Стратхольма на улицах Стратхольма и доставьте ее Лордеронскому магу в квартал магов в Штормграде или Подгороде, если вашей стороне удалось одержать верх в Восточных Чумных землях."}},
[9557] = {"Расшифровка книги",nil, {"Отнесите древние записи на Кровавую заставу и покажите анахорету Пайтеусу."}},
[9558] = {"Длиннобородые",nil, {"Найдите Гремни Длинноборода на северо-западе от логова Хаал'эш на полуострове Адского Пламени."}},
[9559] = {"Логово племени Тихвой",nil, {"Поговорите с верховным вождем в логове племени Тихвой."}},
[9560] = {"Звери Судного дня!",nil, {"Принесите 8 шкур опустошителей Мурдо в логово племени Тихвой."}},
[9561] = {"Записи Нолкаи",nil, {"Найдите место, описанное в дневнике Нолкаи, и отыщите его тайник."}},
[9562] = {"Мурлоки... За что нам эта напасть?",nil, {"Верните 5 мешков зерна племени Тихвой."}},
[9563] = {"Доверие Миррена",nil, {"Принесите Миррену Длиннобороду 1 кружку горькой настойки Стражей Пустоты. Для выполнения этого задания необходимо иметь дружелюбное отношение Оплота Чести."}},
[9564] = {"Клок шерсти Гурфа",nil, {"Верните Гурфу клок его шерсти в логово племени Тихвой на острове Лазурной Дымки."}},
[9565] = {"Поиски в логове племени Тихвой",nil, {"Найдите в логове племени Тихвой причины предательства совухов."}},
[9566] = {"Кровавые кристаллы",nil, {"Поговорите с верховным вождем в логове племени Тихвой."}},
[9567] = {"Лицо врага",nil, {"Найдите в Наззивиане монумент Наззивуса, возьмите один из иероглифов с монумента и вернитесь к воздаятельнице Аалезии на Кровавую заставу."}},
[9568] = {"Лучшая защита",nil, {"Воздаятельница Алезия с Кровавой заставы просит убить 10 сатиров, 5 разбойников и 5 скверноподанных из племени Наззивус."}},
[9569] = {"Противостояние",nil, {"Убейте Зевракса, 5 аксариенских тенеловов и 5 аксариенских призывателей огня, соберите 5 оскверненных кристаллов и отнесите их на Кровавую заставу воздаятельнице Аалезии."}},
[9570] = {"Куркен-жмуркен",nil, {"Убейте Куркена и принесите его шкуру Курцу Прорицателю в логово племени Тихвой на острове Лазурной Дымки."}},
[9571] = {"Шкура Куркена",nil, {"Отнесите шкуру Куркена Мурдо в логово племени Тихвой."}},
[9572] = {"Ослабить оборону валов",nil, {"Убейте начальника стражи Гарголмара, Омора Неодолимого и дракона Назана. Принесите руку Гарголмара, копыто Омора и голову Назана Каза'ресу в Траллмар на Полуострове Адского Пламени."}},
[9573] = {"Вождь Умуру",nil, {"Убейте вождя Умуру и 9 безумных диких совухов."}},
[9574] = {"Отравленные скверной",nil, {"Принесите на Кровавую заставу 6 кусков коры, покрытой кристаллами, и отдайте Морэ."}},
[9575] = {"Ослабить оборону валов",nil, {"Убейте начальника стражи Гарголмара, Омора Неодолимого и дракона Назана. Принесите руку Гарголмара, копыто Омора и голову Назана Пушкаренку в Оплот Чести на полуострове Адского Пламени."}},
[9576] = {"Ожерелье Жестокого Плавника",nil, {"Покажите подвеску красного кристалла Морэ на Кровавой заставе."}},
[9578] = {"В поисках Галена",nil, {"Найдите Галена, мужа Морэ."}},
[9579] = {"Судьба Галена",nil, {"Принесите на Кровавую заставу амулет Галена и отдайте его Морэ."}},
[9580] = {"Вскормленные медвежатиной",nil, {"Принесите зверолову Лисеону на Кровавую заставу 8 кусков мяса с боков старых бурых медведей."}},
[9581] = {"Изучение кристаллов",nil, {"Добудьте образец кристалла с места крушения, отколов фрагмент кайлом для добычи кристаллов. Принесите образец на Кровавую заставу и отдайте предвестнику Миколаасу."}},
[9582] = {"Силой единой",nil, {"Откройте клетку смертоносного опустошителя, убейте его и вернитесь к Руаде на Лазурную заставу на острове Лазурной Дымки."}},
[9584] = {"Второй образец",nil, {"Добудьте образец кристалла с места столкновения, отколов фрагмент кайлом для добычи кристаллов. Принесите образец на Кровавую заставу и отдайте его предвестнику Миколаасу."}},
[9585] = {"Последний образчик",nil, {"Добудьте образец кристалла с места столкновения, отколов фрагмент кайлом для добычи кристаллов. Принесите образец на Кровавую заставу и отдайте предвестнику Миколаасу."}},
[9586] = {"Помощь Таваре",nil, {"Найдите Тавару и исцелите ее раны."}},
[9587] = {"Предвестье беды",nil, {"Доставьте угрожающее письмо боевому командиру Данату Троллебою в Оплот Чести на полуострове Адского Пламени."}},
[9588] = {"Предвестье беды",nil, {"Доставьте угрожающее письмо Назгрелу в Траллмар на Полуострове Адского Пламени."}},
[9589] = {"Кровь есть жизнь",nil, {"Принесите 10 фиалов крови орков Скверны Пушкаренку в Оплот Чести на полуострове Адского Пламени."}},
[9590] = {"Кровь есть жизнь",nil, {"Принесите 10 фиалов крови орков Скверны центуриону Каза'ресу в Траллмар на Полуострове Адского Пламени."}},
[9591] = {"Приручение зверя",nil, {"Используйте тотем приручения и приручите колючего краба. Вернитесь к охотнице Келле Тетиве Ночи."}},
[9592] = {"Приручение зверя",nil, {"Используйте тотем приручения и приручите большого лесонога, после чего вернитесь к охотнице Келле Тетиве Ночи."}},
[9593] = {"Приручение зверя",nil, {"Используйте тотем приручения и приручите ночного ловца. Вернитесь к охотнице Келле Тетиве Ночи."}},
[9594] = {"Знаки Легиона",nil, {"Убейте 8 сатиров из племени Наззивус и 8 скверноподданных из племени Наззивус, а затем отнесите латный доспех Тзерака воздаятельнице Аалезии на Кровавую заставу."}},
[9595] = {"Контроль",nil, {"Убивайте мурлоков с Илистого Берега до тех пор, пока не появится призрак кель'дорайского мага, затем уничтожьте его. После этого поговорите с Бати в Чертоге Света Экзодара."}},
[9598] = {"Искупление",nil, {"Прочтите Фолиант Божественности и поговорите с Джоль."}},
[9599] = {"<UNUSED>",nil, {"Speak with Jol in the Exodar."}},
[9600] = {"Искупление",nil, {"Воскресите молодого фурболга-шамана из племени Тихвой при помощи знака Жизни, а затем вернитесь к Джоль."}},
[9601] = {"В Бастион",nil, {"Поговорите с Меларом Клинком Рассвета из Бастиона."}},
[9602] = {"Избавь их от лукавого...",nil, {"Отнесите переписку предателя экзарху Менелаусу на Лазурную заставу."}},
[9603] = {"Лежанки, лекарства и прочая ерунда",nil, {"Отвезите список Тофера на Кровавую заставу дрессировщику гиппогрифов Лаандо."}},
[9604] = {"На крыльях гиппогрифа",nil, {"Возьмите у Лаандо гиппогрифа и летите в Экзодар. В Экзодаре отдайте список Тофера Нургуни. Нургуни можно найти у трона Наару в Экзодаре."}},
[9605] = {"Укротитель гиппогрифов Стефанос",nil, {"Найдите Стефаноса у ворот Экзодара и наймите гиппогрифа для возвращения на Кровавую заставу."}},
[9606] = {"Возвращение к Тоферу Лоаалу",nil, {"Договоритесь со Стефаносом и наймите у него гиппогрифа для путешествия на Кровавую заставу. На заставе доставьте припасы Нургуни корчмарю Тоферу Ололо."}},
[9607] = {"В сердце ярости",nil, {"Детально изучите Кузню Крови и доложите об увиденном командиру армии Данату Троллебою в Оплот Чести на полуострове Адского Пламени."}},
[9608] = {"В сердце ярости",nil, {"Детально изучите Кузню Крови и доложите об увиденном Назгрелу в Траллмар на Полуострове Адского Пламени."}},
[9609] = {"Помощь дозорному Биггсу",nil, {"Найдите дозорного Биггса на Болоте Печали."}},
[9610] = {"Озеро Слез",nil, {"Добудьте 10 артефактов Атал'ай и вернитесь с ними к Холаару в Убежище на Болото Печали."}},
[9612] = {"Большое спасибо!",nil, {"Поговорите с экзархом Менелаусом на Лазурной заставе."}},
[9616] = {"Бандиты!",nil, {"Отнесите сообщение эльфов крови экзарху Менелаусу на Лазурную заставу."}},
[9617] = {"На поиски Странников",nil, {"Поговорите с лейтенантом Вестницей Рассвета в обители Странников."}},
[9618] = {"Возвращение с донесениями",nil, {"Отнесите донесения Келтуса Темного Листа Зеланису в Луносвет."}},
[9619] = {"Руна Призыва",nil, {"Используйте камень Бездны, чтобы призвать демона Бездны в деревню Золотистой Дымки, одолейте демона и вернитесь к Талионии в Луносвет."}},
[9620] = {"Пропавшие картографы",nil, {"Предвестник Миколаас просит разыскать группу разведчиков, которую он послал к развалинам Лорет'Арана, и помочь группе чем только возможно."}},
[9621] = {"Посол к Орде",nil, {"Отнесите письмо от Лор'темара Терона леди Сильване Ветрокрылой в Подгород, столицу Отрекшихся в Тирисфальских лесах."}},
[9622] = {"Предупреди свой народ",nil, {"Поговорите с экзархом Менелаусом на Лазурной заставе."}},
[9623] = {"Время пришло",nil, {"Передайте распоряжения экзарха Тораллиусу Пастуху. Он находится у Экзодара, недалеко от входа."}},
[9624] = {"Любимое блюдо",nil, {"Принесите 10 песчаных груш Аонару в лагерь Кессела."}},
[9625] = {"Элекки – это серьезно",nil, {"Поговорите с Ворканом Пастухом Элекков на острове Кровавой Дымки."}},
[9626] = {"Встреча с вождем",nil, {"Отнесите письмо с печатью Леди Сильваны Ветрокрылой Траллу в Оргриммар, столицу Дуротара."}},
[9627] = {"Верность Орде",nil, {"Возвращайтесь в Луносвет и поговорите с Лор'темаром Тероном."}},
[9628] = {"Цена знания – жизнь",nil, {"Неподалеку от того места, где были убиты картографы, обитают наги из клана Зловещей Чешуи. Отберите у них кристалл с результатами наблюдений и доставьте его на Кровавую заставу предвестнику Миколаасу."}},
[9629] = {"Поймать и отпустить",nil, {"Морэ с Кровавой заставы просит вас \"пометить\" 6 разведчиков из племени Черного Ила при помощи маркировщика мурлоков."}},
[9630] = {"Дневник Медива",nil, {"По поручению верховного мага Альтура на Перевале Мертвого Ветра войдите в Каражан и поговорите с Вравьеном."}},
[9631] = {"Взаимовыручка",nil, {"Доставьте фрагмент обугленной кости Калинне Латред в Область 52 в Пустоверти."}},
[9632] = {"Новые союзники",nil, {"Доставьте рекомендательное письмо охотнице Келле Тетиве Ночи."}},
[9633] = {"Дорога к Аубердину",nil, {"Сядьте на корабль, идущий в Аубердин. Пристань находится неподалеку от дома охотницы Келлы Тетивы Ночи. В Аубердине отдайте рекомендательное письмо Тандрису Ветропряду."}},
[9634] = {"Чужие-хищники",nil, {"Убейте 10 детенышей опустошителя с острова Кровавой Дымки и вернитесь к Воркану Пастуху Элекков. Воркана можно найти на острове Кровавой Дымки в лагере Кессела."}},
[9635] = {"Удаленный конденсатор выхлопов!",nil, {"Принесите 15 деталей парового насоса Маку Дайверу в Забра'джин в Зангартопи."}},
[9636] = {"Удаленный конденсатор выхлопов!",nil, {"Принесите 15 деталей парового насоса К. Ли Мелюзге в Телредор в Зангартопи."}},
[9637] = {"Требование Калинны",nil, {"Калинна Латред требует отыскать и принести ей фолиант сумерек и книгу забытых имен. Первой владеет главный чернокнижник Пустоклят, его можно найти в Разрушенных залах цитадели Адского Пламени. Вторая принадлежит заклинателю тьмы Ситу, который находится в Сетеккских залах в Аукиндоне.nnЭто задание должно быть выполнено на героическом уровне сложности."}},
[9638] = {"В хороших руках",nil, {"Поговорите с Градавом в Библиотеке стража в Каражане."}},
[9639] = {"Камсис",nil, {"Поговорите с Камсис в Библиотеке Стража в Каражане."}},
[9640] = {"Тень Арана",nil, {"Достаньте дневник Медива и верните его Камсис в Библиотеку Стража в Каражане."}},
[9641] = {"Осколки облученного кристалла",nil, {"Принесите 10 осколков облученного кристалла воздаятелю Боросу на Кровавую заставу."}},
[9642] = {"И снова облученные кристаллы",nil, {"Если у тебя есть еще облученные осколки, я с радостью обменяю их на свои кристаллы."}},
[9643] = {"Удушающая лоза",nil, {"Принесите зверолову Лисеону 6 побегов колючей удушающей лозы."}},
[9644] = {"Ночная Погибель",nil, {"Ступайте на террасу Мастера в Каражане и с помощью урны Калинны призовите Ночную Погибель. Извлеките из тела Ночной Погибели слабую чародейскую сущность и доставьте ее верховному магу Альтуру."}},
[9645] = {"Терраса Мастера",nil, {"Поднимитесь на террасу Мастера в Каражане и прочитайте дневник Медива. После этого возвращайтесь вместе с дневником к верховному магу Альтуру."}},
[9646] = {"РАЗЫСКИВАЕТСЯ: Коготь Смерти",nil, {"Принесите лапу Когтя Смерти предвестнику Миколаасу на Кровавую заставу."}},
[9647] = {"Ловля бражников",nil, {"Зверолов Лисеон просит убить 10 царственных синих бражников."}},
[9648] = {"Грибная похлебочка из Мак'Ари",nil, {"Джессера из Мак'Ари просит принести ему на Кровавую заставу по одному экземпляру водянистого смрадорога, кровавого гриба, губительной полиспоры и скверноконуса."}},
[9649] = {"Слеза Изеры",nil, {"Джессера из Мак'Ари просит принести ему на Кровавую заставу 2 экземпляра слезы Изеры."}},
[9663] = {"Дуга Кессела",nil, {"Сообщите верховному вождю племени Тихвой, экзарху Менелаусу и адмиралу Одиссию о готовящемся вторжении эльфов крови, а затем возвратитесь к Кесселу. У вас есть 15 минут. Элекка следует держать подальше от воды."}},
[9664] = {"Новые форпосты",nil, {"Захватите башню Королевской Стражи, Восточную башню, Башню Северного перевала и башню Проклятого леса в Восточных Чумных землях и вернитесь к посланнику Белобороду в Часовню Последней Надежды."}},
[9665] = {"Укрепим оборону",nil, {"Захватите башню Королевской Стражи, Восточную башню, Башню Северного перевала и башню Проклятого леса в Восточных Чумных землях и возвращайтесь к посланнику Гормоку в Часовню Последней Надежды."}},
[9666] = {"Демонстрация силы",nil, {"Проникните в логово клана Зловещей Чешуи, убейте лорда Ксиза и пронзите его тело древком дренейского знамени."}},
[9667] = {"Спасение принцессы Тихвой",nil, {"Выпустите принцессу Тихвой из клетки, а затем найдите на Кровавой заставе посла племени Тихвой Фрасабу и поговорите с ним.nnЧтобы раздобыть ключ от клетки, убейте верховного вождя племени Косолапов."}},
[9668] = {"Доклад экзарху Адметиусу",nil, {"Поговорите с экзархом Адметиусом на Кровавой заставе."}},
[9669] = {"Пропавшая экспедиция",nil, {"Убейте 8 пауков-ткачих, 8 паучих-кровопийц и матриарха пауков – Зарак. По завершении задания вернитесь на Кровавую заставу к Акелусу."}},
[9670] = {"Они живы! Может быть...",nil, {"Освободите 5 ученых экспедиции по просьбе ученого Корнелиуса."}},
[9671] = {"Срочная доставка",nil, {"Ты - Вентурианец? Что ж, вовремя! Я уже и не надеялся тебя найти.nnЗагляни в почтовый ящик - там тебя ждет срочное письмо. Мне его передал один человек, адмирал с острова Лазурной Дымки. Как же его звали... Одиссей? Одессит?.. Нет, не помню: из головы вылетело. Но он сказал, что ты его непременно вспомнишь.nn<Гермесий пожимает плечами.>"}},
[9672] = {"Наследие Проклятой Крови",nil, {"Адмирал Одиссий просит разыскать капитана Эдварда Хейнса и поговорить с ним."}},
[9673] = {"Дрессировка",nil, {"Поговорите с Хальтенисом в Луносвете."}},
[9674] = {"Проклятые наги",nil, {"Капитан Эдвард Хейнс с острова Драконьей Скорби просит убить 10 наг с острова Проклятой Крови."}},
[9675] = {"Дрессировка",nil, {"Поговорите с Ганааром в Торговом ряду Экзодара."}},
[9676] = {"Наставник паладинов",nil, {"Поговорите с наставником паладинов Джестенисом Солнцесветом в Солнечном Шпиле на острове Солнечного Скитальца."}},
[9677] = {"Призыв предводителя рыцарей Кровосмела",nil, {"Поговорите с предводителем рыцарей Кровосмелом на площади Странников в Луносвете."}},
[9678] = {"Первое испытание",nil, {"Отправляйтесь на остров, описанный предводителем рыцарей Кровосмелом, найдите пещеру и разожгите огонь в жаровне. Пройдя первое испытание, возвращайтесь к предводителю рыцарей Кровосмелу в Луносвет."}},
[9680] = {"Тайны прошлого",nil, {"Верховный маг Альтур желает, чтобы вы нашли в горах к югу от Каражана на Перевале Мертвого Ветра фрагмент обугленной кости и доставили ему."}},
[9681] = {"Источник силы",nil, {"Поговорите с магистром Асталором Кровавой Клятвой в тайной зале, расположенной под штабом рыцарей Крови."}},
[9682] = {"Отчаявшиеся",nil, {"Капитан Эдвард Хейнс с острова Драконьей Скорби просит освободить 4 души с острова Проклятой Крови."}},
[9683] = {"Избавление от Проклятой Крови",nil, {"Капитан Эдвард Хейнс с острова Драконьей Скорби просит уничтожить статую королевы Азшары и убить Атофа Проклятую Кровь."}},
[9684] = {"Получение силы Света",nil, {"Используйте мерцающий сосуд на эльфах крови-магистрах, чтобы наполнить его магической силой и возвращайтесь к предводителю рыцарей Кровосмелу в Луносвет."}},
[9685] = {"Воскрешение",nil, {"Отправляйтесь в таверну Луносвета и воскресите Сангриаса Безмолвного Клинка с помощью силы, заключенной в наполненном мерцающем сосуде."}},
[9686] = {"Второе испытание",nil, {"Пройдите второе испытание, победив воителя Ярокрова, воителя Светореза, воительницу Быстромеч и воителя Солнцесвета в павильоне у наставника Келерана Алого Взмаха."}},
[9687] = {"Святотатство",nil, {"Принц Торет с острова Драконьей Скорби просит отыскать 8 костей драконов."}},
[9688] = {"Дорога в Сон",nil, {"Принц Торет с острова Драконьей Скорби просит убить 5 темно-зеленых дракончиков и 5 детенышей зеленого дракона."}},
[9689] = {"Острозуб",nil, {"Принц Торет с острова Драконьей Скорби просит убить Острозуба."}},
[9690] = {"Второе испытание",nil, {"Поговорите с наставником Келераном Алым Взмахом."}},
[9691] = {"Возвращение в Луносвет",nil, {"Возвращайтесь к предводителю рыцарей Кровосмелу в Луносвет."}},
[9692] = {"Путь адепта",nil, {"Принесите оскверненный самоцвет Кора, ящик со слитками, закаленными в крови, нужное количество крови разгневненных и знак отличия рыцаря крови предводителю рыцарей Кровосмелу в Луносвет."}},
[9693] = {"Что для тебя Аргус?..",nil, {"Экзарх Адметиус с Кровавой заставы просит поговорить с воздаятелем Боросом."}},
[9694] = {"Кровавая застава",nil, {"Убейте 10 шпионов Солнечных Ястребов и сообщите об этом на Кровавую заставу воздаятелю Боросу."}},
[9696] = {"Переводы...",nil, {"Доставьте послание Солнечных Ястребов дознавательнице Элизии на Кровавую заставу."}},
[9697] = {"Наблюдательница Лиса'о",nil, {"Поговорите с наблюдательницей Лиса'о у кенарийского караульного поста в Зангартопи."}},
[9698] = {"Аудиенция у Пророка",nil, {"Доставьте переведенное послание Солнечных Ястребов Пророку Велену в Экзодар."}},
[9699] = {"Правда или вымысел",nil, {"Доставьте распоряжения Велена воздаятелю Боросу на Кровавую заставу."}},
[9700] = {"Магия против Тьмы",nil, {"Воздаятель Борос с Кровавой заставы просит найти солнечный портал и убить 5 аномалий Бездны."}},
[9701] = {"Наблюдение за спорлингами",nil, {"Исследуйте Грибную поляну в Зангартопи, затем вернитесь к наблюдательнице Лиса'о к кенарийскому караульному посту в Зангартопи и расскажите об увиденном."}},
[9702] = {"Вопросы питания",nil, {"Соберите 10 образцов выброшенного корма и вернитесь к наблюдательнице Лиса'о на кенарийский караульный пост в Зангартопи."}},
[9703] = {"Криогенный блок",nil, {"Воздаятель Курос хочет, чтобы вы доставили на Кровавую заставу 12 коробок с медицинскими припасами."}},
[9704] = {"Смерть от рук Презренных",nil, {"Отправляйтесь на юг, в руины Луносвета, и отыщите убитого курьера на Рассветной улице."}},
[9705] = {"Возвращение посылки",nil, {"Вернитесь к курьеру Аларион с посылкой Эроны."}},
[9706] = {"Дневник Галена: судьба воздаятеля Саруана",nil, {"Доберитесь до Кровавой заставы и отдайте дневник Галена воздаятелю Куросу."}},
[9707] = {"Ковка оружия",nil, {"Принесите ящик с материалами Бемаррину в Луносвет."}},
[9708] = {"Знакомые грибы",nil, {"Соберите 15 образцов грибов и принесите их наблюдательнице Лиса'о на караульный пост Кенария в Зангартопи."}},
[9709] = {"Ворованные грибы",nil, {"Соберите 10 ящиков грибов и вернитесь с ними к наблюдательнице Лиса'о на караульный пост Кенария в Зангартопи."}},
[9710] = {"Закаленный кровью протазан",nil, {"Дождитесь, пока Бемаррин смастерит оружие."}},
[9711] = {"Матис Жестокий",nil, {"Воздаятель Курос с Кровавой заставы требует, чтобы Матис Жестокий был пойман."}},
[9712] = {"Талассийский боевой конь",nil, {"Поговорите с предводителем рыцарей Доблестной Кровью, чтобы научиться вызывать талассийского боевого коня."}},
[9713] = {"Включение возможности сбора огнешляпки",nil, nil},
[9714] = {"Принеси мне еще одну клумбу!",nil, {"Кровавый гибискус, который можно найти у шаркунов, болотоходов и подболотных скатов в Нижетопи, в резервуаре Кривого Клыка, нам всегда пригодится. Приноси его мне связками по пять штук, я буду очень тебе благодарен!"}},
[9715] = {"Принеси мне клумбу!",nil, {"Добудьте 5 растений кровавого гибискуса и отнесите их Гзхун'тту в Спореггар в Зангартопи."}},
[9716] = {"Неприятности на озере Тенетопь",nil, {"Выясните причину убывания воды в озере Тенетопь. Затем вернитесь к Исиэль Песни Ветра в Кенарийский приют в Зангартопи."}},
[9717] = {"Удачный ход",nil, {"Добудьте ветку подспорника и вернитесь с ней к Т'шу в Спореггар в Зангартопи."}},
[9718] = {"С высоты птичьего полета",nil, {"Используйте амулет буревестника, чтобы исследовать основные озера Зангартопи по просьбе Исиэли Песни Ветра."}},
[9719] = {"Охота на Охотницу",nil, {"Принесите мозг Черной Охотницы Кхн'иксу в Спореггар в Зангартопи."}},
[9720] = {"Необходимо поддерживать равновесие!",nil, {"Осыпьте семенами железной лозы пульты управления паровым насосом в Змеином озере, озере Тенетопь, озере Болотных Огоньков и в Лагуне. Затем вернитесь к Исиэль Песни Ветра в Кенарийский приют в Зангартопи и отдайте ей оставшиеся семена."}},
[9721] = {"Призыв леди Лиадрин",nil, {"Поговорите с Лордом Соланаром в Луносвете."}},
[9722] = {"Путь мастера",nil, {"Поговорите с Лордом Соланаром еще раз и примите предложение о покровительстве."}},
[9723] = {"Жест преданности",nil, {"Принесите лорду Соланару в Луносвет 40 отрезов рунической ткани, 6 арканитовых слитков, 10 солнечников, 5 темных рун и 150 золотых."}},
[9724] = {"Предупреждение Кругу Кенария",nil, {"Поговорите с Амитиэль Странницей Туманов на Заставе Кенария на полуострове Адского Пламени."}},
[9725] = {"Доказательство лояльности",nil, {"Лорд Соланар Кровогнев из Луносвета просит уничтожить 3 мясных фургона и убить 15 инженеров Плети."}},
[9726] = {"Теперь, когда мы друзья...",nil, {"Убейте 12 надсмотрщиков за рабами из клана Кровавой Чешуи, 6 чародеек из клана Кровавой Чешуи и возвращайтесь к Гзхун'тту в Спореггар в Зангартопи."}},
[9727] = {"Раз уж мы по-прежнему друзья...",nil, {"Убейте 12 надсмотрщиков за рабами из клана Кровавой Чешуи, 6 чародеек из клана Кровавой Чешуи и возвращайтесь к Гзхун'тту в Спореггар в Зангартопи."}},
[9728] = {"Теплый прием",nil, {"Принесите стражу Хамуту из Кенарийского приюта 30 когтей наг."}},
[9729] = {"Фхвур крушить!",nil, {"Проводите Фхвура к паровому насосу на озере Болотных Огоньков, чтобы добыть ковчег Ссслита. Потом доставьте обратно Фхвура и ковчег в целости и сохранности, и вернитесь к Гзхун'тту в Спореггар в Зангартопи."}},
[9730] = {"Предводитель Темного Гребня",nil, {"Убейте раджу Хагазеда в Лагуне и вернитесь к стражу Хамуту из Кенарийской экспедиции в Зангартопи, чтобы получить награду."}},
[9731] = {"Схема стоков",nil, {"Найдите в Змеином озере признаки утечки воды. Вернитесь к Исиэль Песни Ветра в Кенарийском приюте и сообщите о своем открытии."}},
[9732] = {"Назад в болото",nil, {"Вернитесь к Исиэль Песни Ветра в Кенарийском приюте в Зангартопи."}},
[9733] = {"Предупреждение Кругу Кенария",nil, {"Поговорите с Махурамом Крепким Копытом на Полуострове Адского Пламени, в Траллмаре."}},
[9734] = {"Назад в болото",nil, {"Вернитесь к Исиэль Песни Ветра в Кенарийском приюте в Зангартопи."}},
[9735] = {"Истинные владыки Света",nil, {"Принесите фиал святой воды Длани Тира Лорду Соланару Кровогневу в Луносвет."}},
[9736] = {"Истинные владыки Света",nil, {"Принесите Лорду Соланару Кровогневу в Луносвет 1 чародейский катализатор, 1 меру сумеречного порошка, 1 азеротский алмаз и 1 безупречный черный алмаз."}},
[9737] = {"Истинные владыки Света",nil, {"Потушите неугасимое пламя часовни Алонсия с помощью огнетушительной смеси и уничтожьте всех, кто попробует защитить часовню."}},
[9738] = {"Пропавшие без вести",nil, {"Выясните, что стало с заклинателем земли Гневвсом, натуралистом Кусом, культиватором Зеленопалом и призывателем ветров Когтем. Затем возвращайтесь к дозорному Джанг в резервуар Кривого Клыка в Зангартопь."}},
[9739] = {"Проблемы спорлингов",nil, {"Соберите 10 мешочков зрелых спор по просьбе Фассна с Грибной поляны."}},
[9740] = {"Солнечные Ворота",nil, {"Воздаятель Эсом с Кровавой заставы хочет, чтобы солнечные врата были уничтожены."}},
[9741] = {"Твари из Бездны",nil, {"Убейте 12 тварей Бездны и сообщите об этом на Кровавую заставу воздаятелю Эсому."}},
[9742] = {"Новые мешочки со спорами",nil, {"Что, |3-6(дворф), тебе удалось спасти еще несколько мешочков со спорами?"}},
[9743] = {"Природные враги",nil, {"Принесите Фассну с Грибной поляны в Зангартопи 6 усиков болотника."}},
[9744] = {"Еще усиков!",nil, {"Тебе удалось перебить немало великанов, но немало их по-прежнему бродит по болотам. И они по-прежнему охотятся на наши мешочки со спорами!"}},
[9746] = {"Границы возможного",nil, {"Убейте 10 пиромантов и 10 защитников Солнечных Ястребов и сообщите об этом на Кровавую заставу воздаятелю Эсому."}},
[9747] = {"Племя Тенетопи",nil, {"Отправляйтесь в деревню Тенетопь и убейте Катару, 8 провидцев, 6 знахарей и 6 оракулов из племени Тенетопи по просьбе Икейена из Кенарийского приюта."}},
[9748] = {"Не пей эту воду!",nil, {"Воздаятель Эсом на Кровавой заставе попросил вас принести пробу воды с острова Кровавой Дымки во флаконе, который он предоставил."}},
[9749] = {"Они живы! Может быть...",nil, {"Освободите 5 ученых экспедиции по просьбе ученого Корнелиуса."}},
[9750] = {"UNUSED Urgent Delivery",nil, nil},
[9751] = {"Наследие Проклятой Крови",nil, {"Адмирал Одиссий просит разыскать капитана Эдварда Хейнса и поговорить с ним."}},
[9752] = {"Спасение с озера Тенетопь",nil, {"Проводите Кайру Длинную Гриву в Кенарийский приют в Зангартопи. Выполнив эту задачу, поговорите с Исиэль Песнью Ветра."}},
[9753] = {"То, что мы знаем...",nil, {"Поговорите с экзархом Адметиусом на Кровавой заставе."}},
[9756] = {"То, чего мы не знаем...",nil, {"Поговорите с пленным агентом Солнечных Ястребов, которого держат на Кровавой заставе. Передайте полученные сведения экзарху Адметиусу."}},
[9757] = {"Охотница Келла Тетива Ночи",nil, {"Поговорите с охотницей Келлой Тетивой Ночи рядом с причалом Валаара."}},
[9758] = {"Возвращение к чародею Вандрилу",nil, {"Возвращайтесь к чародею Вандрилу в Транкивиллион, что в Призрачных землях."}},
[9759] = {"Отречемся от старого мира",nil, {"Подрывник Легосо собирается разрушить энергоблок и убить Сиронас. Попытайтесь защитить его и, если задание будет успешно выполнено, расскажите об этом экзарху Адметиусу на Кровавой заставе."}},
[9760] = {"Привал Защитника",nil, {"Поговорите с воздаятельницей Корин в селении Привал Защитника на острове Кровавой Дымки;."}},
[9761] = {"Расчистка пути",nil, {"Убейте 8 саботажников и 8 посланников из клана Солнечных Ястребов и сообщите об этом воздаятельнице Корин в Привале Защитника."}},
[9762] = {"Неписаное пророчество",nil, nil},
[9763] = {"Убежище врага",nil, {"Дозорный Джанг хочет, чтобы вы нашли и убили полководца Калитреша в резервуаре Кривого Клыка."}},
[9764] = {"Приказы Леди Вайш",nil, {"Отнесите приказы леди Вайш Исиэль Песни Ветра в Кенарийский приют в Зангартопи."}},
[9765] = {"Готовится к войне",nil, {"Добудьте оружие клана Кривого Клыка у наг в резервуаре Кривого Клыка и принесите его Исиэль Песни Ветра в Кенарийский приют в Зангартопи."}},
[9766] = {"Оружие Кривого Клыка",nil, {"Мы выкуем из стали наг оружие, и обратим его против них! Принеси мне все оружие, которое сможешь унести из резервуара Кривого Клыка."}},
[9767] = {"Врага нужно знать в лицо",nil, {"Добудьте книгу заклинаний клана Кривого Клыка у наг из резервуара Кривого Клыка и принесите ее Ашьену в Кенарийский приют в Зангартопи."}},
[9769] = {"Мода непредсказуема!",nil, {"Принесите 8 прозрачных крыльев любых видов светлячков, обитающих в Зангартопи, Магаше с заставы Болотной Крысы."}},
[9770] = {"Опасные болотные клыки",nil, {"Убейте 10 болотных потрошителей по просьбе Реавижа с заставы Болотной Крысы."}},
[9771] = {"Поиски разведчика Джиобы",nil, {"Ищите разведчика Джиобу."}},
[9772] = {"Донесение разведчика Джиобы",nil, {"Добудьте донесение разведчика Джиобы у высохших великанов и вернитесь с ним к Зураю на заставу Болотной Крысы."}},
[9773] = {"Довольно грибов!",nil, {"Добудьте 8 филе угря с угрей из озера Тенетопь и принесите их Магаше на заставу Болотной Крысы."}},
[9774] = {"Толстая чешуя гидры",nil, {"Принесите Зураю на заставу Болотной Крысы 12 толстых чешуек гидры."}},
[9775] = {"Донесение темному охотнику Денжаю",nil, {"Отнесите донесение Зурая темному охотнику Денжаю в Забра'джин."}},
[9776] = {"Прибежище Оребор",nil, {"Поговорите с Икути в прибежище Оребор."}},
[9777] = {"Споры гриба-блескуна",nil, {"Принесите 6 спор гриба-блескуна Руаму в Телредоре."}},
[9778] = {"Страж Хамут",nil, {"Поговорите со стражем Хамутом в Кенарийском приюте в Зангартопи."}},
[9779] = {"Перехват сообщения",nil, {"Найдите послание Солнечных Ястребов. Возможно, для этого придется убить нескольких из них. Послание должно быть доставлено на Кровавую заставу воздаятелю Боросу."}},
[9780] = {"Филе угрей из озера Тенетопь",nil, {"Принесите Нораани из Телредора 8 филе угря."}},
[9781] = {"Слишком много ртов!",nil, {"Убейте 10 болотных гидр и вернитесь к Хаалруну в Телредор."}},
[9782] = {"Мертвая трясина",nil, {"Принесите пробу почвы Мертвой трясины воздаятелю Идаару в Телредоре."}},
[9783] = {"Неестественная засуха",nil, {"Убейте 12 иссохших великанов по просьбе воздаятеля Идаара из Телредора."}},
[9784] = {"Определение растений",nil, {"Новые части растений мне всегда пригодятся! Сейчас, когда уровень воды в озерах постоянно снижается, у меня возникает чувство, что на болотах растет число пришлых растений. Однако чтобы подтвердить эту теорию, мне нужны точные цифры!"}},
[9785] = {"Благословение Древ",nil, {"Призывательница ветров Черное Копыто просит вас поговорить с древами Ашьеном и Келетом в Кенарийском приюте и получить их благословение."}},
[9786] = {"Руины Боха'му",nil, {"Исследуйте руины Боха'му по просьбе анахорета Ахуурна из Телредора."}},
[9787] = {"Идолы Дикотопи",nil, {"Соберите 6 идолов Дикотопи и вернитесь с ними к анахорету Ахуурну в Телредор."}},
[9788] = {"Темное, сырое место",nil, {"Отыщите вещи Икейена в пещере к югу от Тенетопи. Верните их Икейену в Кенарийском приюте в Зангартопи."}},
[9789] = {"Охота на копытней",nil, {"Убейте 30 копытней и вернитесь к Хемингу Эрнестуэю в его охотничий лагерь в Награнде."}},
[9790] = {"Прозрачные крылья",nil, {"Принесите Хаалруну из Телредора 8 прозрачных крыльев любых видов светляков, обитающих в Зангартопи."}},
[9791] = {"Опасные болотные клыки",nil, {"Убейте 10 болотных потрошителей и вернитесь к Нораани в Телредор."}},
[9792] = {"Послание в Телаар",nil, {"Поговорите с Нахуудом в Телааре в Награнде."}},
[9793] = {"Судьба Туурема",nil, {"Отнесите письмо воздаятеля Идаара Андарлу в бастион Аллерии в лесу Тероккар."}},
[9794] = {"Не время для любопытства",nil, {"Доставьте письмо Тимоти Киалону в Сильванаар в Острогорье."}},
[9795] = {"Угроза со стороны огров",nil, {"Отправляйтесь в оплот Громоборцев в Острогорье и поговорите с Нехтаром."}},
[9796] = {"Новости из Зангартопи",nil, {"Поговорите с Мокасой в форте Камнеломов в лесу Тероккар."}},
[9797] = {"Подкрепление для Гарадара",nil, {"Поговорите с капитаном Кроганом в Гарадаре в Награнде."}},
[9798] = {"Планы эльфов крови",nil, {"Отнесите планы эльфов крови воздаятелю Алдару на место крушения в долине Аммен."}},
[9799] = {"Сбор трав",nil, {"Сорвите 3 оскверненных цветка и вернитесь к ученице Вишаэль на месте крушения в долине Аммен."}},
[9800] = {"Редкий деликатес",nil, {"Принесите повелителю стихий Ло'апу к Трону стихий в Награнде 15 семян караколлы."}},
[9801] = {"Сбор реагентов",nil, {"Принесите анахорету Ахууму в Телредоре 4 глаза спороската и 4 щупальца болотного долгонога."}},
[9802] = {"Растения Зангартопи",nil, {"Принесите 10 неопознанных частей растений Лоранне Тар'велл в Кенарийском приюте в Зангартопи."}},
[9803] = {"Посланец в Дикотопь",nil, {"Отправляйтесь в руины Боха'му и выпейте эликсир Ахуурна. Приняв обличье араккоа, поговорите со старейшиной Курути."}},
[9804] = {"Беспокойные духи озера Небесной Песни",nil, {"Уничтожьте 12 озерных духов по просьбе повелителя cтихий Ло'апа, обитающего у Трона стихий."}},
[9805] = {"Благословение Возжигателя",nil, {"Спалите самый большой дом и три лачуги на западной, восточной и южной окраинах деревни Трепещущего Тростника.nnКогда выполните поручение, верните живой огонь повелителю стихий Ло'апу."}},
[9806] = {"Прорастающие споры",nil, {"Соберите 6 прорастающих спор с различных спороскатов и болотобродов. Выполнив задачу, вернитесь к Гшаффу в Спореггар."}},
[9807] = {"Новые прорастающие споры",nil, nil},
[9808] = {"Грибы-огнешляпки",nil, {"Принесите 10 огнешляпок Мссхи'фну в Спореггар в Зангартопи."}},
[9809] = {"Нужно больше грибов!",nil, {"Если у тебя есть еще огнешляпки, я бы взял. Нашей деревне они всегда пригодятся."}},
[9810] = {"Оскверненный дух",nil, {"Уничтожьте загрязненную сущность Ватусана и 10 озерных волноплесков по просьбе повелителя cтихий Ло'апа."}},
[9811] = {"Друг син'дорай",nil, {"Принесите голову Дар'Хана Лор'темару Терону в Луносвет."}},
[9812] = {"Посол к Орде",nil, {"Отнесите письмо от Лор'темара Терона леди Сильване Ветрокрылой в Подгород, столицу Отрекшихся в Тирисфальских лесах."}},
[9813] = {"Встреча с вождем",nil, {"Отнесите письмо с печатью Леди Сильваны Ветрокрылой Траллу в Оргриммар, столицу Дуротара."}},
[9814] = {"Грибы-пороховики, слышь?",nil, {"Принесите знахарю Тор'геш 6 грибов-пороховиков."}},
[9815] = {"Грязные делишки",nil, {"Принесите повелителю стихий Ло'апу к Трону стихий в Награнде 5 ядер порождений грязи."}},
[9816] = {"Видали вы такое?",nil, {"Отнесите клетку с мурлоками в деревню Остротопь и оставьте ее на плоском камне."}},
[9817] = {"Глава клана Кровавой Чешуи",nil, {"Убейте раджис Фьяше в Змеином озере и вернитесь к стражу Хамуту в Кенарийский приют в Зангартопи, чтобы получить награду."}},
[9818] = {"Из глубин",nil, {"Поговорите с Гордаугом."}},
[9819] = {"Истерзанные духи земли",nil, {"Уничтожьте 15 истерзанных духов земли."}},
[9820] = {"РАЗЫСКИВАЕТСЯ: Главарь Грог'ак",nil, {"Принесите голову главаря Грог'ака темному охотнику Денжаю в Забра'джин."}},
[9821] = {"Попробовать зло на зуб",nil, {"Принесите Гордаугу к Трону стихий 15 средоточий разъяренных сокрушителей."}},
[9822] = {"Угроза нападения",nil, {"Принесите планы нападения клана Анго'рош темному охотнику Денжаю в Забра'джин."}},
[9823] = {"Мы и они",nil, {"Убейте властителя Кровавого Кулака, 10 извергов из клана Анго'рош и 10 пожирателей душ из клана Анго'рош и вернитесь к темному охотнику Денжаю в Забра'джин."}},
[9824] = {"Колебания тайной магии",nil, {"Активируйте кристалл ясновидения Аметистового Ока рядом с подземными источниками вод в Хозяйском погребе и вернитесь к верховному магу Альтуру, находящемуся у входа в Каражан."}},
[9825] = {"Неустанный труд",nil, {"Принесите 10 призрачных сущностей верховному магу Альтуру, находящемуся у входа в Каражан."}},
[9826] = {"Вести из Даларана",nil, {"Доставьте письмо Альтура верховному магу Седрику на окраину Даларана."}},
[9827] = {"Высохшая базидия",nil, {"Отнесите высохшую базидию Руаму в Телредор."}},
[9828] = {"Высохшая базидия",nil, {"Отнесите высохшую базидию Реавижу на заставу Болотной Крысы."}},
[9829] = {"Кадгар",nil, {"Доставьте письмо Альтура Кадгару в Шаттрат - город в лесу Тероккар."}},
[9830] = {"Яд паразита",nil, {"Принесите 6 порций яда болотного паразита Пулуу в прибежище Оребор."}},
[9831] = {"Вход в Каражан",nil, {"Пройдите в Темный Лабиринт в Аукиндоне, найдите там волшебный контейнер и возьмите из него первый фрагмент ключа, после чего вернитесь к Кадгару."}},
[9832] = {"Второй и третий фрагменты",nil, {"Добудьте второй фрагмент ключа из волшебного контейнера в резервуаре Кривого Клыка, а третий фрагмент ключа – из волшебного контейнера в Крепости Бурь, после чего вернитесь к Кадгару в Шаттрат."}},
[9833] = {"Линии связи",nil, {"Убейте 12 болотных терзателей по просьбе Пулуу из прибежища Оребор."}},
[9834] = {"Природная броня",nil, {"Принесите 8 шкур болотных твердозубов Макту в прибежище Оребор."}},
[9835] = {"Вторжение клана Анго'рош",nil, {"Убейте 10 огров из клана Анго'рош и 5 шаманов из клана Анго'рош и вернитесь к Икути в прибежище Оребор."}},
[9836] = {"Разрешение учителя",nil, {"Отправляйтесь в Пещеры Времени и уговорите Медива дать разрешение на использование Восстановленного ключа."}},
[9837] = {"Возвращение к Кадгару",nil, {"Вернитесь в Шаттрат к Кадгару и покажите ему хозяйский ключ."}},
[9838] = {"Аметистовое Око",nil, {"Поговорите с верховным магом Альтуром, который находится снаружи Каражана."}},
[9839] = {"Властитель Кровавый Кулак",nil, {"Убейте властителя Кровавого Кулака и 10 извергов из клана Анго'рош по просьбе Икути. Выполнив задачу, вернитесь в прибежище Оребор."}},
[9840] = {"Оценка ситуации",nil, {"Найдите Корена в Каражане."}},
[9841] = {"Поделом гнусным тварям!",nil, {"Убейте 8 болотных паразитов и вернитесь к Гамбаринке в Забра'джин."}},
[9842] = {"Острейшие резцы",nil, {"Принесите 10 зубов болотного терзателя Гамбаринке в Забра'джине."}},
[9843] = {"Записи Кинны",nil, {"Найдите Гостевые залы в Каражане, а в них - записи Кеанны, и доставьте их верховному магу Альтуру."}},
[9844] = {"Присутствие демонов",nil, {"Верховный маг Альтур просит вас уничтожить демона на вершине башни Каражана."}},
[9845] = {"Не распугивайте рыбу!",nil, {"Убейте 10 болотных твердозубов и вернитесь к Зурджайе в Забра'джин."}},
[9846] = {"Духи Дикотопи",nil, {"Принесите 10 тотемов-хранителей Дикотопи ясновидящей Джаниди в Забра'джин."}},
[9847] = {"Дух-союзник?",nil, {"Отнесите тотем Дикотопи к подножию лестницы руин Боха'му и установите его на землю. Расскажите о том, что вы видели, ясновидящей Джаниди в Забра'джине."}},
[9848] = {"Тайны Остротопи",nil, {"Принесите справочник по ядам Остротопи и фиал яда Остротопи Тимоти Даниэльсу в прибежище Оребор."}},
[9849] = {"Сорвать личину",nil, {"Убейте 30 прислужников Гурока."}},
[9850] = {"Охота на копытней",nil, {"Убейте 30 копытней-быков и вернитесь к Хемингу Эрнестуэю в его охотничий лагерь в Награнде."}},
[9851] = {"Охота на копытней",nil, {"Добудьте рог Бантар и отнесите его Хемингу Эрнестуэю в лагерь Эрнестуэя в Награнде."}},
[9852] = {"Опасный спорт",nil, {"Принесите сердце Клыкач Хемингу Эрнестуэю в его лагерь в Награнде."}},
[9853] = {"Гурок Узурпатор",nil, {"Убейте Гурока Узурпатора и принесите его голову Гордаугу к Трону стихий."}},
[9854] = {"Охота на ветрухов",nil, {"Убейте 30 ветрухов и вернитесь к Фитцу в охотничий лагерь Эрнестуэя в Награнде."}},
[9855] = {"Охота на ветрухов",nil, {"Добудьте 30 прожорливых ветрухов и вернитесь к Фитцу в лагерь Эрнестуэя в Награнде."}},
[9856] = {"Охота на ветрухов",nil, {"Принесите глаз Потрошительницы Фитцу в охотничий лагерь Эрнестуэя в Награнде."}},
[9857] = {"Охота на талбуков",nil, {"Убейте 30 талбуков-самцов и возвращайтесь к Гарольду Лейну в лагерь Эрнестуэя в Награнде."}},
[9858] = {"Охота на талбуков",nil, {"Убейте 30 талбуков-шипоедов и возвращайтесь к Гарольду Лейну в лагерь Эрнестуэя в Награнде."}},
[9859] = {"Охота на талбуков",nil, {"Добудьте копыто Хол'стяка и принесите его Гарольду Лейну в охотничий лагерь Эрнестуэя в Награнде."}},
[9860] = {"Новое направление",nil, {"Поговорите с верховным магом Седриком на окраине Даларана."}},
[9861] = {"Вой-Ветер",nil, {"Разыщите у Трона стихий кого-нибудь, кто знает язык калимаг, и покажите ему или ей Вой-Ветра."}},
[9862] = {"Разлагатели Темной Крови",nil, {"Убейте 8 разлагателей из племени Темной Крови по просьбе повелителя стихий Морха, находящегося у Трона стихий."}},
[9863] = {"Вражеские святыни",nil, {"Принесите 20 идолов племени Темной Крови предсказателю Куркушу в Гарадар."}},
[9864] = {"Пропавший отряд",nil, {"Отыщите пропавший гарадарский отряд."}},
[9865] = {"Да, были воины в наше время...",nil, {"Убейте 40 падальщиков из племени Темной Крови и 20 налетчиков из племени Темной Крови."}},
[9866] = {"Он будет странствовать по земле...",nil, {"Доставьте известие предсказателю Корхуку в Гарадар."}},
[9867] = {"Главари Темной Крови",nil, {"Принесите предсказателю Маргадешу в Гарадар голову Ортора Темной Крови."}},
[9868] = {"Тотем Кардаша",nil, {"Помогите пленному маг'хару бежать с заставы Солнечного Источника. Расскажите Гаррошу в Гарадаре обо всем, что вам удалось узнать."}},
[9869] = {"Трон стихий",nil, {"Поговорите с повелительницей стихий Шарвак у Трона стихий."}},
[9870] = {"Трон стихий",nil, {"Поговорите с повелительницей стихий Шарвак у Трона стихий."}},
[9871] = {"Захватчики из племени Темной Крови",nil, {"Передайте планы вторжения Темной Крови Арехрону."}},
