[9903] = {"Самый здоровенный",nil, {"Убейте Мрагеша и вернитесь к Зурджайе в Забра'джин."}},
[9904] = {"Охота на Жутеклешня",nil, {"Убейте Жутеклешня и вернитесь к Зурджайе в Забра'джин."}},
[9905] = {"Месть Макту",nil, {"Найдите и убейте Мрагеша, и вернитесь к Макту в прибежище Оребор."}},
[9906] = {"Клинок вместо слов",nil, {"Джорин Мертвый Глаз из Гарадара в Награнде просит вас убить 15 сокрушителей и 15 мистиков из клана Тяжелого Кулака."}},
