[9914] = {"Кость на вес золота",nil, {"Добудьте 3 пары бивней элекков и принесите их Шадреку в Небесный лагерь в Награнде."}},
[9915] = {"Кость на вес воздуха",nil, nil},
[9916] = {"Припасы клана Кровавой Глазницы",nil, {"Найдите 20 ящиков с припасами клана Кровавой Глазницы и принесите их Илкай'гану Мистику."}},
[9917] = {"Не верь глазам своим",nil, {"Добудьте планы клана Тяжелого Кулака для охотницы Бинтук из Телаара."}},
[9918] = {"Только не в мою смену!",nil, {"Поговорите с Глыбом. Когда получите необходимые сведения, возвращайтесь к охотнице Бинтук."}},
[9919] = {"Спореггар",nil, {"Отправляйтесь в Спореггар, к северу от Грибной поляны, и отнесите спасенные мешочки спор Мссхи'фну."}},
