[9934] = {"Послание в Гарадар",nil, {"Отнесите мирное подношение клана Пылающего Клинка Гаррошу в Гарадар."}},
[9935] = {"Разыскивается: Гизельда Колдунья",nil, {"Убейте Гизельда Колдунью и 15 любых служителей бастиона Вечной Скорби. Получите награду у стража-смотрителя Быккрока в Гарадаре."}},
[9936] = {"Разыскивается: Гизельда Колдунья",nil, {"Убейте Гизельда Колдунью и 15 любых служителей бастиона Вечной Скорби. Затем вернитесь к стражу-смотрителю Мой'прну Джиллу в Телаар."}},
[9937] = {"Разыскивается: Дарн Ненасытный",nil, {"Убейте Дарна Ненасытного. Вернитесь к стражу-смотрителю Быккроку и получите награду."}},
[9938] = {"Разыскивается: Дарн Ненасытный",nil, {"Убейте Дарна Ненасытного. Вернитесь к стражу-смотрителю Мой'прну Джиллу и получите награду."}},
[9939] = {"Разыскивается: Зорбо Советчик",nil, {"Убейте Зорбо Советчика, 10 шаманов и 10 разорителей из клана Боевого Молота. Получите награду у стража-смотрителя Быккрока."}},
[9940] = {"Разыскивается: Зорбо Советчик",nil, {"Убейте Зорбо Советчика, 10 шаманов и 10 разорителей из клана Боевого Молота. Получите награду у стража-смотрителя Мой'прна Джилла."}},
[9941] = {"Выслеживание преступников",nil, {"Продолжите поиски Дейрома в поселении араккоа к северу от сломанной повозки."}},
