[9947] = {"Обратно к Рокагу",nil, {"Вернитесь к Рокагу в Форт Камнеломов и расскажите ему о том, что вам удалось обнаружить."}},
[9948] = {"Спасательная экспедиция",nil, {"Освободите 15 пленных маг'харов."}},
[9949] = {"Птичьи Глаза",nil, {"Принесите Око Гнездовья Скит, Око Гнездовья Шиенор и Око Гнездовья Рескк Андарлу в Бастион Аллерии."}},
