[9964] = {"Помощь от врага",nil, {"Найдите Кокорека в Гнездовье Литик и поговорите с ним."}},
[9965] = {"Проявление веры",nil, {"Принесите голову Муг'гока Кокореку в Гнездовье Литик."}},
[9966] = {"Проявление веры",nil, {"Принесите голову Муг'гока Кокореку в Гнездовье Литик."}},
[9967] = {"Кольцо Крови: братья Блю",nil, {"Одержите победу над близнецами из племени Темной Крови, которые называют себя \"Братья Блю\". После битвы обратитесь к троллю-прислужнику Водину и получите свою награду."}},
[9968] = {"Странная энергия",nil, {"Соберите 4 образца пухоспинки и 4 образца агрессивной пухоспинки для заклинателя земли Тавгрена. Его можно найти возле перелеска Кенария в лесу Тероккар."}},
[9969] = {"Последние реагенты",nil, {"Принесите 5 сущностей прыгуаны-охотника и 30 ягод тероккарской черной рябины Кокореку в Гнездовье Литик."}},
[9970] = {"Кольцо Крови: Камнедар Выщербленный Повелитель",nil, {"Победите Камнедара Выщербленного Повелителя. После битвы обратитесь к троллю-прислужнику Водину и получите свою награду."}},
[9971] = {"Ответы в чащобе",nil, {"Осмотрите странный предмет рядом с трупом Сломленного и постарайтесь узнать, что случилось в перелеске Кенария. Затем вернитесь к заклинателю земли Тавгрену в лес Тероккар."}},
[9972] = {"Кольцо Крови: Скрагат",nil, {"Одержите победу над Скрагатом. После битвы обратитесь к троллю-прислужнику Водину и получите свою награду."}},
[9973] = {"Кольцо Крови: чемпион клана Боевого Молота",nil, {"Одержите победу над чемпионом клана Боевого Молота. После битвы обратитесь к троллю-прислужнику Водину и получите свою награду."}},
[9974] = {"Последние реагенты",nil, {"Принесите 5 сущностей прыгуаны-охотника и 30 ягод тероккарской черной рябины Кокореку в Гнездовье Литик."}},
[9975] = {"Изначальная магия",nil, {"Используйте талисман Кокорека возле тотемов, охраняющих завесу Шалас, после чего поговорите с геологом Бальморалом."}},
[9976] = {"Изначальная магия",nil, {"Используйте талисман Кокорека возле тотемов, охраняющих завесу Шалас, после чего поговорите с наблюдателем Нодаком."}},
[9977] = {"Кольцо Крови: последнее испытание",nil, {"Одержите победу над Могором, героем клана Боевого Молота. После битвы обратитесь к троллю-прислужнику Водину и получите свою награду."}},
[9978] = {"Любыми средствами",nil, {"Найдите Эмпура и убедите его рассказать вам все, что ему известно."}},
[9979] = {"Латрай Торговец Ветром",nil, {"Поговорите с торговцем ветром Латраем возле таверны \"На краю земли\" в Нижнем городе Шаттрата."}},
[9980] = {"Спасите Дейрома!",nil, {"Проберитесь в гнездовье Шалас, убейте Луангу и освободите Дейрома, после чего доложитесь Леди Дене Кеннеди в Бастионе Аллерии."}},
[9981] = {"Спасите Дугара!",nil, {"Проберитесь в завесу Шалас, убейте Луангу и освободите Дейрома, после чего доложитесь Рокагу в Форте Камнеломов."}},
[9982] = {"Его звали Алтруис...",nil, {"Если у вас есть желание сразиться с Пылающим Легионом, разыщите Алтруиса Страдальца в западном Награнде."}},
[9983] = {"Его звали Алтруис...",nil, {"Если у вас есть желание сразиться с Пылающим Легионом, разыщите Алтруиса Страдальца в западном Награнде."}},
[9984] = {"Хозяин Сокрытого Города",nil, {"Отнесите приказы Луанги Андарлу в бастион Аллерии, что в лесу Тероккар."}},
