[10082] = {"Покой предков",nil, {"Уничтожьте 15 потревоженных духов орков."}},
[10084] = {"Атака на Магеддон",nil, {"Убейте Владыку ночи Малфаса, находящегося на Кузнечной Базе: Магеддон. О выполнении задания доложите командиру армии Данату Троллебою из Оплота Чести."}},
[10085] = {"Навестить предков",nil, {"Мать Кашур хочет, чтобы вы посетили заставу Солнечного Источника, руины Веселого Черепа, Гарадар и руины Кровавой Глазницы."}},
[10086] = {"Я служу... Орде!",nil, {"Принесите 8 кусков металлолома и 8 кусков древесины Мегзегу Крутогруду в Траллмар."}},
[10087] = {"Сжечь! Во имя Орды!",nil, {"Используя горящий факел, подожгите восточную пушку Альянса и западную пушку Альянса. После выполнения задания возвращайтесь к Мегзегу Крутогруду в Траллмар."}},
[10088] = {"Весь рудник ходуном",nil, {"Убейте 12 ган'аргов-саперов и возвращайтесь к Штейгер Разлкразу."}},
[10089] = {"Лагеря Легиона",nil, {"Убейте 10 ган'аргов-прислужников, 5 легионеров лагеря Легиона и 5 сестер Скорби, затем обыщите лагерь Легиона: Ярость, чтобы узнать намерения Легиона."}},
[10090] = {"Планы Легиона",nil, {"Разыщите Лагерь Легиона: Планы Злобы и возвращайтесь к властителю Хану Изуверу в Погибель Сквернобота."}},
[10091] = {"Инструменты души",nil, {"Украдите 5 инструментов души и доставьте их повелительнице шпионов Мелисе Высокий Венец в Шаттрат на Террасу Света."}},
[10092] = {"Атака на Магеддон",nil, {"Убейте Владыку Ночи Малфаса, который находится на Кузнечной базе: Магеддон и возвращайтесь к властителю Хану Изуверу в Погибель Сквернобота."}},
[10093] = {"Храм Телхамат",nil, {"Поговорите с Амааном Мудрым в храме Телхамат, который находится на западе полуострова Адского Пламени."}},
[10094] = {"Кодекс Крови",nil, {"Прочитайте Кодекс Крови в Темном лабиринте в Аукиндоне."}},
[10095] = {"В самом сердце Лабиринта",nil, {"Уничтожьте Бормотуна и доложите о том, что произошло внутри Темного лабиринта, повелительнице шпионов Мелисе Высокий Венец, которую можно найти на террасе Света в Шаттрате."}},
[10096] = {"Спасение спорлоков",nil, {"Убейте 10 болотных черпальщиков и 10 болотных скрытней и вернитесь к Лоранне Тар'велл в Кенарийский приют в Зангартопи."}},
[10097] = {"Брат на брата",nil, {"Проникните в Сетеккские залы и убейте Темнопрядя Сита. Освободите из плена Лакку. После этого вернитесь к Исфару, который находится неподалеку от входа в Залы Сетека."}},
[10098] = {"Наследство Терокка",nil, {"Принесите Маску Терокка, Перо Терокка и Сагу о Терокке Исфару, который находится неподалеку от Сетеккских залов."}},
[10099] = {"Мозговой центр",nil, {"Найдите и убейте З'крала в рудниках под Оплотом Чести, и доложите об этом штейгеру Большие Десны."}},
[10100] = {"Мозговой центр",nil, {"Найдите и убейте Урга'зза в рудниках под Траллмаром, и доложите об этом штейгеру Разлкразу."}},
[10101] = {"Когда говорят духи",nil, {"Разыщите К'уре в Ошу'гуне."}},
[10102] = {"Раскрытая тайна",nil, {"Поговорите с А'далом в Шаттрате."}},
[10103] = {"Донесение Зураю",nil, {"Поговорите с Зураем на заставе Болотной Крысы в Зангартопи."}},
[10104] = {"Опасения за судьбу Туурема",nil, {"Поговорите с Андарлом в бастионе Аллерии в Тероккаре."}},
[10105] = {"Вести для Ракории",nil, {"Поговорите с Ракорией из форта Камнеломов в лесу Тероккар."}},
[10106] = {"Штурмовые укрепления",nil, {"Захватите обзорную площадку, ристалище и Изрезанный холм, и возвращайтесь к прапорщику Трейси Праудвелл в Оплот Чести на полуострове Адского Пламени."}},
[10107] = {"Дипломатическая миссия",nil, {"Джорин Мертвый Глаз из Гарадара в Награнде попросил вас поговорить с Лантрезором Клинком в руинах Пылающего Клинка."}},
[10108] = {"Дипломатическая миссия",nil, {"Мо'мор Разрушитель из Телаара в Награнде попросил вас поговорить с Лантрезором Клинком в руинах Пылающего Клинка."}},
[10109] = {"Я их достану!",nil, {"Принесите 3 порции пара элементалей воздуха Вазату в Южный Награнд."}},
[10110] = {"Штурмовые укрепления",nil, {"Захватите Обзорную площадку, Ристалище и Изрезанный холм, и возвращайтесь в Траллмар к Черноглазке Зову Войны."}},
[10111] = {"Принеси мне яйцо!",nil, {"С помощью Прыготрона-4000 запрыгните в гнездо на ближайшем дереве и украдите оттуда таинственное яйцо."}},
[10112] = {"Личное одолжение",nil, {"Найдите 5 украденных товаров Латрая и верните их хозяину. Латрая можно найти возле таверны \"На краю земли\" в Нижнем городе Шаттрата."}},
[10113] = {"Охотничий лагерь Эрнестуэя",nil, {"Поговорите с Хемингом Эрнестуэем в охотничьем лагере Эрнестуэя на северо-восточной границе Награнда."}},
[10114] = {"Охотничий лагерь Эрнестуэя",nil, {"Олорм Странник просит вас поговорить со Странником Шадо \"Фитцем\" в охотничьем лагере Эрнестуэя в Награнде."}},
[10115] = {"Искажение Остротопи",nil, {"Убейте 3 убийц Остротопи и 15 грязекопов Остротопи."}},
[10116] = {"Разыскивается: вождь Муммаки!",nil, {"Принесите тотем вождя Муммаки Икути в прибежище Оребор."}},
[10117] = {"Разыскивается: вождь Муммаки!",nil, {"Принесите тотем вождя Муммаки темному охотнику Денжаю."}},
[10118] = {"Разговор с Остротопью",nil, {"Убейте 3 убийц и 15 грязекопов из племени Остротопи, и вернитесь к темному охотнику Денжаю в Забра'джин."}},
[10119] = {"Через Темный портал",nil, {"Пройдите через Темный портал и найдите командора Дарона. Он ждет вашего прибытия на другой стороне, в Запределье."}},
[10120] = {"Прибытие в Запределье",nil, {"Доставьте донесение Ориона Влагге Легкоперой у Темного портала на полуострове Адского Пламени."}},
[10121] = {"Истребить Пылающий Легион",nil, {"Поговорите с сержантом Лободробом, который находится при караване к востоку от Траллмара."}},
[10122] = {"Вблизи цитадели",nil, {"Найдите следы рядового Имариона у самого северного лагеря орков Скверны, расположенного у Южного бастиона."}},
[10123] = {"Лощина Вспышки Скверны",nil, {"Убейте 4 бесов-поджигателей, 6 инферналов-завоевателей и 1 вестника Ужаса по просьбе сержанта Лободроба, который находится при караване к востоку от Траллмара."}},
[10124] = {"Передовая застава: Останки Сквернобота",nil, {"Доложитесь командиру передовой То'арху по прибытии к останкам Сквернобота."}},
[10125] = {"Задание: разрушить связь",nil, {"Уничтожьте Антенну Легиона: Злоба, Антенну Легиона: Ярость, Антенну Легиона: Ненависть, Антенну Легиона: Страх с помощью взрывчатки То'арха по приказу командира передовой То'арха в Погибели Сквернобота на Полуострове Адского Пламени."}},
[10126] = {"Приказы боевого командира Некрогга",nil, {"Проследуйте к югу вдоль западной части Южного бастиона и разыщите в одном из лагерей следы рядового Имариона."}},
[10127] = {"Задание: Ослабить врага",nil, {"Убейте 4 укротителей Скверны, 8 гневостражей и поработительницу Ши'азив по приказу командира передовой То'арха у останков Сквернобота на Полуострове Адского Пламени."}},
[10128] = {"Спасти рядового Имариона",nil, {"Следуйте дальше к югу вдоль Южного бастиона и постарайтесь найти, где боевой командир Некрогг держит в плену рядового Имариона."}},
[10129] = {"Задание: Врата Муркет и Шаадраз",nil, {"Поговорите с командиром звена Бракком, разбомбите Врата Муркет и Шаадраз, затем возвращайтесь к командиру передовой То'арху к останкам Сквернобота."}},
[10130] = {"Западный фланг",nil, {"Убейте 10 стражников, 10 берсерков и 5 послушников из клана Изувеченной Длани. О выполнении задания доложите лейтенанту Амади, которая будет ждать вас в западной сторожевой башне Оплота Чести."}},
[10131] = {"План побега",nil, {"Убейте гренадера клана Изувеченной Длани и принесите примитивную взрывчатку рядовому Имариону в форт Гор'газ."}},
[10132] = {"Колоссальная угроза",nil, {"Убейте 5 яростных колоссов и возвращайтесь к Тола'тиону на Кенарийскую заставу на полуострове Адского Пламени."}},
[10133] = {"Задание: убить гонца",nil, {"Найдите Арикс'малидаша, убейте его и заберите Послание Легиона по приказу командира передовой То'арха у останков Сквернобота."}},
[10134] = {"Ключ к разгадке алого кристалла",nil, {"Доставьте осколок алого кристалла Тола'тиону на Кенарийскую заставу на полуострове Адского Пламени."}},
[10135] = {"Задание: доставить послание",nil, {"Доставьте Послание Легиона Назгрелу в Траллмар на Полуострове Адского Пламени."}},
[10136] = {"Безжалостные планы",nil, {"Убейте Араззиуса Безжалостного и возвращайтесь к Назгрелу в Траллмар."}},
[10137] = {"Провокация",nil, {"Используйте примитивную взрывчатку, чтобы создать шум и выманить боевого командира Некрогга из его башни. Убейте его, заберите Ключ Некрогга и откройте клетку рядового Имариона. Об освобождении рядового Имариона доложите лейтенанту Амади."}},
[10138] = {"Кто отдает приказы?",nil, {"Убейте 20 рубак и 10 чернокнижников клана Изувеченной Длани и захватите распоряжения военного инструктора Зурока. После этого возвращайтесь к лейтенанту Амади в западную сторожевую башню Оплота Чести."}},
[10139] = {"Разжаловать командира",nil, {"Уничтожьте командира соединения Горакса и доложите о выполнении задания лейтенанту Амади, которая находится в западной башне Оплота Чести."}},
[10140] = {"Путешествие в Оплот Чести",nil, {"Долетите до Оплота Чести и доставьте донесение Дурона маршалу Исилдору."}},
[10141] = {"Возрожденный Легион",nil, {"Поговорите с сержантом Альтумой, которая находится рядом с восточным караваном, к востоку от Оплота Чести."}},
[10142] = {"Путь Страданий",nil, {"Убейте 4 бесов-поджигателей, 6 инферналов-завоевателей и 1 вестника ужаса по приказу сержанта Альтумы из восточного каравана, который находится на востоке от Оплота Чести на полуострове Адского Пламени."}},
[10143] = {"Лагерь экспедиции",nil, {"Доложитесь командиру передовой Кингстону в лагере экспедиции."}},
[10144] = {"Лишить Легион подкрепления!",nil, {"Уничтожьте врага вокруг порталов Грим и Каалез и соберите камни с рунами демонов.nnЗабросьте запалы Кингстона в порталы Грим и Каалез.nnПосле выполнения задания возвращайтесь к командиру передовой Кингстону вместе с оставшейся взрывчаткой."}},
[10145] = {"Mission: Sever the Tie UNUSED",nil, {"Уничтожьте 4 укротителя Скверны, 8 гневостражей и Поработителя Ялквиза по приказу командира передовой Кингстона из лагеря экспедиции на Полуострове Адского Пламени."}},
[10146] = {"Задание: Врата Муркет и Шаадраз",nil, {"Поговорите с командиром звена Даби'ри, разбомбите Врата Муркет и Врата Шаадраз, затем возвращайтесь к командиру передовой Кингстону в Лагерь экспедиции."}},
[10147] = {"Задание: убить гонца",nil, {"Отыщите Арикс'малидаша, убейте его и заберите Послание Легиона по приказу командира передовой Кингстона из лагеря экспедиции на Полуострове Адского Пламени."}},
[10148] = {"Задание: доставить послание",nil, {"Доставьте Послание Легиона командиру армии Данату Троллебою в Оплот Чести на Полуострове Адского Пламени."}},
[10149] = {"Задание: со щитом или на щите",nil, {"Убейте Араззиуса Безжалостного по приказу командира армии Даната Троллебоя из Оплота Чести на Полуострове Адского Пламени."}},
[10150] = {"Вблизи цитадели",nil, {"Постарайтесь найти следы разведчицы Махи в одном из лагерей, расположенных вдоль Северного бастиона."}},
[10151] = {"Приказы боевого командира Некрогга",nil, {"Отправляйтесь к югу вдоль западной стороны Северного бастиона и постарайтесь найти следы разведчицы Махи в одном из лагерей."}},
[10152] = {"Западный фланг",nil, {"Убейте 10 стражников, 10 берсерков и 5 послушников из клана Изувеченной Длани. О выполнении задания доложите каменному стражу Амбелану, который находится на заставе Раздробленного Хребта, рядом с Зет'Гором."}},
[10153] = {"Спасти разведчицу Маху",nil, {"Следуйте дальше к югу вдоль Южного бастиона и постарайтесь найти, где боевой командир Некрогг держит в плену разведчицу Маху."}},
[10154] = {"План побега",nil, {"Убейте гренадера клана Изувеченной Длани и принесите примитивную взрывчатку разведчице Махе в форт Гор'газ."}},
[10155] = {"Провокация",nil, {"Используйте примитивную взрывчатку, чтобы создать шум и выманить боевого командира Некрогга из его башни. Убейте его, заберите Ключ Некрогга и откройте клетку разведчицы Махи. Об освобождении разведчицы Махи доложите каменному стражу Амбелану на Заставу Разрушенного Хребта рядом с Зет'Гором."}},
[10156] = {"Кто отдает приказы?",nil, {"Убейте 20 рубак и 10 чернокнижников клана Изувеченной Длани и захватите распоряжения военного инструктора Зурока. После этого возвращайтесь к каменному стражу Амбелану на Заставу Хребтолома."}},
[10157] = {"Разжаловать командира",nil, {"Уничтожьте командира соединения Горакса и доложите о выполнении задания темному охотнику Ти'джину из Траллмара."}},
[10158] = {"Припасы Кровавой Глазницы",nil, {"Уничтожьте 6 складов припасов клана Кровавой Глазницы и доложите о выполнении задания каменному стражу Амбелану с Заставы Хребтолома."}},
[10159] = {"Очистить холм Колючего Клыка!",nil, {"Убейте 8 опустошителей Колючего Клыка и 8 ядоплюев Колючего Клыка. После выполнения задания возвращайтесь к Махураму Крепкому Копыту на Кенарийскую заставу на полуострове Адского Пламени."}},
[10160] = {"Врага нужно знать в лицо",nil, {"Поговорите с лейтенантом Амади в старой сторожевой башне на западе Оплота Чести."}},
[10161] = {"В случае чрезвычайной ситуации...",nil, {"Соберите 30 обломков дирижабля и принесите Скриду Многослову по прозвищу \"Крикун\" на место крушения дирижабля."}},
[10162] = {"Задание: Косогор Бездны",nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по приказу командира передовой То'арха у останков Сквернобота на полуострове Адского Пламени."}},
[10163] = {"Задание: Косогор Бездны",nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по приказу командира звена Грифонгара с Парящей заставы на полуострове Адского Пламени."}},
[10164] = {"Все будет в порядке",nil, {"Проникните в Аукенайские гробницы и уничтожьте экзарха Маладаара по просьбе Великого Отца Алдримуса, который находится снаружи Аукенайских гробниц Аукиндона."}},
[10165] = {"Жестокая вещь – конкуренция",nil, {"Убейте принца нексуса Шаффара по просьбе принца нексуса Харамада, находящегося снаружи Гробниц маны, и принесите ему обмотки Шаффара."}},
[10166] = {"Память о Белостволе",nil, {"Заройте подвеску Старого Белоствола у рунического камня в Выжженной роще по просьбе Ларианны Речной Ветер."}},
[10167] = {"Аукиндон...",nil, {"Отправляйтесь в Аукенайские гробницы в Костяных пустошах леса Тероккар, убейте экзарха Маладаара и освободите дух Д'оре."}},
[10168] = {"Что видит душа",nil, {"Найдите зеркало Души в Аукенайских гробницах и используйте его, чтобы выманить темного духа из древнего орка-предка в Награнде. Уничтожьте 15 темных духов, чтобы орочьи предки могли упокоиться с миром.nnКогда исполните поручение, возвращайтесь к Матери Кашур в Земли Предков."}},
[10169] = {"Проиграть с честью",nil, {"Вылейте сонную сыворотку в котел Харонема на террасе Света, после чего вернитесь к магистру Фальрису."}},
[10170] = {"Возвращение к Великой Матери",nil, {"Возвращайтесь в Гарадар и поговорите с великой матерью Гейей."}},
[10171] = {"Безутешный Вождь",nil, {"Поговорите с Гаррошем."}},
[10172] = {"Надежды нет",nil, {"Вернитесь к Великой Матери Гейе и передайте ей слова Гарроша."}},
[10173] = {"Посох верховного мага",nil, {"Отнимите посох верховного мага Варгота у Эккораш Инквизитора и принесите Равандвиру в Зону 52. Чтобы выманить Эккораш из укрытия, бросьте волшебный порошок на жаровню в центре руин."}},
[10174] = {"Проклятие Аметистовой башни",nil, {"Используйте посох верховного мага Варгота, чтобы связаться с верховным магом Варготом.nnЕсли вы утратили посох верховного мага Варгота, поговорите с Равандвиром в Зоне 52, чтобы получить замену."}},
[10175] = {"Тралл, сын Дуротана",nil, {"Отправляйтесь в Оргриммар и передайте Траллу сообщение от Гейи."}},
[10176] = {"Страж Ар'келос",nil, {"Войдите в Аметистовую башню, расположенную в центре деревни Кирин'Вар, и победите Ар'келоса, затем поговорите с верховным магом Варготом."}},
[10177] = {"В Аукиндоне неспокойно",nil, {"Доложите о своем прибытии боевому командиру Мафууну, который находится неподалеку от входа в Темный Лабиринт в Аукиндоне в Лесу Тероккар."}},
[10178] = {"Найти шпиона То'гуна!",nil, {"Найдите шпиона То'гуна в Темном лабиринте в Аукиндоне."}},
[10179] = {"Смотритель Кирин'Вара",nil, {"Поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10180] = {"Неудержимый",nil, {"Поговорите с Исфаром, которого можно найти неподалеку от входа в Сетеккские залы в Аукиндоне."}},
[10182] = {"Боевой маг Датрик",nil, {"Найдите апартаменты боевого мага Датрика в переулке Волшебников и верните меч в оружейную стойку. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10183] = {"В Зону 52",nil, {"Передайте записки пустолога ведущему ракетостроителю Фьюзеляжу, находящемуся в Зоне 52 в Пустоверти."}},
[10184] = {"Зловещие останки",nil, {"Смотритель Дайворт в деревне Кирин'Вар просит вас победить 20 покалеченных духов на площади Кирин'Вара."}},
[10185] = {"Участь хуже, чем смерть",nil, {"Убейте 10 убийц магов и 10 искателей маны и вернитесь к смотрителю Дайворту в деревню Кирин'Вар."}},
[10186] = {"Тебя наняли!",nil, {"Соберите 10 астраллитиевых матричных кристаллов и принесите их ведущему ракетостроителю Фьюзеляжу в Зону 52 в Пустоверти."}},
[10187] = {"Послание для верховного мага",nil, {"Поговорите с Верховным магом Варготом на вершине Сиреневой башни."}},
[10188] = {"Печать Краса",nil, {"Заберите печать Краса у заклинательницы Марианы, находящейся в манагорне Коруу, к северо-востоку от Аметистовой башни. Когда вы получите печать, используйте посох верховного мага Варгота, чтобы вызвать проекцию верховного мага Варгота и сообщить ему о том, как подвигается дело.nnЕсли вы утратили посох верховного мага Варгота, поговорите с Равандвиром, находящимся в Зоне 52, чтобы получить замену."}},
[10189] = {"Манагорн Б'наар",nil, {"Патрон шпионов Талодиен, находящийся в Зоне 52, хочет, чтобы вы направились в окрестности манагорна Б'наар и отобрали список персонала манагорна Б'наар у капитана Аратина.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10190] = {"Перезарядка батарей",nil, {"Используйте зарядное устройство для батарей применительно к достаточному количеству внепространственных охотников, чтобы довести зарядку батарей до уровня 10. Затем вернитесь к роботехнику Элли в руины Энкаата в Пустоверти."}},
[10191] = {"Робот модели V жив!",nil, {"Сопроводите \"Маккса А. Миллиона V\" через руины Энкаата и обратно, обеспечив ему безопасность. Потом поговорите с роботехником Элли за пределами руин Энкаата в Пустоверти."}},
[10192] = {"Компендиум Краса",nil, {"Заберите первую, вторую и третью главы компендиума Краса с главной площади деревни Кирин'Вар. Затем используйте посох верховного мага Варгота, чтобы связаться с верховным магом.nnЕсли вы утратили посох верховного мага Варгота, поговорите с Равандвиром, находящимся в Зоне 52, чтобы получить замену."}},
[10193] = {"Высокие цели",nil, {"Патрон шпионов Талодиен, находящийся в Зоне 52, просит вас отправиться в манагорн Б'наар и убить 2 мастеров пространственных искривлений, 6 инженеров пространственных искривлений и 8 геологов из клана Ярости Солнца.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10194] = {"Незаметный полет",nil, {"Поговорите с Веронией, находящейся за пределами Зоны 52, и летите к манагорну Коруу.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10195] = {"Наемник видит, наемник делает",nil, {"Принесите 30 перьев араккоа Роз'алете в Бастион Аллерии."}},
[10196] = {"Больше перьев араккоа",nil, {"Принесите 30 перьев араккоа Малуказу в Форт Камнеломов."}},
[10197] = {"Убедительная маскировка",nil, {"Добудьте комплект одеяний чародея из армии Ярости Солнца, одну пару перчаток ученого из армии Ярости Солнца и медальон охранника из армии Ярости Солнца и принесите все это Каледису Сияющему Рассвету в манагорн Коруу в Пустоверти.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10198] = {"Сбор информации",nil, {"Используя маскировку армии Ярости Солнца, проникните в манагорн Коруу и подслушайте разговор между командиром Кузней Солнца и чародеем Ардонисом. По завершении задачи доложите о результатах Каледису Сияющему Рассвету. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10199] = {"Дополнительная сила",nil, {"Принесите 5 шипов скатов Пустоты Бутсу, находящемуся в Зоне 52 в Пустоверти."}},
[10200] = {"Возвращение к Талодиену",nil, {"Вернитесь к патрону шпионов Талодиену в Зону 52 в Пустоверти."}},
[10201] = {"Момент истины",nil, {"Найдите Грека и попросите его смазать маслом свое оружие, после чего вернитесь к Ракории."}},
[10202] = {"Дезертир",nil, {"Отправляйтесь в манагорн Ара и поговорите с магистром Теледорном. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10203] = {"Захват бесценного оборудования",nil, {"Используйте ультра-разуплотняющий захват, чтобы доставить гиперротационный Рой-О-Мат, сервоприводную пневматическую землечерпалку, радужный световой анализатор грунта и большой фургон с взрывчаткой обратно в Зону 52. Затем сообщите старшему саперу Бджимбуму, находящемуся на восточном конце расселины, идущей через Зону 52 в Пустоверти."}},
[10204] = {"Кристаллы камня Крови",nil, {"Магистр Ларинна просит вас отправиться в манагорн Б'наар и отобрать осколок камня крови у магистра из клана Ярости Солнца. Используйте этот осколок поблизости от более крупного кристалла камня крови и возвращайтесь к Ларинне в Зону 52.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10205] = {"Астральный налетчик Несаад",nil, {"Убейте астрального налетчика Несаада, затем возвращайтесь к ловчему Пустоты Кай'джи, находящемуся в Зоне 52 в Пустоверти."}},
[10206] = {"В погоне за технологиями",nil, {"Соберите 10 астральных технологий и вернитесь с ними к Папаше Колесуну, находящемуся в Зоне 52 в Пустоверти."}},
[10207] = {"Forward Base: Reaver's Fall REUSE",nil, {"Доложитесь командиру передовой То'арху по прибытии к останкам Сквернобота."}},
[10208] = {"Лишить Легион подкрепления!",nil, {"Уничтожьте врагов вокруг портала Зилус и портала Круул, соберите камни с рунами демонов.nnИспользуйте запалы То'арха внутри порталов Зилус и Круул.nnВозвращайтесь к То'арху в Гибель Сквернобота на полуострове Адского Пламени вместе с оставшимися запалами."}},
[10209] = {"Трофей призывателя Кантина",nil, {"Победите призывателя Кантина, находящегося в манагорне Даро, на острове к северу от форта Ярости Солнца, и заберите у него камень Студенора. Когда заберете камень, используйте посох верховного мага Варгота, чтобы связаться с верховным магом.nnЕсли вы утратили посох верховного мага Варгота, поговорите с Равандвиром, находящимся в Зоне 52, чтобы получить замену."}},
[10210] = {"А'дал",nil, {"Поговорите с А'далом, предводителем Ша'тар, в центре террасы Света."}},
[10211] = {"Город Света",nil, {"Проследуйте за слугой Кадгара и выслушайте историю, которую он вам расскажет, после чего вернитесь к Кадгару."}},
[10212] = {"Герой маг'харов",nil, {"Вернитесь к великой матери Гейе в Гарадар в Награнде."}},
[10213] = {"Найти место крушения",nil, {"Отыщите уцелевших в катастрофе."}},
[10214] = {"Весь рудник ходуном",nil, {"Убейте 15 ган'аргов-саперов и возвращайтесь к штейгеру Разлкразу."}},
[10216] = {"Прежде всего - безопасность!",nil, {"Убейте 10 эфириалов-осквернителей гробниц, 5 эфириалов-колдунов, 5 ловчих нексуса и 5 эфириалов-чароплетов по просьбе работника Морфалиуса.nnПосле этого найдите эфириальский телепорт в зале, предшествующем палате Шаффара, и активируйте его."}},
[10218] = {"Someone Else's Hard Work Pays Off",nil, {"Проводите криоинженера Ша'хина в Гробницы маны, чтобы он мог собрать астральную субстанцию в коллекторах Шаффара.nnВ случае успеха явитесь к принцу нексуса Харамаду в лагере Консорциума."}},
[10219] = {"Выгул собаки",nil, nil},
[10220] = {"Заставь их слушать",nil, {"Убейте 12 непреклонных пехотинцев, 8 непреклонных рыцарей и 6 непреклонных колдунов в оружейной экспедиции к западу от заставы Хребтолома по просьбе Мордибы."}},
[10221] = {"Доктор Бум!",nil, {"Убейте Доктора Бума и возвращайтесь с хорошими новостями к старшему саперу Бджимбуму в Зону 52 в Пустоверти."}},
[10222] = {"Гарнизон Ярости Солнца",nil, {"Смотритель Дайворт из деревни Кирин'Вар просит вас убить 10 лучников из армии Ярости Солнца."}},
[10223] = {"Покончить с Даэллисом",nil, {"Смотритель Дайворт из деревни Кирин'Вар просит вас убить наставника Даэллиса Стрелка Рассвета."}},
[10224] = {"Эссенция для двигателей",nil, {"Соберите 7 единиц эссенции призрака магии и доставьте главному инженеру Трепу в Зону 52 в Пустоверти."}},
[10225] = {"Рапорт главному инженеру",nil, {"Передайте астраллитиевые матричные кристаллы главному инженеру Трепу, находящемуся в Зоне 52 в Пустоверти."}},
[10226] = {"Извлечение силы стихии",nil, {"Извлеките 5 единиц силы стихии для главного инженера Трепа, находящегося в Зоне 52 в Пустоверти."}},
[10227] = {"Я вижу мертвых дренеев",nil, {"Поговорите с Рамдором Безумным, который стоит на груде костей неподалеку от Аукенайских гробниц."}},
[10228] = {"Иезекииль",nil, {"Найдите Иезекииля из Шаттрата в городе Шаттрат и поговорите с ним."}},
[10229] = {"Секрет фолианта",nil, {"Отнесите таинственный фолиант Альтену Историку на Заставу Хребтолома."}},
[10230] = {"Боевой рог",nil, {"Заберите у лейтенанта-командора Талвоса Боевой рог Непреклонных и принесите его Альтену Историку на Заставу Хребтолома."}},
[10231] = {"Какая книга? Не вижу никакой книги",nil, {"Иезекииль говорит, что книга могла быть украдена. Найдите головорезов и попытайтесь узнать что-нибудь у них."}},
[10232] = {"Легион – на мусор!",nil, {"Убейте 5 мо'аргов-кузнецов рока и 15 ган'аргов-инженеров. Затем возвращайтесь к Папаше Колесуну в Зону 52 в Пустоверти."}},
[10233] = {"Пожар в форте Ярости Солнца",nil, {"Используя горящий факел, уничтожьте 4 баллисты и 4 шатра в форте Ярости Солнца, затем вернитесь к лейтенанту-чародею Моррану в деревню Кирин'Вар."}},
[10234] = {"Что одному демону мусор...",nil, {"Соберите 10 деталей сквернобота и доставьте их Мамаше Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10235] = {"Коготь Рока? Вырвать с мясом!",nil, {"Заберите руку Когтя Рока и доставьте ее Мамаше Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10236] = {"Гадость это ваше Запределье!",nil, {"Найдите 6 частей крошшера и верните их штейгеру Разлкразу на рудник, который находится к северо-западу от Траллмара."}},
[10237] = {"Предупредить Зону 52!",nil, {"Передайте плохие новости ведущему ракетостроителю Фьюзеляжу в Зоне 52 в Пустоверти."}},
[10238] = {"Каково работать на гоблинов",nil, {"Спасите Манни, Моха и Джакка из лагерей орков Скверны и доставьте их штейгеру Разлкразу на рудник, который находится к северо-западу от Траллмара."}},
[10239] = {"Потенциальный источник энергии",nil, {"Лейтенант-чародей Морран из деревни Кирин'Вар просит вас захватить 10 энергетических кубов-изоляторов, используемых эльфами крови в манагорне Коруу."}},
[10240] = {"Постройка периметра",nil, {"Используйте устройство пробуждения рун поблизости от восточной, северо-восточной и западной руны. Руны можно найти по периметру районов переулка Волшебников и главной площади деревни Кирин'Вар. По завершении задачи вернитесь к лейтенанту-чародею Моррану."}},
[10241] = {"Отвлекающий маневр в манагорне Б'наар",nil, {"Убейте 8 магистров и 8 кровостражей клана Ярости Солнца, затем возвращайтесь к экзарху Орелису в Зону 52.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10242] = {"Застава Хребтолома",nil, {"Доставьте образец крови орков из клана Кровавой Глазницы аптекарю Альбреку на заставу Хребтолома по просьбе аптекаря Зеланы из Погибели Сквернобота. Чтобы добраться до заставы на виверне, обратитесь к командиру звена Бракку."}},
[10243] = {"Технология наару",nil, {"Зайдите в манагорн Б'наар и найдите панель управления манагорном.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10244] = {"Учи матчасть!",nil, {"Отнесите инструкцию по созданию сквернобота Мамаше Колесун, находящейся на Заставе срединных земель в заповеднике \"Срединные земли\" в Пустоверти."}},
[10245] = {"Описание панели управления Б'наара",nil, {"Передайте описание панели управления Б'наара анахорету Карже в Зону 52 в Пустоверти.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10246] = {"Нападение на манагорн Коруу",nil, {"Отправляйтесь к манагорну Коруу и убейте 8 чародеев и 5 ученых из армии Ярости Солнца. По завершении задачи возвращайтесь к экзарху Орелису.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10247] = {"Вомиза, доктор псевдотехнических наук",nil, {"Поговорите с доктором псевдотехнических наук Вомизой на Испытательном полигоне в Пустоверти."}},
[10248] = {"Ты, робот",nil, {"Активируйте пульт управления \"Утилизатора Х6000\" и проверьте его возможности. Затем передайте результаты доктору псевдотехнических наук Вомице, находящемуся на испытательном полигоне в Пустоверти. Если возникнут проблемы, разберитесь с ними.."}},
[10249] = {"Обратно к командующему!",nil, {"Немедленно явитесь к ведущему ракетостроителю Фьюзеляжу в Зону 52 в Пустоверти!"}},
[10250] = {"Кровавая месть",nil, {"Подуйте в боевой рог Непреклонных рядом со знаменем Альянса. Убейте Уртрака и возвращайтесь к Альтену Историку на Заставу Хребтолома."}},
[10251] = {"Великий замысел господина?",nil, {"Найдите Нитрина Сведущего в Заброшенной оружейной в Награнде."}},
[10252] = {"Посмотри в глаза мертвым",nil, {"Принесите Нитрину из Заброшенной оружейной Награнда 1 глаз горного гронна, 1 безупречный клюв большого вертуха и 1 сальник старого копытня."}},
[10253] = {"Левиксус Призыватель Душ",nil, {"Убейте Левиксуса и заберите у него Книгу Мертвых.nnОтнесите Книгу Рамдору Безумному, который находится неподалеку от входа в Аукенайские гробницы в Аукиндоне.nnВы ведь с ним об этом договаривались, помните?"}},
[10254] = {"Командир армии Данат",nil, {"Доставьте по приказу маршала Исилдора донесение командиру армии Данату Троллебою в Оплот Чести."}},
[10255] = {"Проверка противоядия",nil, {"Введите кенарийское противоядие крупному зловепрю и пронаблюдайте за результатом. После проведения эксперимента возвращайтесь к Тие Рыжей Гриве на Кенарийскую заставу на полуострове Адского Пламени."}},
[10256] = {"Найти Хранителя Ключей",nil, {"Выследите Камнегора и заберите у него кристальное средоточие. Вернитесь к Сиреневой башне и используйте кристальное средоточие Камнегора поблизости от шара верховного мага Варгота для того, чтобы обнаружить, у кого хранятся ключи от проклятия башни."}},
[10257] = {"Захват краеугольного камня",nil, {"Проникните в Ботанику в Крепости Бурь и заберите ключ у командира Сараннис. Отнесите ключ верховному магу Варготу в Аметистовую башню."}},
[10258] = {"Честь Павших",nil, {"Явитесь к командиру Хогарду, который находится в оружейной экспедиции."}},
[10259] = {"Битва в Проломе",nil, {"Убейте 6 вторгшихся стражей Скверны и затем вернитесь к посланнику ордена Серебряного Рассвета Праудвелл."}},
[10260] = {"Пустолог Медноклеппер",nil, {"Поговорите с Пустологом Медноклеппером у моста, ведущего от Пустоверти к Острогорью."}},
[10261] = {"Разыскивается: аннигиляторный сервопривод!",nil, {"Заберите аннигиляторный сервопривод и доставьте его Папаше Колесуну в Зону 52 Пустоверти."}},
[10262] = {"Груда духов Астрала",nil, {"Соберите 10 знаков отличия братства Заксис и принесите их ловчему Пустоты Кай'джи, находящемуся в Зоне 52 в Пустоверти."}},
[10263] = {"Содействие Консорциуму",nil, {"Поговорите с ловчим Пустоты Кай'джи в Зоне 52 в Пустоверти."}},
[10264] = {"Содействие Консорциуму",nil, {"Поговорите с ловчим Пустоты Кай'джи в Зоне 52 в Пустоверти."}},
[10265] = {"Добыча артефакта для Консорциума",nil, {"Заполучите кристаллический артефакт Арклона и верните его ловчему Пустоты Кай'джи, находящемуся в Зоне 52 в Пустоверти."}},
[10266] = {"Запрос о содействии",nil, {"Найдите Гаруджа и предложите ему свои услуги. Он находится на заставе внутри заповедника \"Срединные земли\" в Пустоверти."}},
[10267] = {"Изъятие по праву",nil, {"Заберите 10 ящиков с геодезическим снаряжением и верните их Гаруджу, находящемуся на заставе внутри заповедника \"Срединные земли\" в Пустоверти."}},
[10268] = {"Аудиенция у принца",nil, {"Доставьте геодезическое снаряжение к проекции принца нексуса Харамада в Штормовую Вершину в Пустоверти."}},
[10269] = {"Триангуляция: точка первая",nil, {"Используйте триангуляционный прибор, чтобы найти путь к первой точке триангуляции. Как только найдете точку, сообщите ее местонахождение дельцу Хаззину, находящемуся на заставе Протектората на острове манагорна Ультрис в Пустоверти."}},
[10270] = {"Нескромное предложение",nil, {"Найдите аккумулятор для телепортатора и отнесите его к площадке астрального телепорта на Небесной гряде. Активируйте площадку и отдайте аккумулятор проекции торговца ветром Марида, чтобы он мог установить его."}},
[10271] = {"Деловая активность",nil, {"Соберите 8 единиц эссенции дракона Пустоты, затем возвращайтесь в зону площадки астрального телепорта. Эссенцию дракона Пустоты можно получить из старых или молодых драконов Пустоты."}},
[10272] = {"Многообещающее начало",nil, {"Соберите 15 яиц драконидов Пустоты, затем возвращайтесь к Тири и Джораду на Небесную гряду."}},
[10273] = {"Пустые хлопоты",nil, {"Поговорите с торговцем Ветром Маридом и выманите его из торгового поста. Покончите с ним, затем вернитесь к Тири и Джораду на Небесную гряду."}},
[10274] = {"Безопасность Небесной гряды",nil, {"Используйте вызов синих драконов, чтобы вызвать на бой Вераку, живущего на южном склоне Небесной гряды. Победите его и возвращайтесь к Тири и Джораду, ожидающим на Небесной гряде."}},
[10275] = {"Триангуляция: точка вторая",nil, {"Используйте триангуляционный прибор, чтобы найти путь ко второй точке триангуляции. Как только найдете точку, сообщите ее местонахождение торговцу ветром Тулуману, находящемуся в лагере Тулумана, по другую сторону моста, ведущего от острова манагорна Ара в Пустоверти."}},
[10276] = {"Полный треугольник",nil, {"Заберите кристалл Ата'мала и передайте его проекции принца нексуса Харамада, находящейся в Штормовой Вершине в Пустоверти."}},
[10277] = {"Пещеры Времени",nil, {"Андорму попросил вас пройти по пещерам вместе с хранительницей времени."}},
[10278] = {"Астральные провалы",nil, {"Используйте нестабильный генератор астральных провалов на Искривленных полях. Соберите три горсти пыли Пустоты с нестабильных демонов Бездны и возвращайтесь к Огафу Безумному на заставу Хребтолома."}},
[10279] = {"Логово господина",nil, {"Поговорите с Андорму в пещерах Времени."}},
[10280] = {"Специальный груз в город Шаттрат",nil, {"Передайте кристалл Ата'мала А'далу на террасе Света в городе Шаттрат."}},
[10281] = {"Формальное представление",nil, {"Найдите Тири на северной оконечности Небесной гряды и поговорите с ней."}},
[10282] = {"Старый Хилсбрад",nil, {"Отправьтесь по тропе времени в старый Хилсбрад и поговорите с Эрозионом."}},
[10283] = {"План Тареты",nil, {"Проберитесь в крепость Дарнхольд и заложите пять зажигательных бомб возле бочек внутри каждого из бараков на выселках. Используйте упаковку зажигательных бомб, полученных от Эрозиона.nnПосле того как подожжете бараки, разыщите Тралла в подземной темнице."}},
[10284] = {"Побег из Дарнхольда",nil, {"Когда вы будете готовы идти, сообщите об этом Траллу. Помогите ему бежать из крепости Дарнхольд, освободить Тарету и исполнить свое предназначение.nnПосле этого возвращайтесь к Эрозиону в Старый Хилсбрад."}},
[10285] = {"Возвращение к Андорму",nil, {"Возвращайтесь к маленькому Андорму в Пещеры Времени."}},
[10286] = {"Тайна Арелиона",nil, {"Расспросите магистра Аледиса, которого в последний раз видели отправляющимся из Соколиного дозора в Зангартопь, по требованию магистра Каринды."}},
[10287] = {"Кем оказалась любовница",nil, {"Возвращайтесь к магистру Каринде в Соколиный дозор."}},
[10288] = {"Прибытие в Запределье",nil, {"Доставьте донесение Дарона Амишу Громовому Молоту по приказу командира Дарона у Темного портала на полуострове Адского Пламени."}},
[10289] = {"Путешествие в Траллмар",nil, {"Долетите на ветрокрыле Влагги Легкоперой в Траллмар. Доставьте донесение Ориона генералу Кракорку."}},
[10290] = {"В поисках фаралита",nil, {"Соберите 4 куска сырого фаралита и вернитесь к Зубену Элгенуби в Штормовую Вершину."}},
[10291] = {"Донесение Назгрелу",nil, {"Поговорите с Назгрелом по приказу генерала Кракорка из Траллмара."}},
[10292] = {"Больше мощи!",nil, {"Зубен Элгенуби хочет, чтобы вы захватили 8 пушек Легиона и принесли их в Штормовую Вершину."}},
[10293] = {"Попасть на главную жилу",nil, {"Зубен Элгенуби, находящийся в Штормовой Вершине, хочет, чтобы вы добыли фаралитовую сердцевину."}},
[10294] = {"Пустынный обрыв",nil, {"Отправляйтесь на Пустынный обрыв и убейте существ, которые там обитают. Соберите 40 осколков душ Пустынного обрыва и возвращайтесь к Огафу Безумному на Заставу Хребтолома."}},
[10295] = {"Сила бездны",nil, {"Убейте Барона Бездны Галаксиса и добудьте осколок его души. Принесите осколок души Огафу Безумному на Заставу Хребтолома."}},
[10296] = {"Черные топи",nil, {"Пройдите сквозь Пещеры времени до Черных топей времен открытия Темного портала и поговорите с Са'атом."}},
[10297] = {"Открытие Темного портала",nil, {"Защищайте Медива, пока он не откроет Темный портал.nnКогда выполните задание, возвращайтесь к Са'ату в Черные топи."}},
[10298] = {"Герой драконьего племени",nil, {"Возвращайтесь к маленькому Андорму в пустыню Танарис в Пещерах Времени."}},
[10299] = {"Отключить манагорн Б'наар",nil, {"Вернитесь в манагорн Б'наар и заберите кристалл доступа у надзирателя Тередиса. Используйте его на панели управления манагорна, чтобы выключить его, затем сообщите об этом анахорету Карже. nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10300] = {"Восстановление посоха",nil, {"Заберите 4 осколка безупречного кристалла у призраков в руинах Энкаата и принесите Равандвиру в Зону 52."}},
[10301] = {"Открыть Компендиум",nil, {"Заберите халцедоновое око у разрушительницы чар Маратель, находящейся в форте Ярости Солнца. Когда достигнете цели, используйте посох верховного мага Варгота, чтобы связаться с верховным магом. nnЕсли вы утратили посох верховного мага Варгота, поговорите с Равандвиром, находящимся в Зоне 52, чтобы получить замену."}},
[10302] = {"Неустойчивые мутации",nil, {"Убейте 8 неустойчивых мутаций и вернитесь к ботанику Таэрикс на месте крушения в долине Аммен."}},
[10303] = {"Эльфы крови",nil, {"Убейте 10 эльфов крови – разведчиков и вернитесь к Толаану на хребет Теней в долине Аммен."}},
[10304] = {"Воздаятель Алдар",nil, {"Поговорите с воздаятелем Алдаром на месте крушения в долине Аммен."}},
[10305] = {"Клятвопреступница Белмара",nil, {"Найдите апартаменты клятвопреступницы Белмары в переулке Волшебников и верните книгу на полку. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10306] = {"Кудесник Люминрат",nil, {"Найдите апартаменты кудесника Люминрата в переулке Волшебников и верните плащ в его гардероб. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10307] = {"Кольен Повелитель Льда",nil, {"Найдите апартаменты Кольена Повелителя Льда в переулке Волшебников и верните шляпу на вешалку. Затем поговорите со смотрителем Дайвортом в деревне Кирин'Вар."}},
[10308] = {"Еще одна группа духов Астрала",nil, nil},
[10309] = {"Это сквернобот, но у него есть сердце!",nil, {"Заберите сердце сквернобота и отнесите Н. Д. Лагерфелту на Груду в Пустоверти."}},
[10310] = {"Саботаж в портале",nil, {"Сопроводите Дризья до точки вторжения: Разрушитель и проследите за безопасностью во время попыток саботажа. Затем поговорите с Гаруджем на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10311] = {"Дризья нужна твоя помощь",nil, {"Поговорите с Дризья, находящимся около Места Вторжения \"Разрушитель\" в Пустоверти."}},
[10312] = {"Анналы Кирин'Вара",nil, {"Заберите анналы Кирин'Вара из ратуши деревни Кирин'Вар и отнесите ее смотрителю Дайворту."}},
[10313] = {"Измерение энергии Искажения",nil, {"Экзарх Орелис, находящийся в Зоне 52, просит вас отнести настроенный на энергию искривления шар к манагорну Б'наар и использовать шар у окончания четырех трубопроводов. nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10314] = {"Длительные подозрения",nil, {"Соберите 10 омерзительных останков искаженных духов во дворе часовни деревни Кирин'Вар и отнесите их смотрителю Дайворту."}},
[10315] = {"Нейтрализовать чародеев Пустоты",nil, {"Убейте 15 пустомантов клана Ярости Солнца и возвращайтесь к ловчему Пустоты Оазулу в Лагерь Тулумана в Пустоверти."}},
[10316] = {"В поисках доказательств",nil, {"Поищите во дворе часовни в деревне Кирин'Вар свидетельства того, кто именно манипулирует духами убитых."}},
[10317] = {"Покончить со штейгером",nil, {"Покончите со штейгером Закатом Солнца, находящимся в руднике Треллеума под манагорном Ара в Пустоверти."}},
[10318] = {"Покончить с Подчинителем",nil, {"Убейте Подчинителя Гриндгарра. Затем вернитесь к торговцу Ветром Тулуману в Лагерь Тулумана в Пустоверти."}},
[10319] = {"Захватить талисман",nil, {"Заберите филактерию Набериуса из района часовни деревни Кирин'Вар и принесите его смотрителю Дайворту."}},
[10320] = {"Уничтожить Набериуса!",nil, {"Смотритель Дайворт в деревне Кирин'Вар просит вас сразиться с Набериусом, находящимся во дворе часовни, и уничтожить его."}},
[10321] = {"Отключить манагорн Коруу",nil, {"Отправляйтесь в манагорн Коруу к востоку от Зоны 52 и заберите кристалл доступа Коруу у надзирательницы Сейланны. Используйте его на панели управления манагорна Коруу, чтобы отключить манагорн, затем вернитесь к анахорету Карже.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10322] = {"Отключить манагорн Даро",nil, {"Анахорет Каржа просит вас отправиться в манагорн Даро и забрать кристалл доступа у надзирателя Атанеля. Используйте этот кристалл на панели управления манагорном Даро, чтобы отключить его.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10323] = {"Отключить манагорн Ара",nil, {"Отправляйтесь в манагорн Ара и заберите кристалл доступа у надзирателя Азарада. Используйте его на панели доступа манагорна Ара, чтобы отключить его.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10324] = {"Великая охота на лунных оленей",nil, {"Принесите 6 шкур самцов лунного оленя Актеону с Лазурной заставы."}},
[10325] = {"Знаки Кил'джедена",nil, {"Принесите 10 знаков Кил'джедена Адуину Стражу Света в Шаттрат.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10326] = {"Больше знаков Кил'джедена",nil, {"Вы принесли еще знаков различия наших врагов, |3-6(Охотник)? Пылающий Легион все еще угрожает нам, и мы преследуем его повсеместно."}},
[10327] = {"Знак Кил'джедена",nil, nil},
[10328] = {"Инструкции Ярости Солнца",nil, {"Отправляйтесь в манагорн Даро и заберите военные инструкции Ярости Солнца и чародейские инструкции Ярости Солнца. По завершении задачи возвращайтесь к экзарху Орелису.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10329] = {"Отключить манагорн Б'наар",nil, {"Вернитесь в манагорн Б'наар и заберите кристалл доступа у надзирателя Тередиса. Используйте его на панели управления манагорна Б'наар, чтобы выключить его, затем сообщите об этом патрону шпионов Талодиену. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10330] = {"Отключить манагорн Коруу",nil, {"Заберите кристалл доступа Коруу у надзирателя Сейланны. Используйте его на панели управления Коруу, чтобы отключить манагорн, затем вернитесь к Каледису Сияющему Рассвету.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10331] = {"Необходимые инструменты",nil, {"Заберите кузнечный молот у кирин'варских учеников и отнесите обратно ученику Андретану в деревню Кирин'Вар."}},
[10332] = {"Мастер-кузнец Ронсус",nil, {"Ученик Андретан из деревни Кирин'Вар просит вас убить призрак Ронсуса, мастера-кузнеца поселения."}},
[10333] = {"Помощь Мамаше Колесун",nil, {"Поговорите с Мамашей Колесун, находящейся на заставе в заповеднике \"Срединные земли\" в Пустоверти."}},
[10334] = {"Колокольчик для коровы",nil, {"Найдите Бесси в полях близ Часовенного холма деревни Кирин'Вар."}},
[10335] = {"Исследование руин",nil, {"Разместите три геодезических знака, затем возвращайтесь к Зефириону в Штормовую Вершину в Пустоверти."}},
[10336] = {"Приспешники Кулутаса",nil, {"Убейте 10 псов Кулутаса и 5 злобоглазов Кулутаса. Затем вернитесь к ловчему Пустоты Наутису, находящемуся в Штормовой Вершине в Пустоверти."}},
[10337] = {"Когда коровы возвращаются домой",nil, {"Благополучно сопроводите Бесси к ее владельцу, Таделлу."}},
[10338] = {"Отключить манагорн Даро",nil, {"Вернитесь в манагорн Даро и заберите кристалл доступа у надзирателя Атанеля. Используйте его на панели управления манагорна Даро, чтобы отключить его, затем сообщите об этом патрону шпионов Талодиену. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10339] = {"Братство Эфириум",nil, {"Командир Амир просит вас уничтожить 5 убийц из Эфириума, 5 штормовиков из Эфириума, 2 ученых Эфириума и капитана Зовакса из Эфириума, затем включить передатчик Эфириума Зета."}},
[10340] = {"Парящая застава",nil, {"По приказу командира передовой Кингстона поговорите с командиром звена Даби'ри, доберитесь на грифоне до Парящей заставы и поговорите с Рунтогом Громовым Молотом."}},
[10341] = {"Лежачего не бьют – лежачего пинают",nil, {"Убейте 8 кудесников, 6 лучников и 4 центурионов клана Ярости Солнца. После завершения задачи вернитесь к магистру Ларинне, находящейся в Зоне 52.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10342] = {"Добыча кожи глиношкурых",nil, {"Соберите 5 кусков кожи глиношкурых и отдайте Бутсу в Зоне 52 в Пустоверти."}},
[10343] = {"Бесконечное вторжение",nil, {"Обыщите кишащий мана-тварями район деревни Кирин'Вар в поисках фрагмента мана-бомбы и принесите его лейтенант-колдуну Моррану."}},
[10344] = {"Командир звена Грифонгар",nil, {"Поговорите с командиром звена Грифонгаром на Парящей заставе."}},
[10345] = {"Усеял мертвыми костями...",nil, {"Примените поджигатель Протектората к 12 высохшим трупам. Когда задача будет завершена, верните поджигатель Протектората посланнику Араксису, находящемуся у входа в шахту Зеон."}},
[10346] = {"Возвращение на Косогор Бездны",nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по просьбе грифоньера Ветрорева с Парящей заставы на полуострове Адского Пламени."}},
[10347] = {"Возвращение на Косогор Бездны",nil, {"Уничтожьте 20 ган'аргов-батраков, 5 мо'аргов-надзирателей и 5 пушек Скверны по просьбе командира звена Бракка из Гибели Сквернобота на полуострове Адского Пламени."}},
[10348] = {"Новые возможности",nil, {"Соберите 15 \"кремовых колокольчиков\" и вернитесь к Мердаду на Заставу срединных земель."}},
[10349] = {"Землепряд",nil, {"Отнесите осколок алого хрусталя заклинательнице земли Галандрии Ночной Бриз на Кенарийскую заставу на полуострове Адского Пламени."}},
[10350] = {"Бехомат",nil, {"Поговорите с Бехоматом в Торговом ряду Экзодара."}},
[10351] = {"Целительная сила природы",nil, {"Посадите Семя Возрождения в Круге землепряда, чтобы исцелить землю вокруг кристалла. После этого возвращайтесь к землепряду Галандрии Ночной Бриз на Кенарийскую заставу и расскажите о том, что получилось."}},
[10352] = {"Пожертвование: шерсть",nil, {"Мы сейчас собираем пожертвования в виде шерстяной ткани. За щедрый дар вы получите признательность Дарнасса. Наши запасы таковы, что нам нужно от вас всего лишь шестьдесят отрезов. Мы могли бы купить их и на стороне, чтобы продолжить работу.nnНо если вы имеете шестьдесят отрезов шерстяной ткани и готовы пожертвовать их в дар, я с готовностью приму их у вас прямо сейчас."}},
[10353] = {"Арконус Ненасытный",nil, {"Посланник Иасикс, находящийся у входа в шахту Зеон, просит вас убить Арконуса Ненасытного и вернуться к командиру Амиру на заставу стражи Протектората в Пустоверти."}},
[10354] = {"Пожертвование: шелк",nil, {"Наши запасы тканей подходят к концу, и особенно нам не хватает шелка. Остается надеяться только на щедрость горожан и заезжих героев.nnЕсли ты можешь пожертвовать шестьдесят отрезов шелка, я с благодарностью приму твой дар прямо сейчас. И помни: добрые дела приносят добрую славу!"}},
[10355] = {"Иссохшее тело",nil, {"Соберите 5 образцов опаленной гидры и 5 образцов ткани высохшего болотника и принесите их Руаму в Телредор."}},
[10356] = {"Пожертвование: магическая ткань",nil, {"Мы занимаемся сбором магической ткани. Если ты пожертвуешь нам на благие цели 60 отрезов, то заслужишь признательность всего народа.nnЕсли у тебя есть 60 отрезов, можешь сдать их нам прямо сейчас."}},
[10357] = {"Пожертвование: руническая ткань",nil, {"Мы занимаемся сбором рунической ткани. Если ты пожертвуешь на благие цели 60 отрезов, то заслужишь признательность всего нашего народа.nnЕсли у тебя есть 60 отрезов, можешь пожертвовать их прямо сейчас."}},
[10358] = {"Больше рунической ткани",nil, {"И снова здравствуй, Эльфвоинша. Если ты готова выделить нам еще двадцать отрезов рунической ткани, то, будь уверена я постараюсь, чтобы все узнали о твоей щедрости. Если у тебя есть 20 отрезов рунической ткани, я охотно ее приму."}},
[10359] = {"Пожертвование: шерсть",nil, {"Принеси 60 отрезов шерстяной ткани. Жители Луносвета будут тебе очень благодарны!nnЕсли необходимое количество ткани у тебя при себе, я с удовольствием приму дар сейчас."}},
[10360] = {"Пожертвование: шелк",nil, {"Принеси 60 отрезов шелка. Жители Луносвета будут тебе очень благодарны!nnЕсли необходимое количество ткани у тебя при себе, я с удовольствием приму дар сейчас."}},
[10361] = {"Пожертвование: магическая ткань",nil, {"Принеси 60 отрезов магической ткани. Жители Луносвета будут тебе очень благодарны!nnЕсли необходимое количество ткани у тебя при себе, я с удовольствием приму дар сейчас."}},
[10362] = {"Пожертвование: руническая ткань",nil, {"Принеси 60 отрезов рунической ткани. Жители Луносвета будут тебе очень благодарны!nnЕсли необходимое количество ткани у тебя при себе, я с удовольствием приму дар сейчас."}},
[10363] = {"Больше рунической ткани",nil, {"Рунической ткани много не бывает. Принеси еще 20 отрезов – и получишь бесконечную признательность жителей Луносвета!nnПросто отдай ее мне, когда найдешь нужное количество."}},
[10364] = {"Кедмос",nil, {"Поговорите с Кедмосом в Чертоге Света Экзодара."}},
[10365] = {"Отключить манагорн Ара",nil, {"Отправляйтесь в манагорн Ара, заберите кристалл доступа Ара у надзирателя Азарадда. Используйте его на панели управления манагорном Ара, чтобы отключить его. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10366] = {"Джоль",nil, {"Поговорите с Джоль в Святилище поборников в Экзодаре."}},
[10367] = {"Предатель среди нас",nil, {"Добудьте ключ Ша'наара, спрятанный в лачуге в руинах Ша'наара неподалеку от Наладу."}},
[10368] = {"Старейшины Отребья",nil, {"Освободите Морода Ветродува, Акору Призывателя Огня и Айлаана Пробуждающего Водув руинах Ша'наара. После выполнения задания возвращайтесь к Наладу."}},
[10369] = {"Наследие Арзета",nil, {"Используйте посох старейшин племени Отребья против Арзета Безжалостного. После того как Арзет потеряет силу, убейте его. После выполнения задания возвращайтесь к Наладу в руины Ша'наара."}},
[10370] = {"Приказ Назгрела <TXT>",nil, nil},
[10371] = {"Йорус Ячменовар",nil, {"Поговорите с Йорусом Ячменоваром."}},
[10372] = {"Деликатная просьба",nil, {"Поговорите с Эраланной в Транквиллионе."}},
[10373] = {"Призыв к оружию: Чумные земли!",nil, {"Найдите командира Ашлама Отважного Кулака в лагере Промозглого Ветра на севере Альтеракских гор."}},
[10374] = {"Призыв к оружию: Чумные земли!",nil, {"Отыщите верховного палача Деррингтона. Его базовый лагерь находится у Бастиона, к востоку от Тирисфальских лугов и Подгорода."}},
[10375] = {"Обсидиановое боевое ожерелье",nil, nil},
[10377] = {"Ничего не бойся",nil, nil},
[10379] = {"Прикосновение слабости",nil, nil},
[10380] = {"Темный пакт",nil, {"Кайлаан, находящийся в Лагере Тулумана в Пустоверти хочет, чтобы вы убили 6 ган'аргов-астральных ремонтников, 3 Дочерей Судьбы и 3 мо'аргов-мастеров искажения.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10381] = {"Нет больше Алдора",nil, {"Вернитесь с сообщением к Экзарху Орелису в Зону 52.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10382] = {"Отправка на фронт",nil, {"Долетите до Пика Чести на грифоне и поговорите с фельдмаршалом Броком."}},
[10383] = {"Хлебные крошки!",nil, {"Поговорите с Джонатаном Лекрафтом насчет кроликов."}},
[10384] = {"Данные Братства Эфириум",nil, {"Проекция командира Амира на полигоне Эфириума – передатчик Эфириума Зета – просит вас забрать данные из информационной ячейки Эфириума."}},
[10385] = {"Риск поражения мозга – высокий",nil, {"Проекция командира Амира на полигоне Эфириума – передатчик Эфириума Зета – просит вас забрать 15 передач данных из ретранслятора Эфириума."}},
[10386] = {"Гроза скверноботов",nil, {"Поговорите с властителем Ханом Изуверским Кулаком."}},
[10387] = {"Гроза скверноботов",nil, {"Поговорите с властителем Ханом Изуверским Кулаком."}},
[10388] = {"Назад в Траллмар",nil, {"Возвращайтесь в Траллмар и поговорите с Назгрелом."}},
[10389] = {"Тьма и агония",nil, {"Убейте 10 исчадий ужаса у прудов Аггонара по приказу магистра Кровавого Ястреба."}},
[10390] = {"Лагерь Легиона: Магеддон",nil, {"Атакуйте лагерь Легиона: Магеддон, убейте 10 ган'аргов-прислужников и вернитесь с головой Остропила к Назгрелу в Траллмар."}},
[10391] = {"Пушки Ярости",nil, {"Уничтожьте 3 пушки Скверны первой модели в лагере Легиона: Ярость и возвращайтесь к Назгрелу в Траллмар."}},
[10392] = {"Дверь в Бездну",nil, {"Убейте Арикс'Амала и заберите ключ врат Пылающего Легиона. Используйте ключ на Руне Злобы и возвращайтесь к Назгрелу в Траллмар."}},
[10393] = {"Коварные планы",nil, {"Доставьте послание Пылающего Легиона магистру Кровавому Ястребу в Траллмар."}},
[10394] = {"Уничтожение – лагерь Легиона: Магеддон",nil, {"Убейте 10 ган'аргов-прислужников и Остропила. После выполнения задания возвращайтесь к передовому командиру Броку на заставу Чести."}},
[10395] = {"Темное послание",nil, {"Доставьте послание Пылающего Легиона провидцу бед Криву в главную крепость Оплота Чести."}},
[10396] = {"Враг моего врага...",nil, {"Уничтожьте 3 пушки Скверны первой модели в лагере Легиона: Ярость и возвращайтесь к фельдмаршалу Броку на заставу Чести."}},
[10397] = {"Точка вторжения: Аннигилятор",nil, {"Уничтожьте Арикс'Амала. Заберите ключ врат пылающего Легиона. Примените ключ на Руне Злобы."}},
[10398] = {"Возвращение в Оплот Чести",nil, {"PH: Вернитесь в Оплот Чести"}},
[10399] = {"Сердце тьмы",nil, {"Убейте 10 исчадий ужаса у прудов Аггонара и возвращайтесь к провидцу бед Криву в главную крепость Оплота Чести."}},
[10400] = {"Владыка",nil, {"Убейте Араззиуса Безжалостного у прудов Аггонара и возвращайтесь к командиру армии Данату Троллебою в крепость Оплота Чести."}},
[10401] = {"Задание: со щитом или на щите",nil, {"Уничтожьте Араззиуса Безжалостного по просьбе Назгрела из Траллмара на Полуострове Адского Пламени."}},
[10403] = {"Наладу",nil, {"Отыщите Наладу в руинах Ша'наара."}},
[10404] = {"Против Легиона",nil, {"Экзарх Орелис, находящийся в Зоне 52, просит вас отправиться к кузнечной базе \"Забвение\" и кузнечной базе \"Геенна\" и убить 12 ган'аргов-работников кузни, 6 мо'аргов-начальников кузни и 6 вестников зла. nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10405] = {"С-А-Б-О-Т-А-Ж",nil, {"Проекция командира Амира на полигоне Эфириума – передатчик Эфириума Зета – просит вас добыть 1 комплект готовых обмоток Эфириума."}},
[10406] = {"Передать послание",nil, {"Проекция командира Амира на полигоне Эфириума – передатчик Эфириума Зета – просит вас сопроводить подрывника из Протектората к трубопроводу манагорна Ультрис и охранять его, пока он готовит диверсию на трубопроводе."}},
[10407] = {"Тень Сокретара",nil, {"Анахорет Каржа, находящийся в Зоне 52, просит вас забрать первую половину камня Сокретара у начальника кузни Моруга с базы Легиона: Забвение и вторую половину камня Сокретара у Силрота с базы Легиона: Геенна.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10408] = {"Соправитель Салхадаар",nil, {"Проекция командира Амира на полигоне Эфириума – передатчик Эфириума Зета – просит вас использовать дезинтегратор Протектората, чтобы отключить защитное поле Салхадаара, а затем уничтожить Соправителя Салхадаара."}},
[10409] = {"Удар по Легиону",nil, {"Верховная жрица Ишана просит вас отправиться к телепорту Легиона в северо-западной части Пустоверти и использовать его, чтобы переместиться к Трону Сокретара.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10410] = {"Помощь Ишаны",nil, {"Отнесите камень телепортации Сокретара верховной жрице Ишане в святилище Вечного Света на возвышенности Алдоров в Шаттрате. nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10411] = {"Слава электрошоку!",nil, {"Ученый Навууд, находящийся на заставе стражей Протектората в Пустоверти просит вас уничтожить 30 каплей отброса Бездны и 30 сочащихся каплей слякоча."}},
[10412] = {"Перстни Огнекрылых",nil, {"Принесите магистру Файленн 10 перстней Огнекрылых.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10413] = {"Ужасы загрязнения",nil, {"Отнесите фиал со слизью ужаса Бездны к ученому Навууду на заставу стражей Протектората в Пустоверти."}},
[10414] = {"Перстень Огнекрылых",nil, {"Даже самая маленькая помощь нашему делу не останется незамеченной, Кофемашина. Армия врага велика, но победа будет за нами!"}},
[10415] = {"Больше перстней Огнекрылых",nil, {"Что вы сделали для нас в последее время, Вродехант? Война против Кель'таса не продолжится сама по себе!"}},
[10416] = {"Преобразование силы",nil, {"Принесите чародейский фолиант Ворен'талю Предсказателю в Шаттрат.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10417] = {"Провести диагностику",nil, {"Заберите результаты диагностики и доставьте их Мердаду на Заставу срединных земель."}},
[10418] = {"Покончить с вредителями",nil, {"Мердад, находящийся на Заставе срединных земель, просит вас убить 8 шипастых кроколисков."}},
[10419] = {"Чародейские фолианты",nil, nil},
[10420] = {"Очищающий Свет",nil, {"Принесите латные перчатки Скверны Ишане в Шаттрат.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10421] = {"Латные перчатки Скверны",nil, {"Не попадались ли вам в последнее время латные перчатки Скверны? Нельзя оставлять эти оскверненные вещи в нашем мире, Медвефект. Нужно очистить их."}},
[10422] = {"Капитан Тиралиус",nil, {"Укротитель плоти Виридиус, находящийся на заставе стражей Протектората в Пустоверти, просит вас найти и освободить капитана Тиралиуса."}},
[10423] = {"В Штормовую Вершину",nil, {"Поговорите с Габаром в Штормовой Вершине."}},
[10424] = {"Диагноз: критический",nil, {"Используйте диагностическое устройство, стоя поблизости от генератора экосферы заповедника\"Сатерон\", и отнесите результаты диагностики Габару в Штормовую Вершину."}},
[10425] = {"Побег с полигона",nil, {"Сопроводите пленного передового бойца Протектората обратно на заставу стражей Протектората в Пустоверти."}},
[10426] = {"Растительность заповедников",nil, {"Используйте модулятор энергетического поля на фаралонских плеточниках. Пронаблюдайте результаты и после того, как проведете 10 тестов, сообщите результаты Аурин Лунное Сияние, находящейся в Штормовой Вершине."}},
[10427] = {"Животные заповедников",nil, {"Пометьте 12 талбуков для Аурин Лунное Сияние из Штормовой Вершины. Вы можете метить как самцов, так и самок талбуков, используя метчик для талбуков."}},
[10428] = {"Пропавший рыбак",nil, {"Найдите Коулена на юго-восточной пристани острова Серебристой Дымки, неподалеку от побережья острова Лазурной Дымки."}},
[10429] = {"Когда природа заходит слишком далеко",nil, {"Аурин Лунное Сияние из Штормовой Вершины просит вас принести ей сердце гигантской гидры."}},
[10430] = {"Испытание прототипа",nil, {"Отнесите ремонтный аппарат Ташару в заповедник \"Дальнее поле\"."}},
[10431] = {"Помощь извне",nil, {"Идите в Лагерь Тулумана, на север от Зоны 52, и поговорите с Кайлааном. nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10432] = {"Надежное свидетельство",nil, {"Заберите у демонов, находящихся в манагорне Ара 8 приказов Кель'таса и отнесите их патрону шпионов Талодиену в Зону 52.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10433] = {"Поддерживая видимость",nil, {"Принесите 10 шкур рыси-клыкодера Шоли Пору на Заставу срединных земель."}},
[10434] = {"Динамический дуэт",nil, {"Поговорите с Оди Иголкой в Штормовой Вершине."}},
[10435] = {"Сбор деталей",nil, {"Оди Иголка, находящаяся в Штормовой Вершине, просит вас принести ей 10 сегментов генератора защитного купола."}},
[10436] = {"Все чисто!",nil, {"Ташар, находящийся в заповеднике \"Дальнее поле\", просит вас убить 12 саблезубых ящеров."}},
[10437] = {"Рецепт уничтожения",nil, {"Профессор Дабири, находящийся на заставе стражей Протектората в Пустоверти, хочет, чтобы вы собрали 8 фрагментов Пространствуса."}},
[10438] = {"На крыльях Пустоты",nil, {"Профессор Дабири, находящийся на заставе стражей Протектората в Пустоверти, хочет, чтобы вы использовали фазовый дезинтегратор, когда окажетесь вблизи от стержня Бездны наверху Ультриса. Скиньте как можно больше зарядов фазового дезинтегратора, чтобы обеспечить полное уничтожение. Заряды фазового дезинтегратора автоматически попадут в стержень Бездны, если вы будете достаточно близко, чтобы использовать их.nnЕсли вы преуспеете, возвращайтесь к профессору Дабири!"}},
[10439] = {"Пространствус Всепоглощающий",nil, {"Профессор Дабири, находящийся на заставе Протектората в Пустоверти, хочет, чтобы вы поговорили с капитаном Саидом, а потом вместе с его армией проследовали к манагорну Ультрис и убили Пространствуса Всепоглощающего!"}},
[10440] = {"Готово!",nil, {"Вернитесь в Штормовую Вершину и поговорите с Габаром."}},
[10441] = {"Торговля по мелочам",nil, {"Поговорите с Аурин Лунное Сияние у подножия Штормовой Вершины."}},
[10442] = {"Помощь Кенарийской заставе",nil, {"Отправьтесь на помощь Тие Рыжей Гриве на Кенарийскую заставу на полуострове Адского Пламени по просьбе сокольничего Дренны Речной Ветер."}},
[10443] = {"Помощь Кенарийской заставе",nil, {"Отправьтесь на Кенарийскую заставу на полуострове Адского Пламени и предложите помощь Тие Рыжей Гриве по просьбе Амаана Мудрейшего."}},
[10444] = {"Послание для Заставы Аллерии",nil, {"Доложите обстановку лейтенанту Меридиану на заставе Аллерии в лесу Тероккар по поручению Дженайи Шепот Звезд."}},
[10445] = {"Сосуды Вечности",nil, {"Соридорми из Пещер времени желает, чтобы вы принесли ей неполный фиал леди Вайш из резервуара Кривого Клыка и неполный фиал Кель'таса из Крепости Бурь."}},
[10446] = {"Финальный код",nil, {"Используйте финальный код, чтобы активировать мана-бомбу, как велел вам лейтенант Меридиан. После этого поговорите с Дженайей Шепот Звезд в бастионе Аллерии в лесу Тероккар."}},
[10447] = {"Финальный код",nil, {"Используйте финальный код, чтобы активировать мана-бомбу, как велела вам сержант Чауни. После этого поговорите с Туки в форте Камнеломов в лесу Тероккар."}},
[10448] = {"Донесение в лагерь Камнеломов",nil, {"Поговорите с сержантом Чауни в лагере Камнеломов в лесу Тероккар по просьбе Туки."}},
[10449] = {"Аптекарь Зелана",nil, {"Доставьте образцы крови орков из клана Костеглодов аптекарю Зелане в Погибель Сквернобота по просьбе Вартока Топоруба из Траллмара."}},
[10450] = {"Кровь клана Костеглодов",nil, {"Принесите 12 фиалов крови орков из клана Костеглодов Вартоку Топорубу в Траллмар."}},
[10451] = {"Спастись из водохранилища Змеиных Колец",nil, {"Выведите целительницу земли Вильду из водохранилища Змеиных Колец и затем разыщите целителя земли Торлока у Алтаря Проклятия, на западной окраине Руки Гул'дана."}},
[10454] = {"FLAG – OFF THE RAILS",nil, nil},
[10455] = {"Угроза хищников",nil, {"Рина Лунный Родник хочет, чтобы вы как можно скорее убили 12 рысей рощи и вернулись к ней в Сильванаар в Острогорье."}},
[10456] = {"Волчья напасть",nil, {"Рина Лунный Родник просит вас принести ей 4 хвоста лютых волков клана Громоборцев. По выполнении задания вернитесь к ней в Сильванаар в Острогорье."}},
[10457] = {"Защитить свой дом",nil, {"Взмахните ветвью Рины над 5 саженцами рощи, чтобы призвать на защиту Живой рощи силы природы. Затем вернитесь к Рине Лунный Родник в Сильванаар в Острогорье."}},
[10458] = {"Разъяренные бесы пламени и земли",nil, {"Поймайте с помощью тотема духов 8 бесов земли и 8 бесов пламени для целителя земли Торлока из Алтаря Проклятия."}},
[10459] = {"Почтительное отношение Кенарийской экспедиции",nil, {"Исиэль Песнь Ветра из Кенарийского приюта хочет, чтобы вы добились почтительного отношения Кенарийской экспедиции."}},
[10460] = {"Посвящение в защитники",nil, {"Ты уверен, что хочешь избрать этот путь, Тириш? Он будет нелегким, так что трижды подумай, прежде чем примешь решение.nnВ этом кольце заключена капля воды из Источника Вечности. Его сила возрастет, когда ты заслужишь доверие драконов Песчаной Чешуи."}},
[10461] = {"Посвящение в целители",nil, nil},
[10462] = {"Посвящение в герои",nil, {"Ты уверен, что хочешь избрать этот путь, Тыж? Он будет нелегким, так что трижды подумай, прежде чем примешь решение.nnВ этом кольце заключена капля воды из Источника Вечности. Его сила возрастет, когда ты заслужишь доверие драконов Песчаной Чешуи."}},
[10463] = {"Посвящение в мудрецы",nil, {"Ты уверен, что хочешь избрать этот путь, Монд? Он будет нелегким, так что трижды подумай, прежде чем примешь решение.nnВ этом кольце заключена капля воды из Источника Вечности. Его сила возрастет, когда ты заслужишь доверие драконов Песчаной Чешуи."}},
[10464] = {"Обет мудреца",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить уважительное отношение драконов Песчаной Чешуи."}},
[10465] = {"Обет целителя",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить уважительное отношение драконов Песчаной Чешуи."}},
[10466] = {"Обет героя",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить уважительное отношение драконов Песчаной Чешуи."}},
[10467] = {"Обет защитника",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить уважительное отношение драконов Песчаной Чешуи."}},
[10468] = {"Клятва мудреца",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить почтительное отношение драконов Песчаной Чешуи."}},
[10469] = {"Клятва целителя",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить почтительное отношение драконов Песчаной Чешуи."}},
[10470] = {"Клятва защитника",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить почтительное отношение драконов Песчаной Чешуи."}},
[10471] = {"Клятва защитника",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени после того, как сумеете заслужить почтительное отношение драконов Песчаной Чешуи."}},
[10472] = {"Договор мудреца",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени, когда сумеете заслужить полное доверие драконов Песчаной Чешуи."}},
[10473] = {"Договор целителя",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени, когда сумеете заслужить полное доверие драконов Песчаной Чешуи."}},
[10474] = {"Договор героя",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени, когда сумеете заслужить полное доверие драконов Песчаной Чешуи."}},
[10475] = {"Договор защитника",nil, {"Принесите кольцо Вечности Соридорми в пещеры Времени, когда сумеете заслужить полное доверие драконов Песчаной Чешуи."}},
[10476] = {"Лютые враги",nil, {"Принесите стражу-смотрителю Мой'прну Джиллу 10 обсидиановых боевых ожерелий, снятых с огров из Награнда."}},
[10477] = {"Боевые ожерелья",nil, nil},
[10478] = {"Боевые ожерелья!",nil, {"Ты добыл еще боевых ожерелий, Солэтьюд?"}},
[10479] = {"Покажи свою силу",nil, {"Принесите 10 обсидиановых боевых ожерелий, снятых с огров, населяющих Награнд, стражу-смотрителю Быккроку в Гарадар."}},
[10480] = {"Разъяренные бесы воды",nil, {"Используйте тотем духов, чтобы поймать 5 бесов воды для целителя земли Торлока у Алтаря Проклятия в Долине Призрачной Луны."}},
[10481] = {"Разъяренные бесы воздуха",nil, {"Поймайте с помощью тотема духов 10 бесов воздуха для целителя земли Торлока из Алтаря Проклятия."}},
[10482] = {"Падальщики орков Скверны",nil, {"Убейте 20 орков из клана Костеглодов и возвращайтесь к Амади в Оплот Чести."}},
[10483] = {"Дурные предзнаменования",nil, {"Принесите 1 проклятый талисман капралу Железному Хребту в лагерь экспедиции по просьбе лейтенанта Амади в Оплоте Чести."}},
[10484] = {"Проклятые талисманы",nil, {"Принесите 12 проклятых талисманов капралу Железному Хребту в Лагерь экспедиции."}},
[10485] = {"Воевода клана Кровавой Глазницы",nil, {"Принесите разбитые доспехи воеводы Морха капралу Железному Хребту в лагерь экспедиции."}},
[10486] = {"Угроза хищников",nil, {"Гор'дрек просит вас убить 12 острокрылых кровопускателей. Вернитесь к нему в оплот Громоборцев в Острогорье после выполнения задания."}},
[10487] = {"Пыльца волшебных драконов",nil, {"Гор'дрек хочет, чтобы вы принесли пыльцу с крыльев 4 волшебных драконов. После этого вернитесь к нему в оплот Громоборцев в Острогорье."}},
[10488] = {"Защитить свой дом",nil, {"Помажьте мазью Гор'дрека 5 лютых волков клана Громоборцев, чтобы сделать их сильнее и укрепить оборону Зазубренной гряды. Затем вернитесь к Горд'дреку в оплот Громоборцев в Острогорье."}},
[10489] = {"Свалить ходячее древо!",nil, {"Добудьте ствол Лесодрева Глубокорня и доставьте его Тор'чанку Двукогтю на постоялый двор оплота Громоборцев в Острогорье."}},
[10490] = {"Зов воды",nil, {"Найдите на острове Лазурной Дымки в Кристальном зале Экзодара предсказателя Нобундо и поговорите с ним."}},
[10491] = {"Зов воздуха",nil, {"Поговорите с предсказателем Нобундо в Кристальном зале Экзодара на острове Лазурной Дымки."}},
[10492] = {"Серьезное предложение",nil, {"Добудьте 15 порций крови животных Зимних Ключей и 20 золотых и отдайте их вместе с наручниками Стихий Делиане в Стальгорне."}},
[10493] = {"Серьезное предложение",nil, {"Добудьте 15 порций яда силитида и 20 золотых и принесите их вместе с наручами из светлостали Моквару в Оргриммар."}},
[10494] = {"Справедливое вознаграждение",nil, {"Принесите шнурованный ремень Стихий и пару рукавиц Стихий в Стальгорн Делиане."}},
[10495] = {"Справедливое вознаграждение",nil, {"Принесите Моквару в Оргриммар пояс из светлостали и рукавицы из светлостали."}},
[10496] = {"Прощальные слова Антиона",nil, {"Вернитесь в Стальгорн к Делиане с сапогами Стихий, килтом Стихий и наплечьем Стихий."}},
[10497] = {"Прощальные слова Антиона",nil, {"Отдайте Моквару в Оргриммаре сапоги из светостали, ножные латы из светостали и наплеч из светостали."}},
[10498] = {"Сладкое – на закуску",nil, {"Отдайте Делиане капюшон Стихий и жилет Стихий."}},
[10499] = {"Сладкое – на закуску",nil, {"Отдайте Моквару шлем из светостали и кирасу из светостали."}},
[10500] = {"Орде нужна твоя помощь!",nil, {"Поговорите с вождем Горчуком на аллее Духов Оргриммара."}},
[10501] = {"Альянсу нужна твоя помощь!",nil, {"Поговорите с фельдмаршалом Снежной Лавиной, который находится в Палатах войны Стальгорна."}},
[10502] = {"Клан Кровавого Молота",nil, {"Командир Тень Небес поручает вам уничтожить 30 любых огров из клана Кровавого Молота на заставе Кровавого Молота. По выполнении этого грязного дела вернитесь к нему в Сильванаар в Острогорье."}},
[10503] = {"Угроза Камнерогов",nil, {"Тор'чанк Двукоготь поручил вам уничтожить 30 любых огров из клана Камнерогов и 10 ящеров в форте Камнерогов и на землях Камнерогов. По выполнении поручения вернитесь в оплот Громоборцев в Острогорье."}},
[10504] = {"Огры из клана Камнерогов",nil, {"Командир Тень Небес просит вас убить 30 любых огров из клана Камнерогов в форте Камнерогов и на землях Камнерогов. По выполнении вернитесь к нему в Сильванаар в Острогорье."}},
[10505] = {"Клан Кровавого Молота",nil, {"Тор'чанк Двукоготь просит вас убить 30 любых огров из клана Кровавого Молота на заставе Кровавого Молота. По выполнении вернитесь к нему в оплот Громоборцев в Острогорье."}},
[10506] = {"Тяжелая ситуация",nil, {"Рина Лунный Родник хочет, чтобы вы взяли ее уменьшающий порошок и посыпали им 5 лютых волков клана Кровавого Молота, которых можно найти на заставе Кровавого Молота и в лощине Кровавого Молота. После этого вернитесь к ней в Сильванаар в Острогорье."}},
[10507] = {"Поворотная точка",nil, {"Используйте камень телепортации Сокретара в точке вторжения: Властитель к северу от базы Легиона: Забвение, чтобы перенести свой отряд в лагерь Сокретара. Оказавшись там, используйте \"Присутствие Ворен'таля\", чтобы победить Сокретара. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10508] = {"Дар для Ворен'таля",nil, {"Патрон шпионов Талодиен, находящийся в Зоне 52, просит вас забрать первую половину камня Сокретара у начальника кузни Моруга с базы Легиона: Забвение и вторую половину камня Сокретара у Силрота с базы Легиона: Геенна. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10509] = {"И все ради славы",nil, {"Отнесите камень телепортации Сокретара Ворен'талю Предсказателю на ярус Провидцев в Шаттрате. nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10510] = {"В дренетистовые копи",nil, {"Бронвин Твердомолот просит вас принести 5 кристаллов из дренетистовых копей и вернуться к ней в Сильванаар в Острогорье."}},
[10511] = {"Странное пиво",nil, {"Боргрим Твердомолот просит вас добыть ему 11 бочонков забористого пойла клана Кровавого Молота. По выполнении вернитесь к нему в Сильванаар в Острогорье."}},
[10512] = {"Пусть Камнероги упьются!",nil, {"Возьмите бочонок забористого пойла клана Кровавого Молота и попытайтесь напоить допьяна 5 огров из клана Камнерогов. Если получится, вернитесь к Боргриму в Сильванаар в Острогорье."}},
[10513] = {"Оронок Горемычный",nil, {"Найдите Оронока Горемычного на Разрушенной Отмели, на северном берегу водохранилища Змеиных Колец."}},
[10514] = {"Кем только я не был...",nil, {"Принесите 10 клубней долины Призрачной Луны с Разоренных равнин Ороноку Горемычному на его ферму.nnЕму также необходимо вернуть его свисток."}},
[10515] = {"Усвоенный урок",nil, {"Уничтожьте 10 яиц прожорливого камнедера на Разоренных равнинах для Оронока Горемычного."}},
[10516] = {"Вещи Поборницы",nil, {"Добудьте клинок и щит воздаятельницы Вуулины. Затем вернитесь к воздаятельнице Вуулине в форт Камнерогов в Острогорье."}},
[10517] = {"Горр'Дим, твой час пробил...",nil, {"Воздаятельница Вуулина просит вас убить Горр'Дима, вождя огров из форта Камнерогов. По выполнении вернитесь к ней в форт Камнерогов в Острогорье."}},
[10518] = {"Водружение знамени",nil, {"Водрузите знамя Камнерогов на башне Северного молота, чтобы раздразнить Гурна Обжору. Принесите знамя и шлем Гурна Обжоры командиру Тень Небес в Сильванаар в Острогорье."}},
[10519] = {"Код Проклятия – правда и миф",nil, {"Поговорите с Ороноком Горемычным на его ферме и выслушайте историю, которую он вам поведает."}},
[10520] = {"Помощь верховному друиду Оленьему Шлему",nil, {"Поговорите с верховным друидом Фэндралом Оленьим Шлемом в Дарнассе."}},
[10521] = {"Гром'тор, сын Оронока",nil, {"Найдите Гром'тора сына Оронока в лагере Змеиных Колец в долине Призрачной Луны."}},
[10522] = {"Код Проклятия: Вклад Гром'тора",nil, {"Найдите первый фрагмент Кода Проклятия для Гром'тора, сына Оронока из лагеря Змеиных Колец в долине Призрачной Луны."}},
[10523] = {"Код Проклятия: Первый Фрагмент",nil, {"Принесите Сейф Гром'тора Ороноку Горемычному на ферму Оронока в Долине Призрачной Луны."}},
[10524] = {"Сокровища клана Громоборцев",nil, {"Добудьте барабан клана Громоборцев, стрелу клана Громоборцев и табличку клана Громоборцев. Принесите их вместе с артефактом клана Громоборцев Рокге Живоглотке в оплот Громоборцев в Острогорье."}},
[10525] = {"Путеводный огонек",nil, {"При помощи фрагмента пылающей души превратите свой дух в путеводное видение. Поговорите с Рокгой Живоглоткой в оплоте Громоборцев после того. Как видение приведет вас туда, где находится последнее сокровище клана Громоборцев."}},
[10526] = {"Громовая пика",nil, {"Рокга Живоглотка дала вам поручение добыть громовую пику. Принесите пику ей в оплот Громоборцев в Острогорье."}},
[10527] = {"Ар'тор, сын Оронока",nil, {"Найдите Ар'тора, сына Оронока, на аванпосте иллидари в долине Призрачной Луны."}},
[10528] = {"Тюрьма из демонических кристаллов",nil, {"На аванпосте иллидари разыщите и убейте госпожу боли Габриссу и вернитесь к телу Ар'тора, сына Оронока, с хрустальным ключом."}},
[10529] = {"Путь охотника",nil, {"Поговорите с лейтенантом Вестницей Рассвета в обители Странников."}},
[10530] = {"Путь охотника",nil, {"Поговорите с лейтенантом Вестницей Рассвета в обители Странников."}},
[10531] = {"Битва за Низину Арати",nil, {"Нападите на рудник, лесопилку, кузницу и ферму и возвращайтесь к фельдмаршалу Освету в Опорный пункт."}},
[10532] = {"Перекрыть линии поставок Аратора",nil, {"Выиграйте битву за низину Арати, добудьте ящик с ресурсами из Арати и принесите его стражу смерти Мортису в Павший Молот."}},
[10533] = {"Новые ящики с ресурсами",nil, nil},
[10534] = {"Возвращение домой",nil, {"Кедмос из Экзодара просит вас прийти поговорить с ним."}},
[10535] = {"Ресурсы Низины Арати",nil, {"Выиграйте битву за низину Арати, добудьте ящик с ресурсами из Арати и принесите его сержанту Маклир в Опорный пункт."}},
[10536] = {"Новые ящики с ресурсами",nil, nil},
[10537] = {"Лон'горон, лук Горемычного",nil, {"Отберите у демонов Лон'горон, лук Горемычного, и верните его духу Ар'тора на аванпосте иллидари."}},
[10538] = {"Кипящая кровь",nil, {"Поместите кровь орков из клана Кровавой Глазницы в проклятый котел, чтобы получить кипящую кровь. Принесите 12 образцов кипящей крови аптекарю Альбреку на заставу Хребтолома."}},
[10539] = {"Возвращение домой",nil, {"Кедмос из Экзодара просит вас прийти и поговорить с ним."}},
[10540] = {"Код Проклятия: Вклад Ар'тора",nil, {"Заберите вторую часть Кода Проклятия у Венератуса Многого для духа Ар'тора с аванпоста иллидари. Существа, убитые или раненые охотником на духов, не приносят вам ни добычи, ни опыта."}},
[10541] = {"Код Проклятия – Второй фрагмент",nil, {"Отнесите сейф Ар'тора Ороноку Горемычному на ферму Оронока в Долине Призрачной Луны.n."}},
[10542] = {"У меня сперли кальян и выпивку!",nil, {"Добудьте кальян Т'чали и 10 кружек забористого пойла клана Кровавого Молота. Принесите все к Т'чали Знахарю в лощину Кровавого Молота в Острогорье."}},
[10543] = {"Гримнок и Коргаах, я за вами!",nil, {"Т'чали Знахарь просит, чтоб вы убили его главных обидчиков, Гримнока Дитя Войны и Коргааха. По выполнении вернитесь к нему в лощину Кровавого Молота в Острогорье."}},
[10544] = {"Чума на оба ваших клана!",nil, {"При помощи гибельного сильного фетиша прокляните 5 домов или башен в крепости Камнерогов и 2 на заставе Кровавого Молота. По выполнении задания вернитесь к Т'чали Знахарю в лощину Кровавого Молота в Острогорье."}},
[10545] = {"Бочонок клана Камнерогов",nil, {"Т'чали просит, чтобы вы отнесли бочонок забористого пойла клана Кровавого Молота и попытались бы напоить им 5 огров из клана Камнерогов. Если все получится, вернитесь к Т'чали Знахарю в лощину Кровавого Молота в Острогорье."}},
[10546] = {"Борак, сын Оронока",nil, {"Найдите Борака сына Оронока возле лагеря Затмения в долине Призрачной Луны."}},
[10547] = {"Выгодный обмен",nil, {"Найдите тухлое яйцо араккоа и доставьте его Тобиасу Грязежору в город Шаттрат, расположенный на северо-западе леса Тероккар."}},
[10548] = {"Горькая правда",nil, {"Эраланна из Транквиллиона просит вас найти усохшую голову археолога в одном из сундуков Зеб'Телы или Зеб'Новы."}},
[10550] = {"Пучок кровопийки",nil, {"Отнесите пучок кровопийки Бораку сыну Оронока на мост возле лагеря Затмения, в долине Призрачной Луны. n."}},
[10551] = {"Присяга верности Алдорам",nil, {"Вы уверены в своем выборе, Тунфокс? Алдоры с радостью примут вас в ряды своих союзников, но Провидцы вряд ли смогут вам это простить."}},
[10552] = {"Присяга верности Провидцам",nil, {"Вы уверены в своем выборе, Солэтьюд? Провидцы с радостью примут вас в ряды своих союзников, но Алдоры вряд ли смогут вам это простить."}},
[10553] = {"Ворен'таль Предсказатель",nil, {"Поговорите с Ворен'талем в его библиотеке на ярусе Провидцев в Шаттрате.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10554] = {"Ишана",nil, {"Поговорите с Ишаной в святилище Вечного Света на возвышенности Алдоров в Шаттрате.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10555] = {"Недомогание",nil, {"Собрать 18 выдернутых перьев Лашш, затем прочесть фолиант Лашш на вершинах деревьев Гнездовья Лашш."}},
[10556] = {"Рисунки",nil, {"При помощи горсти перьев в кругу заклинаний Лашш наложите заклинания, затем вернитесь к Даранелле в Сильванаар прежде, чем дух калири покинет вас."}},
[10557] = {"Испытательный полет: Конденсаторий зефира",nil, {"Поговорите с Ралли Быстроцапом и испытайте Конденсанторий зефира. Вернитесь к Талли Быстроцапу на станцию Тошли."}},
[10558] = {"Уважительное отношение Оплота Чести",nil, {"Командир армии Данат Троллебой из Оплота Чести на полуострове Адского Пламени желает, чтобы вы заслужили уважительное отношение Оплота Чести, продолжив сражаться с орками Скверны в Разрушенных залах цитадели Адского Пламени."}},
[10559] = {"Уважительное отношение Траллмара",nil, {"Заслужите уважение Траллмара, сражаясь с орками Скверны в Разрушенных залах цитадели Адского Пламени по совету Назгрела из Траллмара на полуострове Адского Пламени."}},
[10560] = {"Почтительное отношение Ша'тар",nil, {"Заслужите почтительное отношение Ша'тар, сразившись с силами Кель'таса в Крепости Бурь, после чего поговорите с А'далом в Шаттрате."}},
[10561] = {"Уважение Хранителей Времени",nil, {"Заслужите уважение Хранителей Времени. Для этого необходимо проследить, чтобы никто не нарушил течение времени и не изменил ход прошлых событий в старом Хилсбраде и в Черных топях в пещерах Времени."}},
[10562] = {"Окружены!",nil, {"Убейте 10 боевых инферналов для командира звена Нуаинна из цитадели Громового Молота."}},
[10563] = {"В форт Легиона",nil, {"Отправляйтесь в форт Легиона, найдите там переговорное устройство и, воспользовавшись маскировкой, выведайте планы Легиона. Вернитесь к командиру звена Нуаинну в цитадель Громового Молота, когда выполните задание."}},
[10564] = {"Перебей инферналов!",nil, {"Используйте бомбу Скверны, находясь в центре зеленого знака в форте Легиона, чтобы уничтожить огненных големов. Доложите о выполнении задания командиру звена Нуаинну из цитадели Громового Молота."}},
[10565] = {"Камни Векх'ниров",nil, {"Добудьте кристалл век'ниров и отнесите в круг закинаний век'ниров. Затем принесите заряженный кристалл век'ниров Дертроку в деревню Мок'Натал."}},
[10566] = {"Пробы и ошибки",nil, {"Испытайте все жезлы Дертрока на огре-рабе араккоа, затем возвращайтесь к Дертроку. Для использования каждого жезла понадобится пылающий кристалл Вех'ниров."}},
[10567] = {"Создание подвески",nil, {"Добудьте 6 когтей араккоа из гнездовья Рууан и положите их круг оракула из предвестника Ворона. Убейте его и отнесите подвеску предвестника Тимеону в чащобу Рууан."}},
[10568] = {"Таблички Баа'ри",nil, {"Соберите для анахоретки Сейлы 12 табличек Баа'ри. Таблички могут быть где-то закопаны или находиться у Пеплоустов-рабочих.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10569] = {"Развалины Скет'лона",nil, {"Добудьте у темного конклава араккоа и принесите наезднику на грифоне Киерану первую, вторую и третью страницы дневника командира Скет'лона."}},
[10570] = {"Охота на Посла",nil, {"Заберите письмо Ярости Бури на мосту возле лагеря Затмения для Борака сына Оронока."}},
[10571] = {"Старец Орону",nil, {"Найдите в руинах Баа'ри приказы Акамы, адресованные Орону Старцу, и принесите их анахоретке Сейле к алтарю Ша'тар.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10572] = {"Сборка бомбы",nil, {"Добудьте энергетический сердечник и часть обшивки сквернобота и принесите материалы командиру звена Нуаинну из цитадели Громового Молота."}},
[10573] = {"Кузница Смерти",nil, {"Поговорите с штормером Эваном Дикокрылом в башне Кузницы Смерти."}},
[10574] = {"Осквернители из клана Пеплоустов",nil, {"Добудьте 4 фрагмента медальона у Хаалума, Айкенена, Уйлару, и Лакаана и вернитесь к анахоретке Сейле к алтарю Ша'тар в долине Призрачной Луны.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10575] = {"Клеть Стражницы",nil, {"Отправляйтесь в Клеть Стражницы, к югу от руин Баа'ри и расспросите Санору о местонахождении Акамы для анахоретки Сейлы.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10576] = {"Уловки Призрачной Луны",nil, {"Принесите 6 фрагментов доспехов из лагеря Затмения Бораку, сыну Оронока, на мост возле лагеря Затмения в долине Призрачной Луны."}},
[10577] = {"Чего Иллидан хочет, Иллидан получит...",nil, {"Доставьте послание Иллидана главнокомандующему Рууску в лагерь Затмения."}},
[10578] = {"Код Проклятия – Вклад Борака",nil, {"Отберите для Борака, сына Оронока, третью часть Кода Проклятия у Руула Светокрада.nnДля выполнения этого задания вам потребуются помощники."}},
[10579] = {"Код Проклятия – Третий фрагмент",nil, {"Принесите сейф Борака Ороноку Горемычному на ферму Оронока в Долине Призрачной Луны."}},
[10580] = {"Куда подевались эти клятые гномы?",nil, {"Командир Тень Небес просит вас обыскать южные районы Зазубренной гряды Острогорья, чтобы найти, где сейчас Тошли и прочие гномы."}},
[10581] = {"Иди по крошечкам!",nil, {"Р-3Д0 намекает вам, что если вы пойдете по следу из запчастей вверх по склону на Островерхий гребень, вы найдете гномов в месте, именуемом станция Тошли.nnЕсли найдете Тошли, поговорите с ним."}},
[10582] = {"Приспешники Совета Теней",nil, {"Убейте 10 стражей и 5 призывателей из Кузницы Смерти для штормера Эвана Дикокрыла."}},
[10583] = {"Судьба Фланиса",nil, {"Разыщите Фланиса Быстрокрыла в Кузнице Смерти, и принесите его сумку штормеру Эвану Дикокрылу в башню Кузницы Смерти."}},
[10584] = {"Спасти преобразователи энергии!",nil, {"Тошли просит вас взять протовольтаический магнетоколлектор и поймать 5 электроменталей. По выполнении вернуться к нему на станцию Тошли в Острогорье."}},
[10585] = {"Чертог Призыва",nil, {"Раздобудьте стихийный подчинитель у кузнеца или ремонтника из Кузницы Смерти и используйте его, чтобы прервать ритуал в чертоге призыва. Доложите о выполнении задания штормеру Эвану Дикокрылу из башни Кузницы Смерти."}},
[10586] = {"Смерть вестнику войны!",nil, {"Убейте Разууна Завоевателя, заберите его приказы и вернитесь к командиру звена Нуаинну в цитадель Громового Молота."}},
[10587] = {"Тренировочная площадка Карабора",nil, {"Отберите для экзарха Онаалы у соискателей и посвященных охотников на демонов, сосредоточенных в руинах Карабора, 8 глеф армии Ярости Солнца.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10588] = {"Код Проклятия",nil, {"Используйте Код Проклятия у Алтаря Проклятия, чтобы призвать Цируха Повелителя Огня.nnУничтожьте его и пообщайтесь с целителем земли Торлоком, которого вы найдете там же."}},
[10589] = {"Получение доступа",nil, {"Принесите пульт от телепорта Легиона командиру звена Нуаинну из цитадели Громового Молота. Пульты можно найти у мо'аргов-оружейников в форте Легиона."}},
[10590] = {"Доказательство ненависти",nil, {"Принесите 20 камней Плети приспешников Мелару Клинку Рассвета из Бастиона."}},
[10592] = {"Мудрость королевы банши",nil, {"Отнесите фрагменты камня Плети Леди Сильване Ветрокрылой в Подгород."}},
[10593] = {"Древнее зло",nil, {"Раскройте тайны храма Атал'Хаккар, освободите Атал'алариона и добудьте гнилую лозу из его плоти. По выполнении задания вернитесь к Мелару в Бастион."}},
[10594] = {"Калибровка резонансной частоты",nil, {"Никвинкль Метрогном хочет, чтобы вы расположили осцилляционные сканеры таким образом, чтобы стоя среди них вы оказались в точке перекрытия пяти осцилляционных полей.nnПо выполнении вернитесь к нему на станцию Тошли в Острогорье."}},
[10595] = {"Окружены!",nil, {"Убейте 10 боевых инферналов для кровавого стража Галмока из деревни Призрачной Луны."}},
[10596] = {"В форт Легиона",nil, {"Отправляйтесь в форт Легиона, найдите переговорное устройство и, замаскировавшись, выведайте планы Легиона. Вернитесь к кровавому стражу Галмоку в деревню Призрачной Луны, когда выполните задание."}},
[10597] = {"Сборка бомбы",nil, {"Добудьте энергетический сердечник и часть обшивки сквернобота для кровавого стража Галмока из деревни Призрачной Луны."}},
[10598] = {"Перебей инферналов!",nil, {"Используйте бомбу Скверны, находясь в центре зеленого знака в форте Легиона, чтобы уничтожить инферналов. Доложите о выполнении кровавому стражу Галмоку из деревни Призрачной Луны."}},
[10599] = {"Кузница Смерти",nil, {"Поговорите с разведчиком Заграном на Вышке на золе."}},
[10600] = {"Приспешники Совета Теней",nil, {"Убейте 10 стражей и 5 призывателей из Кузницы Смерти для разведчика Заграна с Вышки на золе."}},
[10601] = {"Судьба Кагроша",nil, {"Разыщите Кагроша в Кузнице Смерти и принесите его сумку разведчику Заграну на вышку на золе."}},
[10602] = {"Чертог Призыва",nil, {"Раздобудьте стихийный подчинитель у кузнеца или ремонтника из Кузницы Смерти и используйте его, чтобы прервать ритуал в чертоге призыва. Доложите о выполнении задания разведчику Заграну на Вышке на золе."}},
[10603] = {"Смерть вестнику войны!",nil, {"Убейте завоевателя Разууна, добудьте у него приказы Разууна и вернитесь к кровавому стражу Галмоку в деревню Призрачной Луны."}},
[10604] = {"Получение доступа",nil, {"Принесите пульт от телепорта Легиона кровавому стражу Галмоку в деревню Призрачной Луны. Пульты можно найти у мо'аргов-оружейников в форте Легиона."}},
[10605] = {"Призыв Карендина",nil, {"Поговорите с Карендином Халгаром в Подгороде."}},
[10606] = {"Искусство ухода за скверноботом",nil, {"Убейте доктора Потрошилло и заберите у него ключ. Откройте этим ключом сейф доктора и заберите книгу \"Искусство ухода за скверноботом\". Принесите ее Плекси в Точку вторжения: Катаклизм."}},
[10607] = {"Шепот Бога-ворона",nil, {"Убейте араккоа Гришны, чтобы на две минуты обрести Понимание речи Ворона. Находясь под действием этого заклятия, вы можете найти и получить пророчества в четырех святилищах. Вернитесь после посещения всех четырех святилищ."}},
[10608] = {"Ясно как день!",nil, {"Никвинкль Метрогном желает, чтобы вы убили 10 кристальных камнедеров. По выполнении вернитесь к нему на станцию Тошли в Острогорье."}},
[10609] = {"Что было первым, дракон или яйцо?",nil, {"Соберите 3 сущностей прародителя драконов Пустоты, 3 субстанции взрослого дракона и 3 субстанции зрелого дракона. Принесите их Физиту Тиктаку по прозвищу Док на станцию Тошли в Острогорье."}},
[10610] = {"Основы ведения разведки",nil, nil},
[10611] = {"Искусство ухода за скверноботом",nil, {"Убейте доктора Потрошиллера и заберите у него ключ. Откройте этим ключом сейф доктора и заберите книгу \"Искусство ухода за скверноботом\". Принесите его Наканси в Точку вторжения: Катаклизм."}},
[10612] = {"Машина для убийства",nil, {"Используйте панель управления скверноботом, чтобы захватить контроль над скверноботом-часовым. Уничтожьте 60 инферналов из Кузницы Смерти, прежде чем ваш сквернобот падет. По выполнении вернитесь к Плекси в точку вторжения: Катаклизм."}},
[10613] = {"Машина для убийства",nil, {"Используйте панель управления скверноботом, чтобы захватить контроль над скверноботом-часовым. Уничтожьте 60 инферналов из Кузницы Смерти, прежде чем ваш сквернобот падет. По выполнении вернитесь к Наканси в точку вторжения: Катаклизм."}},
[10614] = {"Шепот ветра",nil, {"Найдите Леорокса в деревне Мок'Натал в Острогорье."}},
[10615] = {"Чащоба Рууан",nil, {"Поговорите с Времионом в Вечной роще, что в чащобе Рууан в Острогорье."}},
[10617] = {"Коконы шелкокрыла",nil, {"Таэрек из деревни Мок'Натал просит вас принести 8 коконов шелкокрыла с личинок шелкокрыла.nnБыстро убейте личинок, чтобы те не начали превращаться в шелкокрылов, прогрызая свои коконы."}},
[10618] = {"Нежнейшие крылья",nil, {"Силмара из деверни Мок'Натал просит вас принести ей 12 радужных крыльев."}},
[10619] = {"Клан Пеплоустов",nil, {"Убейте для воздаятеля Алуумена 3 укротителей, 4 воинов и 6 шаманов из племени Пеплоустов.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10620] = {"Угроза горного хребта",nil, {"Дина Головокружилка просит вас убить 10 пауков высокогорья. По выполнении вернитесь к ней на станцию Тошли в Острогорье."}},
[10621] = {"Осколок погибели Иллидари",nil, {"Принесите осколок проклятия иллидари Ординну Громовому Кулаку в цитадель Громового Молота."}},
[10622] = {"Доказательство верности",nil, {"Убейте Зандраса в Клети Стражницы и возвращайтесь к Санору."}},
[10623] = {"Осколок погибели Иллидари",nil, {"Принесите осколок проклятия иллидари Грокому Мертвому Глазу в деревню Призрачной Луны."}},
[10624] = {"История про привидений...",nil, {"Принесите главному аптекарю Хильдегард из деревни Призрачной Луны 15 горстей неостывающего пепла."}},
[10625] = {"Спектроскоп",nil, {"Убейте для главного аптекаря Хильдегард из деревни Призрачной Луны 12 ревнителей из клана Призрачной Луны."}},
[10626] = {"Сдать оружие!",nil, {"Принесите глефу Маказрадона и глефу Моргророна Ординну Громовому Кулаку в цитадель Громового Молота."}},
[10627] = {"Сдать оружие!",nil, {"Принесите глефу Мазакрадона и глефу Моргророна Грокому Мертвому Глазу в деревню Призрачной Луны."}},
[10628] = {"Акама",nil, {"Поговорите с Акамой в тайной комнате Клети Стражницы."}},
[10629] = {"Шизанутая работенка",nil, {"Используйте свисток гончей Скверны, чтобы призвать гончую. Выведите ее на прогулку и убейте парочку безумных зловепрей. Поищите ключи от крошшера в помете гончей Скверны. Верните ключи штейгеру Разлкразу, который находится на руднике к северо-западу от Траллмара."}},
[10630] = {"Рядом с Траллмаром",nil, {"Убейте Урга'зза и возвращайтесь к штейгеру Разлкразу на рудник к северо-западу от Траллмара."}},
[10632] = {"Точи зубы!",nil, {"Разак Сковородкер просит вас принести ему 5 острейших зубов Кинжальной Пасти. Принесите их ему на станцию Тошли в Острогорье."}},
[10633] = {"Терон Кровожад – Правда и Вымысел",nil, {"Найдите древнего духа долины Призрачной Луны для главного аптекаря Хильдегард из деревни Призрачной Луны в одноименной долине. Если вы потеряете свой спектроскоп, Хильдегард может сделать вам новый."}},
[10634] = {"Прорицание: Броня Кровожада",nil, {"Завладейте броней Кровожада по просьбе древнего духа Долины Призрачной Луны, что обитает у Алтаря Теней."}},
[10635] = {"Прорицание: Плащ Кровожада",nil, {"Завладейте плащом Кровожада по просьбе древнего духа Долины Призрачной Луны, что обитает у Алтаря Теней."}},
[10636] = {"Прорицание: буздыхан Кровожада",nil, {"Завладейте буздыханом Кровожада по просьбе древнего духа Долины Призрачной Луны, что обитает у Алтаря Теней."}},
[10637] = {"Необходимая уловка",nil, {"Вернитесь в Руины Карабора и освободите для экзарха Онаалы Разрушителя Азалота.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10638] = {"NOT A QUEST",nil, {"Seek <NAME> in the <DISTRICT> of Silvermoon City."}},
[10639] = {"Терон Кровожад, то есть я...",nil, {"Древний дух долины Призрачной Луны обвел вас вокруг пальца! Вы сделали все, что он просил, но оказалось, что этот дух – сам Терон Кровожад. Чтобы освободиться от его власти вы должны выполнить его распоряжение и убить Карсиуса Дозорного Древности.nnПо выполнении задания вернитесь к главному аптекарю Хильдегард в деревню Призрачной Луны."}},
[10640] = {"Алтруис",nil, {"Отправляйтесь в западный Награнд и найдите Алтруиса Страдальца.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10641] = {"Против Легиона",nil, {"Алтруис Страдалец просит вас добыть свежей крови зловещей жрицы на базе Легиона: Геенна в Пустоверти. Пролейте кровь на землю и убейте аватару Саталь. По выполнении задания вернитесь к Алтруису."}},
[10642] = {"Дух из машины",nil, {"Принесите 15 горстей неостывающего пепла Зорусу Ревнителю в цитадель Громового Молота в долине Призрачной Луны."}},
[10643] = {"Вестники Призрачной Луны",nil, {"Убейте 12 предвестников из клана Призрачной Луны для Зоруса Ревнителя из цитадели Громового Молота."}},
[10644] = {"Терон Кровожад – Правда и Вымысел",nil, {"Найдите древнего духа долины Призрачной Луны для Зоруса Ревнителя в цитадели Громового Молота. Если вы потеряете свой спектроскоп, Зорус может сделать вам новый."}},
[10645] = {"Терон Кровожад, то есть я...",nil, {"Древний дух долины Призрачной Луны обвел вас вокруг пальца! Вы сделали все, что он просил, но оказалось, что этот дух – сам Терон Кровожад. Чтобы освободиться от его власти вы должны выполнить его распоряжение и убить Карсиуса Дозорного Древности.nnПо выполнении задания вернитесь к Зорусу Ревнителю в цитадель Громового Молота."}},
[10646] = {"Ученик Иллидана",nil, {"Выслушайте рассказ Алтруиса."}},
[10647] = {"Разыскивается: Увурос, бич долины Призрачной Луны",nil, {"Убейте Увуроса и доставьте его пламенеющую гриву военному глашатаю Сардону Чистому Удару в деревню Призрачной Луны в одноименной долине."}},
[10648] = {"Разыскивается: Увурос, бич долины Призрачной Луны",nil, {"Убейте Увуроса и доставьте его пламенеющую гриву военному глашатаю Пивному Брюху в цитадель Громового Молота в долине Призрачной Луны."}},
[10649] = {"Книга Имен Скверны",nil, {"Проберитесь в Темный лабиринт в Аукиндоне и добудьте у Черносерда Подстрекателя Книгу Имен Скверны. После этого вернитесь в Награнд к Алтруису."}},
[10650] = {"Возвращение к Алдорам",nil, {"Поговорите с экзархом Онаалой у алтаря Ша'тар, в долине Призрачной Луны.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10651] = {"Варедиса нужно остановить!",nil, {"Отправляйтесь в Руины Карабора и убейте Аландиен, Тераса, Нетариеля и самого Варедиса. Используйте Книгу Имен Скверны, когда Варедис будет перевоплощаться, чтобы ослабить его. Вернитесь к экзарху Онаале с книгой Имен Скверны, по выполнении задания.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10652] = {"В тылу врага",nil, {"Поговорите с Веронией, когда будете готовы отбыть в манагорн Коруу. Оказавшись там, поговорите с Каледисом Сияющим Рассветом.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10653] = {"Знаки Саргераса",nil, {"Принесите 10 знаков Саргераса Адуину Стражу Света в Шаттрат.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10654] = {"Больше знаков Саргераса",nil, {"Вы принесли еще знаков различия наших врагов, |3-6(Жрица)? Пылающий Легион все еще угрожает нам, и мы преследуем его повсеместно."}},
[10655] = {"Знак Саргераса",nil, nil},
[10656] = {"Перстни Ярости Солнца",nil, {"Принесите магистру Файленн 10 перстней Ярости Солнца.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10657] = {"Оседлать молнию",nil, {"Тошли хочет, чтобы вы при помощи реполяризованной магнитной сферы абсорбировали 25 ударов молний от чешуекрылых змеев. Также добудьте 5 молниевых желез чешуекрылых. Доставьте все Тошли в Острогорье."}},
[10658] = {"Больше перстней Ярости Солнца",nil, nil},
[10659] = {"Перстень Ярости Солнца",nil, nil},
[10660] = {"Что за странные создания?",nil, {"Добудьте 8 селезенок сквернопламенных деметродонов для ученого Тиоруса в деревне Призрачной Луны в одноименной долине."}},
[10661] = {"Умопотрошительно!",nil, {"Принесите 8 селезенок сквернопламенных деметродонов Гномусу в цитадель Громового Молота."}},
[10662] = {"Кузнец–отшельник",nil, {"Принесите ящик со слитками Дэвиду Уэйну в приют Уэйна, который находится в лесу Тероккар."}},
[10663] = {"Кузнец–отшельник",nil, {"Принесите ящик со слитками Дэвиду Уэйну в приют Уэйна, который находится в лесу Тероккар."}},
[10664] = {"Дополнительные материалы",nil, {"Принесите адамантитовую рамку, 4 куска толстой узловатой кожи и тотем защиты от демонов Дэвиду Уэйну в Приют Уэйна."}},
[10665] = {"Новинка из Механара",nil, {"Принесите избыточно заряженный аккумулятор маны Дэвиду Уэйну в Приют Уэйна."}},
[10666] = {"Лексикон демоника",nil, {"Добудьте книгу \"Лексикон демоника\" у великого мастера Ворпила и принесите его Дэвиду Уэйну в Приют Уэйна."}},
[10667] = {"Суглинок из Нижнего мира",nil, {"Достаньте фиал с суглинком Нижнего мира у гидромантки Теспии и принесите его Дэвиду Уэйну в приют Уэйна."}},
[10668] = {"Против иллидари",nil, {"Алтруис Страдалец просит вас убить Лотроса на аванпосте иллидари в долине Призрачной Луны."}},
[10669] = {"Вопреки всему",nil, {"Алтруис Страдалец хочет, чтобы вы взяли прочное серебряное копье и вонзили его на прогалине с порталом близ озера Болотных Огоньков, чтобы пробудить Кселета. Когда убьете демона, вернитесь к Алтруису."}},
[10670] = {"Слеза Матери-Земли",nil, {"Добудьте слезу Матери-Земли у О'мрогга Завоевателя и принесите ее Дэвиду Уэйну в приют Уэйна."}},
[10671] = {"Не только фунт мяса",nil, {"Дина Головокружилка просит вас добыть 5 кусков мяса, напитанного энергией Астрала. По выполнении вернитесь к ней на станцию Тошли в Острогорье."}},
[10672] = {"Честно говоря, это – бред!",nil, {"Отправляйтесь к потокам лавы возле руин Баа'ри и используйте пульт управления чарами, чтобы контролировать волшебного скорпиона. Переправьтесь на нем через лаву и пометьте 8 больших сквернопламенных деметродонов для ученого Тиоруса."}},
[10673] = {"Хребтоскверн Старший",nil, {"Убейте Хребтоскверна Старшего и принесите его шкуру ученому Тиорусу в деревню Призрачной Луны в одноименной долине."}},
[10674] = {"Ловля фантастического света",nil, {"Тошли желает, чтобы вы при помощи световой ловушки поймали 15 световых сфер братства Разаани. По выполнении задания вернитесь к нему на станцию Тошли в Острогорье."}},
[10675] = {"Попомните гномскую доброту!",nil, {"Тошли хочет, чтобы вы добыли для него коллекцию душ и вернулись к нему на стнацию Тошли в Острогорье."}},
[10676] = {"Проклятие иллидари",nil, {"Подождите, пока Дэвид Уэйн откует для вас клинок."}},
[10677] = {"Второе блюдо...",nil, {"Добудьте для Гномуса из цитадели Громового Молота в долине Призрачной Луны потроха крупного деметродона."}},
[10678] = {"Гвоздь программы",nil, {"Принесите шкуру Хребтоскверна Гномусу в цитадель Громового Молота в долине Призрачной Луны."}},
[10679] = {"Закалка Меча",nil, {"Отправляйтесь к Жгучим прудам возле подножия Руки Гул'дана, и закалите кованый меч погибели иллидари в озерах лавы. После этого возвращайтесь к Дэвиду Уэйну в приют Уэйна."}},
[10680] = {"Рука Гул'дана",nil, {"Поговорите с целителем земли Торлоком у Алтаря Проклятия в Долине Призрачной Луны."}},
[10681] = {"Рука Гул'дана",nil, {"Поговорите с целителем земли Торлоком у Алтаря Проклятия в Долине Призрачной Луны."}},
[10682] = {"Время для переговоров...",nil, {"Хранитель деревьев Чаун просит вас переговорить с надзирателем Нуааром. После вернитесь к хранителю деревьев в Вечную рощу в Острогорье."}},
[10683] = {"Таблички Баа'ри",nil, {"Соберите для чародея Тэлиса из святилища Звезд 12 табличек Баа'ри. Таблички могут быть где-то закопаны или находиться у Пеплоустов-рабочих.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10684] = {"Старец Орону",nil, {"Найдите в руинах Баа'ри приказы Акамы, адресованные старцу Орону, и принесите их чародею Телису.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10685] = {"Осквернители из клана Пеплоустов",nil, {"Соберите 4 фрагмента медальона у Хаалума, Айкенена, Уйлару, и Лакаана и вернитесь к чародею Тэлису в святилище Звезд.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10686] = {"Клеть Стражницы",nil, {"Отправляйтесь в Клеть Стражницы, к югу от руин Баа'ри и расспросите Санору о местонахождении Акамы для чародея Телиса.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10687] = {"Тренировочная площадка Карабора",nil, {"Отберите у соискателей и посвященных охотников на демонов, сосредоточенных в руинах Карабора, 8 глеф армии Ярости Солнца. Принесите их Лариссе Солнцесвет в святилище Звезд.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10688] = {"Необходимая уловка",nil, {"Вернитесь в Руины Карабора и освободите Азалота для Лариссы Солнцесвет.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10689] = {"Алтруис",nil, {"Отправляйтесь в западный Награнд и найдите Алтруиса Страдальца.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10690] = {"Мать логова",nil, {"Убейте Рему и сообщите об этом командиру Тень Небес в Сильванаар в Острогорье."}},
[10691] = {"Возвращение к Провидцам",nil, {"Поговорите с Лариссой Солнцесвет в святилище Звезд в долине Призрачной Луны.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10692] = {"Варедиса нужно остановить!",nil, {"Отправляйтесь в Руины Карабора и убейте Аландиен, Тераса, Нетариеля и самого Варедиса. Используйте Книгу Имен Скверны, когда Варедис будет перевоплощаться, чтобы ослабить его. Вернитесь к Лариссе Солнцесвет с Книгой Имен Скверны по выполнении задания.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
