[10701] = {"Низвергнуть Глыбня Пустоты",nil, {"Убейте Глыбня Пустоты и сообщите об этом ведущему ракетостроителю Фьюзеляжу, находящемуся в Зоне 52 в Пустоверти."}},
[10702] = {"Наряд вне очереди",nil, {"Убейте 20 животных, водящихся в долине Призрачной Луны для воеводы Ор'бароха.nnТварями считаются скверносвины, мерзкокрылые химеры и огнепанцирные клешневики. Соотношение может быть любым."}},
[10703] = {"Зачистка",nil, {"Убейте 20 животных, водящихся в долине Призрачной Луны, для тана Йорегара из цитадели Громового Молота.nnПодойдут скверносвины, мерзкокрылые химеры и огнепанцирные клешневики. Соотношение может быть любым."}},
[10704] = {"Как проникнуть в Аркатрац",nil, {"Принесите А'далу верхний и нижний обломок ключа Аркатраца, чтобы он собрал из них целый ключ."}},
[10705] = {"Провидец Адало",nil, {"Найдите провидца Адало в Аркатраце в Крепости Бурь."}},
[10706] = {"Таинственное знамение",nil, {"Вернитесь к Акаме в Клеть Стражницы в долине Призрачной Луны."}},
[10707] = {"Терраса Ата'мала",nil, {"Отправляйтесь на террасу Ата'мала, в долине Призрачной Луны и раздобудьте Сердце Ярости. По выполнении задания вернитесь к Акаме в Клеть Стражницы."}},
[10708] = {"Обещание Акамы",nil, {"Принесите медальон Карабора А'далу в Шаттрат."}},
[10709] = {"Воссоединение",nil, {"Вернитесь к Рексару в оплот Громоборцев в Острогорье."}},
[10710] = {"Испытательный полет: Звенящий гребень",nil, {"Подпишите отказ от претензий к Талли, затем поговорите с Ралли Быстроцапом насчет Конденсатория зефира. Вернитесь к Талли Цапнабберу на станцию Тошли."}},
[10711] = {"Испытательный полет: лагерь Разаана",nil, {"Поговорите с Ралли Быстроцапом насчет использования Конденсатория зефира. Вернитесь к Талли Быстроцапу на станцию Тошли."}},
[10712] = {"Испытательный полет: чащоба Рууан",nil, {"Поговорите с Ралли Быстроцапом, чтобы узнать, как использовать конденсаторий зефира. При полете в чащобу Рууан крутите флюгер Пустоты. Передайте флюгер Пустоты О'Малли Быстроцапу, который находится в чащобе Рууан."}},
[10713] = {"...и время действовать",nil, {"Отправьтесь в чащобу Рууан и убейте 10 лесорубов из культа Змея, затем вернитесь к хранителю деревьев Чауну в Вечную рощу в Острогорье."}},
[10714] = {"На крыльях Духа",nil, {"Найдите занятых разговором ворожея и надсмотрщика из клана Кровавого Молота, затем при помощи свистка Рексара призовите Духа и проследите за ними. По завершении вернитесь к Рексару в деревню Громоборцев в Острогорье."}},
[10715] = {"Прогулка в Беспокойную лощину",nil, {"Барон Черногрив говорит вам, что для того, чтобы приготовить яд, нужный Рексару, вы должныдобыть 7 ядовитых желез землероев. Вернитесь к нему в Круг Крови в Острогорье по выполнении."}},
[10716] = {"Test Flight: Raven's Wood <needs reward>",nil, {"Поговорите с Ралли Быстроцапом насчет использования Конденсатория зефира. Вернитесь к Талли Быстроцапу на станцию Тошли."}},
[10717] = {"Вор у вора...",nil, {"Самия Недомолвка просит вас добыть 5 сетей Культа Змея и принести ей в Вечную рощу в Острогорье."}},
[10718] = {"У духов есть голоса",nil, {"Гарм Брат Волка поведал вам, где искать Мок'Натал, место, где живет народ Рексара. Расскажите об этом Рексару в оплоте Громоборцев в Острогорье."}},
[10719] = {"Где записка?",nil, {"Отдайте записку о встрече хранителю деревьев Чауну в Вечную рощу в Острогорье."}},
[10720] = {"Самые маленькие создания",nil, {"Ударьте в барабан из шкуры кодо возле норы сурка. Держа сурка под контролем, насыпьте яду в три бочонка в лагере клана Кровавого Молота. По выполнении вернитесь к Рексару в оплот Громоборцев в Острогорье."}},
[10721] = {"Подложим Груллоку свинью",nil, {"Добудьте мешок Груллока и принесите его барону Черногриву в Круг Крови в Острогорье."}},
[10722] = {"Встреча в пещере Крыла Тьмы",nil, {"Соберите костюм инспектора из деталей одежды, собранных у различных адептов Культа Змея. В этом костюме отправьтесь на встречу с Колфисом Черной Чешуей и расскажите обо всем, что узнаете, хранителю деревьев Чауну в Вечную рощу в Острогорье."}},
[10723] = {"Горгром Драконоед",nil, {"Отправляйтесь в Камен'мок и добудьте 3 тотема гризли у огров Камен'мока. Поставьте ловушку Черногрива на алтарь Горгрома и убейте Горгрома Драконоеда.nnКогда Горгром будет убит, поставьте 3 тотема гризли возле его туши и вернитесь к Рексару в оплот Громоборцев в Острогорье."}},
[10724] = {"Пленник Камнерогов",nil, {"Убивайте огров из клана Камнерогов, пока не добудете ключ от клетки виверны. Как только добудете его, идите к клетке виверны и подуйте в свисток, чтобы призвать Духа, который отопрет клетку. После освобождения Леокк, вернитесь в Рексару в деревню Громоборцев в Острогорье."}},
[10725] = {"Аметистовое Око в восхищении",nil, {"Принесите свой перстень Аметистового Ока верховному магу Лериде на перевал Мертвого Ветра. Кроме того, необходимо заслужить полное доверие Аметистового Ока."}},
