[10742] = {"Перед выстрелом",nil, {"Протрубите в боевой рог Рексара у алтаря Гока, затем убейте Гока. По выполнении задания вернитесь к Рексару в деревню Громоборцев в Острогорье."}},
[10744] = {"Вести о Победе",nil, {"Вернитесь в цитадель Громового Молота и поговорите с Курдраном Громовым Молотом."}},
[10745] = {"Вести о Победе",nil, {"Возвращайтесь в деревню Призрачной Луны и поговорите с воеводой Ор'барохом."}},
[10747] = {"Дракончики Культа Змея",nil, {"Самия Недомолвка просит вас поймать при помощи сети для ловли дракончиков 10 черных дракончиков Культа Змея. Затем вернитесь к ней в Вечную рощу в Острогорье."}},
[10748] = {"Да сгинет Макснар!",nil, {"От имени друидов Кенарийской экспедиции из Вечной рощи и ради блага местных жителей чащобы Рууан, хранитель деревьев Чаун просит вас убить Макснара Бледную Пасть. Вернитесь к нему в Вечную рощу в Острогорье по выполнении задания."}},
[10749] = {"Яд барона Черногрива",nil, {"Доставьте яд барона Черногрива Рексару в оплот Громоборцев в Острогорье."}},
[10750] = {"Путь Завоевания",nil, {"Отправляйтесь по Пути Завоевания в долине Призрачной Луны. Как только вы произведете предварительную оценку местности, используйте кор'кронское сигнальное ружье, чтобы подать сигнал кор'кронскому наезднику на ветрокрыле.nnПоговорите с воеводой Ор'барохом по выполнении. Если вы потеряете свое сигнальное ружье, можете потребовать новое."}},
[10751] = {"Перерезанный путь",nil, {"Убейте 20 солдат, 10 кавалеристов и 10 заклинателей из лагеря Затмения, а потом используйте кор'кронское сигнальное ружье, чтобы дать знак другому кор'кронскому наезднику на ветрокрыле, о том, что задание выполнено.nnПоговорите с воеводой Ор'барохом в деревне Призрачной Луны, если вы потеряете сигнальное ружье, чтобы получить новое."}},
[10752] = {"Вперед, в Ясеневый лес",nil, {"Поговорите с Раэной Волкопас в Астранааре."}},
[10753] = {"Зачистка пустоши",nil, {"Убейте 4 скверноподданных чешуекрылых змеев, 4 скверноподданных из каньона Кинжальной Пасти и 2 осквернителей."}},
[10754] = {"Вход в цитадель",nil, {"Принесите форму для отливки ключа командиру армии Данату в Оплот Чести на полуострове Адского Пламени."}},
[10755] = {"Вход в цитадель",nil, {"Принесите форму для отливки ключа Назгрелу в Траллмар на полуострове Адского Пламени."}},
[10756] = {"Великий мастер Рохок",nil, {"Принесите форму для отливки ключа Рохоку в Траллмар."}},
[10757] = {"Просьба Рохока",nil, {"Принесите Рохоку 4 слитка оскверненного железа, 2 горсти чародейской пыли и 4 частицы огня в Траллмар на полуострове Адского Пламени."}},
[10758] = {"Жарче, чем в пекле",nil, {"Уничтожьте сквернобота на полуострове Адского Пламени и погрузите внутрь него необожженную форму для отливки ключа. Принесите обожженную форму для отливки ключа Рохоку в Траллмар."}},
[10759] = {"Найти дезертира",nil, {"Найдите Паршаха в Магмовых полях и поговорите с ним."}},
[10760] = {"Развалины Скет'лона",nil, {"Добудьте у темного конклава араккоа и принесите сержанту Каргулу в деревню Призрачной Луны первую, вторую и третью страницы дневника командира Скет'лона."}},
[10761] = {"Найти дезертира",nil, {"Найдите Паршаха в Магмовых полях и поговорите с ним."}},
[10762] = {"Великий Мастер Болтуй",nil, {"Принесите форму для отливки ключа Болтую в Оплот Чести."}},
[10763] = {"Просьба Болтуя",nil, {"Принесите Болтую 4 слитка оскверненного железа, 2 горсти чародейской пыли и 4 частицы огня в Оплот Чести на полуострове Адского Пламени."}},
[10764] = {"Жарче, чем в пекле",nil, {"Уничтожьте сквернобота на полуострове Адского Пламени и закалите на его остатках необожженную форму для отливки ключа. Принесите обожженную форму для отливки ключа Болтую в Оплот Чести."}},
[10765] = {"Война Миров",nil, {"Убейте канцлера Кровавую Листву и Корока Могучего и добудьте приказ Иллидана. Используйте кор'кронское сигнальное ружье, чтобы дать кор'кронскому наезднику на ветрокрыле сигнал о выполнении этого задания.nnПоговорите с воеводой Ор'барохом в деревне Призрачной Луны, если вы потеряете сигнальное ружье, чтобы получить новое."}},
[10766] = {"Точка вторжения: Катаклизм",nil, {"Доложитесь Плекси в Точке вторжения: Катаклизм."}},
[10767] = {"Точка вторжения: Катаклизм",nil, {"Доложитесь Наканси в Точке вторжения: Катаклизм."}},
[10768] = {"Гербовые накидки Иллидари",nil, {"Соберите 10 гербовых накидок иллидари, а потом подайте сигнал наездника на ветрокрыле, с помощью кор'кронского сигнального ружья.nnПоговорите с воеводой Ор'барохом в деревне Призрачной Луны, если вы потеряете сигнальное ружье, чтобы получить новое."}},
[10769] = {"Удар исподтишка",nil, {"Убейте 5 безумных колоссов в личине эльфа крови – иллидари и потом вернитесь к воеводе Ор'бароху в деревню Призрачной Луны в одноименной долине. Верните ему кор'кронское сигнальное ружье."}},
[10770] = {"Угольки",nil, {"Замшельник Древний просит вас убить 8 пламенных бесов, а затем вернуться к нему в чащобу Рууан."}},
[10771] = {"Из пепла",nil, {"Замшельник Древний хочет, чтобы вы отправились в Гарь и посадили семена железнокорня в три кучи плодородной вулканической почвы и вернулись к нему в чащобу Рууан."}},
[10772] = {"Путь Завоевания",nil, {"Отправляйтесь по Пути Завоевания в долине Призрачной Луны. Как только вы произведете предварительную оценку местности, используйте сигнальное ружье клана Громового Молота, чтобы подать сигнал наезднику на грифоне.nnПоговорите с таном Йорегаром в цитадели Громового Молота в долине Призрачной Луны, если потеряете сигнальное ружье, чтобы получить новое."}},
[10773] = {"Перерезанный путь",nil, {"Убейте 20 солдат, 10 кавалеристов из лагеря Затмения и 5 сынов Корока, а потом используйте сигнальное ружье клана Громового Молота, чтобы дать знак другому наезднику на грифоне, о том, что задание выполнено.nnПоговорите с таном Йорегаром в цитадели Громового Молота в долине Призрачной Луны, если потеряете сигнальное ружье, чтобы получить новое."}},
[10774] = {"Великан плюс эльфийка равно...",nil, {"Убейте канцлера Кровавую Листву и Корока Могучего и верните приказ Иллидана. Используйте сигнальное ружье клана Громового Молота, чтобы дать сигнал наезднику на грифоне, что вы закончили выполнение этого задания.nnПоговорите с таном Йорегаром в цитадели Громового Молота в долине Призрачной Луны, если потеряете сигнальное ружье, чтобы получить новое."}},
[10775] = {"Гербовые накидки Иллидари",nil, {"Соберите 10 гербовых накидок Иллидари, а потом подайте сигнал наезднику на грифоне, с помощью сигнального ружья клана Громового Молота. Поговорите с таном Йорегаром в цитадели Громового Молота в долине Призрачной Луны, если потеряете сигнальное ружье, чтобы получить новое."}},
[10776] = {"Удар исподтишка",nil, {"Убейте 5 безумных колоссов, находясь в облике посланника Иллидана, потом вернитесь к тану Йорегару в цитадель Громового Молота в долине Призрачной Луны. Верните ему сигнальное ружье клана Громового Молота."}},
[10777] = {"Тотем Азгара",nil, {"Принесите Паршаху в Магмовые поля военный тотем Скет'лона."}},
[10778] = {"Жезл Лиантэ",nil, {"Заберите ключ Лиантэ у любого из эльфов крови в лагере Затмения, откройте сейф и заберите жезл Лиантэ. Вернитесь с ветвью к Паршаху в Магмовые поля."}},
[10779] = {"Путь охотника",nil, {"Поговорите с охотницей Келлой Тетивой Ночи."}},
[10780] = {"Перья Скет'лона",nil, {"Соберите 8 перьев Скет'лона и принесите их Паршаху в Магмовые поля."}},
[10781] = {"Битва у Кровавого дозора",nil, {"Сразитесь с войском иллидари возле Кровавого дозора и останьтесь в живых!nnВернитесь к А'далу в Шаттрат, если сумеете победить."}},
[10782] = {"Зарядка набалдашника",nil, {"Возьмите в руки Незавершенный Набалдашник. Отправляйтесь к Алтарю Проклятия и используйте его, пока вы находитесь возле Гул'дана. Когда набалдашник зарядится, возвращайтесь к Паршаху в Магмовые Поля."}},
[10783] = {"Барон Черногрив",nil, {"Поговорите с бароном Черногривом в Круге Крови в Острогорье."}},
[10784] = {"Уничтожить лагерь Кровавого Молота!",nil, {"Тор'чанк Двукоготь просит вас убить 10 извергов и 5 чернокнижников из клана Кровавого Молота. Потом вернитесь к нему в оплот Громоборцев в Острогорье."}},
[10785] = {"Ловушка!",nil, {"Отнесите ловушку пламени дракона Рексару в оплот Громоборцев в Острогорье."}},
[10786] = {"Резня в Камен'моке",nil, {"Убейте 5 громил и 3 шаманов из Камен'мока, затем вернитесь к Тор'чанку Двукогтю в оплот Громоборцев в Острогорье."}},
[10788] = {"Возвращение к Талионии",nil, {"Поговорите с Талионией в Луносвете."}},
[10789] = {"Возвращение к Карендину Халгару",nil, {"Поговорите с Карендином Халгаром в Подгороде."}},
[10790] = {"Возвращение к Ган'рулу Кровавому Глазу",nil, {"Найдите Ган'рула Кровавого Глаза в Оргриммаре и поговорите с ним."}},
[10791] = {"Приветствие духа Волка",nil, {"Зажгите обрядовые благовония около свечей в хижине Малуказа и наблюдайте за церемонией. По завершении церемонии поговорите с Малуказом еще раз."}},
[10792] = {"Сжечь Зет'Гор!",nil, {"Убейте батраков из клана Кровавой Глазницы, заберите у них факелы, зажгите факелы в кузне Зет'Гора и забросьте горящие факелы в строения Зет'Гора."}},
[10793] = {"Записи Вал'зарека: Предвестье Войны",nil, {"Найдите Марка Аврелиона, верховного генерала Ша'тар в Кровавом дозоре.nnИщите его в кристаллической тюрьме."}},
[10794] = {"Разбойники Изувеченной Длани",nil, {"Поговорите с Шентулом в Оргриммаре."}},
[10795] = {"Встреча с Доргоком",nil, {"Командир Гефус Камнестен хочет, чтобы вы убили Доргока в лагере Кровавого Молота. Когда выполните поручение, вернитесь к нему в Вечную рощу в Острогорье."}},
[10796] = {"Уничтожить лагерь Кровавого Молота!",nil, {"Лейтенант Файрвезер приказывает вам убить 10 извергов и 5 чернокнижников из клана Кровавого Молота. По выполнении вернитесь к ней в Вечную рощу в Острогорье."}},
[10797] = {"Благоволение гронна",nil, {"Отнесите благоволение Горгрома командиру Гефусу Камнестену в Вечную рощу в Острогорье."}},
[10798] = {"Визит к барону",nil, {"Поговорите с бароном Черногривом насчет ловушки. Он живет в Круге Крови в Острогорье."}},
[10799] = {"Прогулка в Беспокойную лощину",nil, {"Барон Черногрив просит вас принести ему 7 ядовитых желез землероев. Вернитесь к нему в Круг Крови в Острогорье по выполнении."}},
[10800] = {"Спи спокойно, гронн",nil, {"Барон Черногрив хочет, чтобы вы украли мешок Груллока, а затем вернулись к нему в Круг Крови в Острогорье."}},
[10801] = {"Ловушка!",nil, {"Отнесите ловушку пламени дракона командиру Гефусу Камнестену в Вечную рощу в Острогорье."}},
[10802] = {"Горгром Драконоед",nil, {"Отправляйтесь в Камен'мок и добудьте 3 тотема гризли у огров Камен'мока. Поставьте ловушку Черногрива на алтарь Горгрома и убейте Горгрома Драконоеда.nnКогда Горгром будет убит, поставьте 3 тотема гризли возле его туши и вернитесь к командиру Гефусу Камнестену в Вечную рощу в Острогорье."}},
[10803] = {"Резня в Камен'моке",nil, {"Убейте 5 громил и 3 шаманов Камен'мока. Затем вернитесь к лейтенанту Файрвезер в Вечную рощу в Острогорье."}},
[10804] = {"Доброта",nil, {"Накормите 8 взрослых драконов Пустоты по просьбе Морденая на Полях Крыльев Пустоты."}},
[10805] = {"Резня в логове Груула",nil, {"Барон Черногрив хочет, чтобы вы с друзьями перебили 10 головорезов, 5 боевых магов, 5 опустошителей из клана Камнерогов и их предводителя, Фингрома. Когда свершите этот подвиг, возвращайтесь к нему в Круг Крови в Острогорье."}},
[10806] = {"Перед выстрелом",nil, {"Наденьте перстень Черногрива у алтаря Гока, затем убейте Гока. Вернитесь к барону Черногриву в Круг Крови в Острогорье."}},
[10807] = {"Сломленные из племени Пеплоустов",nil, {"Убейте для Варена Исправителя 3 укротителей, 4 воинов и 6 шаманов из племени Пеплоустов.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10808] = {"Крах темного конклава",nil, {"Убейте ритуалистов Темного конклава и используйте посох Паршаха, чтобы прервать ритуал призыва. По выполнении задания вернитесь к Паршаху в Магмовые поля."}},
[10809] = {"Разыскивается: повелитель воргов Крууш",nil, {"Убейте повелителя воргов Крууша и принесите его голову капитану Мрачному Вою на Заставу Хребтолома."}},
[10810] = {"Поврежденная маска",nil, {"Отнесите поврежденную маску О'Малли Быстроцапу в чащобу Рууан."}},
[10811] = {"Поиски Нельтараку",nil, {"Найдите Нельтараку, предводителя рода Крыльев Пустоты."}},
[10812] = {"Тайная маска",nil, {"Отнесите таинственную маску владыке леса Антелариону в чащобу Рууан."}},
[10813] = {"Очи Гриллока",nil, {"Используйте кристалл Зеззака, чтобы захватить Око Гриллока, затем приблизьтесь к котлу Зеззака, чтобы тот смог извлечь Око. После выполнения задания верните Зеззаку кристалл."}},
[10814] = {"История Нельтараку",nil, {"Поговорите с Нельтараку и выслушайте его историю."}},
[10815] = {"Записи Вал'зарека: Предвестье Войны",nil, {"Найдите Марка Аврелиона, верховного генерала Ша'тар, в Кровавом дозоре."}},
[10816] = {"Восстановление Святых земель",nil, {"Убейте для воздаятеля Алуумена 8 душегубов, 8 избранных и 4 темнопрядов клана Призрачной Луны.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10817] = {"Великое воздаяние",nil, {"Убейте для Варена Исправителя 8 душегубов, 8 избранных и 4 темнопрядов клана Призрачной Луны.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10818] = {"Барон Черногрив желает вас лицезреть",nil, {"Вы получили приглашение от барона Черногрива. Он желает поговорить с вами о каком-то важном деле. Отправляйтесь к нему в Круг Крови в Острогорье."}},
[10819] = {"Респиратор Искаженных",nil, {"Наденьте респиратор Верных Скверне и воспользуйтесь переговорным устройством Легиона в лагере Легиона: Злоба."}},
[10820] = {"Обмануть врага",nil, {"Убейте 4 рабочих Кузни Рока и 4 инженеров Кузни Рока."}},
[10821] = {"Уволена!",nil, {"Убейте Вестницу Рока и доложите об этом Владыке леса Антелариону в Вечную рощу."}},
[10822] = {"Перстень Ярости Солнца",nil, nil},
[10823] = {"Больше перстней Ярости Солнца",nil, nil},
[10824] = {"Перстни Ярости Солнца",nil, {"Принесите 10 перстней армии Ярости Солнца боевому магу Вияре в святилище Звезд.nnВыполнение заданий Провидцев отрицательно скажется на вашей репутации среди Алдоров."}},
[10825] = {"Тайна сферы",nil, {"Доставьте сферу Гришны хранителю деревьев Чауну в чащобу Рууан в Острогорье."}},
[10826] = {"Знаки Саргераса",nil, {"Принесите 10 знаков Саргераса предвестнику Саронену в Долину Призрачной Луны.nnВыполнение заданий Алдоров отрицательно скажется на вашей репутации среди Провидцев."}},
[10827] = {"Больше знаков Саргераса",nil, nil},
[10828] = {"Знак Саргераса",nil, nil},
[10829] = {"Древоствол должен знать!",nil, {"Хранитель деревьев Чаун просит вас найти Древоствола, древо мудрости, обитающее в лесу Ворона в Острогорье, и поговорить с ним."}},
[10830] = {"Экзорцизм для деревьев",nil, {"Найдите 5 гришнатских сфер и 5 перьев лютого ворона и превратите их в перья экзорцизма. При помощи этих перьев изгоните 5 духов кои-кои из листобородов леса Ворона. Как только закончите, вернитесь к Древостволу в лес Ворона в Острогорье."}},
[10831] = {"Обучение шитью из луноткани",nil, {"Возьмите отрез прочной ткани Пустоты, встаньте в лунный колодец Кенарийского приюта и изготовьте изначальную луноткань. Готовую ткань отнесите Назмаре Песне Луны в Нижний город Шаттрата.nnВНИМАНИЕ! Вы можете специализироваться только на одном виде ткани."}},
[10832] = {"Обучение шитью из чароткани",nil, {"Принесите образец сущности призрака Пустоты Гидже Чароплетке в Нижний город Шаттрата.nnВНИМАНИЕ! Вы можете специализироваться только на одном виде шитья."}},
[10833] = {"Обучение шитью из тенеткани",nil, {"Укрепите связь с Алтарем Теней при помощи кристалла Глубоких Теней, после чего вернитесь к Андриону Темнопряду в Нижний город Шаттрата.nnВНИМАНИЕ! Вы можете специализироваться только на одном виде шитья."}},
[10834] = {"Гриллок \"Пустой Глаз\"",nil, {"Убейте Гриллока Пустого Глаза и принесите Зеззаку на Заставу Хребтолома повязку на глаз Гриллока."}},
[10835] = {"Аптекарь Антонивич",nil, {"Доставьте послание Албрека аптекарю Антонивичу в Траллмар."}},
[10836] = {"Переполох в крепости Драконьей Пасти",nil, {"Убейте 15 орков из клана Драконьей Пасти для Нельтараку.nnЗасчитывается орк любого ранга."}},
[10837] = {"На Кряж Крыльев Пустоты!",nil, {"Соберите для Нельтараку 12 кристаллов Лозы Пустоты на Кряже Крыльев Пустоты."}},
[10838] = {"Демонический кристалл",nil, {"Отнесите демонический кристалл в Яму Адского Пламени, подождите, пока произойдет соединение, и примите сообщение.nnВерните демонический кристалл и его сообщение аптекарю Антонивичу в Траллмар."}},
[10839] = {"Гнездовье Скит: Темный камень Терокка",nil, {"Очистите темный камень Терокка в гнездовье Скит при помощи жезла очищения, после чего вернитесь к Киррику в караван беженцев в Костяных пустошах."}},
[10840] = {"Гробница Света",nil, {"Войдите в Гробницу Света и убейте там 10 эфириалов-пустомантов и 10 эфириалов-расхитителей, после чего вернитесь к верховному жрецу Орглуму в Караван беженцев."}},
[10841] = {"Мстительный предвестник",nil, {"[PH] Активировать устройство."}},
[10842] = {"Мстительный предвестник",nil, {"Установите могильную реликвию дренеев возле их древнего алтаря в Гробнице Света, как вам велел верховный жрец Орглум.nnЗащитите стража гробниц и убейте мстительного предвестника. После завершения ритуала поговорите с дренеем-предком возле алтаря."}},
[10843] = {"С незапамятных времен...",nil, {"Чтобы получить признание Леорокса из Мок'Натала, убейте по его приказу Гноша Брогната, вождя огров, на заставе Камнерогов. По выполнении вернитесь к Леороксу в деревню Мок'Натал в Острогорье, или не возвращайтесь вовсе."}},
[10844] = {"Лагерь Легиона: Злоба",nil, nil},
[10845] = {"Убить мать драконов",nil, {"Леорокс посылает вас убить Жуткокрылижу. Вернитесь к нему в деревню Мок'Натал, как только умудритесь ее убить."}},
[10846] = {"Как понять Мок'Натал",nil, {"Чтобы начать понимать Мок'Натал, убейте Веха, предводителя араккоа из племени Вех'нир. Затем вернитесь к Леороксу в деревню Мок'Натал в Острогорье."}},
[10847] = {"Глаза Скеттиса",nil, {"Принесите Око Рескка и Око Шиенор Рилаку Освобожденному в Нижний город Шаттрата."}},
[10848] = {"Гнездовье Рейз: неживое зло",nil, {"Убейте 4 духа Когтя Смерти, 4 кричащих духа и 2 духа ворона, после чего вернитесь к Киррику в Караван беженцев."}},
[10849] = {"Поиски Киррика",nil, {"Отнесите послание Рилака Киррику в караван беженцев в Костяных пустошах."}},
[10850] = {"Газ хаоса в двигателе",nil, {"Вы находите ответстие, через которое в сквернобот загружается топливо."}},
[10851] = {"Тотем врага моего",nil, {"Добудьте 5 тотемов клана Камнерогов для вызывателя духов Догара из деревни Мок'Натал в Острогорье."}},
[10852] = {"Пропавшие друзья",nil, {"Найдите 12 друзей Итана в гнездовье Скит и вернитесь к нему в Караван беженцев."}},
[10853] = {"Призыв духов",nil, {"Вызыватель духов Догар просит вас при помощи тотемов вызова духов собрать 8 духов младших драконов Пустоты и принести ему в деревню Мок'Натал в Острогорье."}},
[10854] = {"Силы Нельтараку",nil, {"Освободите для Нельтараку 5 порабощенных драконов Крыльев Пустоты."}},
[10855] = {"Сквернобот – нет, спасибо!",nil, {"Заберите 5 единиц конденсированного газа Пустоты у ган'аргов-анжинеров на базе Легиона: Забвение, к северо-западу от Штормовой Вершины, и загрузите их в ближайший бездействующий сквернобот. После завершения задачи возвращайтесь к ловчему Пустоты Наутису."}},
[10856] = {"Наилучшая защита",nil, {"Ловчий Пустоты Наутис просит вас убить 12 вестников гнева на базе Легиона: Геенна к северу от Штормовой Вершины."}},
[10857] = {"Телепортируй это!",nil, {"Ловчий Пустоты Наутис просит вас применить жезл ментального вмешательства против мо'аргов, находящихся в лагере Легиона: Геенна и использовать их способность взорвать телепорт для того, чтобы уничтожить три телепорта в лагере Легиона."}},
[10858] = {"Каринаку",nil, {"Найдите Каринаку в крепости клана Драконьей Пасти."}},
[10859] = {"Собрать шары",nil, {"Вызыватель духов Дохгар просит вас при помощи тотема для собирания сфер собрать 15 световых сфер братства Разаани. По выполнении вернуться к нему в деревню Мок'Натал в Острогорье."}},
[10860] = {"Лакомства Мок'Натал",nil, {"Сестра Вара дает вам список продуктов для приготовления ее двух любимых блюд. В списке среди прочего 3 ребра ящера и 3 куска змеиного мяса. Принесите их ей в деревню Мок'Натал в Острогорье."}},
[10861] = {"Гнездовье Литик: превентивный удар",nil, {"Разбейте проклятые яйца в Гнездовье Литик. Освободите 3 птенцов и убейте 3 злобных птенцов, после чего вернитесь к Киррику в Караван беженцев."}},
[10862] = {"Сдаться Орде",nil, {"Найдите Рилака Освобожденного в Шаттрате и потребуйте, чтобы он и его народ сдались Орде, как велел вам Рокаг."}},
[10863] = {"Тайны араккоа",nil, {"Найдите Рилака Освобожденного в Шаттрате и попытайтесь выведать у него несколько секретов араккоа по просьбе леди Дены Кеннеди."}},
[10864] = {"Урожай душ",nil, {"Убейте 20 орков из клана Изувеченной Длани, затем возвращайтесь к аптекарю Антонивичу в Траллмар."}},
[10865] = {"Доложить Леороксу!",nil, {"Вызыватель духов Догар просит вас рассказать Леороксу о том, что затеяли эфириалы из лагеря Разаана. Его можно найти в деревне Мок'Натал в Острогорье."}},
[10866] = {"Зулухед Измученный",nil, {"Убейте Зулухеда Измученного и заберите Ключ Зулухеда. Используйте его на цепях Зулухеда, чтобы освободить Каринаку."}},
[10867] = {"Ответ будет только один",nil, {"Леорокс умоляет вас добыть собрание душ и передать их вызывателю духов Дохгару в деревню Мок'Натал в Острогорье."}},
[10868] = {"Тропа войны араккоа",nil, {"Убейте 14 шиенорских когантов и 6 шиенорских колдунов, после чего вернитесь к советнице Файле в форт Камнеломов."}},
[10869] = {"Уменьшить стаю",nil, {"Убейте 14 шиенорских когантов и 6 шиенорских колдунов, после чего вернитесь к лейтенанту Булыжному Молоту в бастион Аллерии."}},
[10870] = {"Союзник Крыльев Пустоты",nil, {"Позвольте Каринаку отнести вас к Морденаю на Поля Крыльев Пустоты."}},
[10871] = {"Союзник Крыльев Пустоты",nil, {"Позвольте Каринаку отнести вас к Морденаю на Поля Крыльев Пустоты."}},
[10872] = {"Зулухед Измученный",nil, {"Убейте Зулухеда Измученного и заберите Ключ Зулухеда. Используйте его на цепях Зулухеда, чтобы освободить Каринаку."}},
[10873] = {"Захваченные в ночи",nil, {"Пойдите на Гряду Паутины Пустоты и освободите там 6 опутанных воинов Ша'тар, после чего вернитесь к разведчику Наврину на заставу Ша'тар."}},
[10874] = {"Гнездовье Шалас: сигнальные огни",nil, {"Потушите четыре сигнальных огня в Гнездовье Шалас по просьбе Киррика, после чего вернитесь к нему в Караван беженцев."}},
[10875] = {"Донесение Назгрелу",nil, {"Поговорите с Назгрелом в Траллмаре."}},
[10876] = {"Вступить в цитадель",nil, {"Убейте командира соединения Горакса, положите на его труп Вызов Орды, убейте прислужника Каргата и принесите голову прислужника Каргата Назгрелу в Траллмар."}},
[10877] = {"Реликвия Ужаса",nil, {"Отыщите Реликвию Ужаса к востоку от Брошенного каравана, после чего вернитесь к Оакуну."}},
[10878] = {"До наступления тьмы",nil, {"Убейте 8 кабалистов-рыскателей, 4 кабалистов-чаропрядов и 2 кабалистов-посвященных, после чего вернитесь к Мекеде в Караван пустошей."}},
[10879] = {"Нападение Скеттиса",nil, {"Отбейте атаку, а потом поговорите с Рилаком."}},
[10880] = {"Распоряжения заговорщиков",nil, {"Принесите кабалистические приказы Мекеде в караван беженцев в лесу Тероккар."}},
[10881] = {"Гробница Тени",nil, {"Войдите в Гробницу Тени, что к западу от Каравана беженцев и найдите там пелерину Арунена, молот К'алена и свиток Аталора, после чего вернитесь к Мекеде в Караван беженцев."}},
[10882] = {"Вестник Рока",nil, {"Отправьтесь в спутник Крепости Бурь Аркатрац и убейте там предвестника Скайрисса, после чего вернитесь к А'далу на террасу Света в Шаттрат."}},
[10883] = {"Ключ Урагана",nil, {"Поговорите с А'далом в Шаттрате."}},
[10884] = {"Испытание наару: милосердие",nil, {"А'дал из Шаттрата просит добыть неиспользованный топор палача из Разрушенных залов цитадели Адского Пламени.nnЭто задание выполняется только в героическом режиме."}},
[10885] = {"Испытание наару: сила",nil, {"А'дал из Шаттрата просит добыть трезубец Калитреша и сущность Бормотуна.nnЭто задание выполняется только в героическом режиме."}},
[10886] = {"Испытание наару: упорство",nil, {"А'дал из Шаттрата просит спасти Милхауса Манашторма из Аркатраца, что в Крепости Бурь.nnЭто задание выполняется только в героическом режиме."}},
[10887] = {"Побег из гробницы",nil, {"Помогите Акуно добраться до Каравана беженцев, после чего поговорите там с Мекедой."}},
[10888] = {"Испытание наару: Магтеридон",nil, {"А'дал из Шаттрата просит вас убить Магтеридона."}},
[10889] = {"Обратно в Шаттрат",nil, {"Доберитесь до Шаттрата и поговорите там с Рилаком."}},
[10891] = {"Имперские латы",nil, {"Поговорите с Деротайном Грязеглотом в Прибамбасске."}},
[10892] = {"Имперские латы",nil, {"Поговорите с Деротайном Грязеглотом в Прибамбасске."}},
[10893] = {"За всем стоит Длиннохвостка",nil, {"Устраните Драаку Длиннохвостку, предводительницу культистов Змея в туннеле Драконьего черепа. Когда сделаете дело, доложите дозорному Лунному Сумраку в туннеле Драконьего черепа в Острогорье."}},
[10894] = {"Страж Драконьего черепа",nil, {"Передайте записку о встрече дозорному Лунному Сумраку у входа в туннель Драконьего черепа в Острогорье."}},
[10895] = {"Сжечь Зет'Гор!",nil, {"Установите дымовые маяки у каждой из башен Зет'Гора и возвращайтесь к командиру звена Даби'ри в лагерь экспедиции."}},
[10896] = {"Зараженные защитники",nil, {"Убейте 25 лесных клещей и вернитесь к Лакотаю в Караван беженцев. Клещи живут в телах гниющих лесных гневней и зараженных корнеходов."}},
[10897] = {"Мастер-изготовитель зелий",nil, {"Отправляйтесь в Ботанику в Крепости Бурь и добудьте у старшего ботаника Фрейвинна \"Полевой справочник ботаника\". Принесите его Лоранне Тар'велл вместе с 5 отменными лечебными зельями, 5 отменными зельями маны и 5 большими зельями спокойного сна.nn<ВНИМАНИЕ!> Вы можете избрать только одну алхимическую специальность!"}},
[10898] = {"Небокрыл",nil, {"Проводите Небокрыла и вернитесь к Рилаку Освобожденному в Шаттрат."}},
[10899] = {"Мастер трансмутации",nil, {"Заревхи, находящийся Штормовой Вершине в Пустоверти просит вас принести ему 4 единицы изначальной мощи.nnВНИМАНИЕ! Вы можете выбрать только одну алхимическую специализацию."}},
[10900] = {"Медальон Вайш",nil, nil},
[10901] = {"Дубина Кардеша",nil, {"Принесите печати земли и огня Скартису Еретику в Узилище (героический уровень сложности) в резервуар Кривого Клыка."}},
[10902] = {"Мастер эликсиров",nil, {"Отправляйтесь в Черные топи в Пещерах времени и добудьте 10 кристаллов сути бесконечности с повелителей и хранительниц временных разломов. Затем отнесите кристаллы, 5 хороших эликсиров защиты, 5 эликсиров власти и 5 хороших эликсиров ловкости Лорокиму в Нижний город Шаттрата.nnВНИМАНИЕ! Вы можете специализироваться только на одном виде алхимии."}},
[10903] = {"Возвращение в Оплот Чести",nil, {"Поговорите с ассистентом Клату. Его можно найти в таверне Оплота Чести."}},
[10904] = {"Боеприпасы Скверны",nil, {"Друид из Вечной рощи просит вас принести ему 5 ядер пушек Скверны. По выполнении при помощи сигнального огня друидов призовите друида из Вечной рощи к Вратам Смерти в Острогорье."}},
[10905] = {"Мастер зелий",nil, {"Поговорите с Лоранной Тар'велл в Кенарийском приюте в Зангартопи.nnВНИМАНИЕ! Вы можете выбрать лишь одно из направлений алхимии!"}},
[10906] = {"Мастер эликсиров",nil, {"Поговорите с Лорокимом из Нижнего города Шаттрата.nnВНИМАНИЕ!Вы можете выбрать лишь одно из направлений алхимии!"}},
[10907] = {"Мастер трансмутации",nil, {"Поговорите с Заревхи из Штормовой Вершины Пустоверти.nnВНИМАНИЕ!Вы можете выбрать лишь одно из направлений алхимии!"}},
[10908] = {"Поговорите с Рилаком Освобожденным",nil, {"Поговорите с Рилаком Освобожденным в Нижнем городе Шаттрата по просьбе Киррика Пробужденного."}},
[10909] = {"Духи Скверны",nil, {"Задействуйте по просьбе Клату из Оплота Чести реликвию анахорета на Заставе Горгаза, к юго-западу от Оплота Чести, и убивайте берсерков из клана Изувеченной Длани рядом с этой реликвией. После этого уничтожьте 10 освобожденных духов Скверны, чтобы отомстить за полковника Джулса."}},
[10910] = {"Врата Смерти",nil, {"Владыка леса Антеларион поручил вам отправиться к Вратам Смерти. Как только прибудете туда, воспользуйтесь сигнальным огнем друидов, чтобы призвать друида из Вечной рощи и поговорить с ним."}},
[10911] = {"Открыть огонь!",nil, {"Используйте боеприпасы, наполненные силой природы для установления контроля над пушками Скверны Врат Смерти. При помощи их разрушьте и северный, и южный порталы. Затем при помощи сигнального огня друидов призовите друида из Вечной рощи и доложите ему об успешном выполнении операции."}},
[10912] = {"Псарь",nil, {"Друид Вечной рощи говорит, что последнее, что вам надо сделать - это убить Балмона Псаря. Потом вернитесь в Вечную рощу в Острогорье и доложите об успехе Владыке леса Антелариону."}},
[10913] = {"Недостойные похороны",nil, {"Сожгите 8 убитых аукенайских воинов и 8 павших поборников Ша'тар по поручению командира Ра'ваджа. Командира можно найти на заставе Ша'тар в Костяных пустошах в лесу Тероккар."}},
[10914] = {"Требуется герой",nil, {"Убейте 12 аукенайских посвященных и 5 аукенайских прорицателей по поручению командира Ра'ваджа. Командира можно найти на заставе Ша'тар в Костяных пустошах в лесу Тероккар."}},
[10915] = {"Падший экзарх",nil, {"Уничтожьте содержимое аукенайского гроба по поручению командира Ра'ваджа. Командира можно найти на заставе Ша'тар в Костяных пустошах в лесу Тероккар."}},
[10916] = {"Откопать благословенные четки",nil, {"Разыщите дренейские четки и принесите их ассистенту Клату в Оплот Чести."}},
[10917] = {"Затруднительное положение изгнанника",nil, {"Принесите 30 перьев араккоа Векаксу в Нижний город Шаттрата."}},
[10918] = {"Больше перьев",nil, nil},
[10919] = {"Собачья радость",nil, {"Шар-Ик выжидающе на вас смотрит"}},
[10920] = {"За павших",nil, {"Убейте 20 жуткопалых черных вдов и вернитесь к воздаятелю Хайлену на западную сторону Гряды Пустопутов."}},
[10921] = {"Тероккарантул",nil, {"Убейте Тероккарантула и вернитесь к воздаятелю Хайлену на западную сторону Гряды Пустопутов."}},
[10922] = {"Костяные раскопки",nil, {"Проводите Летолла и его команду и защищайте их, пока они будут копать. После этого вернитесь к Дворфовитцу на заставу Ша'тар."}},
[10923] = {"Зло приближается",nil, {"Отнесите Реликвию Ужаса на Перекопанный курган, как вам велел Оакун. В круге убивайте аукенайских вестников смерти и прорицателей, пока не соберете 20 роковых черепов. Затем найдите круг призыва Призрачного Кургана, при помощи Реликвии Ужаса призовите Жутуса Проклятого, убейте его и вернитесь к Оакуну."}},
[10924] = {"Кровная м... есть!",nil, {"Принесите 8 фиалов крови прыгуаны-ловца Саб'аофу, находящемуся в Космовороте."}},
[10925] = {"Зло приближается",nil, nil},
[10926] = {"Обратно в лагерь Ша'тар",nil, {"Вернитесь в лагерь Ша'тар и поговорите с разведчиком Наврином по воздаятеля Хайлена."}},
[10927] = {"Убийство пауков",nil, {"Часовая Шепот Луны у туннеля между Зангартопью и Острогорьем просит вас убить 6 пещерных пауков и доложить о выполнении командиру Тень Небес в Сильванааре."}},
[10928] = {"Убийство пауков",nil, {"Рубака Грак у туннеля между Зангартопью и Острогорьем просит вас убить 6 пещерных пауков и сообщить о выполнении задания Тор'чанку в оплоте Громоборцев."}},
[10929] = {"Барабанный бой",nil, {"Принесите 8 трупов взрослых просеивателей костей Дворфовитцу в лагерь Ша'тар в Костяных пустошах."}},
[10930] = {"Большой костяной червь",nil, {"Принесите органы гигантского костяного червя Дворфовитцу в лагерь Ша'тар в Костяных пустошах.nnЗаодно верните ему барабан Фампер."}},
[10931] = {"Возвращение в Экзодар",nil, {"Поговорите с Кедмосом в Экзодаре."}},
[10932] = {"Возвращение в Экзодар",nil, {"Поговорите с Кедмосом в Экзодаре."}},
[10933] = {"Возвращение в Луносвет",nil, {"Поговорите с Альдрэ из Луносвета."}},
[10934] = {"Возвращение в Луносвет",nil, {"Поговорите с Альдрэ из Луносвета."}},
[10935] = {"Экзорцизм над полковником Джулсом",nil, {"Поговорите с анахоретом Берада. Используйте благословенные четки, чтобы провести ритуал. После окончания ритуала поговорите со спасенным полковником Джулсом. Возвращайтесь к ассистенту Клату."}},
[10936] = {"Тебя ищет Троллебой",nil, {"Ассистент Клату сообщает, что вас искал командир армии Данат Троллебой. Поговорите с Данатом в казармах Оплота Чести на полуострове Адского Пламени."}},
[10937] = {"Перемастерить мастера",nil, {"Убейте военного инструктора Зурока, и как можно скорее. Это приказ боевого командира командира армии Даната Троллебоя. После выполнения задания возвращайтесь к Данату в Оплот Чести на полуострове Адского Пламени."}},
[10938] = {"Колода карт Новолуния: Благословения",nil, {"Принесите колоду Благословений на ярмарку Новолуния."}},
[10939] = {"Колода карт Новолуния: Вихри",nil, {"Принесите карты Вихрей на ярмарку Новолуния."}},
[10940] = {"Колода карт Новолуния: Ярость",nil, {"Принесите карты Ярости на ярмарку Новолуния."}},
[10941] = {"Колода карт Новолуния: Безумие",nil, {"Принесите колоду Безумия на ярмарку Новолуния."}},
[10942] = {"Детская неделя",nil, {"Дуньте в эльфийскую детскую свистульку. Присматривайте за сиротой в течение всей Детской недели."}},
[10943] = {"Детская неделя",nil, {"Продудите в дренейскую детскую свистульку. Присматривайте за сиротой в течение всей Детской недели."}},
[10944] = {"Тайна под угрозой",nil, {"Отправляйтесь в Клеть Стражницы в Долину Призрачной Луны и поговорите с Акамой."}},
[10945] = {"Хч'уу и народ грибов",nil, {"Отведите вашу подопечную Саландрию, в Спореггар в Зангартопь. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом. Затем поговорите с Хч'уу."}},
[10946] = {"Коварство Пеплоустов",nil, {"Отправляйтесь в Крепость Бурь и, облачившись в клобук Пеплоуста, убейте Ал'ара. Затем возвращайтесь к Акаме в Долину Призрачной Луны."}},
[10947] = {"Артефакт из прошлого",nil, {"Отправляйтесь в пещеры Времени в Танарисе и вступите в битву за гору Хиджал. Сразите Лютого Хлада и принесите амулет из прошлого Акаме в долину Призрачной Луны."}},
[10948] = {"Пленная душа",nil, {"Отправляйтесь в Шаттрат и расскажите А'далу о просьбе Акамы."}},
[10949] = {"Вход в Черный храм",nil, {"Отправляйтесь ко входу в Черный храм в Долине Призрачной Луны и поговорите с Кси'ри."}},
[10950] = {"Аукиндон и Ритуальный Круг",nil, {"Отведите свою подопечную, сиротку Дорнаа, к камню встречи у Ритуального Круга в центра Аукиндона. Аукиндон находится в центре Костяных пустошей, что в лесу Тероккар. Обязательно позовите Дорнаа, если по прибытии ее не окажется рядом."}},
[10951] = {"Путешествие к Темному порталу",nil, {"Отведите свою подопечную, сиротку Саландрию, на Ступени Судьбы, к Темному порталу полуострова Адского Пламени. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом."}},
[10952] = {"Путешествие к Темному порталу",nil, {"Отведите свою подопечную, сиротку Дорнаа, на Ступени Судьбы, к Темному порталу полуострова Адского Пламени. Обязательно призовите Дорнаа, если по прибытии ее не будет рядом."}},
[10953] = {"Путешествие к Трону Стихий",nil, {"Отведите свою подопечную, сиротку Саландрию, к Трону Стихий в Награнд. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом. Затем поговорите с повелительницей стихий Шарвак."}},
[10954] = {"Джил находится в Небесном лагере!",nil, {"Отведите свою подопечную, сиротку Дорнаа, в Небесный лагерь в Награнде. Обязательно позовите Дорнаа, если по прибытии ее не окажется рядом. Затем поговорите с Джилом."}},
[10955] = {"Мортис Шелест Крыла",nil, {"Найдите Мортиса Шелеста Крыла в Кенарийском оплоте в Зангартопи."}},
[10956] = {"Трон наару",nil, {"Отведите свою подопечную, сиротку Дорнаа, к О'росу, который находится у подножия Трона наару в Экзодаре. Обязательно позовите Дорнаа, если по прибытии ее не окажется рядом.nnНе забывайте пользоваться картой, чтобы сориентироваться в столице."}},
[10957] = {"Искупление Пеплоустов",nil, {"Возвратите Акаме власть над его душой, сразив Тень Акамы в Черном храме. Затем возвращайтесь в провидцу Канеи."}},
[10958] = {"Найти Пеплоустов",nil, {"Найдите Служителей Смерти Акамы в Черном храме."}},
[10959] = {"Судьба Предателя",nil, {"Провидец Канеи хочет, чтобы вы сразили Иллидана в Черном храме."}},
[10960] = {"Когда я вырасту...",nil, {"Отведите свою подопечную, сиротку Саландрию, к наставникам паладинов на площадь Странников в Луносвет. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом. Затем поговорите с леди Лиадрин, матриархом рыцарей крови.nnНе забывайте пользоваться картой, чтобы сориентироваться в городе, а найти наставников паладинов вам помогут стражи."}},
[10961] = {"Зелье пробуждения",nil, {"Принесите 10 топлянников, 10 древних лишайников и 10 цветков сияния грез Мортису Шелесту Крыла в Кенарийский оплот."}},
[10962] = {"Время посетить Пещеры",nil, {"Отведите свою подопечную, сиротку Дорнаа, к дракону на возвышении в центре пещер Времени, Заладорму. Обязательно позовите Дорнаа, если по прибытии ее не окажется рядом.nnКупите ей игрушечного дракона у начальника снабжения Хранителей Времени, Алурми, в конце входного туннеля."}},
[10963] = {"Время посетить Пещеры",nil, {"Отведите свою подопечную, сиротку Саландрию, к дракону на возвышении в центре пещер Времени, Заладорму. Обязательно позовите Саландрию, если по прибытии ее не окажется рядом.nnКупите ей игрушечного дракона у начальника снабжения Хранителей Времени, Алурми, у входа в туннель."}},
[10964] = {"Пробуждение спящего",nil, {"Разбудите Клинтара Странника Снов при помощи зелья Пробуждения. Когда он придет в себя, поговорите с ним."}},
[10965] = {"Не просто сон",nil, {"Охраняйте дух Клинтара Странника Снов, пока он будет собирать реликвии Авианы. Потом поговорите с хранителем снов Луросой."}},
[10966] = {"Возвращение в приют",nil, {"Отведите свою подопечную, сиротку Дорнаа, обратно в приют Шаттрата, вернув дренейскую детскую свистульку попечительнице сирот Миле в Нижнем Городе."}},
[10967] = {"Возвращение в приют",nil, {"Отведите свою подопечную обратно в приют Шаттрата, вернув эльфийскую детскую свистульку попечительнице сирот Миле в Нижнем Городе."}},
[10968] = {"Зов предсказателя",nil, {"Отведите свою подопечную, сиротку Дорнаа, к предсказателю Нобундо в Кристальный зал Экзодара. Обязательно позовите Дорнаа, если по прибытии ее не окажется рядом.nnНе забывайте пользоваться картой, чтобы сориентироваться в городе."}},
[10969] = {"В поисках Амира",nil, {"Найдите командира Амира на заставе стражей Протектората."}},
[10970] = {"Миссия милосердия",nil, {"Раздобудьте ключ от найденной темницы Эфириума для командира Амира, находящегося на заставе стражей Протектората в Пустоверти."}},
[10971] = {"Секреты Эфириума",nil, {"Командир Амир, находящийся на заставе стражей Протектората в Пустоверти, просит вас принести ему бирки пленников Эфириума."}},
[10972] = {"Каталог бирок пленников Эфириума",nil, {"Мне нужно больше бирок, Монд. Мне удалось обнаружить, что это не просто способ составить каталог узников, но также указание на различные точки размещения!nnКогда шифр будет раскрыт, мы сможем точно узнать, где содержатся наши союзники, и освободить их с минимальными потерями."}},
[10973] = {"Тысяча миров",nil, {"Командир Амир, находящийся на заставе стражей Протектората в Пустоверти, хочет, чтобы вы принесли ему 5 ключей от тюрьмы братства Эфириум."}},
[10974] = {"Палаты стазиса Баш'ира",nil, {"Используйте табличку-ключ Альфа Эфириума, чтобы открыть палату стазиса Альфа в Лагере Баш'ира в Острогорье.nnПредоставьте свидетельства из палаты Альфа проекции командира Амира в Лагере Баш'ира."}},
[10975] = {"Очищение палат Баш'ира",nil, {"Мы в безвыходном положении, Бахусий. Собранные тобой данные дают нам достаточно информации для того, чтобы спасти наших союзников, содержащихся в меньших тюрьмах Пустоверти, однако мы должны пойти дальше и изничтожить более крупную угрозу со стороны Братства Эфириум здесь, в лагере Баш'ира.nnПрисоединись к сражению против Эфириума! Принеси мне ключи от тюрьмы Эфириума, и я сделаю для тебя ключи от палат стазиса Эфириума, чтобы можно было открыть палаты стазиса Баш'ира."}},
[10976] = {"Знак короля нексуса",nil, {"Проекция командира Амира, находящеяся в Лагере Баш'ира в Острогорье просит вас принести ему знак короля нексуса."}},
[10977] = {"Палаты стазиса в Гробницах маны",nil, {"Проекция командира Амира, находящаяся в лагере Баш'ира в Острогорье просит вас использовать ключ от палат стазиса Гробниц маны, чтобы открыть палату стазиса внутри Гробниц маны Аукиндона. nnЭто задание можно выполнить только в режиме героической сложности."}},
[10978] = {"Возвращение к Мортису Шелесту Крыла",nil, {"Отнесите реликвии Авианы Мортису Шелесту Крыла в Кенарийский оплот в Зангартопи."}},
[10979] = {"В Вечную рощу",nil, {"Поговорите с Арторн Песней Ветра из Вечной рощи, что в Острогорье."}},
[10980] = {"Книга Ворона",nil, {"Усильте свое зрение при помощи ока летучего ската и камня Провидца и разглядите призраков сторонников культа Ворона. Отыщите на Вершине Смерча старейшину Сай'ккала и расспросите его про книгу Ворона. Затем возвращайтесь к Арторн Песне Ветра."}},
[10981] = {"Личная палата принца Шаффара",nil, {"Такое предложить могли лишь самые стойкие и закаленные в битвах, Тафгор. Если ты не боишься повстречаться с ужасами второй палаты стазиса в Гробницах Маны, я могу составить ключ. Принеси мне знак знак короля нексуса, и я это сделаю."}},
[10982] = {"Око Харамада",nil, {"Твои заслуги перед Протекторатом и Консорциумом поистину велики, Бахусий. Так что будет только правильно, если первый из наших ключей будет отдан тебе. Этим ключом ты в любое время можешь открыть личную палату стазиса Шаффара, не утратив при этом сам ключ.nn*Амир кивает.*nnКлюч неотъемлем и пребудет с тобой навечно."}},
[10983] = {"Мог'дорг Мудрый",nil, {"Сходите к Мог'доргу Мудрому, который живет на вершине башни в Острогорье, сразу за Кругом Крови."}},
[10984] = {"Разговор с огром",nil, {"Поговорите с огром по имени Грок в Нижнем городе Шаттрата."}},
[10985] = {"Отвлекающий маневр",nil, {"Убедитесь, что Акама и Майев вошли в Черный храм в Долине Призрачной Луны, после того как войска Кси'ри начнут отвлекающий маневр."}},
[10986] = {"Небесные очи",nil, {"Поговорите с дозорным Элайрой на Сумеречной гряде в Награнде."}},
[10987] = {"Поймать скальную пустельгу",nil, {"Поймайте дикую пустельгу при помощи специальной сети и принесите ее дозорному Элайре на Сумеречную гряду."}},
[10988] = {"Вороньи камни",nil, {"Соберите в Скеттисе 8 вороньих камней и принесите их Арторн Песне Ветра в Вечную рощу в Острогорье."}},
[10989] = {"Мог'дорг Мудрый",nil, {"Посетите Мог'дорга Мудрого на вершине башни, где стоит Фырк, прямо у Круга Крови в Острогорье."}},
[10990] = {"Сущность орла",nil, {"Одолейте стража Орла и добудьте у него сущность орла. Призовите пустельгу Арторн особым манком, чтобы сообщить новости об успешном выполнении задания.nnЕсли вы потеряете манок Арторн для пустельги, вернитесь к Арторн в Вечную рощу за новым."}},
[10991] = {"Сущность сокола",nil, {"Одолейте стража Сокола и добудьте у него сущность сокола. Призовите пустельгу Арторн особым манком, чтобы сообщить новости об успешном выполнении задания.nnЕсли вы потеряете манок Арторн для пустельги, вернитесь к Арторн в Вечную рощу за новым."}},
[10992] = {"Сущность ястреба",nil, {"Одолейте стража Ястреба и принесите сущность ястреба Арторне Песне Ветра в Вечную рощу."}},
[10993] = {"Возвращение в Кенарийский оплот",nil, {"Отнесите сверток Арторны Мортису Шелесту Крыла в Кенарийский оплот."}},
[10994] = {"В поисках лунного камня",nil, {"Принесите лунный камень реки Строптивой Мортису Шелесту Крыла в Кенарийский оплот."}},
[10995] = {"У Груллока два черепа",nil, {"Добудьте драконий череп Груллока и принесите его Мог'доргу Мудрому на башню Круга Крови в Острогорье."}},
[10996] = {"Сундук с сокровищами Маггока",nil, {"Добудьте сундук с сокровищами Маггока и доставьте его Мог'доргу Мудрому на башню в Круге Крови в Острогорье."}},
[10997] = {"Даже у гроннов есть штандарты",nil, {"Добудьте штандарт Слаага и доставьте его к Мог'доргу Мудрому на башню в Круге Крови в Острогорье."}},
[10998] = {"Черно(книжн)ый бизнес",nil, {"Добудьте гримуар Вимгола Зловещего. Отнесите его Мог'доргу Мудрому на башню в Круге Крови в Острогорье."}},
[11000] = {"В сердце Разрушителя Душ",nil, {"Добудьте душу Скуллока и принесит ее Мог'доргу Мудрому на вершину башни Круга Крови в Острогорье."}},
[11001] = {"Изгнание бога-ворона",nil, {"Убейте бога-ворона и возвращайтесь к Мортису Шелесту Крыла в Кенарийский оплот.nnnnЭто задание выполняется в режиме героической сложности."}},
[11002] = {"Падение Магтеридона",nil, {"Доставьте голову Магтеридона командиру армии Данату Троллебою в Оплот Чести на полуострове Адского Пламени."}},
[11003] = {"Падение Магтеридона",nil, {"Доставьте голову Магтеридона Назгрелу в Траллмар на полуострове Адского Пламени."}},
[11004] = {"Мир Теней",nil, {"Проберитесь в Скеттис и добудьте 6 горстей теневой пыли с местных араккоа, после чего отнесите пыль Северину."}},
[11005] = {"Тайны жрецов Когтя",nil, {"Возьмите у Северина эликсир теней и с его помощью найдите и убейте жрецов Когтя: Ишааля, Скиззика и Зеллека в Скеттисе. После этого вернитесь к командиру Адарису."}},
[11006] = {"Больше теневой пыли",nil, {"Похоже, командир все же не бредил. Принесите мне еще пыли, и я приготовлю вам другой эликсир."}},
[11007] = {"Кель'тас и зеленеющая сфера",nil, {"Отнесите зеленеющую сферу А'далу в Шаттрат."}},
[11008] = {"Огонь над Скеттисом",nil, {"Найдите яйца чудовищных калири на верхушках домов Скеттиса и сбросьте на них взрывчатку Стражи Небес, после чего вернитесь к небесному сержанту Дорину."}},
[11009] = {"Огрские небеса",nil, {"Мог'дорг Мудрый просит, чтобы вы поговорили с Чу'а'лором в Огри'ла в Острогорье."}},
[11010] = {"Бомбардировочный заход",nil, {"Уничтожьте 15 груд пушечных ядер Скверны при помощи бомб Стражи Небес."}},
[11011] = {"Вечная бдительность",nil, {"Попросите у Мортиса Шелеста Крыла лунный камень Сущности."}},
[11012] = {"Клятва в верности Крыльям Пустоты",nil, {"Наши земли потеряны, наши дети похищены под покровом ночи, проданы в рабство, а то и того хуже...nn<Голос Морденая срывается.>nnПора, мой соратник, пришло время все вернуть! Ты проявил себя как отважный воитель и к тому же отзывчив душою.nnОбещаешь ли ты помочь клану Крыльев Пустоты вернуть наши земли и восстановить наши дома? Клянешься ли ты поддерживать наше племя, пока род не будет продолжен? Поклянись, и мы начнем битву за наши дома и наших близких!"}},
[11013] = {"В услужении у Иллидари",nil, {"Отправляйтесь на кряж Крыльев Пустоты и доставьте докладные записки Иллидари властителю Мор'гору, в лагере Драконьей Пасти."}},
[11014] = {"Задание десятника",nil, {"Поговорите с надсмотрщиком Варкулом Дыханием Дракона в лагере клана Драконьей Пасти."}},
[11015] = {"Кристаллы Крыльев Пустоты",nil, {"Принесите 30 кристаллов Крыльев Пустоты надсмотрщику Варкулу Дыханию Дракона в лагерь Драконьей Пасти."}},
[11016] = {"Шкуры живодеров-пустокопов",nil, {"Принесите 35 шкур живодеров-пустокопов из Копей Пустоты надсмотрщику Варкулу Дыханию Дракона в лагерь Драконьей Пасти в долине Призрачной Луны."}},
[11017] = {"Пыльца пустопраха",nil, {"Принесите 40 порций пыльцы пустопраха надсмотрщику Варкулу Дыханию дракона в лагерь Драконьей Пасти."}},
[11018] = {"Хаотитовая руда",nil, {"Принесите 40 порций хаотитовой руды надсмотрщику Варкулу Дыханию Дракона в лагерь Драконьей Пасти в долине Призрачной Луны."}},
[11019] = {"Друг в тылу врага",nil, {"Не думай, что тебе придется встретиться с армией орков Скверны в одиночку.nn*В глазах Ярзила мерцают синие искры.*nnТак вот мы здесь с тобой, Шушуль. В любой услуге, которую ты сможешь оказать оркам Скверны, действуя во имя отвоевания земель Крыльев Пустоты, мы будем рядом, чтобы удостовериться, что все идет по нашему плану.nnТебе надо вести себя осторожно и не привлекать к себе нежелательного внимания во время твоих акций саботажа."}},
[11020] = {"Медленная смерть",nil, {"Возьмите у Язрилла Наемника из долины Призрачной Луны баранину. Смешайте ее с порчеными железами и отравите 12 лагерей батраков из клана Драконьей Пасти."}},
[11021] = {"Альманах Ишааля",nil, {"Принесите альманах Ишааля небесному командиру Адарису к северу от Скеттиса."}},
[11022] = {"Разговор с Мог'доргом",nil, {"Поговорите с Мог'доргом Мудрым. Он находится на вершине башни на восточной стороне Круга Крови в Острогорье."}},
[11023] = {"Еще разок задать им жару!",nil, {"Уничтожьте 15 груд пушечных ядер Скверны при помощи бомб Стражи Небес."}},
[11024] = {"Союзник в Нижнем Городе",nil, {"Принесите альманах Ишааля Рилаку Освобожденному в Нижний Город Шаттрата."}},
[11025] = {"Кристаллы",nil, {"Чу'а'лор проучил вам добыть 5 осколков апексита. Принесите их ему в Огри'ла на вершинах Острогорья."}},
[11026] = {"Гони демонов!",nil, {"При помощи кристалла изгоните 15 демонов из лагеря Легиона: Гнев или Ужас в Острогорье."}},
[11027] = {"Есть у тебя темная руна?",nil, nil},
[11028] = {"Оставшееся время",nil, {"Расскажите небесному капитану Адарису о возвращении Терокка. Вы сможете найти его в лагере Черного Ветра в лесу Тероккар."}},
[11029] = {"Потрепанная маскировка",nil, {"Оденьтесь в потрепанный маскировочный костюм араккоа, возьмите книгу \"Кровная вражда\" у Сахаака и вернитесь к Хаззику."}},
[11030] = {"Наш мальчик хочет быть следопытом Стражи Небес",nil, {"Добудьте нестабильный настой Зверя."}},
[11031] = {"Перестать быть верховным магом из Аметистового Ока",nil, nil},
[11032] = {"Перестать быть защитником из Аметистового Ока",nil, nil},
[11033] = {"Перестать быть убийцей из Аметистового Ока",nil, nil},
[11034] = {"Перестать быть исцелителем",nil, nil},
[11035] = {"Недружелюбные небеса",nil, {"Верните Язриллу Наемнику в лагере Драконьей Пасти в долине Призрачной Луны 10 реликвий Крыльев Пустоты."}},
[11036] = {"Еще один пункт долой!",nil, {"Доставьте консервированные фрукты командующему ракетами Фюзеляжу в Зону 52."}},
[11037] = {"Странное видение",nil, {"Поговорите с ведущим ракетостроителем Фьюзеляжем в Зоне 52."}},
[11038] = {"Помощь экзарху Орелису",nil, {"Поговорите с экзархом Орелисом в Зоне 52."}},
[11039] = {"Отчет патрону шпионов Талодиену",nil, {"Доложитесь патрону шпионов Талодиену в Зоне 52."}},
[11040] = {"Детали для ведущего ракетостроителя",nil, {"Доставить запчасти Босси командующему ракетами Фюзеляжу в Зону 52."}},
[11041] = {"Неоконченное дело",nil, {"Убейте 10 надземных живодеров и Бараш Мать Стаи. По выполнении вернитесь к властителю Мор'гору в лагерь Драконьей Пасти."}},
[11042] = {"Таинственное видение",nil, {"Поговорите с ведущим ракетостроителем Фьюзеляжем в Зоне 52."}},
[11043] = {"Усовершенствованный грифон",nil, {"Поговорите с Брюнном Жаробородом в цитадели Громового Молота."}},
[11044] = {"Страшные видения",nil, {"Поговорите с командиром звена Нуаинном в цитадели Громового Молота."}},
[11045] = {"Зорус Ревнитель",nil, {"Поговорите с Зорусом Ревнителем в цитадели Громового Молота."}},
[11046] = {"Главный аптекарь Хильдегард",nil, {"Поговорите с главным аптекарем Хильдегард в деревне Призрачной Луны."}},
[11047] = {"Просьба ученика",nil, {"Поговорите с Дамой Буйногривой в деревне Призрачной Луны."}},
[11048] = {"Рапорт Крогхана",nil, {"Объясните кровавому стражу Галмоку в деревне Призрачной Луны, почему Крогхан не может явиться к нему."}},
[11049] = {"Большая Охота",nil, {"Найдите яйцо дракона Крыльев Пустоты для Язрилла Наемника, находящегося в лагере Драконьей Пасти, что в долине Призрачной Луны."}},
[11050] = {"Собрать их все!",nil, {"Мы не хотим, чтобы драконьи яйца попали в чужие руки, Фоммаа. Принеси мне все, сколько сможешь найти.nnТолько по возможности тихо и не привлекая к себе внимания."}},
[11051] = {"Демонов – долой!",nil, {"При помощи кристалла изгоните 15 демонов из лагеря Легиона: Гнев или Ужас в Острогорье."}},
[11052] = {"Обещание Акамы",nil, {"Принесите медальон Карабора А'далу в Шаттрат."}},
[11053] = {"Новое назначение",nil, {"Поговорите с властителем Мор'гором в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11054] = {"Ты – инспектор: как делать все правильно",nil, {"Принесите 10 кусков узловатой кожи и прочную шкуру Тирантия надзирателю Грязевику в лагерь Драконьей Пасти в долине Призрачной Луны."}},
[11055] = {"Ботиранг: лекарство для нерадивых батраков",nil, {"Призовите к порядку 20 ленивых батраков в лагере Драконьей Пасти. Ботиранг нужно будет вернуть надзирателю Грязевику."}},
[11056] = {"Просьба Хаззика",nil, {"Заберите сверток Хаззика из его дома в восточном Скеттисе и верните сверток хозяину."}},
[11057] = {"Огрское подполье",nil, {"Чу'а'лор просит вас поговорить с Мог'доргом Мудрым на вершине одной из башен рядом с Кругом Крови в Острогорье."}},
[11058] = {"Апекситовая реликвия",nil, {"Получите вибрации Апекса от апекситовой реликвии."}},
[11059] = {"Страж монумента",nil, {"Принесите голову стража апекситов."}},
[11060] = {"Кристалл с темными рунами",nil, nil},
[11061] = {"Отцовский долг",nil, {"Добудьте нестабильный настой волшебника."}},
[11062] = {"Застава Стражи Небес",nil, {"Поговорите с небесным командиром Келлером на заставе Стражи Небес на вершинах Острогорья."}},
[11063] = {"Заработай крылья",nil, {"Так ты хочешь примкнуть к усмирителям небес? Ты неплохо себя показал в клане Драконьей Пасти, но это ничего не значит тут, в небесах Запределья. Вопрос в том, умеешь ли ты летать?nnПолет – это не просто взгромоздиться на спину дракону Пустоты и отвезти в форт партию кристаллов, с этим-то любой дурак справится. Полет – это быть лучшим, первым среди орков.nnК западу отсюда есть шесть пилотов, победи их всех – и я пожалую тебе крылья усмирителя небес."}},
[11064] = {"Гонки Драконьей Пасти: баллада о старине Макстаре",nil, {"Следуйте за \"Стариной\" Маргом Сквернословом, пытаясь удержаться на своем средстве передвижения. Если вам удастся победить его, сообщите об этом Джаю Нослаю в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11065] = {"Ловим летучих скатов!",nil, {"Поймайте 5 летучих скатов при помощи веревочного аркана."}},
[11066] = {"Нам нужно больше летучих скатов!",nil, {"Поймайте 5 летучих скатов при помощи веревочного аркана."}},
[11067] = {"Гонки Драконьей Пасти: Троп Смраднорыг",nil, {"Следуйте за Тропом Смраднорыгом, пытаясь удержаться на своем средстве передвижения. Если вам удастся победить его, сообщите об этом Джаю Нослаю в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11068] = {"Гонки Драконьей Пасти: Корлок Ветеринар",nil, {"Следуйте за Корлоком Ветеринаром, пытаясь удержаться на своем средстве передвижения. Если вам удастся победить его, сообщите об этом Джаю Нослаю в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11069] = {"Гонки Драконьей Пасти: командир звена Ромеон",nil, {"Следуйте за командиром звена Ромеоном, пытаясь удержаться на своем средстве передвижения. Если вам удастся победить его, сообщите об этом Джаю Нослаю в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11070] = {"Гонки Драконьей Пасти: командир звена Маэстр",nil, {"Следуйте за командиром звена Маэстром, пытаясь удержаться на своем средстве передвижения. Если вам удастся победить его, сообщите об этом Джаю Нослаю в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11071] = {"Гонки Драконьей Пасти: капитан Небокрушитель",nil, {"Следуйте за капитаном Небокрушителем, пытаясь удержаться на своем средстве передвижения. Если вам удастся победить его, сообщите об этом Джаю Нослаю в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11072] = {"Кровные враги",nil, {"Найдите груды черепов в середине кругов призыва Скеттиса. С помощью 10 свитков затерянных во времени призовите каждого потомка Скеттика, уничтожьте их и отнесите трофеи Хаззику в лагерь Черного Ветра."}},
[11073] = {"Падение Терокка",nil, {"Возьмите приготовленное Хаззиком Подношение Затерянного во Времени и отнесите его к груде черепов в центре Скеттиса. С помощью подношения призовите Терокка и сразитесь с ним, после чего вернитесь к небесному командиру Адарису."}},
[11074] = {"Кровные враги",nil, {"Добудьте у затерянных во времени араккоа их свитки и отнесите их к груде черепов в центре круга призыва в Скеттисе. Вызовите и победите потомков врагов Терокка, после чего отнесите Хаззику коготь Аккарая, хребет Гарокка, чешую Векказа и коготь Геззарака."}},
[11075] = {"Копи Крыльев Пустоты",nil, {"Найдите Госпожу Копей на Кряже Крыльев Пустоты."}},
[11076] = {"Собрать по кусочкам...",nil, {"Соберите для Госпожи Копей груз 15 тачек в Долине Призрачной Луны."}},
[11077] = {"Драконы – это не самое страшное",nil, {"Убейте 15 живодеров-пустокопов и 5 опустошителей-пустокопов из Копей Пустоты для штейгера из клана Драконьей Пасти."}},
[11078] = {"В небесах останется только один!",nil, {"Добудьте драконьи зубы."}},
[11079] = {"Бич Скверны для Гахка",nil, {"Добудьте бич Скверны."}},
[11080] = {"Излучение реликвии",nil, {"Испытайте на себе излучение от апекситовой реликвии."}},
[11081] = {"Восстание в племени Темной Крови",nil, {"Отнесите планы побега племени Темной Крови Госпоже Копей."}},
[11082] = {"В поисках правды",nil, {"Найдите надзирателя из племени Темной Крови и допросите его. Вернитесь к Госпоже Копей с информацией об организаторе побега и рукой надзирателя."}},
[11083] = {"Спятившие и очень опасные...",nil, {"Убейте для Ронага Деспота из копей Крыльев Пустоты безумного штейгера и 5 шахтеров племени Темной Крови."}},
[11084] = {"Стой прямо, капитан!",nil, {"Поговорите с властителем Мор'гором в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11085] = {"Побег из Скеттиса",nil, {"Проводите плененного Стража Небес в безопасное место, а затем доложите небесному сержанту Дорину."}},
[11086] = {"Разрушение сумеречного портала",nil, {"По поручению властителя Мор'гора из лагеря Драконьей Пасти в долине Призрачной Луны убейте 20 посланников Смертной Тени."}},
[11089] = {"Пушка души Рет'хедрона",nil, {"Принесите повелителю Иллидари Балтазу, находящемуся в лагере Драконьей Пасти, 2 слитка оскверненной стали, 1 адамантитовую рамку, 1 кориевый сердечник и 1 безупречную чародейскую сущность."}},
[11090] = {"Покорить Покорителя",nil, {"Используйте Пушку Души против Рет'хедрона Покорителя в Награнде. Потом верните Пушку Души повелителю Иллидари Балтазу в лагерь Драконьей Пасти, в долине Призрачной Луны."}},
[11091] = {"Особая благодарность",nil, {"Чу'а'лор предлагает вам поговорить с Джо'нассом в Огри'ла на вершинах Острогорья."}},
[11092] = {"Приветствую тебя, Командир!",nil, {"Поговорите с властителем Мор'гором в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11093] = {"Пропитание для скатов Пустоты",nil, {"Возьмите клетку для скатов Пустоты в лес к югу от лагеря Черного Ветра и убейте прыгуан-ловцов долины Черного Ветра около голодного ската Пустоты. После этого вернитесь к укротителю Диисаку из Стражи Небес."}},
[11094] = {"Всех убей, один останься!",nil, {"Доставьте информацию о вашей новой миссии вместе со сигнальным ружьем чародею Тэлису в Святилище Звезд."}},
[11095] = {"Командир Хобб",nil, {"Отдайте сигнальную ракету клана Драконьей Пасти командиру Хоббу, стоящему у Святилища Звезд в долине Призрачной Луны."}},
[11096] = {"Атака с воздуха",nil, {"Убейте 20 любых огров из клана Гордунни в Защитных холмах, после чего вернитесь к Юуле в Шаттрат."}},
[11097] = {"Самая опасная ловушка",nil, {"Помогите Командиру Хоббу защитить Святилище Звезд. Когда войска Драконьей Пасти будут разгромлены, вернитесь к властителю Мор'гору в лагерь Драконьей Пасти с медалями Провидцев."}},
[11098] = {"В Скеттис!",nil, {"Отнесите упаковку взрывчатки сержанту Дорину в лагерь Черного Ветра рядом со Скеттисом."}},
[11099] = {"Всех убей, один останься!",nil, {"Доставьте информацию о вашей новой миссии вместе с сигнальным ружьем анахорету Сейле к алтарю Ша'тар."}},
[11100] = {"Командир Аркус",nil, {"Отдайте сигнальную ракету клана Драконьей Пасти командиру Аркусу у алтаря Ша'тар."}},
[11101] = {"Самая Опасная Ловушка",nil, {"Помогите Командиру Аркусу защитить алтарь Ша'тар. Когда войска клана Драконьей Пасти будут разгромлены, вернитесь к властителю Мор'гору в лагерь Драконьей Пасти с медалями Алдоров."}},
[11102] = {"Бомбардировочный заход",nil, {"Уничтожьте 15 груд пушечных ядер Скверны при помощи бомб Стражи Небес."}},
[11103] = {"Отказ мудреца",nil, nil},
[11104] = {"Перестать быть исцелителем",nil, {"The power within your ring is malleable, though reversing your choice is not easy. Are you sure you want to replace its current form?"}},
[11105] = {"Отказ героя",nil, nil},
[11106] = {"Отказ защитника",nil, {"The power within your ring is malleable, though reversing your choice is not easy. Are you sure you want to replace its current form?"}},
[11107] = {"Склонитесь перед верховным господином",nil, {"Поговорите с властителем Мор'гором в лагере Драконьей Пасти в долине Призрачной Луны."}},
[11108] = {"Владыка Иллидан Ярость Бури",nil, {"Встретьтесь с Иллиданом Яростью Бури. Найдите Бартамуса в Нижнем Городе Шаттрата, если останетесь в живых."}},
[11109] = {"Кобальтовый дракон Пустоты Хорус",nil, {"Познакомиться с тобой, Медоке, большая честь. Мы внимательно следили за всеми твоими приключениями и крайне благодарны тебе за все, что ты сделал для племени Крыльев Пустоты.nnЕсли Ты выберешь меня своим спутником, я пойду за тобой на край света или еще дальше. В любом случае, я сделаю все, чтобы оправдать оказанную мне честь.nnНо ты можешь выбрать только одного из нас, и сделанный выбор нельзя будет изменить."}},
[11110] = {"Лиловый дракон Пустоты Малфас",nil, {"Познакомиться с тобой, Гностикмаг, большая честь. Мы внимательно следили за всеми твоими приключениями и крайне благодарны тебе за все, что ты сделал для племени Крыльев Пустоты.nnЕсли Ты выберешь меня своим спутником, я пойду за тобой на край света или еще дальше. В любом случае, я сделаю все, чтобы оправдать оказанную мне честь.nnНо ты можешь выбрать только одного из нас, и сделанный выбор нельзя будет изменить."}},
[11111] = {"Ониксовый дракон Пустоты Ониксиен",nil, {"Познакомиться с тобой, Фоммаа, большая честь. Мы внимательно следили за всеми твоими приключениями и крайне благодарны тебе за все, что ты сделал для племени Крыльев Пустоты.nnЕсли Ты выберешь меня своим спутником, я пойду за тобой на край света или еще дальше. В любом случае, я сделаю все, чтобы оправдать оказанную мне честь.nnНо ты можешь выбрать только одного из нас, и сделанный выбор нельзя будет изменить."}},
[11112] = {"Лазурный дракон Пустоты Сураку",nil, {"Познакомиться с тобой, Амат, большая честь. Мы внимательно следили за всеми твоими приключениями и крайне благодарны тебе за все, что ты сделал для племени Крыльев Пустоты.nnЕсли Ты выберешь меня своим спутником, я пойду за тобой на край света или еще дальше. В любом случае, я сделаю все, чтобы оправдать оказанную мне честь.nnНо ты можешь выбрать только одного из нас, и сделанный выбор нельзя будет изменить."}},
[11113] = {"Пурпурный дракон Пустоты Воранаку",nil, {"Познакомиться с тобой, Йша, большая честь. Мы внимательно следили за всеми твоими приключениями и крайне благодарны тебе за все, что ты сделал для племени Крыльев Пустоты.nnЕсли ты выберешь меня своим спутником, я пойду за тобой на край света или еще дальше. В любом случае, я сделаю все, чтобы оправдать оказанную мне честь.nnНо ты можешь выбрать только одного из нас, и сделанный выбор нельзя будет изменить."}},
[11114] = {"Зои, изумрудный дракон Пустоты",nil, {"Познакомиться с тобой, Алаверды, большая честь. Мы внимательно следили за всеми твоими приключениями и крайне благодарны тебе за все, что ты сделал для племени Крыльев Пустоты.nnЕсли ты выберешь меня своим спутником, я пойду за тобой на край света или еще дальше. В любом случае, я сделаю все, чтобы оправдать оказанную мне честь.nnНо ты можешь выбрать только одного из нас, и сделанный выбор нельзя будет изменить."}},
[11117] = {"Поймать дикого зайцелопа!",nil, {"Глодрак Меткий Охотник попросил вас поймать 5 оглушенных летучих зайцелопов с помощью особой сети. Он намекнул, что необходимо достичь состояния сильного алкогольного опьянения."}},
[11118] = {"Розовые элекки на марше",nil, {"Отправляйтесь в пивные сады рядом с Штормградом, Дарнассом и Экзодаром, истребите по три элекка в каждом из мест и возвращайтесь к Глодраку Меткому Охотнику. Чтобы увидеть розовых элекков, необходимо напиться."}},
[11119] = {"Налет на Лагерь Баш'ира!",nil, {"Поговорите с учеником эфир-теха на заставе Стражи Небес в Острогорье."}},
[11120] = {"Розовые элекки на марше",nil, {"Отправляйтесь в пивные сады рядом с Луносветом, Громовым Утесом и Подгородом, истребите по три элекка в каждом из них и возвращайтесь к Глодраку Меткому Охотнику. Чтобы увидеть розовых элекков, необходимо напиться."}},
[11122] = {"Туда и обратно",nil, {"Возьмите бочку у Флинна Огневара в Караносе и доставьте ее Полу Янтарлену. Если баран сбежит от вас, возьмите еще одного у Нейла Рамштайна."}},
[11123] = {"Исследование пепелища",nil, {"Поговорите с инспектором Таремом у трактира \"Последний привал\"."}},
[11124] = {"Исследование пепелища",nil, {"Поговорите с инспектором Таремом у трактира \"Последний привал\"."}},
[11126] = {"Чужой среди своих",nil, {"Прогоните 5 дезертиров-подстрекателей и вернитесь к докам Терамора к Калии Гастингс."}},
[11128] = {"Пропагандистская война",nil, {"Доставьте дезертирскую пропаганду Калии Гастингс к докам в Тераморе."}},
[11129] = {"Пропал Кайл!",nil, {"Анаб Пшеничная Грива из деревни Кровавого Копыта в Мулгоре попросил найти его лучшего щенка, Кайла Бесноватого.nnПокормите Кайла нежным мясом долгонога и возвращайтесь к Анабу Пшеничной Гриве."}},
[11130] = {"Ой, блестяшечки!",nil, {"Жуль хочет, чтобы вы взяли его записку и отнесли ее Бадду в Призрачные земли к Зул'Аману."}},
[11131] = {"Гаси пожар!",nil, {"Переодетая попечительница сирот просит вас помочь потушить пожар в деревне. Когда пожар будет потушен, снова поговорите с попечительницей сирот."}},
[11132] = {"Все обещаешь и обещаешь",nil, {"Разыщите карту Бадда Недрека на террасе верховного шамана Налоракка в Зул'Амане и верните ее хозяину."}},
[11133] = {"Дискредитация вражеской агентуры",nil, {"Калия Гастингс просит вас подбросить листовки с изменениями 6 тераморским стражникам. Затем вернитесь к докам Терамора и доложите Калии о выполнении."}},
[11134] = {"Конец вражеской пропаганде",nil, {"Арестуйте Гависа Серого Щита, затем доложитесь Калии Гастингс у доков Терамора."}},
[11135] = {"Всадник без головы",nil, {"Найдите святилище тыквы Всадника без головы."}},
[11136] = {"Тревожные события",nil, {"Поговорите с капитаном Ваймором на Сторожевом посту."}},
[11137] = {"Братство Справедливости в Пылевых топях?",nil, {"Добудьте приказы Братства Справедливости у Гарна Маферса и принесите его капитану Ваймору на Сторожевой пост."}},
[11138] = {"Ренн Макгилл",nil, {"Отнесите приказы Братства Справедливости Ренну Макгиллу. Он плавает недалеко от Зловещего берега."}},
[11139] = {"Старое водолазное снаряжение",nil, {"Принесите набор инструментов и испорченное водолазное снаряжение Ренну Макгиллу в Пылевые топи."}},
[11140] = {"Вернуть груз!",nil, {"Принесите 6 поднятых со дна сундуков Ренну Макгиллу в Пылевые топи."}},
[11141] = {"Джайна должна быть в курсе",nil, {"Поговорите с леди Джайной Праудмур в Тераморе."}},
[11142] = {"Исследование острова Алькац",nil, {"Поговорите с Коссой Багровое Крыло и возьмите одного из ее грифонов, чтобы обследовать остров Алькац. После выполнения поручения отрапортуйте леди Джайне Праудмур в Тераморе."}},
[11143] = {"Мрачные выводы",nil, {"Доложитесь капитану Дариллу в Северной башне."}},
[11144] = {"Подтвержденные подозрения",nil, {"Принесите боевой план племени Зловещий Тотем капитану Дариллу в Северную башню."}},
[11145] = {"Пленники Зловещего Тотема",nil, {"Сержант Лукас из Северной башни просит вас спасти 5 тераморских пленников."}},
[11146] = {"Ловец ящеров",nil, {"Сержант Лукас из Северной башни просит вас поймать 6 ящеров. Вы можете ловить как ящеров Кровавой Топи, так и крикунов Кровавой Топи."}},
[11147] = {"Боевые ящеры",nil, {"Сержант Лукас из Северной башни хочет, чтобы вы положили приманку на ящера близ ветряной мельницы в деревне Черного Копыта."}},
[11148] = {"Оружие племени Зловещего Тотема",nil, {"По просьбе капитана Дарилла из Северной башни принесите ему 7 предметов вооружения из деревни Черного Копыта."}},
[11149] = {"Помощь Табеты",nil, {"Поговорите с Табетой в ее доме."}},
[11150] = {"Уничтожение заставы Дикого Рога",nil, {"Сожгите северную, восточную и северо-восточную палатки на заставе Дикого Рога, затем вернитесь в усадьбу Табеты."}},
[11151] = {"Справедливая месть за Хьялей",nil, {"Поговорите с капитаном Гарраном Ваймсом в цитадели в Тераморе."}},
[11152] = {"Обрести покой",nil, {"По просьбе капитана Гаррана Ваймса из цитадели возложите венок к памятнику семейства Хьяль."}},
[11156] = {"Налетчики Дикого Рога",nil, {"Убейте 12 тауренов из племени Зловещего Тотема с заставы Дикого Рога и вернитесь к ученику Морланну в усадьбу Табеты."}},
[11158] = {"Перья Кровавой Топи",nil, {"Принесите 5 перьев Кровавой Топи Броггу в Шестермуть."}},
[11159] = {"Духи в руинах деревни Каменного Молота",nil, {"Брогг из Шестермути просит вас дать покой 10 духам клана Каменного Молота."}},
[11160] = {"Знамя Каменного Молота",nil, {"Добудьте знамя Каменного Молота и принесите его Броггу в Шестермуть."}},
[11161] = {"Сущность вражды",nil, {"Принесите 10 сущностей черного дракона Броггу в Шестермуть."}},
[11162] = {"Бросить вызов черным драконам",nil, {"Установите знамя Каменного Молота у входа в Логово Ониксии, затем вернитесь к Броггу в Шестермуть."}},
[11163] = {"Тайная Сестрица",nil, {"Доберитесь до Донны Браско, чтобы получить причитающуюся вам награду. Найти Донну можно на Призрачных Землях, сразу за окраиной Зул'Амана."}},
[11164] = {"Клыкастые всадники",nil, {"Соберите для Пригмона 10 клыков лесных троллей в Зул'Амане и принесите их ему в лагерь Бадда в Призрачных Землях."}},
[11165] = {"Тролль в стане троллей",nil, {"Найдите Зунгама и помогите ему выбраться из Зул'Амана."}},
[11166] = {"Крестом на карте обозначена... твоя смерть!",nil, {"Отправляйтесь в Зул'Аман и посетите чертог Халаззи, платформу Джан'алая и платформу Акил'зона. Заметьте все подробности и по возвращении доложите о них Бадду в его лагере в Призрачных землях."}},
[11169] = {"Оружие племени Зловещего Тотема",nil, {"Ученик Гарион из усадьбы Табеты хочет, чтобы вы испытали захваченный тотем на 10 крапчатых кроколисках-грязекупах или грязекупах-острозубах."}},
[11171] = {"Повелитель Проклятий? Пффф!",nil, {"Убейте в Зул'Амане повелителя проклятий Малакрасса и вернитесь к Бадду Недреку на Холмы Томагавков."}},
[11172] = {"Крушение дирижабля",nil, {"Поговорите с Мокси Сталегриль на месте крушения дирижабля Бизила."}},
[11173] = {"Подмена реактива",nil, {"Принесите 6 флаконов болотного яда ученику Гариону в усадьбу Табеты."}},
[11174] = {"Профилактика коррозии",nil, {"Мокси Сталегриль хочет, чтобы вы уничтожили 10 слизнюков и доложили ей о выполнении задания на месте крушения дирижабля Бизила."}},
[11177] = {"Отшельник из сторожки \"Болотный огонек\"",nil, {"Поговорите с Ярлом \"Болотным глазом\" в сторожке \"Болотный огонек\"."}},
[11178] = {"Кровь Вождя",nil, {"Принесите кровь Зул'джина Бадду в его лагерь в Призрачных Землях. За пределами Зул'Амана."}},
[11180] = {"Что таится на Ведьмином холме?",nil, {"Моргант Мрачник из сторожки \"Болотный огонек\" хочет, чтобы вы добыли информацию у 10 восставших мертвецов или духов."}},
[11181] = {"Ведьмина погибель",nil, {"Принесите 9 кустиков ведьминой погибели Морганту Мрачнику в сторожку \"Болотный огонек\"."}},
[11183] = {"Очищение Ведьминого холма",nil, {"Моргант Мрачник из сторожки \"Болотный огонек\" хочет, чтобы вы установили факел в конце дока и сразились с тем, что выйдет на свет."}},
[11184] = {"Разыскивается: Кровокоготь Ненасытный",nil, {"Уничтожьте Кровокогтя Ненасытного и сообщите об этом Драззиту Кранотеку, который находится в Шестермути."}},
[11185] = {"Письмо аптекаря",nil, {"Отнесите запечатанное письмо привратнику Анделло в усадьбу Табеты."}},
[11186] = {"Свидетельство предательства?",nil, {"Доставьте запечатанное письмо Назиру Кровавому Копью в Деревню Гиблотопь."}},
[11191] = {"Этот старый маяк",nil, {"Поговорите с Бабс Шипокруткой на маяке на острове к востоку от Терамора."}},
[11192] = {"Жир крепкозуба",nil, {"Принесите 4 контейнера жира крепкозуба Бабс Шипокрутке на маяк близ Терамора."}},
[11193] = {"Подлые обитатели глубин",nil, {"Отнесите книгу \"Подлые обитатели глубин\" в доки Терамора и отдайте \"Весельчаку\" Ленни Полечуду."}},
[11194] = {"Так это правда?",nil, {"Поговрите с Натом Пэглом в лагере Ната в Заливе Яростных Волн."}},
[11195] = {"Игра в куклы",nil, {"Отнесите потрепанную куклу вуду Жулю в Шаттрат."}},
[11196] = {"Вождь Амани",nil, {"Победите нового вождя племени Амани, Даакару."}},
[11198] = {"Сразить Тетура!",nil, {"Майор Миллз из доков Терамора хочет, чтобы вы покончили с Тетуром."}},
[11200] = {"Не просто совпадение",nil, {"Крог из Деревни Гиблотопь хочет, чтобы вы принесли ему 7 предметов оружия Черного Копыта."}},
[11201] = {"Замысел племени Зловещего Тотема",nil, {"Принесите боевые планы племени Зловещего Тотема Крогу в деревню Гиблотопь."}},
[11203] = {"Найти Табету",nil, {"Поговорите с Табетой в ее доме на Трясине."}},
[11204] = {"Возвращение к Крогу",nil, {"Поговорите с Крогом в деревне Гиблотопь."}},
[11205] = {"Уничтожение заставы Дикого Рога",nil, {"Сожгите северную, восточную и северо-восточную палатки на заставе Дикого Рога, а затем вернитесь к Табете в ее усадьбу."}},
[11206] = {"Акт правосудия",nil, {"Вернитесь к Крогу в деревню Гиблотопь."}},
[11207] = {"Защитить груз!",nil, {"Мокси Сталегриль попросила вас собрать 8 контейнеров груза с дирижабля и принести ей на место крушения Бизиля."}},
[11208] = {"Поставка для Драззита",nil, {"Доставьте груз с дирижабля Драззиту Кранотеку в Шестермуть."}},
[11209] = {"Сделка Ната",nil, {"Нат Пэгл из лагеря Ната хочет, чтобы вы испытали его новую наживку для рыбы."}},
[11210] = {"Он настоящий!",nil, {"Поговорите с майором Миллзом в доках Терамора."}},
[11211] = {"Помощь деревне Шестермуть",nil, {"Поговорите с Драззитом Кранотеком в Шестермути."}},
[11212] = {"Ферма Табеты",nil, {"Поговорите с Табетой в ее доме на Трясине."}},
[11213] = {"Навестить Табету",nil, {"Поговорите с Табетой в ее усадьбе на Трясине."}},
[11214] = {"Миссия в Шестермуть",nil, {"Поговорите с Драззитом Кранотеком в Шестермути."}},
[11215] = {"Помощь Шестермути",nil, {"Поговорите с Драззитом Кранотеком в Шестермути."}},
[11216] = {"Верховный маг Альтур",nil, {"Поговорите с верховным магом Альтуром близ Каражана на перевале Мертвого Ветра."}},
[11217] = {"Тяни змею за хвост!",nil, {"Гиззикс Грязебульк из Шестермути проист принести ему 8 стеблей змеехвостки."}},
[11219] = {"Гаси пожар!",nil, {"Попечительница сирот в маске просит вас помочь потушить пожар в деревне. Когда пожар потухнет, снова поговорите с попечительницей сирот в маске в городе."}},
[11220] = {"Всадник без головы",nil, {"Найдите святилище тыквы Всадника без головы."}},
[11222] = {"Предупредить Болвара!",nil, {"Поговорите с верховным лордом Болваром Фордрагоном в Крепости Штормграда."}},
[11223] = {"Возвращение к Джайне",nil, {"Поговорите в Тераморе с леди Джайной Праудмур."}},
[11225] = {"Отшельник с Ведьминого холма",nil, {"Поговорите с Ярлом \"Болотным глазом\" в сторожке \"Болотный огонек\"."}},
[11242] = {"Наконец, свободен!",nil, {"Возьмите книгу Томаса Томсона и отнесите ее переодетой попечительнице сирот в Златоземье, Каранос или на Лазурную заставу."}},
[11293] = {"Пивоварня Ячменоваров ищет зазывалу!",nil, {"Зазывайте посетителей на Хмельной фестиваль у флагов в Палатах Войны, в Зале Исследователей и в Палатах Магии."}},
[11294] = {"Пивоварня Громоваров ищет зазывалу!",nil, {"Зазывайте посетителей на Хмельной фестиваль у флагов в Палатах Войны, в Зале Исследователей и в Палатах Магии."}},
[11318] = {"А теперь гонки на баранах... Или вроде того.",nil, {"У вас есть 4 минуты, чтобы погонять барана на всех трех скоростях по 8 секунд."}},
[11321] = {"Кто сказал \"сувениры\"?",nil, {"Отоварьте свой купон на праздничную кружку у Белби Быстросмен в лагере Хмельного фестиваля."}},
[11335] = {"Призыв к оружию: низина Арати",nil, {"Победите на поле боя Низины Арати и вернитесь к бригадному генералу сил Альянса в любую из столиц Альянса или в Шаттрат."}},
