[11657] = {"Поймай факел!",nil, {"Поймайте подряд 4 факела, затем поговорите с мастером-огнеглотателем."}},
[11665] = {"Кроколиски в городе",nil, {"Принесите старине Барло детеныша кроколиска. Он рыбачит на озере Силмир на северо-востоке от Шаттрата."}},
[11666] = {"Поймай бандюгу",nil, {"Поймайте черноперого ерша и принесите его старине Барло. Он рыбачит к северо-востоку от Шаттрата на озере Силмир."}},
[11667] = {"Та самая рыбка",nil, {"Поймайте величайшую ильную рыбу в мире и принесите ее старине Барло. Он рыбачит к северо-востоку от Шаттрата на озере Силмир."}},
[11668] = {"Ловить креветок – это не жук чихнул",nil, {"Принесите 10 гигантских пресноводных креветок старине Барло. Он рыбачит на озере Силмир на северо-востоке от Шаттрата."}},
[11669] = {"Филе сквернокровного луциана",nil, {"Принесите старине Барло чудовищного сквернокровного луциана. Старик удит рыбу к северо-востоку от Шаттрата в озере Силмир."}},
