[6548] = {"Rächt mein Dorf", nil, {"Tötet 8 Grobiane der Grimmtotem und 6 Söldner der Grimmtotem. Kehrt dann zu Makaba Flachhuf am südöstlichen Rand des Steinkrallengebirges zurück."}},
[6561] = {"Schurkerei in der Tiefschwarzen Grotte", nil, {"Bringt den Kopf von Zwielichtfürst Kelris zu Bashana Runentotem in Donnerfels."}},
[6562] = {"Ärger in der Tiefe", nil, {"Sprecht im Eschental mit Je'neu Sancrea."}},
[6563] = {"Die Essenz von Aku'mai", nil, {"Bringt 20 Saphire von Aku'mai zu Je'neu Sancrea im Eschental."}},
[6564] = {"Treue zu den Alten Göttern", nil, {"Bringt die durchfeuchtete Notiz zu Je'neu Sancrea im Außenposten von Zoram'gar im Eschental."}},
[6565] = {"Treue zu den Alten Göttern", nil, {"Tötet Lorgus Jett in der Tiefschwarzen Grotte und kehrt dann zu Je'neu Sancrea ins Eschental zurück."}},
