[7027] = {"Verwaiste Ställe", nil, {"Findet einen Alteracwidder im Alteractal. Wenn Ihr Euch in der Nähe eines Alteracwidders befindet, benutzt das Zähmungshalsband der Sturmlanzen um das Tier einzufangen. Nach erfolgreicher Zähmung, folgt Euch der Alteracwidder bis zum Stallmeister zurück. Sprecht mit dem Stallmeister um für Euren Fang belohnt zu werden."}},
[7028] = {"Dunkles Böses", nil, {"Sammelt 15 theradrische Kristallschnitzereien für Trista in Desolace."}},
[7029] = {"Schlangenzunges Verderbnis", nil, {"Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.\n\nBenutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.\n\nHeilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Vark Schlachtnarbe in Schattenflucht zurück."}},
[7041] = {"Schlangenzunges Verderbnis", nil, {"Füllt die beschichtete himmelblaue Phiole am orangefarbenen Kristallteich in Maraudon.\n\nBenutzt die gefüllte himmelblaue Phiole mit den Schlangenstrunkranken, damit der verderbte Noxxiousspross herausgezwungen wird.\n\nHeilt 8 Pflanzen, indem Ihr diesen Noxxiousspross tötet und kehrt dann zu Talendria an der Nijelspitze zurück."}},
[7042] = {"Gestohlene Winterhauchleckereien", nil, {"Sucht den Ermittler von Kokelwälder, der im Alteracgebirge vermisst wird."}},
