[7495] = {"Sieg für die Allianz", nil, {"Bringt Onyxias Kopf zu König Varian Wrynn in Sturmwind."}},
[7496] = {"Gute Zeiten feiern", nil, {"Sucht Major Mattingly im Tal der Helden auf."}},
