[7839] = {"Schläger der Blutfratzen", nil, {"Schmied Aschenbaum aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr seine verlorenen Werkzeuge ausfindig macht und dann zu ihm zurückkehrt.\n\nIhr erinnert Euch an Schmied Aschenbaums Worte, dass die Werkzeuge möglicherweise bei einem der Tempel der Blutfratzen zu finden seien, im Nordosten des Hinterlands. Ihr solltet auch den Lauerfels absuchen."}},
[7840] = {"Lards vergessene Brotzeit", nil, {"Lard aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Lards Brotzeit wieder findet. Kehrt nach Erfüllung dieser Aufgabe zu ihm zurück.\n\nLard erwähnte, dass er es auf der Insel im Norden vergessen habe. Achtet auf die Trolle der Blutfratzen."}},
[7841] = {"Eine Botschaft an die Wildhämmer", nil, {"Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 5 Hochtalkundschafterinnen, 8 Hochtalspäher, 2 Hochtalschützen und 5 Hochtalwaldläuferinnen tötet. Kehrt nach vollendeten Taten zu ihm zurück.\n\nIhr findet die Hochtalelfen bei der Jagdhütte von Quel'Danil im Nordwesten des Hinterlands."}},
[7842] = {"Eine weitere Botschaft an die Wildhämmer", nil, {"Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihm 1 lange elegante Feder von Greifen besorgt, die das Hinterland bewohnen. Kehrt mit der Feder zu ihm zurück.\n\nEs ist bekannt, dass Greifen überall im Hinterland zu finden sind."}},
[7843] = {"Die letzte Botschaft an die Wildhämmer", nil, {"Otho Moji'ko aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr zum Nistgipfel im Westen des Hinterlands reist und die letzte Botschaft an die Wildhämmer beim Brunnen im Stadtzentrum platziert. Kehrt nach Vollendung der Aufgabe zu ihm zurück.\n\nHütet Euch vor zornigen Wildhammerzwergen und niederträchtigen Mitgliedern der Allianz."}},
[7844] = {"Kannibalistische Vettern", nil, {"Mystikerin Yayo'jin aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr 6 Skalpierer der Blutfratzen und 2 Sterndeuter der Blutfratzen tötet. Kehrt nach Vollendung dieser Aufgabe zu ihr zurück.\n\nYayo'jin deutete an, dass man diese Trolle nahe der Tempel von Shaol'watha und Agol'watha finden könnte. Sie liegen im Nordosten des Hinterlands."}},
[7845] = {"Die Entführung des Ältesten Stumpfhauer", nil, {"Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr ihren Gefährten, den Ältesten Stumpfhauer, aus Jintha'Alor befreit.\n\nMan sagte Euch, dass er hoch oben in Jintha'Alor gefangen gehalten wird - der Hauptstadt der Blutfratzen im Süden des Hinterlands."}},
[7846] = {"Erlangt den Schlüssel!", nil, {"Der Älteste Stumpfhauer in Jintha'Alor möchte, dass Ihr Hitah'ya die Bewahrerin tötet, um in den Besitz des Fesselnschlüssels zu kommen. Kehrt zum Ältesten Stumpfhauer zurück, wenn Ihr den Schlüssel habt."}},
[7847] = {"Rückkehr zu Ura Stumpfhauer", nil, {"Der Älteste Stumpfhauer in Jintha'Alor möchte, dass Ihr Ura Stumpfhauer im Dorf der Bruchhauer die Neuigkeit von seiner Befreiung überbringt."}},
[7848] = {"Abstimmung mit dem Kern", nil, {"Begebt Euch zum Portal in den Schwarzfelstiefen, das in den geschmolzenen Kern führt, und findet ein Kernfragment. Kehrt mit dem Fragment zu Lothos Felsspalter am Schwarzfels zurück."}},
[7849] = {"Die getrennten Brüder", nil, {"Jäger Markhor aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr die Überreste seines Zwillingsbruders bergt. Findet den Schädel und die Knochen des Jägers Malkhor und bringt sie zu Jäger Markhor zurück.\n\nMarkhor erwähnte, dass die Überreste seines Bruders an die Schlamme und Wölfe der Blutfratzen verfüttert wurden. Er sagte weiterhin, dass es in Jintha'Alor eine Grube mit Schlammen gibt, sowie einen Wolfsbau. Beide liegen in der Nähe der Tempelspitze."}},
[7850] = {"Dunkle Gefäße", nil, {"Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland möchte, dass Ihr Euch 5 Gefäße mit besudeltem Blut aus Jintha'Alor beschafft. Kehrt dann damit zu Ura Stumpfhauer zurück."}},
[7861] = {"GESUCHT: Üble Priesterin Hexx und ihre Diener", nil, {"Ihr habt die Anweisung erhalten die üble Priesterin Hexx und 5 Aman'zasiwachen der Blutfratzen zu töten. Habt Ihr Eure Aufgabe erfüllt, so sprecht mit Ura Stumpfhauer aus dem Dorf der Bruchhauer im Hinterland.\n\nDie üble Priesterin Hexx und die Aman'zasiwachen halten sich hoch oben in Jintha'Alor im Hinterland auf."}},
[7862] = {"Offener Posten: Wachbefehlshaber der Bruchhauer", nil, {"Ihr wurdet mit der Vernichtung von 5 Berserkern der Blutfratzen, 3 Schattenjägern der Blutfratzen, 3 Bluttrinkern der Blutfratzen und 2 Seelenfressern der Blutfratzen beauftragt.\n\nKehrt nach Beendigung dieser Aufgabe zu Ura Stumpfhauer ins Dorf der Bruchhauer im Hinterland zurück."}},
