[8304] = {"Liebste Natalia", nil, {"Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr die Bewohner von Bronzebarts Lager befragt. Das Lager befindet sich südlich von Burg Cenarius.\n\nKehrt zu Kommandant Mar'alith zurück, wenn Ihr die benötigten Informationen gesammelt habt."}},
[8305] = {"Lang Vergessenes", nil, {"Sucht die Kristallträne in Silithus und blickt in ihr Innerstes."}},
[8306] = {"Der Schlund des Wahnsinns", nil, {"Kommandant Mar'alith auf Burg Cenarius in Silithus möchte, dass Ihr seine geliebte Natalia wiederfindet. Die von Euch gesammelten Informationen deuten darauf hin, dass Ihr sie im Bau des Regalschwarms im Süden finden könntet.\n\nSprecht noch einmal mit den Zwergen in Bronzebarts Lager, bevor Ihr Euch in den Schwarmbau begebt. Ihr Wissen könnte sich als hilfreich erweisen und sicherlich, haben sie noch einige Aufgaben für Euch.\n\nUnd Sharmon, denkt an die Worte des Kommandanten: \"Tut, was Ihr tun müsst...\""}},
[8307] = {"Wüstenrezept", nil, {"Haltet beim Lager des Schattenhammerklans westlich von Burg Cenarius nach Informationen zum Zubereiten von Sandwurmfleisch Ausschau."}},
[8308] = {"Der verlorene Brief von Brann Bronzebart", nil, {"Liefert Brann Bronzebarts verlorenen Brief in Bronzebarts Lager im südlichen Silithus ab."}},
[8309] = {"Glyphenjagd", nil, {"Rutgar Glyphenschnitzer in Bronzebarts Lager in Silithus möchte, dass Ihr in den Bau des Ashischwarms, Bau des Zoraschwarms und Bau des Regalschwarms eindringt und dort Abpausbilder von den glyphenverzierten Kristallen anfertigt.\n\nBenutzt das Abschriftenset für Geologen, um Abpausbilder der uralten Glyphen herzustellen. Kehrt nach Abschluss der Aufgabe zu Rutgar Glyphenschnitzer zurück."}},
[8310] = {"Knackt den Kode", nil, {"Frankal Steinsteg in Bronzebarts Lager in Silithus möchte, dass Ihr ihm jeweils ein Silithidengehirn des Zoraschwarms, Ashischwarms und des Regalschwarms bringt. Kehrt nach der Erfüllung Eurer Aufgabe zu Frankal zurück."}},
[8311] = {"Schlotternachtssüßigkeiten für Jesper!", nil, {"Sprecht mit den Gastwirten in Sturmwind, Eisenschmiede und Darnassus, sowie mit Talvash del Kissel in Eisenschmiede. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.\n\nKehrt zu Jesper ins Waisenhaus von Sturmwind mit einem Marzipan aus Darnassus, einer Kaugummikugel aus Gnomeregan, Nugat aus Sturmwind und einem Pfefferminzbonbon aus Eisenschmiede zurück."}},
[8312] = {"Schlotternachtssüßigkeiten für Spuks!", nil, {"Sprecht mit den Gastwirten in Orgrimmar, Unterstadt und Donnerfels, sowie mit Kali Remik in Sen'jin. Führt ihnen vor, nach was sie im Gegenzug für die Süßigkeiten verlangen.\n\nKehrt zu Spuks ins Waisenhaus von Orgrimmar mit einem Marzipan aus Donnerfels, einer Kaugummikugel der Dunkelspeere, Nugat aus Orgrimmar und einem Pfefferminzbonbon aus Unterstadt zurück."}},
[8313] = {"Geteiltes Wissen", nil, {"Bringt die ausgerissene Rezeptseite zu Calandrath ins Gasthaus der Burg Cenarius."}},
[8314] = {"Des Rätsels Lösung", nil, {"Rutgar Glyphenschnitzer in Bronzebarts Lager möchte, dass Ihr Geologin Lerchenbann auf Burg Cenarius in Silithus das glyphenverzierte Kristallprisma bringt."}},
[8315] = {"Die Anrufung", nil, {"Geologin Lerchenbann auf Burg Cenarius in Silithus möchte, dass Ihr ihr den Kristallentriegelungsmechanismus vom Abgesandten der Qiraji beschafft.\n\nNehmt die Glyphen der Anrufung, begebt Euch zu den Knochen von Grakkarond südlich von Burg Cenarius und zeichnet sie dort in den Sand. Sollte der Abgesandte der Qiraji erscheinen, tötet ihn und nehmt den Kristallentriegelungsmechanismus an Euch. Kehrt anschließend zu Geologin Lerchenbann zurück.\n\nFür diese Aufgabe werdet Ihr eine kleine Armee benötigen, Sharmon!"}},
