[10123] = {"Teufelsfunkenklamm", nil, {"Unteroffizier Schädelbrecher bei der Versorgungskarawane östlich von Thrallmar auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet."}},
[10124] = {"Vorposten: Häschersturz", nil, {"Meldet Euch bei Vorpostenkommandant To'arch im Häschersturz."}},
[10125] = {"Mission: Die Kommunikation unterbrechen", nil, {"Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr To'archs Sprengstoff benutzt, um den Sender der Legion: Bosheit, Sender der Legion: Zorn, Sender der Legion: Hass und Sender der Legion: Furcht zu zerstören."}},
[10127] = {"Mission: Das Band zerreißen", nil, {"Vorpostenkommandant To'arch am Häschersturz auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Shi'aziv tötet."}},
[10129] = {"Mission: Tore Murketh und Shaadraz", nil, {"Sprecht mit Schwadronskommandant Brack, bombardiert Tor Murketh und Tor Shaadrez und kehrt dann zu Vorpostenkommandant To'arch im Häschersturz zurück."}},
[10130] = {"Die westliche Flanke", nil, {"Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Leutnant Amadi im westlichen Wachturm der Ehrenfeste."}},
[10132] = {"Eine kolossale Bedrohung", nil, {"Tötet 5 tobende Kolosse und kehrt dann zu Tola'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück."}},
[10134] = {"Hinweise des purpurnen Kristalls", nil, {"Bringt den Purpurkristallsplitter zu Tola'thion im Posten des Cenarius auf der Höllenfeuerhalbinsel."}},
[10136] = {"Grausame Pläne", nil, {"Tötet Arazzius den Grausamen und kehrt danach zu Nazgrel in Thrallmar zurück."}},
[10140] = {"Reise zur Ehrenfeste", nil, {"Fliegt zur Ehrenfeste und übergebt Marschall Isildor Durons Bericht."}},
[10141] = {"Wiedergeburt der Brennenden Legion", nil, {"Sprecht mit Unteroffizier Altumus bei der östlichen Versorgungskarawane außerhalb der Ehrenfeste im Osten."}},
[10142] = {"Der Pfad der Pein", nil, {"Unteroffizier Altumus bei der östlichen Versorgungskarawane östlich der Ehrenfeste auf der Höllenfeuerhalbinsel möchte, dass Ihr 4 Flammenschürerwichtel, 6 höllische Kriegshetzer und 1 Schreckensrufer tötet."}},
[10143] = {"Expeditionsposten", nil, {"Meldet euch bei Vorpostenkommandant Königstein im Expeditionsposten."}},
[10144] = {"Die Kommunikation unterbrechen", nil, {"Besiegt die Feinde um Portal Grimh und Portal Kaalez herum, um dämonische Runensteine zu sammeln.\n\nBenutzt Königsteins Zünder in Portal Grimh und Portal Kaalez.\n\nKehrt danach mit dem Zünder zu Vorpostenkommandant Königstein zurück."}},
[10145] = {"Mission: Sever the Tie UNUSED", nil, {"Vorpostenkommandant Wa'ppun beim Expeditionsposten auf de Höllenfeuerhalbinsel möchte, dass Ihr 4 Teufelstreiber, 8 Zornwächter und Unterwerferin Yalqiz tötet."}},
[10146] = {"Mission: Tor Murketh und Tor Shaadraz", nil, {"Sprecht mit Schwadronskommandant Dabir'ee, bombardiert Tor Murketh und Tor Shaadraz und kehrt dann zu Vorpostenkommandant Wa'ppun am Expeditionsposten zurück."}},
[10150] = {"Die Erstreckung der Zitadelle", nil, {"Durchsucht das nördlichste Lager entlang des nördlichen Bollwerks nach einer Spur von Späherin Makha."}},
[10152] = {"Die westliche Flanke", nil, {"Tötet 10 Wachen der Zerschmetterten Hand, 10 Berserker der Zerschmetterten Hand und 5 Akolythen der Zerschmetterten Hand und meldet Euch dann bei Steingardist Ambelan im Rückenbrecherposten in der Nähe von Zeth'Gor."}},
[10159] = {"Haltet den Dornnebelhügel sauber!", nil, {"Tötet 8 Dornenfangfelshetzer und 8 Dornenfanggiftspucker und kehrt dann zu Mahuram Starkhuf im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück."}},
[10160] = {"Den Feind kennen", nil, {"Sprecht mit Leutnant Amadi im alten Wachturm im Westen der Ehrenfeste."}},
[10161] = {"Im Notfall...", nil, {"Sammelt 30 Zeppelintrümmer und bringt sie zu \"Schreihals\" Quack Lufthans bei der Zeppelinabsturzstelle."}},
[10162] = {"Mission: Die Abyssische Untiefe", nil, {"Vorpostenkommandant To'arch im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen vernichtet."}},
[10163] = {"Mission: Die Abyssische Untiefe", nil, {"Schwadronskommandant Greifgar im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen vernichtet."}},
[10164] = {"Alles wird gut", nil, {"Großvater Aldrimus außerhalb der Auchenaikrypta von Auchindoun möchte, dass Ihr in die Auchenaikrypta geht und Exarch Maladaar vernichtet."}},
[10165] = {"Die Konkurrenz untergraben", nil, {"Nexusprinz Haramad außerhalb der Managruft möchte, dass Ihr Nexusprinz Shaffar tötet und ihm Shaffars Bandagen bringt."}},
[10166] = {"Weißborkes Erinnerung", nil, {"Larianna Flusswind möchte, dass Ihr Weißborkes Anhänger beim Runenstein im versengten Hain vergrabt."}},
[10167] = {"Auchindoun...", nil, {"Reist zur Auchenaikrypta in der Knochenwüste der Wälder von Terokkar und tötet Exarch Maladaar um den Geist von D'ore zu befreien."}},
[10168] = {"Was die Seele sieht", nil, {"Sucht einen Seelenspiegel in der Auchenaikrypta und benutzt ihn, um einen getrübten Geist eines uralten Orcahnen von Nagrand zu rufen. Zerstört 15 getrübte Geister, damit die Ahnen in Frieden ruhen können.\n\nKehrt zu Mutter Kashur im Ahnengrund zurück, wenn Ihr fertig seid."}},
[10169] = {"In Würde verlieren", nil, {"Magister Falris in Shattrath möchte, dass Ihr die Phiole mit Beruhigungsserum zur Terrasse des Lichts bringt und sie in den Kochtopf von Haronems Regiment schüttet."}},
[10170] = {"Rückkehr zur Großmutter", nil, {"Kehrt nach Garadar zurück und sprecht mit Großmutter Geyah."}},
[10171] = {"Der untröstliche Häuptling", nil, {"Großmutter Geyah in Garadar möchte, dass Ihr mit Garrosh sprecht."}},
[10172] = {"Es gibt keine Hoffnung", nil, {"Sprecht mit Großmutter Geyah und erzählt ihr, was Garrosh Euch gesagt hat."}},
[10173] = {"Der Stab des Erzmagiers", nil, {"Besorgt Erzmagier Vargoths Stab von Ekkorash der Inquisitorin und bringt ihn zu Ravandwyr in Area 52. Um Ekkorash aus ihrem Versteck hervorzulocken, streut das Beschwörungspulver auf die Kohlenpfanne in der Mitte der Ruinen."}},
[10174] = {"Der Fluch des Violetten Turms", nil, {"Benutzt Erzmagier Vargoths Stab um Kontakt mit Erzmagier Vargoth aufzunehmen.\n\nFalls Ihr Erzmagier Vargoths Stab verlieren solltet, bittet Ravandwyr in Area 52 um Ersatz."}},
[10175] = {"Thrall, Sohn Durotans", nil, {"Reist nach Orgrimmar und überbringt Thrall Geyahs Nachricht."}},
[10176] = {"Ar'kelos der Wächter", nil, {"Betretet den Violetten Turm in der Mitte von Kirin'Var und besiegt Ar'kelos. Sprecht danach mit Erzmagier Vargoth."}},
[10177] = {"Ärger in Auchindoun", nil, {"Meldet Euch bei Feldkommandeur Mahfuun beim Eingang zum Schattenlabyrinth in Auchindoun in den Wäldern von Terokkar."}},
[10178] = {"Findet Spion To'gun", nil, {"Findet Spion To'gun im Schattenlabyrinth von Auchindoun."}},
[10179] = {"Der Hüter von Kirin'Var", nil, {"Sprecht mit Hüter Dierwert in Kirin'Var im Nethersturm."}},
[10180] = {"Kann es einfach nicht lassen", nil, {"Sprecht mit Isfar beim Eingang zu den Sethekkhallen in Auchindoun."}},
[10182] = {"Kampfmagier Dathric", nil, {"Sucht die Wohnung von Kampfmagier Dathric in der Zaubergasse und bringt die Klinge zurück in sein Waffenregal. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
[10183] = {"Auf nach Area 52", nil, {"Bringt die Notizen des Netheorologen zu Raketenchef Doppeldecker in Area 52 im Nethersturm."}},
[10184] = {"Bösartige Überbleibsel", nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr 20 abtrünnige Geister auf dem Ratsplatz tötet."}},
[10185] = {"Schlimmer als der Tod", nil, {"Tötet 10 Magiertöter und 10 Manasucher und kehrt zu Hüter Dierwert in Kirin'Var zurück."}},
[10186] = {"Ihr seid eingestellt!", nil, {"Sammelt 10 Astrallithiummatrixkristalle und kehrt dann zu Raketenchef Doppeldecker in Area 52 im Nethersturm zurück."}},
[10187] = {"Eine Nachricht für den Erzmagier", nil, {"Sprecht mit Erzmagier Vargoth im Violetten Turm im Nethersturm."}},
[10188] = {"Das Siegel von Krasus", nil, {"Holt Euch das Siegel von Krasus von Zauberbinderin Maryana in der Manaschmiede Coruu, nordwestlich vom Violetten Turm."}},
[10189] = {"Manaschmiede B'naar", nil, {"Spionagemeister Thalodien in Area 52 möchte, dass Ihr zum Rand der Manaschmiede B'naar geht und die Stammrolle von B'naar von Hauptmann Arathyn besorgt.\n\nDas Abschließen von Aufgaben für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10190] = {"Batterien aufladen", nil, {"Benutzt den Batterieladeblaster bei genügend Phasenjägern, um den Batterieladestatus auf 10 zu bringen. Bringt die Batterie danach zu Bot-Spezialistin Alley in den Ruinen von Enkaat im Nethersturm zurück."}},
[10191] = {"Nummer V lebt!", nil, {"Begleitet Maxx E. Million Nr. V durch die Ruinen von Enkaat und wieder in Sicherheit. Sprecht dann mit Bot-Spezialistin Alley außerhalb der Ruinen von Enkaat im Nethersturm."}},
[10192] = {"Krasus' Kompendium", nil, {"Holt Euch Krasus' Kompendium - Kapitel 1, Krasus' Kompendium - Kapitel 2 und Krasus' Kompendium - Kapitel 3 vom Ratsplatz von Kirin'Var."}},
[10193] = {"Hochrangige Ziele", nil, {"Spionagemeister Thalodien in Area 52 möchte, dass Ihr in die Manaschmiede B'naar geht und 2 Warpmeister des Sonnenzorns, 6 Warpingenieure des Sonnenzorns und 8 Geologen des Sonnenzorns tötet.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10194] = {"Heimlicher Flug", nil, {"Sprecht mit Veronia außerhalb von Area 52 und fliegt zur Manaschmiede Coruu.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10196] = {"Mehr Arakkoafedern", nil, {"Bringt 30 Arakkoafedern zu Malukaz in der Steinbrecherfeste."}},
[10197] = {"Eine überzeugende Verkleidung", nil, {"Bringt ein Set Arkanistenroben des Sonnenzorns, ein Paar Forscherhandschuhe des Sonnenzorns und ein Gardistenmedaillon des Sonnenzorns zu Caledis Morgenhell bei der Manaschmiede Coruu im Nethersturm.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10198] = {"Informationen sammeln", nil, {"Benutzt die Sonnenzornverkleidung, geht in die Manaschmiede Coruu und belauscht das Gespräch zwischen Sonnenzornkommandant Morgenschmied und Arkanist Ardonis. Meldet Euch bei Caledis Morgenhell in der Manaschmiede Coruu, wenn Ihr die Aufgabe abgeschlossen habt.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10199] = {"Der kleine Extrakick", nil, {"Besorgt 5 Netherrochenstachel und bringt sie zu Stiefels in Area 52 im Nethersturm."}},
[10200] = {"Rückkehr zu Thalodien", nil, {"Kehrt zu Spionagemeister Thalodien in Area 52 im Nethersturm zurück."}},
[10201] = {"Der Moment der Wahrheit", nil, {"Geht zu Grek und bittet ihn, das Öl an seiner Waffe auszuprobieren. Bringt das Ergebnis zu Rakoria, wenn er fertig ist."}},
[10202] = {"Ein Abtrünniger", nil, {"Geht zur Manaschmiede Ara und sprecht mit Magister Theledorn.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10203] = {"Unschätzbare Ausrüstung beamen", nil, {"Benutzt den ultrastarken Dekonsolidierungsstrahler, um den Hyperdreh-Grab-o-Matik, die servopneumatische Baggerklaue, den multispektralen Geländeabtaster und den großen Wagen voller Sprengstoff zurück nach Area 52 zu beamen. Meldet Euch dann beim leitenden Ingenieur Zischeldruck beim östlichen Ende des Risses in Area 52 im Nethersturm."}},
[10204] = {"Blutjuwelenkristalle", nil, {"Magistrix Larynna möchte, dass Ihr zur Manaschmiede B'naar geht und einen Blutjuwelensplitter von einem Magister des Sonnenzorns besorgt. Benutzt den Splitter in der Nähe von größeren Blutjuwelenkristallen und kehrt zu ihr in Area 52 zurück.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10205] = {"Sphärenräuber Nesaad", nil, {"Tötet Sphärenräuber Nesaad und kehrt dann zu Netherpirscher Khay'ji in Area 52 im Nethersturm zurück."}},
[10206] = {"Wählt Eure Rolle", nil, {"Sammelt 10 Stück Astraltechnologie und bringt sie zu Papa Mauschel in Area 52 im Nethersturm."}},
[10208] = {"Die Verstärkung abschneiden", nil, {"Besiegt die Feinde um Portal Xilus und Portal Kruul herum, um dämonische Runensteine zu sammeln.\n\nBenutzt To'archs Zünder in Portal Xilus und Portal Kruul.\n\nKehrt danach mit To'archs Zünder zu To'arch im Häschersturz auf der Höllenfeuerhalbinsel zurück."}},
[10209] = {"Beschwörer Kanthins Preis", nil, {"Besiegt Beschwörer Kanthin in der Manaschmiede Duro auf der Insel nördlich des Sonnenzornpostens und holt den Stein des Gletscharius zurück."}},
[10210] = {"A'dal", nil, {"Sprecht mit A'dal, Anführer der Sha'tar in der Mitte der Terrasse des Lichts."}},
[10211] = {"Stadt des Lichts", nil, {"Folgt Khadgars Diener und hört Euch an, was er zu erzählen hat. Kehrt nach dieser Aufgabe zu Khadgar zurück."}},
[10212] = {"Held der Mag'har", nil, {"Kehrt zu Großmutter Geyah in Garadar in Nagrand zurück."}},
[10213] = {"Untersuchung der Absturzstelle", nil, {"Durchsucht die Absturzstelle nach Überlebenden."}},
[10214] = {"Bringt diese Mine zum beben!", nil, {"Tötet 15 Pioniere der Gan'arg und kehrt dann zu Großknecht Razelcraz zurück."}},
[10216] = {"Sicherheit geht vor", nil, {"Konstrukteur Morphalius möchte, dass Ihr 10 astrale Grufträuber, 5 astrale Zauberer, 5 Nexuswandler und 5 astrale Zauberbinder tötet.\n\nWenn Ihr damit fertig seid, sucht den astralen Transporter in der Kammer vor Shaffars Kammern und aktiviert ihn."}},
[10218] = {"Jemandes Arbeit zahlt sich aus", nil, {"Begleitet Kryoingenieur Sha'heen sicher durch die Managruft, damit er die Astralmaterie aus Shaffars Astralsammlern entnehmen kann.\n\nMeldet Euch bei Nexusprinz Haramad außerhalb der Managruft beim Lager des Konsortiums, wenn Ihr erfolgreich wart."}},
[10220] = {"Bringt sie zum Zuhören", nil, {"Mordiba möchte, dass Ihr 12 unerschütterliche Fußsoldaten, 8 unerschütterliche Krieger und 6 unerschütterliche Zauberhexer beim Expeditionsrüstlager westlich des Rückenbrecherpostens tötet."}},
[10221] = {"Dr. Bumm!", nil, {"Tötet Dr. Bumm und kehrt dann mit den guten Nachrichten zu dem leitenden Ingenieur Zischeldruck in Area 52 im Nethersturm zurück."}},
[10222] = {"Die Garnison des Sonnenzornpostens", nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr 10 Bogenschützen des Sonnenzorns tötet."}},
[10223] = {"Nieder mit Daellis", nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr Meister Daellis Morgenschlag tötet."}},
[10224] = {"Essenzen für die Motoren", nil, {"Sammelt 7 Essenzen eines Managespensts und bringt sie zu Chefingenieur Trep bei Area 52 im Nethersturm."}},
[10225] = {"Meldung beim Chefingenieur", nil, {"Bringt die Astrallithiummatrixkristalle zu Chefingenieur Trep in Area 52 im Nethersturm."}},
[10226] = {"Elementarenergieextraktor", nil, {"Extrahiert 5 Einheiten Elementarenergie und bringt sie zu Chefingenieur Trep in Area 52 im Nethersturm."}},
[10227] = {"Ich sehe tote Draenei", nil, {"Sprecht mit Ramdor dem Wahnsinnigen auf dem Knochenhaufen außerhalb der Auchenaikrypta."}},
[10228] = {"Ezekiel", nil, {"Sprecht mit Ezekiel von Shattrath in Shattrath."}},
[10229] = {"Den Folianten entziffern", nil, {"Bringt den geheimnisvollen Folianten zu Althen dem Historiker im Rückenbrecherposten."}},
[10230] = {"Das Kampfhorn", nil, {"Besorgt Euch das Kampfhorn der Unerschütterlichen von Feldkommandant Thalvos und bringt es zu Althen dem Historiker im Rückenbrecherposten."}},
[10231] = {"Welches Buch? Ich sehe kein Buch.", nil, {"Ezekiel sagt, dass das Buch gestohlen wurde. Eure einzige Spur führt Euch zu den Grobianen. Schaut nach, ob sie irgendwelche Informationen haben."}},
[10232] = {"Legion in Trümmern", nil, {"Tötet 5 Schicksalsschmiede der Mo'arg und 15 Ingenieure der Gan'arg. Kehrt danach zu Papa Mauschel in Area 52 im Nethersturm zurück."}},
[10233] = {"Den Sonnenzornposten anzünden", nil, {"Benutzt eine flammende Fackel, zerstört 4 Sonnenzornballisten und 4 Sonnenzornzelte und kehrt dann zu Zaubererleutnant Morran in Kirin'Var zurück."}},
[10234] = {"Der Müll eines Dämons", nil, {"Sammelt 10 Teile eines Teufelshäschers und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10235] = {"Verdammnisklaue die Krallen stutzen", nil, {"Besorgt Euch Verdammnisklaues Hand und bringt sie zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10236] = {"Die Scherbenwelt ist doof!", nil, {"Großknecht Razelcraz möchte, dass Ihr ihm 6 Schredderteile besorgt. Kehrt danach zu ihm bei der Mine nordwestlich von Thrallmar zurück."}},
[10237] = {"Warnt Area 52!", nil, {"Überbringt Raketenchef Doppeldecker in Area 52 in Nethersturm die schlechten Nachrichten."}},
[10238] = {"Wie man Goblins zubereitet", nil, {"Rettet Manni, Moh und Jakk aus den Lagern der Höllenorcs und bringt sie zu Großknecht Razelcraz bei der Mine nordwestlich von Thrallmar zurück."}},
[10239] = {"Mögliche Energiequelle", nil, {"Zaubererleutnant Morran in Kirin'Var möchte, dass Ihr ihm 10 Energieisolationswürfel von den Blutelfen der Manaschmiede Coruu bringt."}},
[10240] = {"Eine Verteidigung errichten", nil, {"Benutzt das Gerät zur Aktivierung von Runen bei der östlichen Rune, der nordöstlichen Rune und der westlichen Rune. Die Runen befinden sich entlang der Verteidigung der Zaubergasse und des Ratsplatz von Kirin'Var. Kehrt zu Zaubererleutnant Morran zurück, wenn Ihr die Aufgabe erfüllt habt."}},
[10241] = {"Ablenkung in der Manaschmiede B'naar", nil, {"Tötet 8 Magister des Sonnenzorns und 8 Blutwärter des Sonnenzorns und kehrt dann zu Exarch Orelis in Area 52 zurück.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Ruf bei den Sehern."}},
[10242] = {"Der Rückenbrecherposten", nil, {"Apothekerin Zelana beim Häschersturz möchte, dass Ihr mit Schwadronskommandant Brack sprecht, um auf einem Flügeldrachen zum Rückenbrecherposten zu fliegen. Bringt die Blutprobe des Blutenden Auges zu Apotheker Albreck im Rückenbrecherposten."}},
[10243] = {"Die Technologie der Naaru", nil, {"Geht in die Manaschmiede B'naar und sucht die Steuerkonsole von B'naar.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10244] = {"Liesmich", nil, {"Bringt die Bauanleitung für Teufelshäscher zu Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10245] = {"Steuerungsabschrift von B'naar", nil, {"Bringt die Steuerungsabschrift von B'naar zu Anachoret Karja in Area 52 im Nethersturm.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10246] = {"Angriff auf Manaschmiede Coruu", nil, {"Reist zur Manaschmiede Coruu und tötet 8 Arkanisten des Sonnenzorns und 5 Forscher des Sonnenzorns. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10247] = {"Dr. h. c. tüft. Vomisa", nil, {"Sprecht mit Dr. h. c. tüft. Vomisa in den Versuchsgründen im Nethersturm."}},
[10248] = {"Du, der Roboter", nil, {"Aktiviert die Steuerung für Schrotthäscher X6000 und testet seine Fähigkeiten aus. Gebt Dr. h. c. tüft. Vomisa bei den Versuchsgründen im Nethersturm danach Eure Rückmeldung."}},
[10249] = {"Zurück zum Chef!", nil, {"Meldet Euch sofort bei Raketenchef Doppeldecker in Area 52 im Nethersturm!"}},
[10250] = {"Blutige Rache", nil, {"Stoßt am alten Belagerungsturm der Allianz in das Kampfhorn der Unerschütterlichen. Tötet Urtrak und kehrt danach zu Althen dem Historiker im Rückenbrecherposten zurück."}},
[10251] = {"Der große Plan des Meisters?", nil, {"Sucht Nitrin den Gelehrten im verlassenen Rüstlager in Nagrand."}},
[10252] = {"Vision der Toten", nil, {"Nitrin im verlassenen Rüstlager in Nagrand möchte, dass Ihr ihm 1 Augapfel eines Berggronns, 1 fehlerloser Schnabel eines großen Windrocs und 1 Tran eines gealterten Grollhufs bringt."}},
[10253] = {"Levixus der Seelenrufer", nil, {"Vernichtet Levixus und nehmt Euch das Buch der Toten aus seinem Besitz.\n\nBringt das Buch der Toten zu Ramdor dem Wahnsinnigen außerhalb der Auchenaikrypta am Eingang zu Auchindoun.\n\nIhr hattet eine Vereinbarung, erinnert Ihr Euch?"}},
[10254] = {"Truppenkommandant Danath", nil, {"Marschall Isildor möchte, dass Ihr Euch bei Truppenkommandant Danath Trollbann in der Ehrenfeste meldet."}},
[10255] = {"Das Gegengift testen", nil, {"Probiert das cenarische Gegengift an einem bulligen Hölleneber aus und beobachtet die Folgen. Sobald Ihr die Ergebnisse des Experiments habt, kehrt zu Thiah Rotmähne im Posten des Cenarius auf der Höllenfeuerhalbinsel zurück."}},
[10256] = {"Den Schlüsselmeister finden", nil, {"Sucht Apex und besorgt Euch Apex' Kristallfokus. Kehrt dann zum Violetten Turm zurück und benutzt Apex' Kristallfokus in der Nähe von Erzmagier Vargoths Kugel um herauszufinden, wer den Schlüssel zum Fluch des Turms hat."}},
[10257] = {"Den Schlüsselstein erobern", nil, {"Reist zur Botanika der Festung der Stürme und besorgt Euch den Schlüsselstein von Kommandant Sarannis. Bringt ihn danach zu Erzmagier Vargoth im Violetten Turm."}},
[10258] = {"Die Gefallenen ehren", nil, {"Meldet Euch bei Hogarth, dem Kommandanten des Expeditionsrüstlagers."}},
[10259] = {"Auf in den Kampf!", nil, {"Tötet 6 einfallende Teufelswachen und kehrt dann zum Argentumabgesandten Stolzbrunn zurück."}},
[10260] = {"Netheorologe Kupfernickel", nil, {"Sprecht mit Netheorologe Kupfernickel bei der Brücke, die vom Nethersturm zum Schergrat führt."}},
[10261] = {"Gesucht: Vernichterservo!", nil, {"Besorgt einen Vernichterservo und bringt ihn zu Papa Mauschel in Area 52 im Nethersturm."}},
[10262] = {"Ein Hügel voll Astraler", nil, {"Sammelt 10 Insignien der Zaxxis und kehrt dann zu Netherpirscher Khay'ji in Area 52 im Nethersturm zurück."}},
[10263] = {"Dem Konsortium helfen", nil, {"Sprecht mit Netherpirscher Khay'ji in Area 52 im Nethersturm."}},
[10264] = {"Dem Konsortium helfen", nil, {"Sprecht mit Netherpirscher Khay'ji in Area 52 im Nethersturm."}},
[10265] = {"Kristallsammlung des Konsortiums", nil, {"Besorgt ein Kristallartefakt von Arklon und bringt es zu Netherpirscher Khay'ji in Area 52 im Nethersturm."}},
[10266] = {"Bitte um Unterstützung", nil, {"Sucht Gahruj und bietet ihm Eure Hilfe an. Er befindet sich im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10267] = {"Rechtmäßiger Besitz", nil, {"Sammelt 10 Kästen mit Vermessungsausrüstung und bringt sie zu Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10268] = {"Eine Audienz beim Prinzen", nil, {"Bringt die Vermessungsausrüstung zum Bild von Nexusprinz Haramad in der Sturmsäule im Nethersturm."}},
[10269] = {"Triangulationspunkt Eins", nil, {"Benutzt das Triangulationsgerät, um den Weg zum ersten Triangulationspunkt zu bestimmen. Wenn Ihr ihn gefunden habt, meldet die Position an Händler Hazzin beim Wachposten des Protektorats auf der Insel der Manaschmiede Ultris im Nethersturm."}},
[10270] = {"Ein nicht-so-bescheidenes Angebot", nil, {"Besorgt Euch die Teleporterenergiezelle und bringt sie zur Astralteleportplattform im Sternensturz östlich der Manaschmiede Ultris. Aktiviert die Plattform und bringt die Teleporterenergiezelle zum Bild von Windhändler Marid, damit er sie einbauen kann."}},
[10271] = {"Zur Sache", nil, {"Sammelt 8 Einheiten Netherdrachenessenz und kehrt dann zur Gegend bei der Astralteleporterplattform zurück. Die Netherdrachenessenz könnt Ihr von Netherdrachen oder Nethergroßdrachen sammeln."}},
[10272] = {"Ein viel versprechender Anfang", nil, {"Sammelt 15 Netherdrachkineier und kehrt dann zu Tyri und Jorad im Sternensturz zurück."}},
[10273] = {"Störende Ablenkung", nil, {"Sprecht mit Windhändler Marid und lockt ihn von seinem Handelsposten fort. Vernichtet ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück."}},
[10274] = {"Den Sternensturz sichern", nil, {"Benutzt die Herausforderung des blauen Drachenschwarms um Veraku am südlichen Ende des Sternensturzes zu provozieren. Besiegt ihn und kehrt dann zu Tyri und Jorad im Sternensturz zurück."}},
[10275] = {"Triangulationspunkt Zwei", nil, {"Benutzt das Triangulationsgerät, um den Weg zum zweiten Triangulationspunkt zu bestimmen. Wenn Ihr ihn gefunden habt, meldet die Position an Windhändler Tuluman auf Tulumans Landeplatz auf der anderen Seite der Brücke zu der Insel der Manaschmiede Ara im Nethersturm."}},
[10276] = {"Das volle Dreieck", nil, {"Besorgt Euch den Kristall von Ata'mal und bringt ihn zum Bild von Nexusprinz Haramad in der Sturmsäule im Nethersturm."}},
[10277] = {"Die Höhlen der Zeit", nil, {"Andormu in den Höhlen der Zeit möchte, dass Ihr der Bewahrerin der Zeit durch die Höhle folgt."}},
[10278] = {"Sphärenrisse", nil, {"Benutzt den instabilen Sphärenrissgenerator in den Sphärenfeldern. Sammelt 3 Einheiten Sphärennether von instabilen Leerwandlern und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück."}},
[10279] = {"Zum Hort des Meisters", nil, {"Sprecht mit Andormu in den Höhlen der Zeit."}},
[10280] = {"Sonderlieferung nach Shattrath", nil, {"Bringt den Kristall von Ata'mal zu A'dal auf der Terrasse des Lichts in Shattrath."}},
[10281] = {"Formelle Vorstellung", nil, {"Sucht Tyri am nördlichen Ende des Sternensturzes und sprecht mit ihr."}},
[10282] = {"Das alte Hügelland", nil, {"Andormu in den Höhlen der Zeit bittet Euch, ins Alte Hügelland zu reisen und mit Erozion zu sprechen."}},
[10283] = {"Tarethas Ablenkungsmanöver", nil, {"Reist zur Burg Durnholde und platziert mithilfe des Bündels mit Brandbomben, das Ihr von Erozion erhalten habt, 5 Brandsätze auf den Fässern in jeder Internierungsbaracke.\n\nSprecht mit Thrall im Kellergefängnis der Burg Durnholde, wenn Ihr die Internierungsbaracken angezündet habt."}},
[10284] = {"Flucht aus Durnholde", nil, {"Gebt Thrall Bescheid, wenn Ihr bereit seid. Folgt Thrall aus der Burg Durnholde und helft ihm, Taretha zu befreien und sein Schicksal zu erfüllen.\n\nSprecht mit Erozion im Alten Hügelland, wenn Ihr diese Aufgabe erfüllt habt."}},
[10285] = {"Rückkehr zu Andormu", nil, {"Kehrt zu dem jungen Andormu in den Höhlen der Zeit in Tanaris zurück."}},
[10286] = {"Arelions Geheimnis", nil, {"Magistrix Carinda möchte, dass Ihr Magister Aledis befragt. Aledis wurde zuletzt auf einer Reise von der Falkenwacht zu den Zangarmarschen gesehen."}},
[10287] = {"Die enttarnte Geliebte", nil, {"Kehrt zu Magistrix Carinda in die Falkenwacht auf der Höllenfeuerhalbinsel zurück."}},
[10288] = {"Ankunft in der Scherbenwelt", nil, {"Kommandant Duron beim Dunklen Portal auf der Höllenfeuerhalbinsel möchte, dass Ihr Durons Bericht zu Flugmeister Amish Wildhammer bringt."}},
[10289] = {"Reise nach Thrallmar", nil, {"Nehmt Vlagga Freyfeders Windreiter nach Thrallmar. Bringt Orions Bericht zu General Krakork."}},
[10290] = {"Auf der Suche nach Farahlit", nil, {"Sammelt 4 Stück Rohfarahlit und kehrt dann zu Zuben Elgenubi in der Sturmsäule zurück."}},
[10291] = {"Bericht an Nazgrel", nil, {"General Krakork in Thrallmar möchte, dass Ihr mit Nazgrel sprecht."}},
[10292] = {"Mehr Energie!", nil, {"Zuben Elgenubi möchte, dass Ihr 8 Schusswaffen der Legion besorgt und sie zur Sturmsäule bringt."}},
[10293] = {"Die Kristallader anzapfen", nil, {"Zuben Elgenubi in der Sturmsäule möchte, dass Ihr einen Farahlitkern besorgt."}},
[10294] = {"Der Leerengrat", nil, {"Geht zum Leerengrat und tötet die Kreaturen, die Ihr dort findet. Sammelt 40 Seelensplitter des Leerengrats und kehrt dann zu Ogath dem Verrückten im Rückenbrecherposten zurück."}},
[10295] = {"Aus dem Abgrund", nil, {"Tötet Galaxis und sammelt seinen Seelensplitter. Bringt den Splitter zu Ogath dem Verrückten im Rückenbrecherposten."}},
[10296] = {"Der schwarze Morast", nil, {"Reist durch die Höhlen der Zeit in den schwarzen Morast während der Öffnung des Dunklen Portals und sprecht mit Sa'at."}},
[10297] = {"Die Öffnung des Dunklen Portals", nil, {"Sa'at im schwarzen Morast der Höhlen der Zeit möchte, dass Ihr Medivh verteidigt, bis er das Dunkle Portal geöffnet hat.\n\nKehrt zu Sa'at zurück, wenn Ihr erfolgreich wart."}},
[10298] = {"Held des Schwarms", nil, {"Kehrt zum Kind, Andormu, in den Höhlen der Zeit in Tanaris zurück."}},
[10299] = {"Manaschmiede B'naar abschalten", nil, {"Anachoret Karja möchte, dass Ihr in die Manaschmiede B'naar geht und einen Zugangskristall von Vorarbeiter Theredis besorgt. Benutzt ihn bei der Steuerkonsole von B'naar, um sie abzuschalten und meldet Euch danach bei Anachoret Karja zurück.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10300] = {"Den Stab wiederherstellen", nil, {"Besorgt 4 fehlerlose Kristallsplitter von den Geistern in den Ruinen von Enkaat und bringt sie zu Ravandwyr in Area 52."}},
[10301] = {"Das Kompendium öffnen", nil, {"Besorgt Euch das heliotrope Oculus von Zauberhäscherin Marathelle im Sonnenzornposten."}},
[10302] = {"Unberechenbare Mutanten", nil, {"Tötet 8 unberechenbare Mutanten und kehrt dann zu Botanikerin Taerix bei der Absturzstelle im Am'mental zurück."}},
[10303] = {"Die Blutelfen", nil, {"Tötet 10 Blutelfenspäher und kehrt dann zu Tolaan im Schattenkamm im Am'mental zurück."}},
[10304] = {"Verteidiger Aldar", nil, {"Sprecht mit Verteidiger Aldar bei der Absturzstelle im Am'mental."}},
[10305] = {"Bannzauberin Belmara", nil, {"Sucht die Wohnung von Bannzauberin Belmara in der Zaubergasse und bringt das Buch zurück auf seinen Platz im Bücherregal. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
[10306] = {"Beschwörer Luminrath", nil, {"Sucht die Wohnung von Beschwörer Luminrath in der Zaubergasse und bringt den Mantel zurück in seine Kommode. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
[10307] = {"Cohlien Frostwirker", nil, {"Sucht die Wohnung von Cohlien Frostwirker in der Zaubergasse und bringt die Kappe zurück in seine Schließkiste. Sprecht danach mit Hüter Dierwert in Kirin'Var."}},
[10308] = {"Noch ein Hügel voller Astraler", nil, nil},
[10309] = {"Teufelshäscher mit Herz", nil, {"Besorgt das Herz des Teufelshäschers und bringt es zu N. D. Mieskamp beim Hügel im Nethersturm."}},
[10310] = {"Sabotage am Warptor!", nil, {"Begleitet Drijya zum Warptor beim Invasionspunkt: Zerstörer und sorgt für seine Sicherheit, während er das Tor sabotiert. Sprecht danach mit Gahruj im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10311] = {"Drijya braucht Eure Hilfe", nil, {"Sprecht mit Drijya in der Nähe des Invasionspunkts: Zerstörer im Nethersturm."}},
[10312] = {"Die Annalen von Kirin'Var", nil, {"Besorgt die Annalen von Kirin'Var aus dem Rathaus von Kirin'Var und bringt es zu Hüter Dierwert."}},
[10313] = {"Warpenergien messen", nil, {"Exarch Orelis in Area 52 möchte, dass Ihr die Warpmesskugel zur Manaschmiede B'naar bringt und sie in der Nähe der vier Rohrleitungen benutzt.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eurer Ehre bei den Sehern."}},
[10314] = {"Ein hartnäckiger Verdacht", nil, {"Sammelt 10 abscheuliche Überreste von den wahnsinnigen Geistern auf dem Kapellenhof in Kirin'Var und bringt sie zu Hüter Dierwert."}},
[10315] = {"Die Nethermanten neutralisieren", nil, {"Tötet 15 Nethermanten des Sonnenzorns und kehrt dann zu Netherpirscher Oazul auf Tulumans Landeplatz im Nethersturm zurück."}},
[10316] = {"Auf der Suche nach Beweisen", nil, {"Durchsucht die Häuser auf dem Kapellenhof von Kirin'Var nach Hinweisen darauf, wer sich hinter der Beeinflussung der Geister verbirgt."}},
[10317] = {"Den Großknecht erledigen", nil, {"Vernichtet Großknecht Sonnenend in der Trelleummine, die sich unter der Manaschmiede Ara im Nethersturm befindet."}},
[10318] = {"Den Übermeister erledigen", nil, {"Tötet Übermeister Grindgarr. Kehrt dann zu Windhändler Tuluman auf Tulumans Landeplatz im Nethersturm."}},
[10319] = {"Das Phylakterium entwenden", nil, {"Holt Naberius' Phylakterium vom Kapellenhof in Kirin'Var und bringt es zu Hüter Dierwert."}},
[10320] = {"Vernichtet Naberius!", nil, {"Hüter Dierwert in Kirin'Var möchte, dass Ihr Naberius auf dem Kapellenhof des Dorfes besiegt."}},
[10321] = {"Manaschmiede Coruu abschalten", nil, {"Reist zur Manaschmiede Coruu, östlich von Area 52, und besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Manaschmiede abzuschalten, und kehrt dann zu Anachoret Karja zurück.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10322] = {"Manaschmiede Duro abschalten", nil, {"Anachoret Karja möchte, dass Ihr zur Manaschmiede Duro geht und den Zugangskristall von Vorarbeiter Athanel besorgt. Benutzt ihn, um die Steuerungskonsole von Duro abzuschalten.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10323] = {"Manaschmiede Ara abschalten", nil, {"Reist zur Manaschmiede Ara und besorgt Euch einen Zugangskristall von Vorarbeiter Azarad. Benutzt ihn, um die Steuerungskonsole von Ara abzuschalten.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10324] = {"Die große Mondweidenjagd", nil, {"Bringt 6 Bälge eines Mondweidenbocks zu Acteon in der Azurwacht."}},
[10325] = {"Male von Kil'jaeden", nil, {"Bringt 10 Male von Kil'jaeden zu Adyen dem Lichtwächter in Shattrath.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10326] = {"Mehr Male von Kil'jaeden", nil, nil},
[10327] = {"Einzelne Male von Kil'jaeden", nil, nil},
[10328] = {"Anweisungen des Sonnenzorns", nil, {"Reist zur Manaschmiede Duro und beschafft die Truppenbefehle des Sonnenzorns und die Arkanbefehle des Sonnenzorns von den Einheiten des Sonnenzorns, die dort postiert sind. Kehrt zu Exarch Orelis zurück, wenn Ihr Eure Aufgabe erledigt habt.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10329] = {"Manaschmiede B'naar abschalten", nil, {"Geht zurück zur Manaschmiede B'naar und besorgt Euch den Zugangskristall von Vorarbeiter Theredis. Benutzt ihn bei der Steuerkonsole von B'naar um sie abzuschalten und meldet Euch danach bei Spionagemeister Thalodien zurück.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10330] = {"Manaschmiede Coruu abschalten", nil, {"Besorgt den Zugangskristall von Coruu von Vorarbeiterin Seylanna. Benutzt ihn, um die Steuerkonsole der Manaschmiede abzuschalten und meldet Euch dann bei Caledis Morgenhell.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10331] = {"Unverzichtbare Werkzeuge", nil, {"Besorgt den Schmiedehammer von den Lehrlingen von Kirin'Var und bringt ihn zurück zu Lehrling Andrethan in Kirin'Var."}},
[10332] = {"Meisterschmied Rhonsus", nil, {"Lehrling Andrethan in Kirin'Var möchte, dass Ihr den Geist von Rhonsus, dem Meisterschmied des Dorfes, vernichtet."}},
[10333] = {"Helft Mama Mauschel", nil, {"Sprecht mit Mama Mauschel im Mittelreichposten in der Biokuppel Mittelreich im Nethersturm."}},
[10334] = {"Braucht mehr Kuhglocke", nil, {"Sucht Bessy in den Feldern nordöstlich des Kapellenhügels von Kirin'Var im Nethersturm."}},
[10335] = {"Die Ruinen beobachten", nil, {"Platziert die drei Vermessungsmarkierungen und kehrt dann zu Zephirion in der Sturmsäule im Nethersturm zurück."}},
[10336] = {"Diener von Culuthas", nil, {"Tötet 10 Hunde von Culuthas und 5 Augen von Culuthas. Kehrt danach zu Netherpirscher Nauthis in der Sturmsäule im Nethersturm zurück."}},
[10337] = {"Almabtrieb", nil, {"Begleitet Bessy sicher zu ihrem Besitzer Thadell zurück."}},
[10338] = {"Manaschmiede Duro abschalten", nil, {"Kehrt zur Manaschmiede Duro zurück und besorgt Euch den Zugangskristall von Vorarbeiter Athanel. Benutzt ihn, um die Steuerkonsole von Duro abzuschalten, und meldet Euch danach bei Spionagemeister Thalodien.\n\nDas Ausführen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10339] = {"Das Astraleum", nil, {"Kommandant Ameer möchte, dass Ihr 5 Auftragsmörder des Astraleums, 5 Stoßtruppsoldaten des Astraleums, 2 Forscher des Astraleums und Hauptmann Zovax tötet und dann den Transponder des Astraleums Zeta aktiviert."}},
[10340] = {"Trümmerposten", nil, {"Vorpostenkommandant Königstein möchte, dass Ihr mit Schwadronskommandant Dabir'ee sprecht, auf einem Greifen zum Trümmerposten fliegt und Euch bei Runetog Wildhammer meldet."}},
[10341] = {"Tretet sie, solange sie am Boden liegen", nil, {"Tötet 8 Beschwörer des Sonnenzorns, 6 Jadgschützen des Sonnenzorns und 4 Zenturios des Sonnenzorns. Kehrt zu Magistrix Larynna in Area 52 zurück, wenn Ihr die Aufgabe erledigt habt.\n\nDas Abschließen von Quests für die Seher führt zu einer Verringerung Eures Rufs bei den Aldor."}},
[10342] = {"Schieferhautschiefer", nil, {"Sammelt 5 Stück Schieferhautschiefer und bringt sie zu Stiefels in Area 52 im Nethersturm."}},
[10343] = {"Die unendliche Invasion", nil, {"Sucht die von Manakreaturen heimgesuchte Gegend in Kirin'Var nach einem Manabombenfragment ab und bringt sie zu Zaubererleutnant Morran."}},
[10344] = {"Schwadronskommandant Greifgar", nil, {"Sprecht mit Schwadronskommandant Greifgar im Trümmerposten."}},
[10345] = {"Fleisch geworden", nil, {"Benutzt den Anzünder des Protektorats um 12 siechende Leichen zu zerstören. Bringt den Anzünder des Protektorats zu Agent Araxes außerhalb des Zugangsschachts Zeon zurück, wenn Ihr erfolgreich wart."}},
[10346] = {"Rückkehr zur abyssischen Untiefe", nil, {"Greifnerin Heulwind im Trümmerposten auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört."}},
[10347] = {"Rückkehr zur abyssischen Untiefe", nil, {"Schwadronskommandant Brack im Häschersturz auf der Höllenfeuerhalbinsel möchte, dass Ihr 20 Peons der Gan'arg, 5 Vorarbeiter der Mo'arg und 5 Teufelskanonen zerstört."}},
[10348] = {"Neue Möglichkeiten", nil, {"Sammelt 15 Elfenbeinglocken und kehrt dann zu Mehrdad im Mittelreichposten zurück."}},
[10349] = {"Die Erdbinderin", nil, {"Bringt den Purpurkristallsplitter zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel."}},
[10350] = {"Behomat", nil, {"Sprecht mit Behomat bei der Händlertreppe in der Exodar."}},
[10351] = {"Natürliche Heilmittel", nil, {"Benutzt den Samen der Wiederbelebung beim Zirkel des Erdbinders, um das Land um den Kristall herum zu heilen. Kehrt dann zu Erdbinderin Galandria Nachtluft im Posten des Cenarius auf der Höllenfeuerhalbinsel mit Euren Beobachtungen zurück."}},
[10352] = {"Spendet Wolle", nil, nil},
[10353] = {"Arconus der Unersättliche", nil, {"Agent Sechs-Uhr beim Zugangsschacht Zeon im Nethersturm möchte, dass Ihr Arconus den Unersättlichen tötet und dann zu Kommandant Ameer im Wachposten des Protektorats im Nethersturm zurückkehrt."}},
[10354] = {"Spendet Seide", nil, nil},
[10355] = {"Siechendes Fleisch", nil, {"Sammelt 5 Proben einer verdurstenden Hydra und 5 Proben eines siechenden Sumpflords und bringt sie zu Ruam in Telredor."}},
