[10377] = {"Keine Angst", nil, nil},
[10379] = {"Berührung der Schwäche", nil, nil},
[10380] = {"Ein dunkler Pakt", nil, {"Kaylaan bei Tulumans Landeplatz in Nethersturm möchte, dass Ihr 6 Warptüftler der Gan'arg, 3 Töchter des Schicksals und 3 Warpmeister der Mo'arg tötet.\n\nDas Abschließen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10381] = {"Nie mehr Aldor", nil, {"Meldet Euch bei Exarch Orelis in Area 52.\n\nDas Ausführen von Quests für die Aldor führt zu einer Verringerung Eures Rufs bei den Sehern."}},
[10382] = {"Geht zur Front", nil, {"Nehmt einen Greifen zum Ehrenposten und sprecht mit Feldmarschall Brock."}},
[10383] = {"Dies ist ein Brotkrumen!", nil, {"Sprecht mit Jonathan LeCraft über die Kaninchen."}},
[10384] = {"Daten der Astralen", nil, {"Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr die Datenzelle des Astraleums besorgt."}},
[10385] = {"Hirnschadengefahr = hoch", nil, {"Das Bild von Kommandant Ameer beim Transponder des Astraleums Zeta im Stützpunkt des Astraleums möchte, dass Ihr 15 Melderdaten des Astraleums besorgt."}},
[10386] = {"Der Teufelshäschertöter", nil, {"Sprecht mit Oberanführer Hun Stümmelfaust."}},
[10387] = {"Der Teufelshäschertöter", nil, {"Sprecht mit Oberanführer Hun Stümmelfaust."}},
[10388] = {"Rückkehr nach Thrallmar", nil, {"Kehrt nach Thrallmar zurück und sprecht mit Nazgrel."}},
[10389] = {"Das Leid und die Dunkelheit", nil, {"Magister Blutfalke möchte, dass Ihr 10 Terrorbestien bei den Teichen von Aggonar tötet."}},
[10390] = {"Konstruktionslager: Mageddon", nil, {"Nazgrel in Thrallmar möchte, dass Ihr das Konstruktionslager: Mageddon angreift, 10 Diener der Gan'arg tötet und mit Schmiedebestie Sägeklinges Kopf zurückkehrt."}},
[10391] = {"Kanonen des Zorns", nil, {"Zerstört 3 Teufelskanonen V1 im Konstruktionslager: Zorn und kehrt dann zu Nazgrel in Thrallmar zurück."}},
