[10515] = {"Eine Lektion gelernt", nil, {"Oronok Herzeleid bei Oronoks Hof im Schattenmondtal möchte, dass Ihr 10 Eier eines gefräßigen Schinders in den zerschlagenen Weiten zerstört."}},
[10516] = {"Die Gefangenschaft der Verteidigerin", nil, {"Holt Verteidigerin Vuuleens Klinge und Verteidigerin Vuuleens Schild zurück."}},
[10517] = {"Eure Zeit ist gekommen, Gorr'Dim...", nil, {"Tötet Gorr'Dim, den Anführer der Oger in der Wehr der Speerspießer."}},
