[12173] = {"Abstimmung auf Dalaran", nil, {"Das Abbild des Erzmagiers Aethas Sonnenhäscher in Agmars Hammer möchte, dass Ihr Euch auf Nordends Leylinien abstimmt."}},
[12174] = {"Oberkommandant Halford Wyrmbann", nil, {"Überbringt Oberkommandant Halford Wyrmbann das Schreiben der Allianz. Er befindet sich in der Feste Wintergarde im Osten der Drachenöde."}},
