[12202] = {"Fotosafari", nil, {"Benutzt Rokars Kamera, um in Dun Argol die Bilder von 8 Eisenzwergen aufzunehmen und kehrt dann zu Späher Vor'takh im Camp Oneqwah zurück."}},
[12203] = {"Lokens Befehle", nil, {"Verkleidet Euch als Eisenrunenaufseher, fangt die Nachricht von Loken ab und erstattet dann Späher Vor'takh im Camp Oneqwah Bericht."}},
[12204] = {"In Lokens Namen", nil, {"Redet Mit Hugh Glass und Gavrock über Loken und übermittelt Eure Erkenntnisse Späher Vor'takh im Camp Oneqwah."}},
[12205] = {"GESUCHT: Der Scharlachrote Ansturm", nil, {"Tötet 20 Mitglieder des Scharlachroten Ansturms und meldet Euch anschließend bei Hochexekutor Zürner in Gallgrimm."}},
[12206] = {"Verseuchte letzte Riten", nil, {"Apothekerin Vicky Levine in Gallgrimm möchte, dass Ihr das Seuchenfläschchen an dem Gefangenen des Scharlachroten Ansturms austestet."}},
[12207] = {"Vordrassils Fall", nil, {"Windseherin Grauhorn in Burg Siegeshall möchte, dass Ihr zu Vordrassils Ast, Vordrassils Herz oder Vordrassils Tränen geht und 6 Schleimproben des entropischen Schlamms besorgt."}},
[12208] = {"Gute Trolljagd", nil, {"Unteroffizier Thurkin möchte, dass Ihr mit Samir am Granitquell sprecht."}},
[12209] = {"Kriegsbeute", nil, {"Quartiermeister Bartlett in Gallgrimm möchte, dass Ihr 8 Rüstungen des Scharlachroten Ansturms und 8 Waffen des Scharlachroten Ansturms stehlt."}},
[12210] = {"Trollsaison!", nil, {"Leutnant Dumont bei der Ammertannhütte möchte, dass Ihr mit Samir am Granitquell sprecht."}},
[12211] = {"Lasst sie nicht wieder auferstehen!", nil, {"Apothekerin Vicky Levine aus Gallgrimm hat Euch gebeten, Ihr Behältnis voller Ratten zu benutzen, um 15 Leichen von Anhängern des Scharlachroten Ansturms in Neuherdweiler zu beseitigen."}},
[12212] = {"Das Lagerhaus auffüllen", nil, {"Förstermeister Anderhol bei der Ammertannhütte möchte, dass Ihr Großhornhirsche tötet und 8 Stück saftiges Wildbret sammelt."}},
[12213] = {"Die Dunkelheit darunter", nil, {"Windseherin Grauhorn möchte, dass Ihr zu Vordrassils Herz, Vordrassils Tränen und Vordrassils Ast geht und die Kugel des Geomanten in den Tiefen unter der Erdoberfläche anwendet."}},
