[12217] = {"Adleraugen", nil, {"Anderhol bei der Ammertannhütte möchte, dass Ihr 5 Kaiseradler tötet und ihm anschließend Bericht erstattet."}},
[12218] = {"Verbreitet die guten Neuigkeiten", nil, {"Oberster Seuchenfürst Middleton in Gallgrimm in der Drachenöde möchte, dass Ihr in den Seuchenverbreiter der Verlassenen steigt, zu den Randgebieten der Aasfelder fahrt und dort mithilfe der Seuchenbombe 30 hungernde Tote vernichtet."}},
[12219] = {"Der eingegangene Weltenbaum", nil, {"Hierophant Thayreen bei der Ammertannhütte möchte, dass Ihr zu Vordrassils Ast, Vordrassils Herz oder zu Vordrassils Tränen geht und 6 Schleimproben des entropischen Schlamms beschafft."}},
[12220] = {"Ein dunkler Einfluss", nil, {"Hierophant Thayreen bei der Ammertannhütte will, dass Ihr zu Vordrassils Herz, Vordrassils Tränen und Vordrassils Ast geht und die Kugel des Geomanten in den Tiefen unter der Erdoberfläche anwendet."}},
[12221] = {"Die Seuche der Verlassenen", nil, {"Liefert die Seuche der Verlassenen bei Doktor Sintar Malefius in Agmars Hammer ab."}},
[12222] = {"Geheimnisse der Flammenbinder", nil, {"Bringt dem Förster Drach bei der Ammertannhütte 3 flammenerfüllte Talismane."}},
[12223] = {"Die Reihen lichten", nil, {"Tötet 12 Huscarls der Drachenschinder und kehrt anschließend zu Förster Drach in der Ammertannhütte zurück."}},
[12224] = {"Der Vorposten der Kor'kron!", nil, {"Meldet Euch bei Saurfang dem Jüngeren im Vorposten der Kor'kron."}},
[12225] = {"Mmm... Bernsteinsamen!", nil, {"Sprecht mit Förstermeister Anderhol in der Ammertannhütte."}},
[12226] = {"Wieder raus damit", nil, {"Anderhol bei der Ammertannhütte möchte, dass Ihr ihm 5 Schwarzwurzelstrünke bringt."}},
[12227] = {"Tut Eure Pflicht", nil, {"Anderhol bei der Ammertannhütte möchte, dass Ihr Anderhols Karussellstöffsche nehmt und das nahegelegene Plumpsklo aufsucht, um die teilweise verarbeiteten Bernsteinsamen zurückzuholen."}},
[12229] = {"Eine mögliche Verbindung", nil, {"Windseherin Grauhorn in der Burg Siegeswall möchte, dass Ihr 8 Proben des Bluts eines wahnsinnigen Furbolgs von den Furbolgs am Schrein des Herzbluts und am Schrein des Grollfangs sammelt."}},
[12230] = {"Von den Belagerungsschmieden klauen", nil, {"Todeswache Molder in Gallgrimm möchte, dass Ihr 6 Bomben des Belagerungsschmiedes beschafft."}},
[12231] = {"Die Jungen des Bärengottes", nil, {"Windseherin Grauhorn in der Burg Siegeswall möchte, dass Ihr mit Orsonn am Schrein des Grollfangs und Kodian am Schrein des Herzbluts sprecht."}},
[12232] = {"Bombardiert die Ballisten", nil, {"Todeswache Molder in Gallgrimm möchte, dass Ihr mit den Bomben des Belagerungsschmiedes 5 Ballisten von Neuherdweiler unter Beschuss nehmt."}},
[12233] = {"Den Samen aussäen", nil, nil},
[12234] = {"Das muss ich wissen", nil, {"Spionagemeisterin Repine möchte, dass Ihr die \"Tagesübersicht des Scharlachroten Ansturms: Abtei\", \"Tagesübersicht des Scharlachroten Ansturms: Kaserne\" und die \"Tagesübersicht des Scharlachroten Ansturms: Strand\" stehlt."}},
[12235] = {"Naxxramas und der Fall von Wintergarde", nil, {"Sprecht mit dem Greifenkommandanten Urik an der Greifenstation in der Feste Wintergarde."}},
[12236] = {"Ursoc, der Bärengott", nil, {"Windseherin Grauhorn in Burg Siegeswall möchte, dass Ihr Tur Wutpranke in der Nähe von Ursocs Höhle findet und Ursoc mit seiner Hilfe besiegt. Streut die geläuterte Asche von Vordrassil auf Ursocs Leiche, nachdem Ihr ihn getötet habt."}},
[12237] = {"Flug des Verteidigers von Wintergarde", nil, {"Rettet 10 hilflose Dorfbewohner von Wintergarde und kehrt anschließend zum Greifenkommandanten Urik in Wintergarde zurück."}},
[12238] = {"Die Reinigung Drak'Tharons", nil, {"Drakuru möchte, dass Ihr Drakurus Elixier an seinem Kohlenbecken innerhalb Drak'Tharons benutzt. Um das Elixier dort benutzen zu können, benötigt Ihr 5 Ausdauermojos."}},
[12239] = {"Der Spion in Neuherdweiler", nil, {"Liefert die Tagesübersicht des Scharlachroten Ansturms bei Agentin Skully in Neuherdweiler ab."}},
[12240] = {"Ein Mittel zum Zweck", nil, {"Apothekerin Vicky Levine in Gallgrimm möchte, dass Ihr mithilfe der Termiten der Levinefamilie Großknecht Kaleiki hervorlockt und ihn tötet."}},
[12241] = {"Zerstört den Schössling", nil, {"Windseherin Grauhorn in Burg Siegeswall möchte, dass Ihr die grüne Fackel nehmt und sie benutzt, um Vordrassils Schössling zu verbrennen. Bringt Vordrassils Asche zurück zu Windseher Grauhorn."}},
[12242] = {"Vordrassils Samen", nil, {"Windseherin Grauhorn in Burg Siegeswall möchte, dass Ihr nach Grauschlund geht und 8 Samen von Vordrassil beschafft."}},
[12243] = {"Feuer auf dem Wasser", nil, {"Apothekerin Vicky Levine in Gallgrimm möchte, dass Ihr mithilfe des Feuerwassers des Apothekers die Segel der Sündenbabel in Brand steckt.\n\nGeht unter Deck, während die Deckmatrosen damit beschäftigt sind, das Feuer zu löschen, und sucht nach Kapitän Shelys Seekarten."}},
[12244] = {"Schredder reparieren", nil, {"Synipus beim Holzfällerposten Blauhimmel möchte, dass Ihr ihm 3 kaputte Schredder bringt."}},
[12245] = {"Keine Gnade für Gefangene", nil, {"Spionagemeisterin Repine hat Euch gebeten, Todeswache Schneider, Chefschreiber Kinnedius, Ingenieur Hindermann und Kanzler Amai zu töten.\n\nFalls Ihr Erfolg habt, berichtet Hochexekutor Zürner in Gallgrimm davon."}},
[12246] = {"Eine mögliche Verbindung", nil, {"Hierophant Thayreen bei der Ammertannhütte möchte, dass Ihr am Schrein des Grollfangs und am Schrein des Herzbluts 8 Blutproben von wahnsinnigen Furbolgs sammelt."}},
[12247] = {"Kinder von Ursoc", nil, {"Hierophant Thayreen bei der Ammertannhütte möchte, dass Ihr mit Orsonn im Schrein des Grollfangs und mit Kodian im Schrein des Herzensblutes sprecht."}},
[12248] = {"Vordrassils Schössling", nil, {"Hierophant Thayreen in der Ammertannhütte möchte, dass Ihr mithilfe der grünen Fackel Vordrassils Schössling anzündet und ihr die daraus entstehende Asche von Vordrassil bringt."}},
[12249] = {"Ursoc, der Bärengott", nil, {"Hierophant Thayreen bei der Ammertannhütte möchte, dass Ihr Tur Wutpranke in der Nähe von Ursocs Höhle aufsucht und mit seiner Hilfe Ursoc besiegt. Streut anschließend die geläuterte Asche von Vordrassil auf Ursocs Leiche."}},
[12250] = {"Vordrassils Samen", nil, {"Hierophant Thayreen in der Ammertannhütte will, dass Ihr zum Grauschlund geht und 8 Samen von Vordrassil besorgt."}},
[12251] = {"Kehrt zum Oberkommandanten zurück", nil, {"Sprecht mit Oberkommandant Halford Wyrmbann in Wintergarde in der Drachenöde."}},
[12252] = {"Foltert den Folterer", nil, {"Hochexekutor Zürner in Gallgrimm möchte, dass Ihr Folterer Alphonse fünfmal mit dem Brandeisen bearbeitet und die Informationen aus ihm herausquetscht. Anschließend sollt Ihr ihn töten."}},
[12253] = {"Rettung vom Marktplatz", nil, {"Oberkommandant Halford Wyrmbann in der Feste Wintergarde in der Drachenöde möchte, dass Ihr 6 gefangene Dorfbewohner von Wintergarde befreit."}},
[12254] = {"Ohne Gebet", nil, {"Agentin Skully in Neuherdweiler möchte, dass Ihr das Gebetsbuch von Bischof Straß besorgt."}},
[12255] = {"Der Than von Voldrune", nil, {"Besiegt mithilfe von Flammenbringer den Than Torvald Eriksson und meldet Euch anschließend bei Förster Drach bei der Ammertannhütte."}},
[12256] = {"Geheimnis der Flammenbinder", nil, {"Bringt 3 flammenerfüllte Talismane zu Unteroffizier Nazgrim in Burg Siegeswall."}},
[12257] = {"Eine Demonstration der Stärke", nil, {"Tötet 12 Huscarls der Drachenschinder und kehrt zu Unteroffizier Nazgrim am Siegeswall zurück."}},
[12258] = {"Das Schicksal der Toten", nil, {"Kommandantin Lynore Windstreich in der Feste Wintergarde in der Drachenöde möchte, dass Ihr 15 rachsüchtige Spuke tötet."}},
[12259] = {"Der Than von Voldrune", nil, {"Besiegt mithilfe von Flammenbringer Than Torvald Eriksson und meldet Euch anschließend bei Unteroffizier Nazgrim in Burg Siegeswall."}},
[12260] = {"Die perfekte Heuchelei", nil, {"Agentin Skully in Neuherdweiler möchte, dass Ihr das Abbild eines Rabenpriesters des Ansturms stehlt. Bringt den magischen Spiegel der Banshee wieder zurück, wenn Ihr das getan habt."}},
[12261] = {"Kein Ausweg", nil, {"Stellt eine zerstörerische Barriere am Ende des Pfades auf, der aus dem Obsidiandrachenschrein hinausführt; dort, wo die Straße in Schnee übergeht. Schützt die Barriere, bis sie vollständig aufgeladen ist und kehrt dann zu Serinar zurück."}},
[12262] = {"Und niemand, der Euch retten wird...", nil, {"Tötet außerhalb des Obsidiandrachenschreins 6 Nekrolyten der Brennenden Tiefen und 10 glimmende Skelette. Kehrt zu Serinar zurück, sobald Eure Aufgabe abgeschlossen ist."}},
[12263] = {"Nur die besten Absichten", nil, {"Wartet, bis Serinar Euch in die Verkleidung eines Nekrolyten der Brennenden Tiefen gehüllt hat. Dringt in die Tiefen des Schlundes von Neltharion vor, um aufzudecken, was den Kult der Verdammten hierher lockt. Kehrt mit Details ihres Vorhabens zu Serinar zurück."}},
[12264] = {"Das Ausmerzen der Verdammten", nil, {"Tötet in Neltharions Schlund 3 Nekromanten der Brennenden Tiefen, 6 glimmende Konstrukte und 6 glimmende Spuke. Kehrt dann zu Serinar zurück."}},
[12265] = {"Die Schändung der Entweiher", nil, {"Findet 8 nekromantische Runen, die auf den Boden von Neltharions Schlund gezeichnet wurden und löscht sie aus. Kehrt zu Serinar zurück, sobald die Aufgabe abgeschlossen ist."}},
[12266] = {"Kunde der Zerstörung", nil, {"Kehrt zum Wyrmruhtempel zurück und lasst Yntrige wissen, dass Serinars Aufgabe abgeschlossen ist."}},
[12267] = {"Neltharions Flamme", nil, {"Äschert mit Neltharions Flamme die Beschwörungskammer im hinteren Teil von Neltharions Schlund ein. Tötet Rothin den Verfallenen, nachdem Ihr seine Aufmerksamkeit auf Euch gezogen habt, und kehrt anschließend zu Serinar am Eingang der Höhle zurück."}},
[12268] = {"Einzelteile", nil, {"Pipthwak beim Holzfällerposten Blauhimmel möchte, dass Ihr ihm 4 gefalzte Zahnräder, 3 gekerbte Winkel und 2 Hochspannungsfedern bringt."}},
[12269] = {"Nicht in unserer Mine", nil, {"Hochlord Leoric von Zeldig in der Feste Wintergarde in der Drachenöde möchte, dass Ihr 10 Bergarbeiterausweise von Wintergarde beschafft."}},
[12270] = {"Schreddert die Allianz", nil, {"Grekk im Holzfällerlager Blauhimmel möchte, dass Ihr 3 Schredder besorgt und diesen zu ihm zurückkehrt."}},
[12271] = {"Die Rute des Zwangs", nil, {"Liefert den Stab des Folterers bei Hochexekutor Zürner in Gallgrimm ab."}},
[12272] = {"Das blutende Erz", nil, {"Belagerungsingenieur Viertelblitz in der Feste Wintergarde in der Drachenöde möchte, dass Ihr 10 Proben des seltsamen Erzes aus der Mine von Wintergarde beschafft."}},
[12273] = {"Die Verleumdung", nil, {"Hochexekutor Zürner in Gallgrimm hat Euch damit beauftragt, den Stab des Zwangs auf Kommandant Jordan, Kanonierführer Zierhut, Schmied Gutmann und Stallmeister Mercer anzuwenden.\n\nTötet sie, sobald Ihr den Stab verwendet habt, um ihnen als letzte Worte die Verleumdung des Scharlachroten Ansturms abzuverlangen."}},
[12274] = {"In Ungnade fallen", nil, {"Agentin Skully in Neuherdweiler möchte, dass Ihr die Glocke der Abtei läutet und anschließend vom Hochabt die benötigten Informationen beschafft."}},
[12275] = {"Der Sprenggnom", nil, {"Sprecht mit Belagerungsingenieur Viertelblitz in der Feste Wintergarde in der Drachenöde."}},
[12276] = {"Die Suche nach Schleicha", nil, {"Sucht und findet Schleicha den Sprenggnom in den Minen von Wintergarde. Holt Euch Viertelblitz' Zielsuchroboter zu Hilfe, wenn Ihr die Minen nicht finden könnt."}},
[12277] = {"Überlasst nichts dem Zufall", nil, {"Beschafft eine Minenbombe von Wintergarde und verwendet sie, um den oberen und unteren Minenschacht von Wintergarde zu sprengen. Erstattet Belagerungsingenieur Viertelblitz bei der Feste Wintergarde in Drachenöde Bericht, sobald Ihr die Aufgabe erfüllt habt."}},
[12278] = {"Bier des Monats e.V.", nil, {"Bringt Euren Mitgliedschaftsantrag für den \"Bier des Monats e.V.\" zu Larkin Donnerbräu in der Steinfeuertaverne in Eisenschmiede."}},
[12279] = {"Bärenhunger", nil, {"Bringt Hugh Glass am Handelsposten von Rotholz 6 Nordlachse."}},
[12280] = {"Reparaturen", nil, {"Lurz beim Holzfällerposten Blauhimmel möchte, dass Ihr 4 gefalzte Zahnräder, 3 gekerbte Winkel und 2 Hochspannungsfedern sammelt."}},
[12281] = {"Die Kriegsmaschine der Geißel verstehen", nil, {"Liefert Viertelblitz' Paket bei Oberkommandant Halford Wyrmbann in der Feste Wintergarde ab."}},
[12282] = {"Spuren der Vergangenheit", nil, {"Zelig der Visionär in der Feste Wintergarde in der Drachenöde möchte, dass Ihr seine Seherkugel holt."}},
[12283] = {"Die Wahrheit wird herauskommen", nil, {"Agentin Skully möchte, dass Ihr das Tagebuch von Hochgeneral Abbendis beschafft und es zu Hochexekutor Zürner in Gallgrimm bringt."}},
[12284] = {"Haltet sie auf Trab", nil, {"Kommandant Bargok möchte, dass Ihr beim Holzfällerposten Blauhimmel 15 Einheiten oder Spieler der Allianz tötet."}},
[12285] = {"Tu anderen...", nil, {"Hochexekutor Zürner in Gallgrimm hat Euch befohlen, mit dem Kopf des Hochgenerals zurückzukehren."}},
[12286] = {"Eimer mit Süßigkeiten", nil, nil},
[12287] = {"Orik Treuherz und der Vergessene Strand", nil, {"Sucht Orik Treuherz auf der Dämmerkuppe in der Drachenöde auf."}},
[12288] = {"Überfordert!", nil, {"Aumana beim Holzfällerposten Blauhimmel möchte, dass Ihr die erneuernde Abschnürbinde auf 10 verwundete Scharmützler anwendet."}},
[12289] = {"Tritt rein, solang' sie am Boden liegen", nil, {"Unteroffizier Hartsmann möchte, dass Ihr 15 Einheiten oder Spieler der Horde im Holzfällerlager Blauhimmel besiegt."}},
[12290] = {"Das Düstergraselixier", nil, {"Orik Treuherz auf der Dämmerkuppe in der Drachenöde möchte, dass Ihr 5 Düstergräser von den Düsterhöhen holt."}},
[12291] = {"Die vergessene Geschichte", nil, {"Orik Treuherz auf der Dämmerkuppe in der Drachenöde möchte, dass Ihr Euch die Geschichte der vergessenen Arbeiter, vergessenen Scharfschützen, vergessenen Ritter und der vergessenen Fußsoldaten anhört."}},
[12292] = {"Die Unterstützung der Einheimischen", nil, {"Leutnant Dumont bei der Ammertannhütte möchte, dass Ihr Euch zum Handelsposten Immergrün begebt und die Zedernholzkiste beschafft."}},
[12293] = {"Schließt den Handel ab", nil, {"Bringt die Zedernholzkiste zu Ivan im Posten des Espenhains."}},
[12294] = {"Ein vorläufiger Pakt", nil, {"Kehrt zu Leutnant Dumont in der Ammertannhütte zurück."}},
[12295] = {"Eine Übung in Sachen Diplomatie", nil, {"Sprecht mit dem Entsandten Ducal in Silberwasser."}},
