[12297] = {"Von Verrätern und Verrat", nil, {"Hauptmann Adams möchte, dass Ihr das Schreiben der Allianz zu Greer Erzhammer in Westwacht bringt."}},
[12298] = {"Oberkommandant Halford Wyrmbann", nil, {"Überbringt Oberkommandant Halford Wyrmbann das Schreiben der Allianz. Er befindet sich in der Feste Wintergarde im Osten der Drachenöde."}},
