[12302] = {"Worte der Warnung", nil, {"Begebt Euch zum nordwestlichen Ende von Silberwasser."}},
[12303] = {"Die Kriegsanstrengungen finanzieren", nil, {"Quartiermeister Bartlett in Gallgrimm möchte, dass Ihr 6 vergessene Schätze beschafft."}},
[12304] = {"Ein Grundstück am Strand", nil, {"Feldmesser Hansen gleich nördlich des Vergessenen Strands möchte, dass Ihr 20 vergessene Geister vernichtet."}},
[12305] = {"Gedanken des Abschieds", nil, {"Kehrt zu Zelig dem Visionär in der Feste Wintergarde in der Drachenöde zurück."}},
