[12441] = {"GESUCHT: Hochschamane Blutpfote", nil, {"Tötet Hochschamane Blutpfote und kehrt anschließend zu Hochlord Leoric von Zeldig in Wintergarde in der Drachenöde zurück, um Eure Belohnung abzuholen."}},
[12442] = {"GESUCHT: Ansturmkommandant Iustus", nil, {"Tötet den Ansturmkommandanten Iustus und kehrt anschließend zu Hochlord Leoric von Zeldig in Wintergarde in der Drachenöde zurück, um Eure Belohnung abzuholen."}},
