[12460] = {"Beim Rubindrachenschrein Bericht erstatten", nil, {"Meldet Euch bei Ceristrasz, der sich auf dem Grat östlich des Rubindrachenschreins befindet. Der Schrein liegt westlich des Wyrmruhtempels."}},
[12461] = {"Beim Rubindrachenschrein Bericht erstatten", nil, {"Meldet Euch bei Vargastrasz, der sich gleich östlich von Agmars Hammer auf einer Anhöhe in der Nähe des Rubindrachenschreins befindet."}},
[12462] = {"Schon Spaß gehabt?", nil, {"Yord \"Katastrophe\" Eisbart im Mausoleum von Wintergarde in der Drachenöde möchte, dass Ihr Nekrolord X'avius, Nekrolord Horus und 15 Diener von Naxxramas tötet."}},
[12463] = {"Ramschbart muss gefunden werden!", nil, {"Ambo Unkesch im Mausoleum von Wintergarde in der Drachenöde möchte, dass Ihr Ramschbart findet."}},
