[12465] = {"Ramschbarts Tagebuch", nil, {"Beschafft die 4., 5., 6. und 7. Seite von Ramschbarts Tagebuch und bringt sie Ambo Unkesch im Mausoleum von Wintergarde in der Drachenöde."}},
[12466] = {"Dem Eissturm hinterher: Front der 7. Legion", nil, {"Meldet Euch bei Legionskommandant Tyralion an der Front der 7. Legion im Zentrum der Drachenöde."}},
[12467] = {"Dem Eissturm hinterher: Thel'zans Phylakterium", nil, {"Nehmt Eissturm Thel'zans Phylakterium ab und bringt es zurück zu Hochkommandant Halford Wyrmbann in der Feste Wintergarde."}},
[12468] = {"Die Aufgabe des Eroberers", nil, {"Sprecht mit Unteroffizier Nazgrim in Burg Siegeswall."}},
[12469] = {"Zurück an den Absender", nil, {"Folgt dem Grat nach Osten und liefert das Sprengstoffpaket bei Xink im Düsenrostposten im Norden der Drachenöde ab."}},
[12470] = {"Mysterium der Unendlichkeit", nil, {"Chromie auf der Spitze des Wyrmruhtempels möchte, dass Ihr zum Bronzedrachenschrein reist. Stellt das Stundenglas der Ewigkeit dort im Inneren des Schreins auf den Boden.\n\nBeschützt das Stundenglas so lange wie nötig und kehrt anschließend mit den erhaltenen Informationen zu Chromie zurück."}},
[12471] = {"Grausamkeiten der Kvaldir", nil, {"Sprecht mit Karuk über den eingesperrten Tuskarrgefangenen"}},
[12472] = {"Endgültigkeit", nil, {"Bringt Thel'zans Phylakterium zu Legionskommandant Yorik im Inneren des Mausoleums von Wintergarde in der Drachenöde."}},
