[12483] = {"Schimmerkappeneintopf", nil, {"Prigmon am Granitquell möchte, dass Ihr 5 Schimmernde Schneekappen und 5 Eisschlangenaugen in den Ruinen von Drak'Zin und 5 Süßwurzeln aus Zeb'Halak sammelt."}},
[12484] = {"Geißelkebab", nil, {"Prigmon am Granitquell möchte, dass Ihr eine mumifizierte Trollleiche zu Mack Fearsen bringt und sie dort ins Feuer werft."}},
