[6401] = {"Kaya está viva", nil, {"Da las buenas noticias a Tammra Campo de Viento en el Refugio Roca del Sol.|n"}},
[6402] = {"Tienes una cita en Ventormenta", nil, {"Viaja hasta la Ciudad de Ventormenta y aventúrate tras las puertas de la ciudad. Habla con el escudero Rowe para que haga saber al mariscal Windsor sobre tu llegada."}},
[6403] = {"La gran farsa", nil, {"Sigue a Reginald Windsor por Ventormenta. ¡Protégele del peligro!"}},
[6421] = {"Barranco del Alud", nil, {"Explora las profundidades de la cueva del Barranco del Alud y lleva 10 cristales de resonita al Refugio Roca del Sol para que Mor'rogal las estudie."}},
[6441] = {"Los cuernos de sátiro", nil, {"Reúne 16 cuernos de sátiro para Pixie en el Puesto del Hachazo."}},
[6442] = {"Nagas en la Ensenada de Zoram", nil, {"Llévale 20 cabezas de Colafuria a Marukai en La Ensenada de Zoram."}},
