[6481] = {"El despertar del terráneo", nil, {"Abre el barril de resonita con el cristal de resonita encantado y, a continuación, mata a Goggeroc. Vuelve a ver a Mor'rogal para contarle lo ocurrido y entregarle el cristal de resonita encantado."}},
[6482] = {"Liberar a Ruul", nil, {"Escolta a Ruul y después habla con Yama Pezuña Nevada en el Puesto del Hachazo."}},
[6501] = {"El Ojo del Dragón", nil, {"Debes buscar por todo el mundo y encontrar un ser capaz de restaurar el poder del fragmento del Ojo del Dragón. La única información que tienes sobre dicho ser es que existe."}},
[6502] = {"Amuleto Pirodraco", nil, {"Tienes que recuperar la sangre de dragón Negro Campeón, la tiene el general Drakkisath. Puedes encontrar a Drakkisath en su sala del trono, tras las Salas de la Ascensión, en la Cumbre de Roca Negra."}},
[6503] = {"Avanzada de Vallefresno", nil, {"Mata a 9 avanzadas de Vallefresno y regresa con Kuray'bin en Puesto del Hachazo."}},
[6504] = {"Las páginas perdidas", nil, {"Encuentra las 12 páginas que faltan del manual de instrucciones de trituradora y vuelve a componer con ellas los 3 primeros capítulos. Devuélvele las páginas a Gurda Cicafuria cerca del Puesto del Hachazo.|n"}},
[6521] = {"Una alianza impía", nil, {"Lleva la cabeza del embajador Malcin a Bragor Puñosangre, que está en Entrañas."}},
[6522] = {"Una alianza impía", nil, {"Lleva el pergamino pequeño a Bragor Puñosangre que está en el Barrio Real en Entrañas."}},
[6523] = {"Protege a Kaya", nil, {"Escolta a Kaya Pezuña Plana hasta el Campamento Aparaje y ve a ver a Makaba Pezuña Plana cerca del límite sureste de Espolón.|n"}},
[6541] = {"Preséntate ante Kadrak", nil, {"Preséntate ante Kadrak en la torre de vigilancia en Los Baldíos del Norte."}},
[6542] = {"Preséntate ante Kadrak", nil, {"Preséntate ante Kadrak en la torre de vigilancia en Los Baldíos del Norte."}},
[6543] = {"Los informes Grito de Guerra", nil, {"Abre el fardo de informes. Lleva los informes Grito de Guerra al explorador Grito de Guerra, al corredor Grito de Guerra y al escolta Grito de Guerra. Preséntate ante Kadrak en la torre de vigilancia norte en los Baldíos con la información que te hayan dado."}},
[6544] = {"El asalto de Torek", nil, {"Escolta a Torek en su ataque sobre la Avanzada Ala de Plata contra Duriel Fuegolunar e informa después a Ertog Colmillorrabioso en el Puesto del Hachazo."}},
