[6561] = {"La vileza de Brazanegra", nil, {"Llévale la cabeza del señor crepuscular Kelris a Bashana Tótem de Runa en Cima del Trueno."}},
[6562] = {"Problemas en las profundidades", nil, {"Habla con Je'neu Sancrea en Vallefresno."}},
