[7494] = {"Bastión Plumaluna", nil, {"Habla con Latronicus Lanzaluna. Lo encontrarás en el Bastión Plumaluna de Feralas."}},
[7495] = {"Victoria para la Alianza", nil, {"Lleva la cabeza de Onyxia al rey Varian Wrynn en Ventormenta."}},
[7496] = {"Celebrar los buenos momentos", nil, {"Busca al mayor Mattingly en el Valle de los Héroes."}},
