[7682] = {"Hunter test quest2", nil, {"Feed your pet, then talk to your questgiver."}},
[7701] = {"SE BUSCA: Sobrestante Maltorius", nil, {"El traidor Hierro Negro llamado sobrestante Maltorius está en La Fosa de la Escoria de La Caldera, en La Garganta de Fuego. \n\nMátalo y lleva su cabeza al capitán de vigía Lolo Zancalarga a La Garganta de Fuego para recibir tu recompensa."}},
[7702] = {"¡Mátalos de sueño!", nil, {"Los dormitorios de los Hierro Negro están en La Fosa de la Escoria de La Caldera, en La Garganta de Fuego. \n\nRoba 20 almohadas y ve a ver a Evonis Fumollín."}},
[7703] = {"Asunto Gordok sin finiquitar", nil, {"Encuentra el guante del Poderío de Gordok y llévaselo al capitán Kromcrush a La Masacre. \n\nSegún Kromcrush, una \"historia muy, muy vieja\" dice que Tortheldrin, un elfo \"asqueroso\" que se llamaba a sí mismo príncipe, robó el guantelete a uno de los reyes de Gordok."}},
[7704] = {"¡Mira el tamaño de esto!", nil, {"Lleva la almohada de la ayudante de cámara Pilaprieta a Evonis Fumollín a La Garganta de Fuego."}},
[7721] = {"Combustible para el Ultra-reductor", nil, {"Reúne 10 núcleos de elemental de agua de los elementales marinos y de los rocíos marinos que hay en La Costa Olvidada de Feralas, y llévaselos a Zorbin Hipnoviento, que está cerca del embarcadero."}},
[7722] = {"¿Qué pasa con el flujo?", nil, {"Busca los diseños secretos: flujo ígneo robados por el sobrestante Maltorius en La Fosa de la Escoria de La Caldera. Llévaselos al herrero maestro Burninate a La Garganta de Fuego.|n"}},
[7723] = {"¡Malditos dedos gordos!", nil, {"Hansel Manospesadas quiere que mates 20 gólems de guerra pesados. \n\nSuelen estar cerca de La Caldera en La Garganta de Fuego. Después ve a ver a Hansel a La Garganta de Fuego."}},
[7724] = {"Amenaza abrasadora", nil, {"Hansel Manospesadas quiere que mates 20 arañas de lava superiores en La Garganta de Fuego. Las encontrarás en las mesetas y los estanques de lava de La Garganta de Fuego. \n\nCuando hayas matado suficientes arañas de lava superiores ve a ver a Hansel Manospesadas a La Garganta de Fuego."}},
[7725] = {"Encoge más gigantes", nil, {"Utiliza el Ultra-reductor de Zorbin para encoger a cualquier gigante que encuentres en Feralas a un tamaño más manejable. Lleva 10 residuos de miniaturización encontrados en las versiones encogidas de los gigantes a Zorbin Hipnoviento, al embarcadero de La Costa Olvidada de Feralas."}},
[7726] = {"Más combustible para el Ultra-reductor", nil, {"Reúne 6 núcleos de elemental de agua de los elementales marinos y los rocíos marinos que hay en La Costa Olvidada de Feralas, y llévaselos a Zorbin Hipnoviento, que está cerca del embarcadero."}},
[7727] = {"¿Incendosaurio? Loqueseasaurio más bien", nil, {"Hansel Manospesadas quiere que mates 20 incendosaurios.\n\nSuelen estar en los estanques de lava de La Fosa de la Escoria. La Fosa de la Escoria está en La Caldera de La Garganta de Fuego. \n\nCuando hayas matado suficientes incendosaurios ve a ver a Hansel Manospesadas a La Garganta de Fuego."}},
[7728] = {"OBJETOS ROBADOS: Tobera y catalejo", nil, {"Encuentra la tobera y el catalejo y llévaselos al capataz Scrange a La Garganta de Fuego. \n\nLo único que sabes es que fueron robados por enanos Hierro Negro. La tobera es una herramienta de herrero y el catalejo es un instrumento muy valioso para los oteadores."}},
[7729] = {"OPORTUNIDAD LABORAL: Acabar con la competencia", nil, {"Mata a 15 Capataces y 15 esclavistas Hierro Negro y ve a ver al capataz Scrange a La Garganta de Fuego."}},
[7730] = {"Infestación de Zukk'ash", nil, {"Lleva 20 caparazones Zukk'ash a Hadoken Zanco Veloz al Campamento Mojache en Feralas."}},
[7731] = {"Latigador con aguijón", nil, {"Derrota al latigador con aguijón en Las Galerías Retorcidas y lleva sus glándulas a Hadoken Zanco Veloz al Campamento Mojache en Feralas."}},
[7732] = {"El informe Zukk'ash", nil, {"Entrega a Zilzibin Tambor Sabio el informe del campamento Mojache Zukk'ash. Reside en La Calle Mayor de Orgrimmar."}},
[7733] = {"Calidad mejorada", nil, {"Lleva 10 pellejos de yeti Cicatriz de Rabia a Pratt McGrubben al Bastión Plumaluna en Feralas."}},
[7734] = {"Calidad mejorada", nil, {"Lleva 10 pellejos de yeti Cicatriz de Rabia a Jangdor Zanco Veloz al Campamento Mojache de Feralas."}},
[7735] = {"Pellejo de yeti prístino", nil, {"Lleva el pellejo de yeti prístino a Pratt McGrubben al Bastión Plumaluna de Feralas."}},
[7736] = {"Fabricar flujo ígneo con sangrerregia", nil, nil},
[7737] = {"Conseguir más aceptación", nil, nil},
[7738] = {"Pellejo de yeti perfecto", nil, {"Lleva los pellejos de yeti perfecto a Jangdor Zanco Veloz al Campamento Mojache de Feralas."}},
[7761] = {"Orden de Puño Negro", nil, {"¡Será estúpido ese orco! Al parecer, tienes que encontrar la enseña y ganar la marca de Drakkisath para poder acceder al orbe de orden.\n\nSegún la carta, el general Drakkisath guarda la enseña. Quizás deberías investigarlo."}},
[7781] = {"Señor de Roca Negra", nil, {"Lleva la cabeza de Nefarian al rey Varian Wrynn en Ventormenta."}},
[7782] = {"Señor de Roca Negra", nil, {"Vete al Valle de los Héroes, en la Ciudad de Ventormenta, y habla con la mariscal de campo Petraponte."}},
[7783] = {"Señor de Roca Negra", nil, {"Llévale la cabeza de Nefarian a Thrall en Orgrimmar."}},
[7784] = {"Señor de Roca Negra", nil, {"Habla con el señor supremo Colmillosauro en Orgrimmar. Te espera en el Valle de la Fuerza."}},
[7785] = {"Examina la vasija", nil, {"Examina la vasija de renacer y devuélvesela al alto señor Demitrian."}},
[7786] = {"Thunderaan el Hijo del Viento", nil, {"Para liberar a Thunderaan el Hijo del Viento de su cárcel, debes entregarle al alto señor Demitrian en Silithus la mitad izquierda y la derecha de las ataduras del Hijo del Viento, 10 barras de elementium y la esencia del Señor del Fuego."}},
[7787] = {"¡Arriba, Trueno Furioso!", nil, {"Lleva la hoja besada por el viento durmiente al alto señor Demitrian."}},
[7788] = {"¡Expulsa al invasor!", nil, {"Entra en la Garganta Grito de Guerra y derrota a la Horda, obtén una marca de Honor Grito de Guerra y vuelve con la centinela Cantolejano en el Claro Ala de Plata."}},
[7789] = {"Acaba con los usurpadores Ala de Plata", nil, {"Entra en la Garganta Grito de Guerra y derrota a la Alianza, obtén una marca de Honor de la Garganta Grito de Guerra y llévasela al capitán Trizacráneo en el Campamento de Mor'shan."}},
