[8320] = {"Geoseñores Crepusculares", nil, {"Mata 10 geoseñores Crepusculares y, a continuación, ve a ver a Huum Ferocrín en el Fuerte Cenarion."}},
[8321] = {"Vyral el Vil", nil, {"Llévale el sello de Vyral a Huum Ferocrín en el Fuerte Cenarion."}},
