[8324] = {"Seguir creyendo", nil, nil},
[8325] = {"Recuperar la Isla del Caminante del Sol", nil, {"Mata 8 vermis de maná; están cerca de los cristales ardientes que hay junto a la torre y la glorieta."}},
[8326] = {"Medidas extremas", nil, {"Coge 8 colleras de lince de los linces y cachorros garragil muertos. Luego vuelve a ver a la magistrix Erona a Isla del Caminante del Sol."}},
[8327] = {"Informar a Lanthan Perilon", nil, {"Habla con Lanthan Perilon en la Isla del Caminante del Sol. Está cerca del sendero que se aleja de La Aguja del Sol hacia el oeste."}},
[8328] = {"Instrucción de mago", nil, {"Habla con Julia Golpesol, la instructora de magos, en la Aguja del Sol en Isla del Caminante del Sol."}},
[8330] = {"Las posesiones de Solanian", nil, {"Recoge el orbe de visión, el pergamino de magia de la Plaga y el diario del vigía del pozo Solanian. Están en Isla del Caminante del Sol cerca de la charca, la fuente y de uno de los cristales ardientes. Llévaselo todo a La Aguja del Sol en la Isla del Caminante del Sol."}},
[8331] = {"Aurel Hojaurea", nil, {"Habla con Aurel Hojaurea en el Fuerte Cenarion."}},
[8332] = {"Los Duques del Consejo", nil, {"Lleva 1 fragmento luminoso grande y 3 blasones Abisales a Aurel Hojaurea en el Fuerte Cenarion."}},
[8333] = {"Medallón de estatus", nil, nil},
[8334] = {"Agresividad", nil, {"Mata 7 tiernos y 7 tiernos ferales y ve a ver a Lanthan Perilon en la Isla del Caminante del Sol."}},
