[9639] = {"Kamsis", nil, {"Habla con Kamsis en la Biblioteca del Guardián, en Karazhan."}},
[9640] = {"La sombra de Aran", nil, {"Consigue el diario de Medivh y vuelve junto a Kamsis en la Biblioteca del Guardián, en Karazhan."}},
[9641] = {"Fragmentos de cristal irradiado", nil, {"El vindicador Boros de la Avanzada de Sangre quiere que le lleves diez fragmentos de cristal radioactivo."}},
