[9671] = {"Entrega urgente", nil, nil},
[9672] = {"El legado de la Sangre Maldita", nil, {"El almirante Odesyus te ha pedido que encuentres al capitán Edward Hanes y hables con él."}},
[9673] = {"Doma de bestias", nil, {"Ve a la Ciudad de Lunargenta y habla con Halthenis."}},
