[9723] = {"Un gesto de entrega", nil, {"Lleva 40 paños rúnicos, 6 barras de arcanita, 10 soleas, 5 runas oscuras y 150 piezas de oro a Lord Solanar Sangre Colérica en la Ciudad de Lunargenta."}},
[9724] = {"Advertir al Círculo Cenarion", nil, {"Habla con Amythel Caminaniebla en el Puesto Cenarion, en la Península del Fuego Infernal."}},
