[10475] = {"El pacto del defensor", nil, {"Devuelve tu sortija de eternidad a Soridormi en las Cavernas del Tiempo después de alcanzar la reputación de exaltado ante La Escama de las Arenas."}},
[10476] = {"Enemigos encarnizados", nil, {"El celador Moi'bff Jill de Telaar quiere que consigas 10 abalorios de guerra de obsidiana de los distintos ogros que viven en Nagrand."}},
[10477] = {"Más abalorios de guerra", nil, nil},
[10478] = {"¡Más abalorios de guerra!", nil, nil},
[10479] = {"Demostrar tu fuerza", nil, {"El celador Tororroc de Garadar quiere que consigas diez abalorios de guerra de obsidiana de los diferentes ogros que habitan Nagrand."}},
[10480] = {"Espíritus de agua iracundos", nil, {"El ensalmador de la tierra Torlok de El Altar de Condenación, en el Valle Sombraluna, quiere que uses el Tótem de espíritus para capturar 5 almas acuáticas.|n"}},
[10481] = {"Espíritus de aire iracundos", nil, {"El ensalmador de la tierra Torlok de El Altar de Condenación, en el Valle Sombraluna, quiere que uses el Tótem de espíritus para capturar 10 almas aéreas."}},
[10482] = {"Orcos viles carroñeros", nil, {"Mata a 20 orcos Mascahuesos y vuelve con Amadi al Bastión del Honor."}},
[10483] = {"Malos augurios", nil, {"La teniente Amadi, del Bastión del Honor, quiere que le lleves un dije maldito al cabo Crestahierro, de la Punta de Expedición."}},
[10484] = {"Dijes malditos", nil, {"Lleva 12 dijes malditos al cabo Crestahierro, en la Punta de Expedición."}},
[10485] = {"Señor de la guerra del Foso Sangrante", nil, {"Lleva la armadura destrozada del señor de la guerra Morkh al cabo Crestahierro, en la Punta de Expedición."}},
[10486] = {"La naturaleza invasora", nil, {"Mata a doce flebotomistas Alacuchilla."}},
[10487] = {"Polvo de los dracos", nil, {"Reúne cuatro polvos de draco hada."}},
[10488] = {"Proteger a los nuestros", nil, {"Usa el ungüento de Gor'drek en 5 de los lobos temibles del Señor del Trueno."}},
[10489] = {"Talar un antiguo árbol", nil, {"Consigue el tronco de Manofirme Raizhonda."}},
[10490] = {"La llamada del agua", nil, {"Habla con el clarividente Nobundo en La Sala de Cristal dentro de El Exodar, en la Isla Bruma Azur."}},
[10491] = {"La llamada del aire", nil, {"Habla con el clarividente Nobundo en La Sala de Cristal dentro de El Exodar, en la Isla Bruma Azur."}},
[10492] = {"Una propuesta seria", nil, {"Lleva 15 muestras de sangre de Cuna del Invierno, 20 piezas de oro y 1 par de ataduras de elementos a Deliana, en Forjaz."}},
[10493] = {"Una propuesta seria", nil, {"Lleva 15 muestras de veneno de Silithus, 20 piezas de oro y 1 par de brazales Forjaluz a Mokvar, en Orgrimmar."}},
[10495] = {"Una compensación justa", nil, {"Lleva 1 cinturón Forjaluz y 1 par de guanteletes Forjaluz a Mokvar a Orgrimmar."}},
[10497] = {"La despedida de Anthion", nil, {"Lleva 1 par de botas, 1 par de quijotes y 1 par de bufas Forjaluz a Mokvar a Orgrimmar.|n"}},
[10499] = {"Guardar lo mejor para el final", nil, {"Dale a Mokvar tu yelmo y tu coraza Forjaluz."}},
[10500] = {"¡La Horda te necesita!", nil, {"Habla con el señor de la guerra Gorchuk en el Valle de los Espíritus de Orgrimmar."}},
[10501] = {"¡La Alianza te necesita!", nil, {"Habla con el mariscal de campo Nevanur en La Sala Militar de Forjaz."}},
[10502] = {"Los ogros Machacasangre", nil, {"Destruye 30 ogros Machacasangre en la Avanzada Machacasangre."}},
[10503] = {"La amenaza Aguja del Filo", nil, {"Destruye 30 ogros Aguja del Filo y 10 raptores Aguja del Filo en el Bastión Aguja del Filo y los Dominios Aguja del Filo."}},
[10504] = {"Los ogros Aguja del Filo", nil, {"Destruye 30 ogros Aguja del Filo en el Bastión Aguja del Filo y en los Dominios Aguja del Filo."}},
[10505] = {"Los ogros Machacasangre", nil, {"Destruye 30 ogros Machacasangre en la Avanzada Machacasangre."}},
[10506] = {"Una situación extrema", nil, {"Rina Lunabril quiere que te lleves sus polvos de disminución y se los apliques a 5 de los lobos temibles Machacasangre, que se pueden encontrar en la Avanzada Machacasangre y en el Barranco Machacasangre."}},
[10507] = {"Punto de inflexión", nil, {"Usa la piedra de teletransporte de Socrethar en el Punto de Invasión: Señor Supremo, al norte de la Base Forja: Olvido, para transportar a tu grupo hasta el Embarcadero de Socrethar. Una vez allí, utiliza la Presencia de Voren'thal para vencer a Socrethar.\n\nLlevar a cabo misiones para los Arúspices hará que tu reputación Aldor disminuya."}},
[10508] = {"Un regalo para Voren'thal", nil, {"El maestro de espías Thalodien, del Área 52, quiere que consigas la primera mitad de la piedra de Socrethar del maestro de forja Morug, en la Base Forja: Olvido, y la segunda mitad de la piedra de Socrethar del maestro de Silroth, en la Base Forja: Gehenna.\n\nCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10509] = {"Destinado a la gloria", nil, {"Lleva la piedra de teletransporte de Socrethar a Voren'thal el Vidente, en la Grada del Arúspice, de la Ciudad de Shattrath.\n\nLlevar a cabo misiones para los Arúspices hará que tu reputación Aldor disminuya."}},
[10510] = {"En el interior de la Mina Draenetista", nil, {"Consigue 5 cristales de la Mina Draenetista."}},
[10511] = {"Una extraña cerveza", nil, {"Reúne 11 cervezas Machacasangre aterrabrutos."}},
[10512] = {"Repostando Aguja del Filo", nil, {"Utiliza el barril de Machacasangre aterrabrutos para intentar emborrachar a 5 ogros Aguja del Filo."}},
[10513] = {"Oronok Corazón Roto", nil, {"Busca a Oronok Corazón Roto en la Barrera Arrasada - al norte de la Cisterna Cicatriz Espiral.|n"}},
[10514] = {"Yo era muchas cosas...", nil, {"Oronok Corazón Roto, de la Granja de Oronok, en el Valle Sombraluna, quiere que recuperes diez tubérculos de Sombraluna de las Llanuras Devastadas.\n\nTambién quiere que le devuelvas el silbato de jabalíes de Oronok cuando hayas terminado."}},
[10515] = {"Una lección aprendida", nil, {"Oronok Corazón Roto, de la Granja de Oronok, en el Valle Sombraluna, quiere que destruyas diez huevos de despellejador voraz de las Llanuras Devastadas."}},
[10516] = {"Las armas de un vindicador", nil, {"Recupera la espada y el escudo de la vindicadora Vuuleen."}},
[10517] = {"Gorr'Lelo, ha llegado tu hora...", nil, {"Mata a Gorr'Lelo, líder de los ogros del Bastión Aguja del Filo."}},
[10518] = {"Clavar el estandarte", nil, {"Usa el estandarte de los Aguja del Filo sobre la Torre Quiebranorte para atraer a Gurn Jalazampa. Obtén el estandarte Aguja del Filo y el yelmo de Gurn Jalazampa."}},
[10519] = {"La Clave de Condenación: historia y verdad", nil, {"Oronok Corazón Roto, en la Granja de Oronok del Valle Sombraluna, quiere que escuches su relato. Habla con Oronok para comenzar a escuchar su relato."}},
[10520] = {"Ayuda al archidruida Corzocelada", nil, {"Habla con el archidruida Fandral Corzocelada en Darnassus."}},
[10521] = {"Grom'tor, hijo de Oronok", nil, {"Encuentra a Grom'tor, hijo de Oronok, en el Alto Cicatriz Espiral del Valle Sombraluna."}},
[10522] = {"La Clave de Condenación: La carga de Grom'tor", nil, {"Grom'tor, hijo de Oronok, en el Alto Cicatriz Espiral del Valle Sombraluna, quiere que recuperes el primer fragmento de la Clave de Condenación."}},
[10523] = {"La Clave de Condenación - Primer trozo recuperado", nil, {"Lleva la caja fuerte de Grom'tor a Oronok Corazón Roto en la Granja de Oronok, en el Valle Sombraluna."}},
[10524] = {"Artefactos del clan Señor del Trueno", nil, {"Recupera el tambor del clan Señor del Trueno, la flecha del clan Señor del Trueno, y la tablilla del clan Señor del Trueno."}},
[10525] = {"Visión guía", nil, {"Usa el trozo de alma ígnea para transformar tu espíritu en una visión guía, que te revelará la ubicación de la última reliquia del Señor del Trueno."}},
[10526] = {"La Punta de Trueno", nil, {"Recupera la Punta de Trueno."}},
[10527] = {"Ar'tor, hijo de Oronok", nil, {"Encuentra a Ar'tor, hijo de Oronok, en el Alto Illidari del Valle Sombraluna."}},
[10528] = {"Cárceles de cristal demoníacas", nil, {"Busca a la maestra del dolor Gabrissa en el Alto Illidari, mátala y vuelve junto al cadáver de Ar'tor, hijo de Oronok, con la llave cristalina."}},
[10529] = {"El camino del cazador", nil, {"Habla con la teniente Correalba en El Retiro del Errante, en el Bosque Canción Eterna."}},
[10530] = {"El camino del cazador", nil, {"Habla con la teniente Correalba en El Retiro del Errante, en el Bosque Canción Eterna."}},
[10531] = {"La batalla por la Cuenca de Arathi", nil, {"Asalta la mina, el aserradero, la herrería y la granja y vuelve entonces junto al mariscal de campo Uluz en el Refugio de la Zaga."}},
[10532] = {"Córtale el suministro a la Liga de Arathor", nil, {"Llévate la victoria en la batalla por la Cuenca de Arathi, consigue una caja de suministros de Arathi y llévasela a Mortacechador Mortis en Sentencia."}},
[10534] = {"La vuelta a casa", nil, {"Caedmos, en El Exodar, te ha pedido que vayas a verle."}},
[10535] = {"Los recursos de la Cuenca de Arathi", nil, {"Llévate la victoria en la batalla por la Cuenca de Arathi, consigue una caja de suministros de Arathi y llévasela al sargento Maclear en el Refugio de la Zaga."}},
[10537] = {"Lohn'goron, arco del corazón roto", nil, {"El espíritu de Ar'tor en el Alto Illidari, en el Valle Sombraluna, quiere que recuperes Lohn'goron, arco del corazón roto. Lo tiene un demonio de la zona."}},
[10538] = {"Sangre en ebullición", nil, {"Vierte la sangre de Foso Sangrante en su caldera maldita para obtener sangre hervida. Lleva 12 muestras de sangre hervida al boticario Albreck en la Avanzada Rompeloma."}},
[10539] = {"La vuelta a casa", nil, {"Caedmos, en El Exodar, te ha pedido que vayas a verle."}},
[10540] = {"La Clave de Condenación. La carga de Ar'tor", nil, {"El espíritu de Ar'tor en el Alto Illidari, en el Valle Sombraluna, quiere que consigas el segundo fragmento de la Clave de Condenación que tiene Veneratus el Múltiple.\n\nLas criaturas que ataque o dañe el espíritu cazador no proporcionan botín ni experiencia."}},
[10541] = {"La Clave de Condenación. Segundo trozo recuperado", nil, {"Lleva la caja fuerte de Ar'tor a Oronok del corazón roto en la Granja de Oronok, en el Valle Sombraluna.|n"}},
[10542] = {"Mi pipaaa me la robaaaron...", nil, {"Recupera la pipa de agua de T'chali y 10 cervezas Machacasangre aterrabrutos."}},
[10543] = {"Grimnok y Korgaah, ¡voy por ustedes!", nil, {"Mata a Grimnok Belinato y a Korgaah."}},
[10544] = {"¡Una maldición para ambos clanes!", nil, {"Utiliza el fetiche poderoso desalmado para maldecir 5 edificios en el Bastión Aguja del Filo y 2 en la Avanzada Machacasangre."}},
[10545] = {"La mezcla Aguja del Filo", nil, {"Utiliza el barril de Machacasangre aterrabrutos para emborrachar a 5 ogros Aguja del Filo."}},
[10546] = {"Borak, hijo de Oronok", nil, {"Encuentra a Borak, hijo de Oronok, cerca de Punta Eclipse, en el Valle Sombraluna."}},
[10547] = {"Sobre cabezacardos y huevos...", nil, {"Borak, hijo de Oronok en el puente al norte de Punta Eclipse, quiere que encuentres un huevo de arakkoa podrido y se lo lleves a Tobias el Comeporquería en la Ciudad de Shattrath, al noroeste del Bosque de Terokkar."}},
[10548] = {"La triste verdad", nil, {"Eralan de Tranquillien quiere que recuperes la cabeza reducida del arqueólogo de uno de los cofres de Zeb'Tela o Zeb'Nowa."}},
[10550] = {"El fardo de cardos de sangre", nil, {"Llévale el fardo de cardos de sangre a Borak, hijo de Oronok, en el puente próximo a la Punta Eclipse en el Valle Sombraluna.|n"}},
[10551] = {"Lealtad hacia los Aldor", nil, nil},
[10552] = {"Lealtad a los Arúspices", nil, nil},
[10553] = {"Voren'thal el vidente", nil, {"Habla con Voren'thal el Vidente dentro de la Biblioteca del Profeta, en la Grada del Arúspice de la Ciudad de Shattrath.\n\nLlevar a cabo misiones para los Arúspices hará que tu nivel de reputación Aldor disminuya."}},
[10554] = {"Ishanah", nil, {"Habla con Ishanah dentro del Santuario de Luz Inagotable, en Alto Aldor, de la Ciudad de Shattrath.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10555] = {"Malestar", nil, {"Reúne 18 plumas de Lashh'an arrancadas."}},
[10556] = {"Arañazos", nil, {"Usa el manojo de plumas en el círculo de hechizos de Lashh'an."}},
[10557] = {"Vuelo de prueba: el Cefirium Capacitorium", nil, {"Habla con Rally Pillazas para usar el Cefirium Capacitorium."}},
[10558] = {"Venerado con el Bastión del Honor", nil, {"El comandante en jefe Danath Aterratrols, del Bastión del Honor, en la Península del Fuego Infernal, quiere que seas venerado en el Bastión del Honor luchando contra los orcos viles dentro de Las Salas Arrasadas de la Ciudadela del Fuego Infernal."}},
[10559] = {"Venerado con Thrallmar", nil, {"Nazgrel, de Thrallmar, en la Península del Fuego Infernal, quiere que seas venerado en Thrallmar luchando contra los orcos viles que hay dentro de Las Salas Arrasadas de la Ciudadela del Fuego Infernal."}},
[10560] = {"Venerado con Sha'tar", nil, {"A'dal de la Ciudad de Shattrath quiere que seas venerado en Sha'tar venciendo a las tropas de Kael'thas dentro de El Castillo de la Tempestad."}},
[10561] = {"Venerado con los Vigilantes del Tiempo", nil, {"Andormu, dentro de las Cavernas del Tiempo de Tanaris, quiere que seas venerado entre los Vigilantes del Tiempo, al continuar defendiendo los sucesos de la Antigua Trabalomas y la Ciénaga Negra, en las Cavernas del Tiempo."}},
[10562] = {"¡Asediados!", nil, {"El comandante del aire Nuainn, fuera del Bastión Martillo Salvaje, quiere que mates a diez agresores infernales."}},
[10563] = {"Al Bastión de la Legión", nil, {"Ponte en camino hacia el Bastión de la Legión y ubica el aparato de comunicación de la Legión. Cuando estés cerca del aparato, usa tu disfraz y averigua los planes de la Legión. Vuelve con el comandante del aire Nuainn al Bastión Martillo Salvaje cuando hayas completado tu misión."}},
[10564] = {"¡Infernales por los aires!", nil, {"Acciona la bomba vil cuando estés en el glifo verde, en Bastión de la Legión. Informa al comandante del aire Nuainn en Bastión Martillo Salvaje cuando hayas completado tu misión."}},
[10565] = {"Las piedras de Vekh'nir", nil, {"Recoge y usa un cristal Vekh'nir del círculo de hechizos de los Vekh'nir para crear un cristal Vekh'nir cargado."}},
[10566] = {"Ensayo y error", nil, {"Prueba cada varita de Dertrok en un esclavo ogro arakkoa. Con cada varita tendrás que usar un cristal imbuido Vekh'nir."}},
[10567] = {"Crear el colgante", nil, {"Busca 6 garras de Ruuan'ok y úsalas para invocar a un presagista del Cuervo en el Círculo del Oráculo Ruuan'ok. Mátalo y obtén su colgante de presagista."}},
[10568] = {"Las tablillas de Baa'ri", nil, {"La anacoreta Ceyla del Altar de Sha'tar quiere que recojas 12 tablillas Baa'ri del suelo y de los trabajadores Lengua de ceniza en las Ruinas de Baa'ri.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10569] = {"Las Ruinas de Sketh'lon", nil, {"Recupera las páginas 1, 2 y 3 del diario del comandante Skethlon del cónclave oscuro arakkoa en las Ruinas de Sketh'lon y entrégaselas al jinete de grifo Kieran."}},
[10570] = {"Perder la cabeza... por unos cardos", nil, {"Borak, hijo de Oronok, en el puente próximo a la Punta Eclipse en el Valle Sombraluna, quiere que recuperes la misiva de Tempestira."}},
[10571] = {"Oronu el Anciano", nil, {"La anacoreta Ceyla del Altar de Sha'tar quiere que consigas las órdenes de Akama de Oronu el Anciano en las Ruinas de Baa'ri.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10572] = {"Preparación de la bomba", nil, {"Consigue 1 batería de atracador vil y 1 placa de armadura de atracador vil. Llévaselas al comandante del aire Nuainn en el Bastión Martillo Salvaje."}},
[10573] = {"La Forja Muerta", nil, {"Preséntate al agitador Ewan Alasalvaje en la Torre de La Forja Muerta."}},
[10574] = {"Los corruptores Lengua de Ceniza", nil, {"Recupera los 4 trozos de los medallones de Haalum, Eyeknen, Lakaan y Uylaru, y regresa junto a la anacoreta Ceyla del Altar de Sha'tar, en el Valle Sombraluna.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10575] = {"La Jaula de la Guardiana", nil, {"La anacoreta Ceyla quiere que entres en la Jaula de la Guardiana, al sur de las Ruinas de Baa'ri, e interrogues a Sanoru sobre el paradero de Akama.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10576] = {"Revuelo en Sombraluna", nil, {"Borak, hijo de Oronok, en el puente próximo a la Punta Eclipse en el Valle Sombraluna, quiere que recuperes 6 piezas de armadura eclipsion.|n"}},
[10577] = {"Lo que Illidan quiere, lo consigue...", nil, {"Borak, hijo de Oronok, en el puente próximo a la Punta Eclipse en el Valle Sombraluna, quiere que entregues el mensaje de Illidan al Gran Comandante Ruusk de Punta Eclipse."}},
[10578] = {"La Clave de Condenación: La carga de Borak", nil, {"Borak, hijo de Oronok, en el puente próximo a la Punta Eclipse en el Valle Sombraluna, quiere que recuperes la tercera parte de la Clave de Condenación de Ruul el Ensombrecedor.\n\nPara esta misión hará falta un grupo."}},
[10579] = {"La Clave de Condenación: Tercer trozo recuperado", nil, {"Lleva la caja fuerte de Borak a Oronok Corazón Roto en la Granja de Oronok, en el Valle Sombraluna."}},
[10580] = {"¿Dónde se han metido esos malditos gnomos?", nil, {"Busca hacia el sur de la Cresta Dentada, en las Montañas Filospada, alguna pista sobre el paradero de Toshley y los demás gnomos."}},
[10581] = {"Sigue el rastro", nil, {"Sigue el rastro de piezas sueltas por la rampa que conduce a Loma Tajo. Habla con Toshley en la Estación de Toshley, en las Montañas Filoespada."}},
[10582] = {"Esbirros del Consejo de la Sombra", nil, {"El agitador Ewan Alasalvaje, en la Torre de La Forja Muerta, quiere que mates a 10 guardianes Forja Muerta y a 5 invocadores Forja Muerta."}},
[10583] = {"El destino de Flanis", nil, {"El agitador Ewan Alasalvaje, en la Torre de La Forja Muerta, quiere que busques en La Forja Muerta a Flanis Alapresta y recuperes su talega."}},
[10584] = {"Recoger transformadores de energía", nil, {"Usa el acumulador magnético protovoltaico para reunir 5 electromentales."}},
[10585] = {"La cámara de invocación", nil, {"Consigue 1 desplazador elemental de un herrero Forja Muerta o de un manitas Forja Muerta, y úsalo para perturbar el ritual de la cámara de invocación. Preséntate al agitador Ewan Alasalvaje en la Torre de La Forja Muerta cuando hayas completado la tarea."}},
[10586] = {"¡Tumba al belisario!", nil, {"Derrota al belisario Razuun, recupera sus órdenes y después preséntate al comandante del aire Nuainn del Bastión Martillo Salvaje."}},
[10587] = {"Patio de Armas de Karabor", nil, {"La exarca Onaala del Altar de Sha'tar quiere que consigas 8 gujas de Furia del Sol de los suplicantes cazademonios y de los iniciados cazademonios que se encuentran en las Ruinas de Karabor, al sureste del Altar de Sha'tar.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10588] = {"La Clave de Condenación", nil, {"Usa la Clave de Condenación en El Altar de Condenación para invocar a Cyrukh el señor del Fuego.\n\nDestruye a Cyrukh el señor del Fuego y habla con el ensalmador de la tierra Torlok, que también se encuentra en El Altar de Condenación."}},
[10589] = {"Obtener acceso", nil, {"Llévale un control de teletransportador de la Legión al comandante del aire Nuainn del Bastión Martillo Salvaje. Sabemos que los forjadores de armas y los caminantes de cólera Mo'arg del Bastión de la Legión llevan estos instrumentos."}},
[10590] = {"Demuestra tu odio", nil, {"Mehlar Hojalba en El Baluarte quiere que le lleves 20 piedras de la Plaga de los esbirros."}},
[10592] = {"La sabiduría de la reina alma en pena", nil, {"Llévale los fragmentos de piedra de la Plaga a Lady Sylvanas Brisaveloz en Entrañas."}},
[10593] = {"Mal ancestral", nil, {"Descubre los secretos de El Templo de Atal'Hakkar para soltar a Atal'alarion y recuperar la vid pútrida de su carne. Vuelve a ver a Mehlar en El Baluarte cuando lo hayas hecho."}},
[10594] = {"Medir la frecuencia resonante", nil, {"Coloca los escáneres de frecuencia oscilante de tal forma que cuando estés de pie en medio de los mismos, te alcancen 5 campos de oscilación superpuestos."}},
[10595] = {"¡Asediados!", nil, {"El Guardia de sangre Gulmok, de las afueras de la Aldea Sombraluna, quiere que mates a 10 agresores infernales."}},
[10596] = {"Al Bastión de la Legión", nil, {"Encamínate al Bastión de la Legión, encuentra el aparato de comunicación de la Legión y averigua los planes de la Legión mientras estés disfrazado. Regresa junto al Guardia de sangre Gulmok en la Aldea Sombraluna cuando hayas completado la misión."}},
[10597] = {"Preparación de la bomba", nil, {"Consigue 1 batería de atracador vil y 1 placa de armadura de atracador vil. Llévaselos al Guardia de sangre Gulmok en la Aldea Sombraluna."}},
[10598] = {"¡Infernales por los aires!", nil, {"Usa la bomba vil cuando estés de pie sobre el glifo verde del Bastión de la Legión. Preséntate al Guardia de sangre Gulmok en la Aldea Sombraluna."}},
[10599] = {"La Forja Muerta", nil, {"Preséntate al explorador Zagran en la Vigía de la Escoria."}},
[10600] = {"Esbirros del Consejo de la Sombra", nil, {"El explorador Zagran, en la Vigía de la Escoria, quiere que mates a 10 guardianes Forja Muerta y a 5 invocadores Forja Muerta."}},
[10601] = {"El destino de Kagrosh", nil, {"El explorador Zagran, en la Vigía de la Escoria, quiere que busques en La Forja Muerta a Kagrosh y recuperes su paquete."}},
[10602] = {"La cámara de invocación", nil, {"Consigue 1 desplazador elemental de un herrero Forja Muerta o de un manitas Forja Muerta, y úsalo para perturbar el ritual de la cámara de invocación. Vuelve junto al explorador Zagran en la Vigía de la Escoria después de completar esta tarea."}},
[10603] = {"¡Tumba al belisario!", nil, {"Derrota al belisario Razuun, recupera sus órdenes y después preséntate al Guardia de sangre Gulmok en la Aldea Sombraluna."}},
[10604] = {"Obtener acceso", nil, {"Llévale un control de teletransportador de la Legión al Guardia de sangre Gulmok de la Aldea Sombraluna. Sabemos que los forjadores de armas y los caminantes de cólera Mo'arg del Bastión de la Legión llevan estos instrumentos."}},
[10605] = {"Cita con Carendin", nil, {"Habla con Carendin Halgar en Entrañas."}},
[10606] = {"El Arte del Cuidado de los Atracadores Viles", nil, {"Mata al doctor Gutrick y consigue su llave. Usa la llave para abrir la caja fuerte del doctor para recuperar El Arte del Cuidado de los Atracadores Viles y llévale este libro a Plexi en el Punto de invasión: Cataclismo."}},
[10607] = {"Susurros del Dios Cuervo", nil, {"Mata al arakkoa Grishna para poder entender la palabra del Cuervo durante 2 minutos. Mientras estés bajo los efectos del hechizo, podrás encontrar y recibir la profecía de los cuatro santuarios."}},
[10608] = {"Limpieza de despellajadores", nil, {"Mata a 10 despellejadores de cristal."}},
[10609] = {"¿Qué fue primero? ¿El draco o el huevo?", nil, {"Reúne 3 esencias de draco protoinfernal, 3 esencias de draco adolescente y 3 esencias de draco adulto."}},
[10611] = {"El Arte del Cuidado de los Atracadores Viles", nil, {"Mata al Doctor Maleficus y consigue la llave del doctor. Usa la llave para abrir la caja fuerte del doctor, para recuperar El Arte del Cuidado de los Atracadores Viles y entrégale el libro a Nakansi, en el Punto de invasión: Cataclismo."}},
[10612] = {"El vil y la furia", nil, {"Usa una consola de control de atracador vil para controlar un atracador vil centinela. Destruye a sesenta infernales Forja Muerta antes de que falle tu atracador vil. Cuando hayas completado tu misión, informa a Plexi en el Punto de Invasión: Cataclismo."}},
[10613] = {"El vil y la furia", nil, {"Usa una consola de control de atracador vil para controlar un atracador vil centinela. Destruye a sesenta infernales Forja Muerta antes de que falle tu atracador vil. Cuando hayas completado tu misión, informa a Nakansi en el Punto de Invasión: Cataclismo."}},
[10614] = {"Susurros en el viento", nil, {"Busca a Leoroxx en la Aldea Mok'Nathal en las Montañas Filospada."}},
[10615] = {"La Foresta Ruuan", nil, {"Habla con Timeon en Soto Eterno, que está en la Foresta Ruuan, en las Montañas Filospada."}},
[10616] = {"<nyi>Breadcrumb", nil, nil},
[10617] = {"Capullos de Alaseda", nil, {"Recoge ocho capullos de Alaseda de las larvas de Alaseda.\n\nMata a la larva rápidamente o se convertirá en Alaseda, destruyendo el capullo."}},
[10618] = {"Las alas más suaves", nil, {"Recoge doce alas iridiscentes."}},
[10619] = {"La tribu Lengua de Ceniza", nil, {"El vindicador Aluumen quiere que vayas a las Ruinas de Baa'ri y mates a 3 cuidadores Lengua de ceniza, 4 guerreros Lengua de ceniza y 6 chamanes Lengua de ceniza.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10620] = {"La amenaza de Crestaespina", nil, {"Mata a diez acechadoras Crestaespina."}},
[10621] = {"El fragmento aterraillidari", nil, {"Lleva el fragmento aterraillidari a Ordinn Tronapuño en el Bastión Martillo Salvaje."}},
[10622] = {"Prueba de lealtad", nil, {"Mata a Zandras en la Jaula de la Guardiana, en el Valle Sombraluna y vuelve a ver a Sanoru."}},
[10623] = {"El fragmento aterraillidari", nil, {"Lleva el fragmento aterraillidari a Grokom Ojomuerte en la Aldea Sombraluna."}},
[10624] = {"Una de fantasmas", nil, {"La boticaria jefe Hildagard de la Aldea Sombraluna, en el Valle Sombraluna, quiere que le lleves 15 muestras de ceniza siempreardiente."}},
[10625] = {"Espectróculos", nil, {"La boticaria jefe Hildagard de la Aldea Sombraluna, en el Valle Sombraluna, quiere que mates a 12 zelotes de Sombraluna."}},
[10626] = {"Tomar las armas", nil, {"Ordinn Tronapuño en el Bastión Martillo Salvaje quiere que le lleves las gujas de Mazakradon y de Morgroron."}},
[10627] = {"Tomar las armas", nil, {"Grokom Ojomuerte, de la Aldea Sombraluna, quiere que le lleves la guja de Mazakradon y la guja de Morgroron."}},
[10628] = {"Akama", nil, {"Habla con Akama en la cámara oculta, en la Jaula de la Guardiana."}},
[10629] = {"Un trabajo que te cagas", nil, {"Usa el silbato de can manáfago para invocar un can de guardia vil. Dale un paseo al can de guardia vil y mata varios jabinfernos trastornados. Busca las llaves de la trituradora en los \"desechos\" del can de guardia vil. Devuélvele las llaves de la trituradora al supervisor Razelcraz en la mina al noroeste de Thrallmar."}},
[10630] = {"El subsuelo de Thrallmar", nil, {"Mata a Urga'zz y regresa junto al supervisor Razelcraz en la mina al noroeste de Thrallmar."}},
[10632] = {"Cortar tus dientes", nil, {"Consigue 5 dientes Faucedaga extra afilados."}},
[10633] = {"Teron Sanguino: Tradición y Leyenda", nil, {"La boticaria jefe Hildagard de la Aldea Sombraluna, en el Valle Sombraluna, quiere que encuentres el espíritu Sombraluna ancestral.\n\nSi pierdes tus espectróculos, la boticaria jefe Hildagard puede hacerte otro par."}},
[10634] = {"Adivinación: la armadura de Sanguino", nil, {"El espíritu Sombraluna ancestral en El Altar de las Sombras, al sureste del Valle Sombraluna, quiere que consigas la armadura de Sanguino."}},
[10635] = {"Adivinación: la capa de Sanguino", nil, {"El espíritu Sombraluna ancestral en El Altar de las Sombras, al sureste del Valle Sombraluna, quiere que consigas la capa de Sanguino."}},
[10636] = {"Adivinación: la clava de Sanguino", nil, {"El espíritu Sombraluna ancestral en El Altar de las Sombras, al sureste del Valle Sombraluna, quiere que consigas la clava de Sanguino."}},
[10637] = {"Una distracción necesaria", nil, {"La exarca Onaala quiere que vuelvas a las Ruinas de Karabor y encuentres una forma de liberar a Azaloth de sus captores.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10638] = {"NOT A QUEST", nil, {"Seek <NAME> in the <DISTRICT> of Silvermoon City."}},
[10639] = {"Yo, Teron Sanguino", nil, {"El espíritu Sombraluna ancestral te ha utilizado como si fueras un títere. Ahora que has conseguido los objetos que necesitaba, ha revelado su verdadera identidad, Teron Sanguino. Para liberarte de su posesión, debes hacer lo que te pide y matar a Karsius el vigía anciano.\n\nSi tienes éxito, vuelve a ver a la boticaria jefe Hildagard en la Aldea Sombraluna."}},
[10640] = {"Altruis", nil, {"Dirígete al oeste de Nagrand y encuentra a Altruis el Sufridor.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10641] = {"Contra la Legión", nil, {"Altruis el Sufridor quiere que consigas sangre recién extraída de una sacerdotisa de la cólera de la Base Forja: Gehenna en Tormenta Abisal. Derrámala en el suelo y mata al avatar de Sathal. Regresa junto a Altruis cuando hayas completado esta tarea."}},
[10642] = {"Un fantasma en la máquina", nil, {"Zorus el Justiciero en el Bastión Martillo Salvaje, en el Valle Sombraluna, quiere que le lleves 15 muestras de ceniza siempreardiente."}},
[10643] = {"Presagistas de Sombraluna", nil, {"Zorus el Justiciero en el Bastión Martillo Salvaje, en el Valle Sombraluna, quiere que mates a 12 presagistas de Sombraluna."}},
[10644] = {"Teron Sanguino: Tradición y Leyenda", nil, {"Zorus el Justiciero en el Bastión Martillo Salvaje,en el Valle Sombraluna, quiere que encuentres al espíritu Sombraluna ancestral.\n\nSi pierdes los espectróculos, Zorus el Justiciero te hará un nuevo par.|n"}},
[10645] = {"Yo, Teron Sanguino", nil, {"El espíritu Sombraluna ancestral te ha utilizado como si fueras un títere. Ahora que has conseguido los objetos que necesitaba, ha revelado su verdadera identidad, Teron Sanguino. Para liberarte de su posesión, debes hacer lo que te pide y matar a Karsius el vigía anciano.\n\nSi tienes éxito, vuelve a ver a Zorus el Justiciero en el Bastión Martillo Salvaje."}},
[10646] = {"El pupilo de Illidan", nil, {"Escucha el relato de Altruis."}},
[10647] = {"Se busca: Uvuros, plaga de Sombraluna", nil, {"Mata a Uvuros y recupera su crin ígnea. Vuelve junto al clamaguerras Sardon Cortecertero de la Aldea Sombraluna, en el Valle Sombraluna."}},
[10648] = {"Se busca: Uvuros, plaga de Sombraluna", nil, {"Mata a Uvuros y recupera su crin ígnea. Vuelve junto al clamaguerras Birramorro del Bastión Martillo Salvaje, en el Valle Sombraluna."}},
[10649] = {"El Libro de Nombres Viles", nil, {"Adéntrate en el Laberinto de las Sombras en Auchindoun y consigue el Libro de Nombres Viles de Negrozón el Incitador. Vuelve junto a Altruis, en Nagrand, después de completar esta tarea."}},
[10650] = {"Regresa junto a los Aldor", nil, {"Habla con la exarca Onaala del Altar de Sha'tar, en el Valle Sombraluna.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10651] = {"Hay que detener a Varedis", nil, {"La exarca Onaala quiere que vayas a las Ruinas de Karabor y mates a Alandien, Theras, Netharel y Varedis. Utiliza el Libro de Nombres Viles para debilitar a Varedis cuando use la metamorfosis. Vuelve a ver al exarca Onaala con el libro cuando hayas acabado.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
[10652] = {"Tras las líneas enemigas", nil, {"Habla con Veronia cuando quieras ir a la Forja de Maná Coruu. Cuando llegues, habla con Caledis Alba Brillante.\n\nCompletar misiones para los Arúspices hará que disminuya tu nivel de reputación con los Aldor."}},
[10653] = {"Marcas de Sargeras", nil, {"Lleva 10 marcas de Sargeras a Adyen el Celador de la Luz en la Ciudad de Shattrath.\n\nCompletar misiones para los Aldor hará que disminuya tu nivel de reputación con los Arúspices."}},
