[10717] = {"Robar a un ladrón", nil, {"Consigue 5 redes del Culto Vermis."}},
[10718] = {"Los espíritus tienen voces", nil, {"Habla con Rexxar en el Bastión Señor del Trueno, en las Montañas Filospada."}},
[10719] = {"¿Has conseguido la nota?", nil, {"Entrega la nota de cita al celador de árboles Chawn en Soto Eterno en las Montañas Filospada."}},
[10720] = {"Las criaturas más pequeñas", nil, {"Utiliza el tambor de pellejo de kodo en la madriguera de marmotas. Cuando estés controlando a la marmota, envenena los tres barriles del Campamento Machacasangre."}},
[10721] = {"Un jabalí para Grulloc", nil, {"Consigue el saco de Grulloc."}},
[10722] = {"Encuentro en el Aquelarre Alanegra", nil, {"Recolecta suficientes retales de los trajes de varios cultores del Culto Vermis para crear un disfraz de sobrestante. Usa el disfraz para asistir a la reunión con Kolphis Rangoscuro."}},
[10723] = {"Gorgrom el Comedragones", nil, {"Coge 3 Tótems repelentes de los ogros Peña'mok. Después, utiliza la trampa de Sablecrín en el altar de Gorgrom para colocar la trampa, invocar y matar a Gorgrom el Comedragones.\n\nCuando Gorgrom esté muerto, coloca los 3 Tótems repelentes cerca de su cadáver."}},
[10724] = {"Prisionero de los Aguja del Filo", nil, {"Mata a ogros Aguja del Filo para conseguir la llave de la jaula del dracoleón. Cuando la tengas, ve a la jaula y utiliza el silbato de Espíritu para invocar a Espíritu, que abrirá la jaula para liberar a Leokk."}},
[10725] = {"Eminencia ante El Ojo Violeta", nil, {"Entrega tu sello Violeta a la archimaga Leryda en el Paso de la Muerte tras obtener reputación exaltada con El Ojo Violeta."}},
