[11538] = {"La batalla por el Arsenal de Tramo del Sol", nil, {"El presagista Inuuro quiere que mates a 6 demonios de la Legión Ardiente y al emisario del Odio en La Plaza Crepuscular o en el Arsenal de Tramo del Sol. Usa el estandarte de Sol Devastado para empalar el cadáver del emisario del Odio."}},
[11539] = {"Tomar el puerto", nil, {"El Magister Ilastar en el Arsenal de Tramo del Sol quiere que mates a 6 invocadores Hojalba, a 6 Caballeros de sangre Hojalba y a 3 tiradores Hojalba."}},
[11540] = {"Aplasta a los Hojalba", nil, {"El Magister Ilastar en el Arsenal de Tramo del Sol quiere que mates a 6 invocadores Hojalba, a 6 Caballeros de sangre Hojalba y a 3 tiradores Hojalba."}},
[11541] = {"Perturbar la Costa Branquia Verde", nil, {"La capitana Valindria quiere que liberes a 10 esclavos Branquia Verde. Vuelve junto a ella a bordo del Orgullo de Lunargenta en el Puerto de Tramo del Sol cuando hayas terminado."}},
[11542] = {"Interceptar a los refuerzos", nil, {"El Vindicador Kaalan del Arsenal de Tramo del Sol quiere que hables con Ayren Rompenubes y que vueles sobre la flota de refuerzo de Hojalba. Usa el aceite en llamas para incendiar las velas de los barcos mientras los sobrevuelas y cuando aterrices, mata a 6 reservistas Hojalba."}},
[11543] = {"Mantén al enemigo a raya", nil, {"El Vindicador Kaalan del Arsenal de Tramo del Sol quiere que hables con Ayren Rompenubes y que vueles sobre la flota de refuerzo de Hojalba. Usa el aceite en llamas para incendiar las velas de los barcos mientras los sobrevuelas y cuando aterrices, mata a 6 reservistas Hojalba."}},
[11544] = {"Armamentos de Ata'mal", nil, {"La herrera Hauthaa necesita que crees 5 metales de Ata'mal purificados. Puedes crearlos destruyendo armamentos de Ata'mal en su yunque en el Arsenal de Tramo del Sol en la Isla de Quel'Danas."}},
[11545] = {"Una donación caritativa", nil, {"La anacoreta Ayuri de Tramo del Sol, en la Isla de La Fuente del Sol, quiere que dones 10 oros."}},
[11546] = {"Abierto al negocio", nil, {"Mar'nah del Puerto de Tramo del Sol quiere que recojas 5 bayas de sangre de los arbustos de Quel'Danas."}},
[11547] = {"Conoce tus líneas Ley", nil, {"El astromante Darnarian, en el Sagrario de Tramo del Sol, quiere que uses el cristal de astromante en el cristal de sangre, el portal de La Plaza Crepuscular y el sagrario naga en la Costa Branquia Verde."}},
[11548] = {"Tu incesante apoyo", nil, {"La anacoreta Ayuri de Tramo del Sol, en la Isla de La Fuente del Sol, quiere que dones 10 oros."}},
[11549] = {"Un benefactor magnánimo", nil, {"El anacoreta Kairthos quiere que dones 1000 oros para ayudar a la causa de la anacoreta Ayuri. Si completas esta misión, serás conocido como Sharmon del Sol Devastado."}},
[11550] = {"Adelante, Falsario...", nil, {"Preséntate ante el archimago Ne'thul en la Isla de Quel'Danas, al norte de Quel'Thalas.\n\nLos maestros de vuelo de Forjaz y de Ciudad de Lunargenta te llevarán hasta tu destino."}},
