[11565] = {"El traje de muda", nil, {"Devuélvele al rey Mrgl-Mrgl su traje de muda. Lo encontrarás en el Refugio Aleta Invernal."}},
[11566] = {"¡De rendirse... nada!", nil, {"El rey Mrgl-Mrgl en el Refugio Aleta Invernal te ha pedido que le traigas una garra de Claximus."}},
[11567] = {"La antigua armadura de los Kvaldir", nil, {"Recupera la antigua armadura de los Kvaldir y entrégasela a Orfus de Komawa en el Fiordo Aquilonal."}},
[11568] = {"Volver a descansar en paz", nil, {"Usa el fardo de artefactos vrykul en los túmulos funerarios de Colina Escudo para devolver el corazón congelado de Isuldof, el escudo de los Aesirites, el bastón de la Furia de la Tormenta y la antigua armadura de los Kvaldir.\n\nVuelve con Orfus de Komawa en El Antiguo Elevador si lo consigues."}},
[11569] = {"El maestro de llaves Urmgrgl", nil, {"Glrglrglr en el interior de las Cavernas Aleta Invernal te ha pedido que consigas la llave de Urmgrgl."}},
[11570] = {"Escapa de las Cavernas Aleta Invernal", nil, {"Escolta a Lurgglbr hasta un lugar seguro y después dale la buena noticia al rey Mrgl-Mrgl en el Refugio Aleta Invernal."}},
[11571] = {"Aprendiendo a comunicarse", nil, {"El rey Mrgl-Mrgl te ha dado una caracola para que la uses sobre los restos de Escaldadora. Devuélvele la caracola del Rey llena en el Refugio Aleta Invernal."}},
