[11789] = {"Un soldado en apuros", nil, {"El médico Espino, a las afueras de la Fortaleza Denuedo, quiere que te adentres en la bodega de El Rompetormentas y recuperes el contraveneno de Espino de un cajón de suministros de primeros auxilios."}},
[11790] = {"Cultores entre nosotros", nil, {"Habla con el capitán \"Zurdo\" Vela al Tercio a bordo de El Rompetormentas en la Fortaleza Denuedo."}},
[11791] = {"Informa a Arlos", nil, {"Habla con el general Arlos dentro del cuartel en la Fortaleza Denuedo."}},
[11792] = {"Enemigos de la Luz", nil, {"El presagista Vurenn quiere que luches contra los cultores necrólitos cerca de Lindeallá al norte de la Fortaleza Denuedo y les arrebates el comunicado de cultor."}},
[11793] = {"Más investigación", nil, {"Lleva el comunicado de cultor a la vindicadora Yaala dentro de la posada en la Fortaleza Denuedo."}},
[11794] = {"La veda está abierta", nil, {"La vindicadora Yaala quiere que uses el óculo del exorcista en tres puntos diferentes de la Fortaleza Denuedo para descubrir y derrotar a los miembros del Culto de los Malditos."}},
[11795] = {"Protocolo de emergencia: sección 8.2, párrafo C", nil, {"Recupera 6 insignias de piloto de Palanqueta de los cuerpos de los pilotos de reconocimiento de Palanqueta y devuélveselas a Iggy \"Barrena\" Conmutador en Las Pozas Escaldantes."}},
[11796] = {"Protocolo de emergencia: sección 8.2, párrafo D", nil, {"Iggy \"Barrena\" Conmutador quiere que hundas unos restos al este, unos restos al sur y unos restos al noroeste."}},
[11797] = {"El asedio", nil, {"El sargento Colinamartillo quiere que mates 6 reptadores de cripta en las Arenas de Nasam, a las afueras de la Fortaleza Denuedo."}},
[11798] = {"El maestro de palancas", nil, {"Busca el manual del maestro de palancas y vuelve con la cabeza de Mecazod junto a Palanqueta Todogás."}},
