[11866] = {"Las orejas de nuestros enemigos", nil, {"El archidruida Lathorius en el Campamento D.E.H.T.A. en la Tundra Boreal ha dicho que te recompensará si le traes 15 orejas de lacayo de Nesingwary."}},
[11867] = {"No tenemos suficientes orejas...", nil, {"El archidruida Lathorius en el Campamento D.E.H.T.A. en la Tundra Boreal ha dicho que te recompensará si le traes 15 orejas de lacayo de Nesingwary."}},
[11868] = {"La llegada de la Asesina", nil, {"Zaza en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que mates a Karen la Asesina de Caribús."}},
[11869] = {"Cada almeja con su pareja", nil, {"La hierofante Cenius en el Campamento D.E.H.T.A. en la Tundra Boreal quiere que mates a 10 buzos enloquecidos despojadores."}},
[11870] = {"El Tramo Abandonado", nil, {"Busca a la hierofante Liandra en El Tramo Abandonado."}},
[11871] = {"No durante nuestra guardia", nil, {"La hierofante Liandra en El Tramo Abandonado en la Tundra Boreal quiere que recuperes 12 envíos de piezas de animales."}},
