[12018] = {"Dulce venganza", nil, {"Consigue dos trozos de piedra de gárgola de gárgolas carroñeras en el este de Los Campos de Carroña. Vuelve con Lord Bevis cuando termines."}},
[12019] = {"Los últimos ritos", nil, {"Ve hasta la Ciudad Templo de En'kilah y encuentra el orbe de teletransporte que se encuentra bajo la ciudadela flotante de la Plaga en Naxxanar. Úsalo para alcanzar el punto más alto y ayudar allí a Thassarian."}},
[12020] = {"Ese día que estaba borracho...", nil, {"Habla con Boxey Bolahuso en el campamento de la Fiesta de la cerveza."}},
[12021] = {"Una carta a casa", nil, {"Lleva la carta a casa a Lady Alustra a Los Campos de Carroña del este."}},
[12022] = {"¡Traga y tira!", nil, {"Utiliza la jarra de degustación de la Fiesta de la cerveza de obsequio para golpear a la G.U.A.O. 5 veces, y luego ve a hablar con Boxey Bolahuso."}},
[12024] = {"Registro de los fallecidos", nil, {"Busca documentos de Acuerdo de la Alianza entre los esqueletos de Campos de Carroña."}},
[12025] = {"Un Escarlata entre nosotros", nil, {"Busca pistas sobre la Cruzada Escarlata sobre los necrófagos carroñeros recién levantados en Los Campos de Carroña. Vuelve a ver a Lady Alustra con cualquier información que encuentres."}},
[12026] = {"El diario deteriorado", nil, {"Recupera 8 hojas de diario desaparecidas, completa con ellas el diario incompleto y devuélvele el diario completo a la sabia Paluna en el Campamento Oneqwah."}},
[12027] = {"La arriesgada aventura de Floppy", nil, {"Ayuda a Emily y a Floppy a volver al Campamento de la Brigada de los Páramos de Poniente."}},
[12028] = {"Visión espiritual", nil, {"Habla con Toalu'u el Místico en el Puerto Moa'ki una vez que hayas alcanzado tu visión espiritual."}},
